import { Observable } from 'rxjs';
import { Epic } from 'redux-observable';
import { StoreState, Action, ActionStatus, Thread, MessageBody, ShareLink, Draft } from '@src/types/index';
import {
  SharedThread, Comment, SharedThreadActivity,
  SharedThreadActivityType, SharedThreadPermissionScope,
} from '@src/types/sharedThreads';
import { Deps } from '@src/lib/epic';
import {
  SHARED_THREAD_CREATE,
  sharedThreadCreate,
  SHARED_THREAD_SELECT_BY_THREAD,
  sharedThreadSelectByThread,
  sharedThreadSelect,
  SHARED_THREAD_SELECT,
  SHARED_THREAD_LIST,
  sharedThreadList,
  sharedThreadOriginalFetch,
  SHARED_THREAD_ORIGINAL_FETCH,
  SharedThreadCreateMeta,
  SHARED_THREAD_UPDATE,
  sharedThreadUpdate,
  SHARED_THREAD_ASANA_TASK_LIST,
  sharedThreadAsanaTaskList,
  SharedThreadAsanaTaskCreateMeta,
  SHARED_THREAD_ASANA_TASK_CREATE,
  SHARED_THREAD_ACTIVITY_CREATE,
  SharedThreadActivityCreateMeta,
  sharedThreadActivityCreate,
  TTL,
  SHARED_THREAD_DELETE,
  sharedThreadDelete,
  SHARED_THREAD_DRAFTS_LIST,
  sharedThreadDraftsList,
  SHARED_THREAD_DRAFT_FETCH,
  sharedThreadDraftFetch,
  SharedThreadDraftFetchMeta,
} from '@src/ducks/sharedThreads';
import {
  getSharedThreadByThreadId,
  // getSharedThreadById,
  listSharedThreads,
  getSharedThreadById,
} from '@src/lib/sharedThread';
import { commentList, commentCreate, CommentCreateMeta } from '@src/ducks/comments';
import { DB_READY } from '@src/ducks/db';
import { threadFetch, threadSelect } from '@src/ducks/threads';
import { LOCATION_CHANGE, LocationChangeAction } from 'react-router-redux';
import { recordEvent, UserEvents } from '@src/lib/analytics';
import {
  sharedThreadMessageBodiesFetch,
  SharedThreadMessageBodiesFetchMeta,
} from '@src/ducks/messageBodies';
import { publicTeamFetch } from '@src/ducks/publicTeams';
import { shareLinkCreate, ShareLinkMeta } from '@src/ducks/shareLinks';
import { AsanaTask } from '@src/types/asana';
import { toastSet } from '@src/ducks/toasts';
import { asanaUserList } from '@src/ducks/asana';
import { selectAsanaEnabled } from '@src/ducks/integrations';
import { NavbarTab } from '@src/types/leftSidebar';
import browserHistory from '@src/lib/browserHistory';

const dbReadyTriggerListSharedThread: Epic<Action<null, SharedThread[]>, StoreState> = action$ =>
  action$
    .ofType(DB_READY)
    .map(() => sharedThreadList(null));

const locationChangeSelectSharedThread: Epic<
  Action<any, any>, StoreState, Deps
> = (action$, store, deps) =>
  action$
    .ofType(LOCATION_CHANGE)
    .debounceTime(200, deps.scheduler)
    .filter((action: any) => {
      const state = store.getState();
      const path = (action as LocationChangeAction).payload.pathname;
      if (path && state.selectedThread && path.endsWith(state.selectedThread.id)) {
        return false;
      }
      return true;
    })
    .map((action: any): string => {
      const split = (action as LocationChangeAction).payload.pathname.split('/');
      if (split.length > 2 && (split[1] === 'mentions' || split[1] === 'sharedThreads')) {
        return split[split.length - 1];
      }

      // Handle split for public shared threads
      if (split.length > 2 && ((split[1] === 'public' && split[2] === 'sharedThreads'))) {
        return split[3];
      }

      return '';
    })
    .flatMap((sharedThreadId) => {
      const state = store.getState();
      const actions: Array<Observable<Action<any, any>>> = [
        Observable.of(sharedThreadSelect(sharedThreadId ? sharedThreadId : null)),
      ];
      const loggedIn = !!state.me.data;

      if (loggedIn && !Object.keys(state.sharedThreads).length) {
        actions.push(Observable.of(sharedThreadList(null)));
      }

      return Observable.merge(
        ...actions,
      );
    });

const sharedThreadDraftsListEpic:
  Epic<Action<
    string,
    Draft
  >, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_DRAFTS_LIST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sharedThreads.listDrafts(action.meta)
          .map(drafts => sharedThreadDraftsList(action.meta, drafts[0]))
          .catch(err => Observable.of(sharedThreadDraftsList(action.meta, err))),
      );

const sharedThreadDraftFetchEpic:
  Epic<Action<
    SharedThreadDraftFetchMeta,
    Draft
  >, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_DRAFT_FETCH)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sharedThreads.getDraft(action.meta.sharedThread, action.meta.draft)
          .map(draft => sharedThreadDraftFetch(action.meta, draft))
          .catch(err => Observable.of(sharedThreadDraftFetch(action.meta, err))),
      );

const createSharedThreadEpic:
  Epic<Action<
    SharedThreadCreateMeta|null|CommentCreateMeta|string|ShareLinkMeta,
    SharedThread|Comment|ShareLink
  >, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_CREATE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<SharedThreadCreateMeta, SharedThread>) =>
        deps.sharedThreads.create({
          ...action.meta,
          permissions: {
            drafts: store.getState().preferences.defaultShareDraft,
            ...action.meta.permissions,
            scope: action.meta.permissions.scope || SharedThreadPermissionScope.Private,
          },
        })
          .do(sharedThread => recordEvent(UserEvents.ThreadShared, {
            team: sharedThread.team,
          }))
          .flatMap((sharedThread) => {
            const actions: Array<Observable<Action<
              SharedThreadCreateMeta | null | CommentCreateMeta | string | ShareLinkMeta,
              SharedThread | Comment | ShareLink
              >>> = [
                Observable.of(sharedThreadCreate(action.meta, sharedThread)),
                Observable.of(sharedThreadSelect(sharedThread.id)),
                ...(action.meta.comments || []).map(pending =>
                  Observable.of(commentCreate({
                    thread: sharedThread.id,
                    rfcId: pending.rfcId,
                    body: pending.body,
                    attachments: pending.attachments,
                  }))),
              ];

            if (action.meta.message) {
              actions.push(Observable.of(shareLinkCreate({
                sharedThread: sharedThread.id,
                message: action.meta.message,
              })));
            }
            return Observable.merge(
              ...actions,
            );
          })
          .catch(err => Observable.of(sharedThreadCreate(action.meta, err))),
      );

const selectSharedThreadByThreadEpic:
  Epic<Action<string|null, string|null|SharedThread|Comment[]>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_SELECT_BY_THREAD)
      .filter(action => action.status === ActionStatus.Start)
      .debounceTime(200, deps.scheduler)
      .flatMap((action: Action<string, string|null>) =>
        getSharedThreadByThreadId(action.meta)
          .map(sharedThreads =>
            sharedThreadSelect(sharedThreads.length ? sharedThreads[0].id : null))
          .catch(err => Observable.of(sharedThreadSelectByThread(action.meta, err))),
      );

// TODO(SHIN): Fetch SharedThread if does not exist in the state
// TODO(SHIN): Handle refetch policy for permissions change
const selectSharedThreadEpic:
  Epic<Action<string|null, SharedThread|Comment[]|Thread|null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_SELECT)
      .filter(action => action.status === ActionStatus.Start && !!action.meta)
      .debounceTime(200, deps.scheduler)
      .flatMap((action: Action<string, SharedThread>) =>
        getSharedThreadById(action.meta)
          .flatMap((sharedThread) => {
            if (!!sharedThread) {
              return Observable.of(sharedThread);
            }

            return deps.sharedThreads.get(action.meta);
          })
          .filter(sharedThread => !!sharedThread)
          .flatMap((sharedThread: SharedThread) => {
            const state = store.getState();
            const actions: Array<Observable<Action<any, any>>> = [
              Observable.of(sharedThreadSelect(sharedThread.id, sharedThread)),
              Observable.of(commentList(sharedThread.id)),
              Observable.of(sharedThreadAsanaTaskList(sharedThread.id)),
            ];

            const loggedIn = state.loggedIn;
            if (loggedIn && selectAsanaEnabled(state)) {
              actions.push(Observable.of(asanaUserList('')));
            }

            // TODO(SHIN): Make sure user selected check for threadSelect fetching is correct
            // TODO(SHIN): Make sure that the type of thread fetch is robust & performant
            const userOwned = state.me && state.me.data && state.me.data.id === sharedThread.user;
            if (loggedIn && userOwned && (!state.selectedThread ||
              state.selectedThread.id !== sharedThread.thread)) {
              actions.push(Observable.of(threadSelect(sharedThread.thread)));
            }

            // Always fetch original if not logged in or not on team
            // TODO(SHIN): Consider fetch mechanisms for permission changes
            const isNotOnTeam = !state.me || !state.me.data ||
              state.me.data.team !== sharedThread.team;
            const wasError = state.sharedThreads && state.sharedThreads[sharedThread.id] &&
              !!state.sharedThreads[sharedThread.id].error;
            if (!loggedIn || isNotOnTeam || wasError || !userOwned) {
              actions.push(Observable.of(sharedThreadOriginalFetch(sharedThread.id)));
            }

            // Push public team fetch as well
            if (isNotOnTeam && !state.publicTeams[sharedThread.team]) {
              actions.push(Observable.of(publicTeamFetch(sharedThread.id)));
            }

            // Handle drafts fetch if drafts exist
            if (!!sharedThread.drafts?.length) {
              actions.push(
                Observable.of(sharedThreadDraftsList(sharedThread.id)),
              );
            }

            return Observable.merge(
              ...actions,
            );
          })
          .catch(e => Observable.of(sharedThreadSelect(action.meta, e))),
      );

const listSharedThreadsEpic: Epic<Action<null, SharedThread[]>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_LIST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        listSharedThreads()
          .flatMap(sharedThreads =>
            Observable.merge(
              Observable.of(sharedThreadList(action.meta, sharedThreads)),
            ),
          ),
      );

const fetchSharedThreadOriginalEpic: Epic<
  Action<string | SharedThreadMessageBodiesFetchMeta, Comment[]|Thread|MessageBody[]|null|Error
>, StoreState, Deps> = (action$, store, deps) =>
  action$.ofType(SHARED_THREAD_ORIGINAL_FETCH)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action<string, Thread>) =>
      deps.sharedThreads.fetchOriginal(action.meta)
        .flatMap((thread) => {
          const state = store.getState();
          const actions: Array<Observable<Action<
            string|SharedThreadMessageBodiesFetchMeta, Comment[]|Thread|MessageBody[]|null|Error
          >>> = [
            Observable.of(threadFetch(thread.id, thread)),
            Observable.of(sharedThreadMessageBodiesFetch({ sharedThread: action.meta, thread })),
            Observable.of(commentList(action.meta)),
          ];

          const selectedSharedThread = state.sharedThreads[action.meta] &&
            state.sharedThreads[action.meta].data;
          const updateSelectedThread = (
            state.selectedThread && state.selectedThread.id === thread.id
          ) || selectedSharedThread && selectedSharedThread.thread === thread.id;

          // Updates the selected thread with the updated thread (w. new messages)
          if (updateSelectedThread) {
            actions.push(Observable.of(threadSelect(thread.id, thread)));
          }
          return Observable.merge(...actions);
        },
      ),
    );

export const deleteSharedThreadEpic:
  Epic<Action<string, null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_DELETE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sharedThreads.delete(action.meta)
          .do(() => {
            // Handle deselection behavior when user is in mentions or shared threads
            const navbarTab = store.getState().leftSidebar.selectedTab;
            switch (navbarTab) {
              case NavbarTab.Mentions:
                browserHistory.push('/mentions');
                break;
              case NavbarTab.SharedThreads:
                browserHistory.push('/sharedThreads');
                break;
              default:
                break;
            }
          })
          .flatMap(() => Observable.of(sharedThreadDelete(action.meta, null)))
          .catch(e => Observable.of(sharedThreadDelete(action.meta, e))),
      );

export const updateSharedThreadEpic:
  Epic<Action<SharedThread, SharedThread>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_UPDATE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sharedThreads.update(action.meta)
          .flatMap(sharedThread => Observable.of(sharedThreadUpdate(action.meta, sharedThread)))
          .catch(e => Observable.of(sharedThreadUpdate(action.meta, e))),
      );

const updateSharedThreadSuccessEpic:
  Epic<Action<SharedThread|string, SharedThread|Thread>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_UPDATE)
      .filter((action: Action<SharedThread, SharedThread>) => {
        const state = store.getState();
        return action.status === ActionStatus.Success && !!action.payload &&
          ((state.selectedThread && state.selectedThread.id === action.payload.thread) ||
          false);
      })
      .map((action: Action<SharedThread, SharedThread>) =>
        sharedThreadOriginalFetch(action.payload!.id));

const getAsanaTasks: Epic<Action<string|null|Error, AsanaTask[]|null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_ASANA_TASK_LIST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<string, AsanaTask[]>) =>
        deps.sharedThreads.getAsanaTasks(action.meta)
          .map(tasks => sharedThreadAsanaTaskList(action.meta, tasks))
          .catch(err => Observable.of(toastSet(err))),
      );

const createAsanaTask: Epic<
  Action<SharedThreadAsanaTaskCreateMeta|null|Error|string, AsanaTask|null|AsanaTask[]>,
  StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_ASANA_TASK_CREATE)
      .filter(action => action.status === ActionStatus.Start)
      .do(() => recordEvent(UserEvents.AsanaTaskCreated))
      .flatMap((action: Action<SharedThreadAsanaTaskCreateMeta, AsanaTask>) =>
        deps.sharedThreads.createAsanaTask(action.meta.sharedThread, action.meta.task)
          .map(task => sharedThreadAsanaTaskList(action.meta.sharedThread))
          .catch(err => Observable.of(toastSet(err))),
      );

const createSharedThreadActivity: Epic<
  Action<SharedThreadActivityCreateMeta|Error|null, null|SharedThreadActivity>,
  StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(SHARED_THREAD_ACTIVITY_CREATE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<SharedThreadActivityCreateMeta, null>) => {
        const state = store.getState();
        const user = state.me.data && state.me.data.id || state.sessionId.toString();

        return deps.sharedThreads.createActivity(
          action.meta.sharedThread,
          action.meta.type,
          user,
          TTL,
        )
        .map(() => sharedThreadActivityCreate(action.meta, null))
        .catch(e => Observable.of(toastSet(e)));
      });

// Fetches a draft based off a sharedThreadReply activity
const sharedThreadActivityHandlerEpic: Epic<
    Action<
    SharedThreadActivityCreateMeta|Error|null|SharedThreadDraftFetchMeta,
    null|SharedThreadActivity|Draft
  >, StoreState, Deps
  > = (action$, store, deps) =>
      action$.ofType(SHARED_THREAD_ACTIVITY_CREATE)
        .delay(2000)
        .filter((action: Action<any, SharedThreadActivity>) => {
          return action.status === ActionStatus.Success &&
            action.payload?.type === SharedThreadActivityType.Reply &&
            !!action.payload.draft && !!action.payload.sharedThread &&
            action.payload.sharedThread === store.getState().selectedSharedThread;
        })
        .flatMap((action: Action<any, SharedThreadActivity>) => {
          return Observable.of(sharedThreadDraftFetch({
            draft: action.payload?.draft || '',
            sharedThread: action.payload?.sharedThread || '',
          }));
        });

export default [
  createSharedThreadEpic,
  selectSharedThreadByThreadEpic,
  selectSharedThreadEpic,
  listSharedThreadsEpic,
  dbReadyTriggerListSharedThread,
  fetchSharedThreadOriginalEpic,
  locationChangeSelectSharedThread,
  updateSharedThreadSuccessEpic,
  updateSharedThreadEpic,
  deleteSharedThreadEpic,
  getAsanaTasks,
  createAsanaTask,
  createSharedThreadActivity,
  sharedThreadDraftsListEpic,
  sharedThreadDraftFetchEpic,
  sharedThreadActivityHandlerEpic,
];
