import React from 'react';
import ReactDataGrid from 'react-data-grid';
import moment from 'moment';
import { Resource } from '@src/types';
import {
  SequenceRecipient,
  SequenceStage,
  Sequence,
  SequenceRecipientStatus,
  SequenceRecipientFilter,
  SequenceRecipientSort,
  SequenceRecipientsState,
} from '@src/types/sequences';

import './SequenceRecipientsList.scss';
import SearchInput from '@src/components/Form/SearchInput/SearchInput';
import { cloneDeep, filter, values, compact, findIndex, capitalize, debounce } from 'lodash';
import RecipientNameCell from '@src/components/Cells/RecipientNameCell/RecipientNameCell';
import RecipientPausedCell from '@src/components/Cells/RecipientPausedCell/RecipientPausedCell';
import RecipientRepliedCell from '@src/components/Cells/RecipientRepliedCell/RecipientRepliedCell';
import RecipientStatusCell from '@src/components/Cells/RecipientStatusCell/RecipientStatusCell';
import {
  SequenceRecipientUpdateMeta, SequenceRecipientListMeta,
} from '@src/ducks/sequenceRecipients';
import Dropdown from '@src/components/Form/Dropdown';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import { RouteComponentProps } from 'react-router';

export interface StateProps {
  recipients?: { [recipient: string]: Resource<SequenceRecipient> };
  stages?: Array<Resource<SequenceStage>>;
  sequence?: Resource<Sequence>;
  recipientsState?: SequenceRecipientsState;
}

interface Props {
  onAddRecipients: () => void;
}

export interface DispatchProps {
  updateRecipient: (recipient: SequenceRecipientUpdateMeta) => void;
  deleteRecipient: (recipient: SequenceRecipient) => void;
  reactivateRecipient: (recipient: SequenceRecipient) => void;
  listRecipients: (meta: SequenceRecipientListMeta) => void;
}

export interface Row {
  activeProps: {
    recipientId: string;
    active: boolean | null,
    onPauseToggle: (id: string) => void,
  };
  name: { email: string, fullName: string };
  stage: number | string;
  nextStage: Date | string;
  opens: number | string;
  clicks: number | string;
  downloads: number | string;
  replied: boolean;
  status: string;
  isSelected: boolean;
}

interface State {
  rows: Row[];
  counts: { [filter: string]: number };
  confirmProps?: ConfirmProps;
}

const columns = [
  {
    key: 'activeProps',
    name: 'Active',
    width: 60,
    resizable: false,
    sortable: true,
    formatter: RecipientPausedCell,
  },
  {
    key: 'name',
    name: 'Recipient',
    resizable: false,
    sortable: true,
    formatter: RecipientNameCell,
  },
  {
    key: 'stage',
    name: 'Last Stage',
    width: 90,
    resizable: false,
    sortable: true,
  },
  {
    key: 'sent',
    name: 'Sent',
    width: 110,
    resizable: false,
    sortable: true,
  },
  {
    key: 'nextStage',
    name: 'Next Stage Will Send',
    resizable: false,
    width: 150,
    sortable: true,
  },
  {
    key: 'opens',
    name: 'Opens',
    cellClass: 'opens',
    width: 60,
    resizable: false,
    sortable: true,
  },
  {
    key: 'clicks',
    name: 'Clicks',
    cellClass: 'clicks',
    width: 60,
    resizable: false,
    sortable: true,
  },
  {
    key: 'downloads',
    name: 'Downloads',
    cellClass: 'download',
    width: 90,
    resizable: false,
    sortable: true,
  },
  {
    key: 'replied',
    name: 'Replied',
    width: 60,
    resizable: false,
    sortable: true,
    formatter: RecipientRepliedCell,
  },
  {
    key: 'status',
    name: 'Status',
    width: 90,
    resizable: false,
    sortable: true,
    formatter: RecipientStatusCell,
  },
];

const EMPTY_TEXT = {
  [SequenceRecipientFilter.Active]: 'No recipients are active in this sequence yet.',
  [SequenceRecipientFilter.Completed]: 'No recipients have completed this sequence yet.',
  [SequenceRecipientFilter.Paused]: 'No recipients are paused in this sequence.',
  [SequenceRecipientFilter.Bounced]: 'No recipients have bounced from this sequence.',
  [SequenceRecipientFilter.Delivered]: 'No recipients have been sent an email in this sequence yet',
  [SequenceRecipientFilter.Opened]: 'No recipients have opened a message in this sequence yet.',
  [SequenceRecipientFilter.Clicked]: 'No recipients have clicked any links in this sequence yet.',
  [SequenceRecipientFilter.Downloaded]:
    'No recipients have downloaded any attachments in this sequence yet.',
  [SequenceRecipientFilter.Replied]:
    'No recipients have replied to a message in this sequence yet.',
  [SequenceRecipientFilter.Unsubscribed]: 'No recipients have unsubscribed from this sequence.',
  [SequenceRecipientFilter.Failed]: 'No recipients have failed from this sequence.',
};

class SequenceRecipientsList extends React.PureComponent<
  StateProps & DispatchProps & Props & RouteComponentProps<any>,
  State
> {
  listRecipientsDebounce: any;

  constructor(props: StateProps & DispatchProps & Props & RouteComponentProps<any>) {
    super();

    this.state = {
      rows: props.recipients ?
        this.createRows(
          props.recipients,
          props.stages,
        )
        : [],
      counts: this.getCounts(props.sequence),
    };

    this.listRecipientsDebounce = debounce(props.listRecipients, 200);
  }

  UNSAFE_componentWillReceiveProps(nextProps: StateProps & DispatchProps & Props) {
    if (nextProps.recipients !== this.props.recipients
      || nextProps.sequence !== this.props.sequence) {

      this.setState({
        rows: nextProps.recipients ?
          this.createRows(
            nextProps.recipients,
            nextProps.stages,
          ) : [],
        counts: this.getCounts(nextProps.sequence),
      });
    }
  }

  componentWillUnmount() {
    if (this.listRecipientsDebounce) {
      this.listRecipientsDebounce.cancel();
    }
  }

  getCounts = (sequence?: Resource<Sequence>) => {
    if (!sequence || !sequence.data) {
      return {
        [SequenceRecipientFilter.All]: 0,
        [SequenceRecipientFilter.Active]: 0,
        [SequenceRecipientFilter.Completed]: 0,
        [SequenceRecipientFilter.Paused]: 0,
        [SequenceRecipientFilter.Bounced]: 0,
        [SequenceRecipientFilter.Delivered]: 0,
        [SequenceRecipientFilter.Opened]: 0,
        [SequenceRecipientFilter.Clicked]: 0,
        [SequenceRecipientFilter.Downloaded]: 0,
        [SequenceRecipientFilter.Replied]: 0,
        [SequenceRecipientFilter.Unsubscribed]: 0,
        [SequenceRecipientFilter.Failed]: 0,
      };
    }

    const recipients = sequence && sequence.data && sequence.data.recipients;
    const analytics = sequence && sequence.data && sequence.data.analytics;
    const recipientsNum = recipients ? recipients.active + recipients.completed +
      recipients.paused + recipients.bounced + recipients.failed : 0;
    return {
      [SequenceRecipientFilter.All]: recipientsNum,
      [SequenceRecipientFilter.Active]: recipients ? recipients.active : 0,
      [SequenceRecipientFilter.Completed]: recipients ? recipients.completed : 0,
      [SequenceRecipientFilter.Paused]: recipients ? recipients.paused : 0,
      [SequenceRecipientFilter.Bounced]: recipients ? recipients.bounced : 0,
      [SequenceRecipientFilter.Delivered]: analytics ? analytics.delivered : 0,
      [SequenceRecipientFilter.Opened]: analytics ? analytics.opened : 0,
      [SequenceRecipientFilter.Clicked]: analytics ? analytics.clicked : 0,
      [SequenceRecipientFilter.Downloaded]: analytics ? analytics.downloaded : 0,
      [SequenceRecipientFilter.Replied]: analytics ? analytics.replied : 0,
      [SequenceRecipientFilter.Unsubscribed]: analytics ? analytics.unsubscribed : 0,
      [SequenceRecipientFilter.Failed]: recipients ? recipients.failed : 0,
    };
  }

  handleRecipientPauseToggle = (id: string) => {
    const { recipients, updateRecipient } = this.props;

    const recipient = recipients && recipients[id] && recipients[id].data;
    if (recipient) {
      updateRecipient({
        id: recipient.id,
        sequence: recipient.sequence,
        paused: !recipient.paused,
      });
    }
  }

  createRows = (
    recipients: { [recipient: string]: Resource<SequenceRecipient> },
    stages?: Array<Resource<SequenceStage>>,
  ): Row[] => {

    return filter(recipients, r => !!r.data).map((resource) => {
      const r = resource.data!;
      let stage;

      if (stages) {
        stage = stages.find(s => s.data!.id === r.lastStage);
      }

      let nextStage = '-';
      if (r.scheduled && !r.paused && moment().isBefore(r.scheduled)) {
        nextStage = moment(r.scheduled).format('MMM D - h:mma');
      } else if (r.scheduled && !r.paused) {
        nextStage = 'Sending';
      }

      let status = capitalize(r.status);
      if (r.status === SequenceRecipientStatus.Unsubscribed) {
        status = 'Unsub';
      } else if (r.paused) {
        status = 'Paused';
      }

      let lastSent = '-';
      if (r.lastSent) {
        lastSent = moment(r.lastSent).fromNow();
      }

      const oldRow = this.getRowForRecipient(r.id);

      return {
        activeProps: {
          recipientId: r.id,
          active: r.status === SequenceRecipientStatus.Complete ||
            r.status === SequenceRecipientStatus.Bounced ? null : !r.paused,
          onPauseToggle: this.handleRecipientPauseToggle,
        },
        name: { email: r.email, fullName: `${r.givenName} ${r.familyName}` },
        stage: stage && stage.data ? stage.data.order : '-',
        sent: lastSent,
        nextStage,
        opens: r.analytics.opened || '-',
        clicks: r.analytics.clicked || '-',
        downloads: r.analytics.downloaded || '-',
        replied: !!(r.analytics.replied),
        status,
        title: `${r.givenName} ${r.familyName}`,
        isSelected: oldRow && oldRow.isSelected || false,
      };
    });
  }

  handleSearch = (query: string, complete: boolean) => {
    const { sequence, recipientsState } = this.props;

    if (sequence && sequence.data && recipientsState) {
      this.listRecipientsDebounce({
        query,
        sequence: sequence.data.id,
        filter: recipientsState.filter,
        sort: recipientsState.sort,
        desc: recipientsState.desc,
      });
    }
  }

  getColumns = () => {
    const { sequence } = this.props;
    const clonedColumns = columns.slice();

    if (sequence && sequence.data && sequence.data.paused) {
      return clonedColumns.filter(c => c.key !== 'nextStage');
    }

    return clonedColumns;
  }

  getRowAt = (index: number): object => {
    const { recipientsState, sequence } = this.props;
    const { rows } = this.state;

    if (index === rows.length - 1 && sequence && sequence.data &&
      recipientsState && recipientsState.offset !== rows.length) {
      this.listRecipientsDebounce({
        filter: recipientsState.filter,
        sequence: sequence.data.id,
        sort: recipientsState.sort,
        query: recipientsState.query,
        offset: rows.length,
        desc: recipientsState.desc,
      });
    }

    return rows[index];
  }

  getRowForRecipient = (recipientId: string): Row | undefined => {
    // Special handle case since state could be undefined when first setting rows
    if (!this.state) {
      return;
    }

    const { rows } = this.state;
    const index = findIndex(
      rows,
      row => row.activeProps.recipientId === recipientId,
    );
    return rows[index];
  }

  handleRowSelect = (selected: Array<{ rowIdx: number, row: Row }>) => {
    const { rows } = this.state;

    if (selected.length) {
      const newRows = cloneDeep(rows);
      selected.forEach(s => newRows[s.rowIdx].isSelected = true);
      this.setState({ rows: newRows });
    }
  }

  handleRowDeselect = (deselected: Array<{ rowIdx: number, row: Row }>) => {
    const { rows } = this.state;

    if (deselected.length) {
      const newRows = cloneDeep(rows);
      deselected.forEach(s => newRows[s.rowIdx].isSelected = false);
      this.setState({ rows: newRows });
    }
  }

  handleCheckedDelete = () => {
    const { recipients, deleteRecipient, sequence } = this.props;
    const { rows } = this.state;
    if (!recipients) {
      return;
    }

    // Grab all recipients
    const checkedRecipients = rows
      .filter(row => row.isSelected)
      .map(row => recipients && recipients[row.activeProps.recipientId] &&
        recipients[row.activeProps.recipientId].data);
    const flattenedRecipients = compact(checkedRecipients);

    // Handle deletion of all checked recipients
    const deleteHandler = () => {
      flattenedRecipients.forEach((recipient) => {
        deleteRecipient(recipient);
      });
      this.setState({
        confirmProps: undefined,
      });
    };

    // Confirm dialogue for deleting recipients
    if (checkedRecipients.length > 0) {
      const sequenceName = sequence && sequence.data && sequence.data.name || 'this sequence';
      const title = checkedRecipients.length > 1
        ? `Remove ${checkedRecipients.length} Recipients?`
        : 'Remove Recipient?';
      const recipientDescriptor = checkedRecipients.length > 1
        ? `these recipients`
        : 'this recipient';
      const message = `Are you sure you want to remove ${recipientDescriptor} from ${sequenceName}?`
      + ' Any analytics and recipients data will be lost forever';
      const confirmProps: ConfirmProps = {
        title,
        message,
        confirmCopy: 'Remove',
        cancelCopy: 'Cancel',
        onConfirm: () => deleteHandler(),
        onCancel: () => this.setState({
          confirmProps: undefined,
        }),
      };

      // Show confirm dialogue
      this.setState({ confirmProps });
    }
  }

  handleCheckedPause = () => {
    const { recipients, updateRecipient } = this.props;
    const { rows } = this.state;

    if (!recipients) {
      return;
    }

    // Grab all recipients
    const checkedRecipients = rows
      .filter(row => row.isSelected)
      .map(row => recipients && recipients[row.activeProps.recipientId] &&
          recipients[row.activeProps.recipientId].data);
    const flattenedRecipients = compact(checkedRecipients);

    flattenedRecipients.forEach((recipient) => {
      // Do not update if already paused
      if (recipient.paused) {
        return;
      }

      updateRecipient({
        id: recipient.id,
        sequence: recipient.sequence,
        paused: true,
      });
    });
  }

  handleCheckedUnpause = () => {
    const { recipients, updateRecipient } = this.props;
    const { rows } = this.state;

    // Grab all recipients
    const checkedRecipients = rows
      .filter(row => row.isSelected)
      .map(row => recipients && recipients[row.activeProps.recipientId] &&
        recipients[row.activeProps.recipientId].data);
    const flattenedRecipients = compact(checkedRecipients);

    flattenedRecipients.forEach((recipient) => {
      // Do not update if already unpaused
      if (!recipient.paused) {
        return;
      }
      updateRecipient({
        id: recipient.id,
        sequence: recipient.sequence,
        paused: false,
      });
    });
  }

  handleCheckedReactivate = () => {
    const { recipients, reactivateRecipient } = this.props;
    const { rows } = this.state;

    // Grab all recipients
    const checkedRecipients = rows
      .filter(row => row.isSelected)
      .map(row => recipients && recipients[row.activeProps.recipientId] &&
        recipients[row.activeProps.recipientId].data);
    const flattenedRecipients = compact(checkedRecipients);

    const confirmProps: ConfirmProps = {
      title: 'Reactivate these recipients?',
      message: 'Any recipients that had previously completed the sequence will receive the remaining stages.',
      onConfirm: () => {
        this.setState(
          { confirmProps: undefined },
          () => {
            flattenedRecipients.forEach((recipient) => {
              reactivateRecipient(recipient);
            });
          },
        );
      },
      onCancel: () => this.setState({ confirmProps: undefined }),
    };

    this.setState({ confirmProps });
  }

  handleGridSort = (col: string, sortBy: string) => {
    const { recipientsState, sequence } = this.props;
    const mapping = {
      activeProps: SequenceRecipientSort.Paused,
      name: SequenceRecipientSort.Name,
      stage: SequenceRecipientSort.LastStage,
      sent: SequenceRecipientSort.LastSent,
      nextStage: SequenceRecipientSort.Scheduled,
      opens: SequenceRecipientSort.Opened,
      clicks: SequenceRecipientSort.Clicked,
      downloads: SequenceRecipientSort.Downloaded,
      replied: SequenceRecipientSort.Replied,
      status: SequenceRecipientSort.Status,
    };

    if (recipientsState && sequence && sequence.data) {
      this.listRecipientsDebounce({
        query: recipientsState.query,
        sequence: sequence.data.id,
        filter: recipientsState.filter,
        sort: mapping[col],
        desc: sortBy === 'DESC',
      });
    }
  }

  handleFilterSelect = (value: string, index: number, id?: string) => {
    const { recipientsState, sequence } = this.props;
    const sequenceFilter = value.slice(0, value.indexOf('(') - 1);

    if (recipientsState && sequence && sequence.data) {
      this.listRecipientsDebounce({
        query: recipientsState.query,
        sequence: sequence.data.id,
        filter: sequenceFilter === 'All Recipients' ?
          SequenceRecipientFilter.All : sequenceFilter.toLowerCase() as SequenceRecipientFilter,
        sort: recipientsState.sort,
        desc: recipientsState.desc,
      });
    }
  }

  render() {
    const { onAddRecipients, recipientsState } = this.props;
    const { rows, counts, confirmProps } = this.state;
    const numChecked = rows.filter(row => row.isSelected).length;

    if (!recipientsState) {
      return <div />;
    }

    return (
      <div className="sequence-recipients-list">
        {confirmProps &&
          <Confirm
            {...confirmProps}
          />
        }
        <div className="header">
          <div className="filter">
            <Dropdown
              values={values(SequenceRecipientFilter)
                .map(d => `${
                  d === SequenceRecipientFilter.All ? 'All Recipients' : capitalize(d)
                } (${counts[d]})`)}
              onSelect={this.handleFilterSelect}
              startIndex={values(SequenceRecipientFilter)
                .map(d => d === SequenceRecipientFilter.All ? 'All Recipients' : d)
                // TODO: Need to fix filtering in SequenceDetail eventually
                .indexOf(recipientsState.filter as any)}
              value={`${
                recipientsState.filter === SequenceRecipientFilter.All ?
                'All Recipients' : capitalize(recipientsState.filter)
                } (${counts[recipientsState.filter.toLowerCase()]})`}
            />
          </div>
          {!!numChecked && <div className="action-bar">
            <span className="selected-num">
              {`${numChecked} selected`}
            </span>
            <button className="pause" onClick={this.handleCheckedPause}>Pause</button>
            <button className="unpause" onClick={this.handleCheckedUnpause}>Unpause</button>
            <button className="reactivate" onClick={this.handleCheckedReactivate}>
              Reactivate
            </button>
            <button className="delete" onClick={this.handleCheckedDelete}>Delete</button>
          </div>}
          <div className="search">
            <SearchInput
              onSearch={this.handleSearch}
              placeholder="Search recipients"
              defaultValue=""
            />
          </div>
        </div>
        <div className="table">
          {rows && !!rows.length && <ReactDataGrid
            enableCellSelect={true}
            columns={this.getColumns()}
            rowGetter={this.getRowAt}
            rowsCount={rows.length}
            enableRowSelect={true}
            rowHeight={60}
            minHeight={60 * 11}
            rowScrollTimeout={200}
            rowSelection={{
              selectBy: { isSelectedKey: 'isSelected' },
              showCheckbox: true,
              onRowsSelected: this.handleRowSelect,
              onRowsDeselected: this.handleRowDeselect,
            }}
            onGridSort={this.handleGridSort}
          />}
          {!rows || !counts[SequenceRecipientFilter.All] && <div className="empty">
            <div className="emoji"></div>
            <h2>No recipients yet</h2>
            <div className="subtitle">Add your first recipients to this sequence below!</div>
            <button className="add-recipients" onClick={onAddRecipients}>
              Add Recipients
            </button>
          </div>}
          {!rows.length && <div className="empty">
            {EMPTY_TEXT[recipientsState.filter]}
          </div>}
        </div>
      </div>
    );
  }
}

export default SequenceRecipientsList;
