import React from 'react';
import { Spring, animated, config } from 'react-spring';
import classnames from 'classnames';
import {
  Hotkeys, Preferences,
  KeyCode, OnboardingType, Action, Features, Account, Onboarding,
} from '@src/types';
import './OnboardingSectionDetail.scss';
import { Hotkey, HotkeyCode, HotkeyAction, hotkeysTypeahead, getHotkey, getHotkeyByName } from '@src/lib/hotkeys';
import RoundCheckmark from '@src/components/Icons/RoundCheckmark.svg';
import KeyboardSymbol from '@src/components/Base/KeyboardSymbol/KeyboardSymbol';
import { indexOf } from 'lodash';
import { createMailToDraft } from '@src/lib/draft';
import { draftCreate } from '@src/ducks/drafts';
import {
  hotkeyForOnboarding,
  randomizedReinforcement, onboardingRequiresThread,
  onboardingRequiresDraft,
} from '@src/lib/onboarding';
import { onboardingPatch } from '@src/ducks/me';
import { RouteComponentProps } from 'react-router';
import browserHistory from '@src/lib/browserHistory';
import { getOnboardingActions } from '@src/lib/OnboardingDispatcher';
import { Subscription } from 'rxjs';
import { IS_MAC } from '@src/lib/device';
import OnboardingTip, { AnchorPosition } from '@src/components/Onboarding/components/OnboardingTip/OnboardingTip';
import DemoVideoPreview from '@src/components/DemoVideo/DemoVideoPreview';
import { isFocusedOnEditableContent } from '@src/lib/utils';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface StateProps {
  accounts: Account[];
  features: Features;
  preferences: Preferences;
  onboarding: Onboarding;
  isDraft?: boolean;
  isThread?: boolean;
  allSteps: OnboardingType[];
}

interface State {
  hotkeyType: Hotkeys;
  transitionAnimation: boolean;
  hotkeyActivated: boolean;
  continueActivated: boolean;
  manualCompletionActivated: boolean;
  showOnboardingTip?: boolean;
  activatedHotkeys: {
    [key: string]: boolean,
  };
}

export interface DispatchProps {
  updatePreferences: (preferences: Preferences) => void;
  dispatchAction: (action: Action<any, any>) => void;
  claimCredit: (action: Partial<Onboarding>) => void;
  toggleMute: (action: boolean) => void;
}

export interface Props {
  currentStep?: OnboardingType;
  activeStep?: OnboardingType;
  onManualNext?: () => void;
  onLegendClick: () => void;
}

// tslint:disable-next-line:max-line-length
// TODO(SHIN): Introduction (Getting Started Information)
class OnboardingSectionDetail extends React.PureComponent<
  Props & StateProps & DispatchProps & RouteComponentProps<any>, State
> {

  timeout: NodeJS.Timer;

  onboardingSubscription: Subscription;

  constructor(props: Props & StateProps & DispatchProps & RouteComponentProps<any>) {
    super(props);

    this.state = {
      hotkeyType: props.preferences.hotkeys,
      transitionAnimation: false,
      hotkeyActivated: false,
      continueActivated: false,
      manualCompletionActivated: false,
      activatedHotkeys: {},
    };
  }

  // Observes firing of onboarding events through dispatcher mechanism (since unneeded by redux)
  componentDidMount() {
    this.setState({ transitionAnimation: false });
    document.body.addEventListener('keydown', this.observeKeyDown);

    if (this.props.currentStep === OnboardingType.SendMessage) {
      this.setState({ showOnboardingTip: true });
    }

    this.observeOnboardingEvents();
  }

  // Unmounts subscriptions and removes event listeners
  componentWillUnmount() {
    this.onboardingSubscription.unsubscribe();
    document.body.removeEventListener('keydown', this.observeKeyDown);
    clearTimeout(this.timeout);
  }

  // Handles Observing Onboarding Event that are manually completed
  observeOnboardingEvents = () => {
    this.onboardingSubscription = getOnboardingActions().subscribe({
      next: (a) => {
        const { activeStep, currentStep } = this.props;
        const { transitionAnimation } = this.state;

        // Do not observe actions if transition animation activated
        if (transitionAnimation) {
          return;
        }

        // Let default handling occur if active step is equal to current step
        if (activeStep === currentStep) {
          return;
        }

        // Mark completion if active step is true
        if (a === activeStep) {
          // Handle state updates for manual completions
          this.setState({
            transitionAnimation: true,
            continueActivated: false,
            manualCompletionActivated: true,
          });
        }
      },
    });
  }

  // Custom observation for key events to show intermediate step completions
  observeKeyDown = (e: KeyboardEvent) => {
    const { activeStep, isDraft, isThread } = this.props;
    const { transitionAnimation } = this.state;
    const hotkey = getHotkey(e);
    if (!activeStep) {
      return;
    }

    if (transitionAnimation && e.keyCode === KeyCode.Enter) {
      this.handleContinue();
      e.stopPropagation();
      e.preventDefault();
    }

    // Do not observe hotkeys when prerequisites have not been met
    if (activeStep && !transitionAnimation && !isThread && onboardingRequiresThread(activeStep)) {
      return;
    }

    // Do not observe hotkeys when prerequisites have not been met, except for draft create
    if (activeStep && !transitionAnimation && !isDraft && onboardingRequiresDraft(activeStep)) {

      // Don't activate new draft if in a editable element
      if (isFocusedOnEditableContent()) {
        return;
      }

      if (activeStep && hotkey?.name === HotkeyAction.NewDraft) {
        if (activeStep === OnboardingType.FollowUpReminder) {
          logAmplitudeEvent(AmplitudeEvents.TourStepTwo, { skipped: false });
        }

        this.handleDraftCreate();
        e.stopPropagation();
        e.preventDefault();
        return;
      }
    }

    const { activatedHotkeys } = this.state;

    // TODO(SHIN): Activate hotkey
    switch (activeStep) {
      case OnboardingType.NextThread:
      case OnboardingType.Archive:
        if (
          !activatedHotkeys[HotkeyAction.NextThread] &&
          [KeyCode.J, KeyCode.Down].indexOf(e.keyCode) > -1
        ) {
          const updated = {
            ...activatedHotkeys,
            [HotkeyAction.NextThread]: true,
          };
          this.setState({ activatedHotkeys: updated });
        }

        if (!activatedHotkeys[HotkeyAction.PreviousThread] &&
          [KeyCode.K, KeyCode.Up].indexOf(e.keyCode) > -1
        ) {
          const updated = {
            ...activatedHotkeys,
            [HotkeyAction.PreviousThread]: true,
          };
          this.setState({ activatedHotkeys: updated });
        }

        if (hotkey && activeStep && hotkeyForOnboarding(activeStep) === hotkey.name) {
          const updated = {
            ...activatedHotkeys,
            [hotkey.name]: true,
          };
          this.setState({ activatedHotkeys: updated });
        }

        break;
      default:
        if (hotkey && activeStep && hotkeyForOnboarding(activeStep) === hotkey.name) {
          const updated = {
            ...activatedHotkeys,
            [hotkey.name]: true,
          };
          this.setState({ activatedHotkeys: updated });
        }
    }
  }

  componentDidUpdate(oldProps: Props & StateProps & DispatchProps, oldState: State) {
    if (oldProps !== this.props) {
      this.setState({
        hotkeyType: this.props.preferences.hotkeys,
      });
    }

    const nextOnboarding = this.props.currentStep;
    if (nextOnboarding !== oldProps.currentStep) {
      this.setState({
        transitionAnimation: true, continueActivated: false,
      }); return;
    }
  }

  handleContinue = () => {
    const { manualCompletionActivated } = this.state;
    this.setState({ continueActivated: true, hotkeyActivated: true }, () => {
      clearTimeout(this.timeout);
      this.timeout = global.setTimeout(
        () => {
          this.setState(
            {
              transitionAnimation: false,
              activatedHotkeys: {},
              manualCompletionActivated: false,
            },
            () => {
              if (manualCompletionActivated) {
                this.handleManualNext();
              }
            },
          );
        },
        1000,
      );
    });
  }

  handleCreditClaim = () => {
    const { claimCredit } = this.props;
    claimCredit({ claimedCredit: true });
    logAmplitudeEvent(AmplitudeEvents.TourStepFourteen, { skipped: false });
  }

  handleDownloadNativeApps = () => {
    const { dispatchAction } = this.props;
    dispatchAction(onboardingPatch({
      type: { downloadNativeApps: true },
      skipped: false,
    }));
    logAmplitudeEvent(AmplitudeEvents.TourStepThirteen, { skipped: false });
    getOnboardingActions().next(OnboardingType.DownloadNativeApps);
    window.open('https://polymail.io/download?download=1&utm_source=polymail&utm_campaign=webapp', '_blank');
  }

  handleDraftCreate = () => {
    const { preferences, dispatchAction, accounts, features, activeStep } = this.props;

    if (preferences && activeStep && activeStep === OnboardingType.SaveAsTemplate) {
      const mailToString = 'Poly Bot <onboarding+extras@polymail.io>?subject= '
        + 'Saving a Message Template!&body=Hello! \n\n'
        + 'We\'ll be using this draft to create a template from a message you are creating.'
        + '\n\n'
        + 'Try following the instructions on the right to save this as a template!';
      browserHistory.push('/mail');
      const draft = createMailToDraft(accounts[0], preferences, features, mailToString);
      dispatchAction(draftCreate(draft));
      return;
    }

    if (preferences && activeStep && activeStep !== OnboardingType.SendMessage) {
      const mailToString = 'Poly Bot <onboarding+extras@polymail.io>?subject= '
        + 'Sending Messages in Polymail!&body=Hello! \n\n'
        + 'Let\'s learn some other features to supercharge your messages in Polymail.\n\n'
        + 'Check the right sidebar for more information! ------->';
      browserHistory.push('/mail');
      const draft = createMailToDraft(accounts[0], preferences, features, mailToString);
      dispatchAction(draftCreate(draft));
      return;
    }

    if (preferences) {
      const mailToString = 'Poly Bot <onboarding@polymail.io>?subject= '
        + 'Send your first message with tracking!&body=Hello! \n\n'
        + 'Polymail automatically gives you insights on the emails you send!'
        + '\n\n'
        + 'Try clicking send to see how it works!';
      browserHistory.push('/mail');
      const draft = createMailToDraft(accounts[0], preferences, features, mailToString);
      dispatchAction(draftCreate(draft));
    }
  }

  // Handles skipping sections
  handleSkip = () => {
    const { dispatchAction, currentStep } = this.props;
    if (dispatchAction && currentStep) {
      switch (currentStep) {
        case OnboardingType.NextThread:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { nextThread: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepSix, { skipped: true });
          return;
        case OnboardingType.Archive:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { archive: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepSeven, { skipped: true });
          return;
        case OnboardingType.ReadLater:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { readLater: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepNine, { skipped: true });
          return;
        case OnboardingType.Undo:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { undo: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepEight, { skipped: true });
          return;
        case OnboardingType.SaveAsTemplate:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { saveAsTemplate: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepFive, { skipped: true });
          return;
        case OnboardingType.SendMessage:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { sendMessage: true },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepOneSend, { skipped: true });
          return;
        case OnboardingType.SendLater:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { sendLater: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepTwo, { skipped: true });
          return;
        case OnboardingType.FollowUpReminder:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { followUpReminder: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepThree, { skipped: true });
          return;
        case OnboardingType.MessageTemplates:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { messageTemplates: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepFour, { skipped: true });
          return;
        case OnboardingType.ShareThread:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { shareThread: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepTen, { skipped: true });
          return;
        case OnboardingType.Comment:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { comment: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepEleven, { skipped: true });
          return;
        case OnboardingType.Scheduling:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { steps: { scheduling: true } },
          }));
          return;
        case OnboardingType.DownloadNativeApps:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { downloadNativeApps: true },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepThirteen, { skipped: true });
          return;
        case OnboardingType.ClaimCredit:
          dispatchAction(onboardingPatch({
            skipped: true,
            type: { claimedCredit: true },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepFourteen, { skipped: true });
          return;
        default: return;
      }
    }
  }

  renderStep = (h: Hotkey & HotkeyCode) => {
    const { activatedHotkeys } = this.state;
    const navigateInboxActivation = h.name === HotkeyAction.NavigateInbox &&
      browserHistory.location.pathname.startsWith('/mail');
    return (
      <div
        className="steps-wrapper"
        key={`${h.name}-onboarding-step`}
      >
        <div
          className="hotkeys"
        >
          <div
            className={
              classnames('hotkey', {
                activated: navigateInboxActivation || !!(activatedHotkeys[h.name]),
              })
            }
            key={h.name}
          >
            <div className="name">{h.name}</div>
            {!!h.metaKey && <KeyboardSymbol character={KeyCode.LeftWindowKey} />}
            {!!h.ctrlKey && <KeyboardSymbol character={KeyCode.Ctrl} />}
            {!!h.shiftKey && <KeyboardSymbol character={KeyCode.Shift} />}
            {!!h.altKey && <KeyboardSymbol character={KeyCode.Alt} />}
            {!!h.keyCode && <KeyboardSymbol character={h.keyCode} />}
            {!!h.altKeyCode && <span>/</span>}
            {!!h.altKeyCode && <KeyboardSymbol character={h.altKeyCode} />}
          </div>
        </div>
      </div>
    );
  }

  renderSteps = (step: OnboardingType) => {
    let hotkey: Hotkey & HotkeyCode | undefined;

    switch (step) {
      case OnboardingType.NextThread:
        // Identify counter
        const renderArray = [];
        const nextThreadAction = hotkeyForOnboarding(OnboardingType.NextThread);
        if (step && nextThreadAction) {
          hotkey = hotkeysTypeahead(nextThreadAction)[0] as Hotkey & HotkeyCode;
          if (hotkey) {
            renderArray.push(this.renderStep(hotkey));
          }
        }

        const previousThreadAction = hotkeyForOnboarding(OnboardingType.PreviousThread);
        if (step && previousThreadAction) {
          hotkey = hotkeysTypeahead(previousThreadAction)[0] as Hotkey & HotkeyCode;
          if (hotkey) {
            renderArray.push(this.renderStep(hotkey));
          }
        }

        return renderArray;

      default:

        // Identify counter
        const action = hotkeyForOnboarding(step);
        if (step && action) {
          hotkey = hotkeysTypeahead(action)[0] as Hotkey & HotkeyCode;
        }

        if (hotkey) {
          return this.renderStep(hotkey);
        }
    }

    return null;
  }

  renderDraftCreateStep = () => {
    const createDraft = HotkeyAction.NewDraft;
    const createDraftHotkey = getHotkeyByName(createDraft);
    if (!createDraftHotkey) { return null; }
    return (
      this.renderStep(createDraftHotkey as Hotkey & HotkeyCode)
    );
  }

  renderPrerequisiteStep = (step: OnboardingType) => {
    const { isDraft, isThread } = this.props;

    // Draft Step
    if (onboardingRequiresDraft(step) && !isDraft) {
      const createDraft = HotkeyAction.NewDraft;
      const createDraftHotkey = getHotkeyByName(createDraft);
      if (!createDraftHotkey) { return null; }
      return (
        this.renderStep(createDraftHotkey as Hotkey & HotkeyCode)
      );
    }

    // Prerequisites for handling thread actions
    if (onboardingRequiresThread(step) && !isThread) {
      const navigateInbox = HotkeyAction.NavigateInbox;
      const navigateInboxHotkey = getHotkeyByName(navigateInbox);
      if (!navigateInboxHotkey) { return null; }

      const nextThread = HotkeyAction.NextThread;
      const nextThreadHotkey = getHotkeyByName(nextThread);
      if (!nextThreadHotkey) { return null; }
      return (
        [
          this.renderStep(navigateInboxHotkey as Hotkey & HotkeyCode),
          this.renderStep(nextThreadHotkey as Hotkey & HotkeyCode),
        ]
      );
    }

    return null;
  }

  renderCommandControlStep = (step: OnboardingType) => {
    let copy = '';
    const { transitionAnimation } = this.state;
    switch (step) {
      case OnboardingType.SaveAsTemplate:
        copy = 'Save as Template';
        break;
      case OnboardingType.ReadLater:
        copy = 'Tomorrow Morning';
        break;
      case OnboardingType.ShareThread:
        copy = 'Share Conversation';
        break;
      case OnboardingType.SendLater:
        copy = '5 mins';
        break;
      case OnboardingType.FollowUpReminder:
        copy = '2 days';
        break;
      case OnboardingType.Scheduling:
        copy = 'Meeting Times';
        break;
      default:
        break;
    }

    if (!copy) {
      return null;
    }

    return (
      <div
        className={classnames('steps-wrapper', 'secondary')}
        key={`${step}-onboarding-step-command`}
      >
        <div
          className={classnames('hotkeys')}
        >
          <div
            className={classnames('hotkey', { activated: transitionAnimation })}
            key={`${step}-next-step`}
          >
            <span className="name">Type: <span className="bold">"{copy}"</span></span>
          </div>
        </div>
      </div>
    );
  }

  renderTitle = (h: OnboardingType | undefined) => {
    const { onboarding } = this.props;
    let c = 'You Are Unstoppable! ';
    if (h) {
      switch (h) {
        case OnboardingType.Archive:
          c = 'Marking things as done';
          break;
        case OnboardingType.ReadLater:
          c = 'Snoozing Emails';
          break;
        case OnboardingType.NextThread:
          c = 'Becoming a Polymail Master! ';
          break;
        case OnboardingType.Undo:
          c = 'Undoing Actions';
          break;
        case OnboardingType.Block:
          c = 'Blocking Senders';
          break;
        case OnboardingType.CommandLater:
          c = 'The Command Center';
          break;

        // Draft handlers

        case OnboardingType.SendMessage:
          c = 'Send Your First Message';
          break;

        case OnboardingType.SendLater:
          c = 'Scheduling Emails';
          break;
        case OnboardingType.FollowUpReminder:
          c = 'Never miss a Follow up!';
          break;
        case OnboardingType.MessageTemplates:
          c = 'Message Templates';
          break;
        case OnboardingType.SaveAsTemplate:
          c = 'Save as Template';
          break;
        case OnboardingType.Complete:
          c = 'Getting Started!';
          break;

        case OnboardingType.ShareThread:
          c = 'Sharing Conversations';
          break;

        case OnboardingType.Comment:
          c = 'Adding Comments';
          break;

        case OnboardingType.ActivityFeed:
          c = 'Activity Feed';
          break;

        case OnboardingType.Scheduling:
          c = 'Sharing Meeting Times';
          break;

        case OnboardingType.DownloadNativeApps:
          c = 'Download Desktop App';
          break;

        case OnboardingType.ClaimCredit:
          c = 'Claim Your $10 Credit!';
          if (onboarding.claimedCredit) {
            c = 'You\'ve claimed your $10 credit for Polymail!';
          }
          break;

        default:
          c = 'Section Complete!';
          break;
      }
    }
    return (
      <div className="copy">
        <h5 key={c}>{c}</h5>
      </div>
    );
  }

  renderInfoSection = (h: OnboardingType | undefined) => {
    const { isDraft, isThread } = this.props;
    // tslint:disable-next-line:max-line-length
    let c: string | JSX.Element = <span />;
    if (h) {
      switch (h) {
        case OnboardingType.Archive:
          // tslint:disable-next-line:max-line-length
          // tslint:disable-next-line:max-line-length
          c = <span> <br />When you're done with an email, always make sure to archive it.<br /><br />This keeps your inbox clean and full of only important tasks for your day!<br/><br/>Choose any email -- we'll teach you how to undo any action next!</span>;
          if (!isThread) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Let's start by going to your inbox and selecting a thread!<br/><br/>Feel free to use the suggested <b>hotkeys</b> or the <b>Command Center</b></span>;
          }
          break;
        case OnboardingType.ReadLater:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />You can read later an email when you don't need to handle it today. <br /><br />Try setting an email to read <b>tomorrow morning</b></span>;
          break;
        case OnboardingType.NextThread:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />Let's teach you how to get around Polymail quickly! <br /><br />Tap either of the keys below to move between threads!</span>;
          break;
        case OnboardingType.Undo:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />Wait!  <br /><br />If you took an action you didn't want, you can always bring the message back by tapping undo! <br/><br/> Try undoing the last action you just took</span>;
          break;
        case OnboardingType.Block:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />Have any emails you want to stop receiving? <br /><br /> We've made it simple to block or unsubscribe from anyone!</span>;
          break;
        case OnboardingType.CommandLater:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />If you ever forget a hotkey, we make it easy to find any action you'd like to perform!<br /> <br /> Try typing <b>Read Later</b> in the Command Center, then set an email to read <b>3 days</b> from now!</span>;
          break;

        // Draft Actions
        case OnboardingType.SendMessage:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />Polymail gives you tools to save time and get insights from the messages you send.<br/><br/>Try sending this example message to start.</span>;
          if (!isDraft) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Lets send your first message on Polymail!<br /><br /> Start by creating a new message!</span>;
          }
          break;

        case OnboardingType.SendLater:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />If you want to schedule to send later, you can choose a date and time to send in the future.<br /><br />Try scheduling an email for <b>5 minutes</b> from now</span>;
          if (!isDraft) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Learn how to supercharge the emails you send out!<br /><br /> Start by creating another message!</span>;
          }
          break;
        case OnboardingType.FollowUpReminder:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />Set a followup reminder on an email to get notified when you haven't received a reply within a set amount of time!<br /><br />Try setting a follow up reminder for <b>2 days</b> from now!</span>;
          if (!isDraft) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Learn how to supercharge the emails you send out!<br /><br /> Start by creating another message!</span>;
          }
          break;
        case OnboardingType.SaveAsTemplate:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />If you ever want to reuse a draft you can save it as a template!<br /> <br /> Try opening the Command Center and type <b>Save as Template</b> to save a draft as a template!</span>;
          const templateRoute = this.props.location.pathname.startsWith('/templates');
          if (!isDraft && !templateRoute) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Learn how to supercharge the emails you send out!<br /><br />Start by creating another message!</span>;
          }
          break;
        case OnboardingType.MessageTemplates:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />You can access any Message Templates you have created in the past to automatically fill in content and files for your new messages!<br/><br/>Try selecting a Message Template to insert into this draft!</span>;
          if (!isDraft) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Learn how to supercharge the emails you send out!<br /><br /> Start by creating another message!</span>;
          }
          break;
        case OnboardingType.ShareThread:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />You can share a live link to any of your conversations to provide context to your team.<br /><br />Try typing <b>Share Conversation</b> in the Command Center</span>;
          if (!isThread) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Let's start by going to your inbox and selecting a thread!<br/><br/>Feel free to use the suggested <b>hotkeys</b> or the <b>Command Center</b></span>;
          }
          break;

        case OnboardingType.Comment:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />Leave a comment to provide yourself or any teammates with additional information surrounding a conversation. <br /><br />It's useful for context when you visit a conversation in the future! Try to <b>post a comment</b> on one of your messages</span>;
          if (!isThread) {
            // tslint:disable-next-line:max-line-length
            c = <span> <br />Let's start by going to your inbox and selecting a thread!<br/><br/>Feel free to use the suggested <b>hotkeys</b> or the <b>Command Center</b></span>;
          }
          break;

        case OnboardingType.ActivityFeed:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />You can also check out our Activity Feed in the Right Sidebar to get a live feed of all the interactions with your sent emails so you can always follow up promptly!<br /><br />Try typing <b>Activity Feed</b> in the <b>Command Center</b></span>;
          break;

        case OnboardingType.Scheduling:
          // tslint:disable-next-line:max-line-length
          c = <span><br />If you're setting up a meeting, share your calendar availabilities, so your recipient can book a mutual time that works for them! <br/> <br /> Try typing <b>Meeting Times</b> in the Command Center</span>;
          break;

        case OnboardingType.ClaimCredit:
          // tslint:disable-next-line:max-line-length
          c = <span><br />Congratulations, you're all set up to hit the ground running in Polymail!</span>;
          break;

        case OnboardingType.Complete:
          c = <span><br />Great job! Moving onto the next section!</span>;
          break;

        case OnboardingType.DownloadNativeApps:
          // tslint:disable-next-line:max-line-length
          c = <span><br />Download the desktop client on Polymail for the best experience!<br/><br />Use Polymail with improved search, offline capabilities, and better performance!</span>;
          break;

        default:
          c = <span> <br />Great job! Now you're ready to manage your inbox at full speed!</span>;
          break;
      }
    }
    return (
      <div
        key={`info-${h}`}
        className="info-section"
      >
        <div className="copy">
          {c}
        </div>
      </div>
    );
  }

  renderSecondInfoSection = (h: OnboardingType | undefined) => {
    let c: JSX.Element | string = '';
    if (h) {
      switch (h) {
        case OnboardingType.ReadLater:
          // tslint:disable-next-line:max-line-length
          c = <span><br />At the date and time you choose, we'll pop that email to the top of your inbox! <br/><br/>You can also find these emails in your <b>Read Later</b> folder.</span>;
          break;
        case OnboardingType.Archive:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />Don't worry! You can find any email you archive by searching or going to your <b>Archive</b> folder.</span>;
          break;
        case OnboardingType.NextThread:
          // c = <span><br />Give it a try! </span>;
          break;
        case OnboardingType.PreviousThread:
          // c = <span><br />Let's keep it going! </span>;
          break;
        case OnboardingType.Undo:
          // c = <span><br />Try undoing the last action that you just took! </span>;
          break;
        case OnboardingType.Block:
          // tslint:disable-next-line:max-line-length
          c = <span><br />Try blocking any email and see what happens! Don't worry, we'll give you a chance to confirm if you really want to block someone! </span>;
          break;
        case OnboardingType.CommandLater:
          // tslint:disable-next-line:max-line-length
          c = <span><br />You can use the command center to find a hotkey, or you can type any action and perform it directly from there!</span>;
          break;
        case OnboardingType.SendLater:
          // tslint:disable-next-line:max-line-length
          c = <span><br /> Once you choose a time, we'll handle everything on our end to make sure your message gets sent at that point in time!<br /><br />You can find these drafts in your <b>Send Later</b> folder.</span>;
          break;
        case OnboardingType.FollowUpReminder:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />If you haven't received a reply by the time period you chose, we'll bring this email back into your inbox so you can take action on it!<br/> <br/>You can find these messages in your <b>Follow Up</b> folder</span>;
          break;

        case OnboardingType.MessageTemplates:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />You can save a ton of time by using pre-written responses to messages!</span>;
          break;
        case OnboardingType.ShareThread:
          // tslint:disable-next-line:max-line-length
          c = <span> <br />You can update permissions to anyone by clicking <b>Manage</b>.<br /><br />All the conversations you've shared can be found in the Left sidebar via <b>Shared By Me</b></span>;
          break;

        case OnboardingType.ClaimCredit:
          // tslint:disable-next-line:max-line-length
          c = <span><br />Claim your free $10 credit for Polymail below</span>;
          break;

        // case OnboardingType.Comment:
        //   // tslint:disable-next-line:max-line-length
        //   c = <span> <br />Try posting a comment below!</span>;
          break;
        case OnboardingType.Complete:
          c = '';
          break;
        default:
          c = '';
          break;
      }
    }
    return (
      <div
        key={`s-info-${h}`}
        className={classnames('info-section', 'secondary')}
      >
        <div className="copy">
          {c}
        </div>
      </div>
    );
  }

  handleClick = (e: React.MouseEvent<HTMLElement>) => {
    const threadList = document.getElementsByClassName('infinite-thread-list');
    const element = threadList[0];
    if (element && element instanceof HTMLElement) {
      element.focus();
    }
    e.stopPropagation();
    e.preventDefault();
  }

  renderNextStepCta = (counter: number) => {
    const { transitionAnimation, continueActivated } = this.state;
    if (!transitionAnimation) {
      return null;
    }

    return (
      <Spring
        config={config.slow}
        delay={1000}
        from={{ opacity: 0, t: 0 }}
        to={{
          opacity: !!transitionAnimation ? 1 : 0,
          t: !!transitionAnimation ? 1 : 0,
        }}
      >
        {checkAnimation => transitionAnimation && (
          <animated.div
            className={classnames('next-section-cta', { activated: continueActivated })}
            style={{
              opacity: checkAnimation.opacity,
              transform: checkAnimation.t.to({
                range: [0, 0.25, .5, 0.75, 1],
                output: [0.8, 1.05, 1.1, 1.05, 1.0],
              }).to(x => `scale(${x})`),
            }}
          >
            <div className="copy">
              {`${randomizedReinforcement(counter)} To continue, tap:`}
            </div>
            <KeyboardSymbol character={KeyCode.Enter} />
          </animated.div>
        )}
      </Spring>
    );
  }

  renderSucessCheckmark = () => {
    const { transitionAnimation } = this.state;
    return (
      <Spring
        config={config.gentle}
        from={{ opacity: 0, t: 0 }}
        to={{
          opacity: !!transitionAnimation ? 1 : 0,
          t: !!transitionAnimation ? 1 : 0,
        }}
      >
        {checkAnimation => transitionAnimation && (
          <animated.div
            style={{
              opacity: checkAnimation.opacity,
              transform: checkAnimation.t.interpolate({
                range: [0, 0.25, .5, 0.75, 1],
                output: [0.5, 1.1, 1.15, 1.1, 1.0],
              }).interpolate(x => `scale(${x})`),
            }}
            className="transition"
          >
            <RoundCheckmark className="check" height="20px" width="20px" />
          </animated.div>
        ) || null}
      </Spring>
    );
  }

  handleManualNext = () => {
    const { onManualNext } = this.props;
    if (onManualNext) {
      onManualNext();
    }
  }

  handleTipClear = () => {
    this.setState(
      { showOnboardingTip: false },
      () => {
        const threadList = document.getElementsByClassName('infinite-thread-list');
        const element = threadList[0];
        if (element && element instanceof HTMLElement) {
          element.focus();
        }
      });
  }

  renderVideoSection = (step: OnboardingType) => {
    const { transitionAnimation } = this.state;
    if (transitionAnimation) {
      return null;
    }

    switch (step) {
      case OnboardingType.SendMessage:
        return (
          <DemoVideoPreview
            demoVideo={{
              name: OnboardingType.SendMessage,
              thumbnailUrl: 'https://image.mux.com/GZCODZ01AaA6xlsgYZ7Oi2YkrPb6BUw01iRn300LGjQXBY/thumbnail.png?width=400&height=240&fit_mode=smartcrop&time=02',
              // tslint:disable-next-line:max-line-length
              url: 'https://stream.mux.com/GZCODZ01AaA6xlsgYZ7Oi2YkrPb6BUw01iRn300LGjQXBY/medium.mp4',
              captionUrl: 'https://stream.mux.com/GZCODZ01AaA6xlsgYZ7Oi2YkrPb6BUw01iRn300LGjQXBY/text/rfn9cC00nexhCueC4006VM6Zh01Bkc7qrKfk02Om2Ew57qn7o2mv6vqc5w.vtt',
            }}
            width={200}
            labelDisclosure
          />
        );
      case OnboardingType.SendLater:
        return (
          <DemoVideoPreview
            demoVideo={{
              name: OnboardingType.SendLater,
              thumbnailUrl: 'https://image.mux.com/HAUS2PxEgU2sOdr2ebmtpUS1Okec35LA5HDeZJ3TgXo/thumbnail.png?width=400&height=240&fit_mode=smartcrop&time=02',
              // tslint:disable-next-line:max-line-length
              url: 'https://stream.mux.com/HAUS2PxEgU2sOdr2ebmtpUS1Okec35LA5HDeZJ3TgXo/medium.mp4',
              captionUrl: 'https://stream.mux.com/HAUS2PxEgU2sOdr2ebmtpUS1Okec35LA5HDeZJ3TgXo/text/8ZKVfWpBliLkINpGG01IuUflQFqdlzDvwDNmKPcyk7612Q6Zxu3z01Kw.vtt',
            }}
            width={200}
            labelDisclosure
          />
        );
      case OnboardingType.ReadLater:
        return (
          <DemoVideoPreview
            demoVideo={{
              name: OnboardingType.ReadLater,
              thumbnailUrl: 'https://image.mux.com/E7OFu4Vl46Nu00QHXBvGV00Pg801qEF4lD01whcrw1X00KJs/thumbnail.png?width=400&height=240&fit_mode=smartcrop&time=02',
              // tslint:disable-next-line:max-line-length
              url: 'https://stream.mux.com/E7OFu4Vl46Nu00QHXBvGV00Pg801qEF4lD01whcrw1X00KJs/medium.mp4',
              captionUrl: 'https://stream.mux.com/E7OFu4Vl46Nu00QHXBvGV00Pg801qEF4lD01whcrw1X00KJs/text/X302Jcix6qYwlPAmx2Hclft4aIQh2Ob4y7yPWwxdDXnczpe7TgvTf02Q.vtt',
            }}
            width={200}
            labelDisclosure
          />
        );
      case OnboardingType.SaveAsTemplate:
        return (
          <DemoVideoPreview
            demoVideo={{
              name: OnboardingType.SaveAsTemplate,
              thumbnailUrl: 'https://image.mux.com/BJd8OerEh3oFN8JpgSBfT1VM4XYdF63mj4bg01aZ6s5Y/thumbnail.png?width=400&height=240&fit_mode=smartcrop&time=02',
              // tslint:disable-next-line:max-line-length
              url: 'https://stream.mux.com/BJd8OerEh3oFN8JpgSBfT1VM4XYdF63mj4bg01aZ6s5Y/medium.mp4',
              captionUrl: 'https://stream.mux.com/BJd8OerEh3oFN8JpgSBfT1VM4XYdF63mj4bg01aZ6s5Y/text/OXc6A25Q02VSY00WemQju1V01LC01dzIfxgmhLSYd7CcbjAPfzEpfanOrg.vtt',
            }}
            width={200}
            labelDisclosure
          />
        );
      case OnboardingType.MessageTemplates:
        return (
          <DemoVideoPreview
            demoVideo={{
              name: OnboardingType.MessageTemplates,
              thumbnailUrl: 'https://image.mux.com/VZuLnu1I00EPUR9llhLab526Mx4SK502qYS2jW1YrUfvg/thumbnail.png?width=400&height=240&fit_mode=smartcrop',
              // tslint:disable-next-line:max-line-length
              url: 'https://stream.mux.com/VZuLnu1I00EPUR9llhLab526Mx4SK502qYS2jW1YrUfvg/medium.mp4',
              captionUrl: 'https://stream.mux.com/VZuLnu1I00EPUR9llhLab526Mx4SK502qYS2jW1YrUfvg/text/G01VabuzAfELsamWpsD8HMJQkxvjOGWUw00Sl536o7SzGxGc00rZFPUUQ.vtt',
            }}
            width={200}
