          />
        );

      case OnboardingType.Scheduling:
        return (
          <DemoVideoPreview
            demoVideo={{
              name: OnboardingType.Scheduling,
              thumbnailUrl: 'https://image.mux.com/vhaIiRTZCuVwgq3zWLtuvY02fKi6gLKVvszyh4l01wpm00/thumbnail.png?width=400&height=240&fit_mode=smartcrop',
              url: 'https://stream.mux.com/vhaIiRTZCuVwgq3zWLtuvY02fKi6gLKVvszyh4l01wpm00/medium.mp4',
            }}
            width={200}
            labelDisclosure
          />
        );
      default:
        return null;
    }
  }

  render() {
    const {
      currentStep, isDraft, activeStep, isThread, allSteps, onboarding,
    } = this.props;
    const { transitionAnimation, manualCompletionActivated, showOnboardingTip } = this.state;

    const displayedStep = activeStep || currentStep;
    let counter = 0;
    let step: OnboardingType | undefined;

    // Identify counter
    const stepsLength = allSteps.length;

    // Set counter for what step we're on
    counter = displayedStep ? indexOf(allSteps, displayedStep) : -1;

    // If standard progression, subtract one from current Onboarding while in a transition step.
    if (!manualCompletionActivated && transitionAnimation) {
      counter = counter - 1;
    }

    // Match hotkey
    step = allSteps[counter];

    // Identify if prerequisites for step have to be filled
    let hideCopy = false;
    if (!transitionAnimation) {
      if (onboardingRequiresDraft(step) && !isDraft) {
        hideCopy = true;
      } else if (onboardingRequiresThread(step) && !isThread) {
        hideCopy = true;
      }
    }

    // Default state
    if (!step) {
      step = OnboardingType.Complete;
    }

    // Message for Onboarding Tip
    const onboardingMessage = (
      <span>
        We'll be sharing steps here to help you learn how to use Polymail most effectively!
        <br/>
        <br/>
        Complete all the steps and you'll receive a $10 credit to any paid plan!
      </span>
    );

    return (
      <div className="onboarding-section-detail">
        {showOnboardingTip && (
          <OnboardingTip
            title="Learn How to Use Polymail"
            message={onboardingMessage}
            right="274px"
            anchorPosition={AnchorPosition.Right}
            onNext={this.handleTipClear}
          />
        )}
        <div className="title">
          {this.renderTitle(step)}
        </div>
        <div className="progress">
          <div className="top">
            {counter > -1 &&
              <div className="copy">
                {`${counter} of ${stepsLength || 0} completed`}
              </div>
            }
            {counter === -1 &&
              <div className="copy">
                {`Great job! Moving on`}
              </div>
            }
            {this.renderSucessCheckmark()}
          </div>
          <div className="progress-bar">
            <div
              style={{
                width: `${ 100.0 * counter / stepsLength}%`,
              }}
              className="progress-bar-completed"
            />
          </div>
        </div>
        {this.renderInfoSection(step)}
        {!hideCopy &&
          this.renderSteps(step)
        }
        {!hideCopy && this.renderCommandControlStep(step)}
        {!transitionAnimation && !onboarding.claimedCredit &&
          activeStep === OnboardingType.ClaimCredit && // Show create draft button
          <button onClick={this.handleCreditClaim} className="create-draft">
            Claim Credit
          </button>
        }
        {!transitionAnimation &&
          activeStep === OnboardingType.DownloadNativeApps && // Show create draft button
          <button onClick={this.handleDownloadNativeApps} className="create-draft">
            {IS_MAC ? 'Download Mac App' : 'Download Windows App'}
          </button>
        }
        {hideCopy && // Show create draft button
          this.renderPrerequisiteStep(step)
        }
        {!hideCopy && transitionAnimation && // Do not show when draft is not open and draft section
          this.renderSecondInfoSection(step)
        }
        {this.renderNextStepCta(counter)}
        {
          this.renderVideoSection(step)
        }
        <div className="disclosure-area">
          <button onClick={this.props.onLegendClick}>
            See previous steps
          </button>
          {currentStep === activeStep && (
            <button
              className={classnames('skip-button', { hide: transitionAnimation })}
              onClick={this.handleSkip}
            >
              Skip Step
            </button>
          )}
        </div>
      </div>
    );
  }
}

export default OnboardingSectionDetail;
