import React from 'react';
import ClipboardJS from 'clipboard';
import {
  Calendar as CalendarType,
  Account,
  Features,
  Preferences,
  Me,
  Mailbox,
  MailCount,
  LeftSidebarCount,
  Action,
  CommandControlType,
  Onboarding,
  MailboxType,
  OnboardingType,
  Route as RouteType,
} from '@src/types';
import { Scheduling } from '@src/types/scheduling';
import NavigationHeader from '@src/routes/Settings/components/NavigationHeader';

import './ManageScheduling.scss';
import { isEmpty } from 'lodash';
import { RightSidebarTab, Page } from '@src/types/rightSidebar';
import { getHotkey, HotkeyAction } from '@src/lib/hotkeys';
import { commandControlShow, commandControlHide } from '@src/ducks/commandControl';
import LeftSidebar from '@src/components/LeftSidebar';
import CommandControl from '@src/components/CommandControl';
import Toast from '@src/components/Base/Toast';
import RightSidebar from '@src/components/RightSidebar';
import browserHistory from '@src/lib/browserHistory';
import { OnboardingPatchMeta } from '@src/ducks/me';
import { getOnboardingActions } from '@src/lib/OnboardingDispatcher';
import SchedulingChooser from './components/ManageSchedulingChooser';
import { Switch, Route } from 'react-router';
import routes from './routes';
import { recordEvent, UserEvents } from '@src/lib/analytics';
import Resizable from 're-resizable';
import { SchedulingOnboarding } from './components/SchedulingOnboarding';
import ContactSupport from '@src/components/ContactSupport';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface Props {
  scheduling?: Scheduling;
  calendars: { [account: string]: CalendarType[] };
  accounts: Account[];
  userSlug: string;
  features: Features;
  schedulingUrl?: string;
  preferences: Preferences;
  me?: Me;
  selectedMailbox: Mailbox;
  mailCount: MailCount;
  leftSidebarCount: LeftSidebarCount;
  rightSidebarTab: RightSidebarTab;
  rsbOnboarding?: OnboardingType;
  onboarding?: Onboarding;
  schedulings?: Scheduling[];
}

export interface DispatchProps {
  listCalendars: (account?: string) => void;
  listScheduling: () => void;
  dispatchAction: (action: Action<any, any>) => void;
  collapseRightSidebar: (collapsed: boolean) => void;
  updateOnboarding: (meta: OnboardingPatchMeta) => void;
  hideCommandControl: () => void;
}

const handleStyles = {
  width: '8px',
  height: '8px',
  borderRadius: '2px',
  backgroundColor: '#00aaff',
};

// TODO(SHIN): Connect Learn More Button with HREF for help center
// TODO(SHIN): Do applyDefaults
class ManageScheduling extends React.PureComponent<Props & DispatchProps, {}> {
  ref: HTMLDivElement;

  clipboard = new ClipboardJS('#copy-link');

  constructor(props: Props & DispatchProps) {
    super();
  }

  componentDidMount() {
    const {
      calendars,
      listCalendars,
      schedulings,
      listScheduling,
      collapseRightSidebar,
      updateOnboarding,
      rsbOnboarding,
      onboarding,
      dispatchAction,
    } = this.props;
    if (isEmpty(calendars)) {
      listCalendars();
    }

    if (!schedulings?.length) {
      listScheduling();
    }

    // Record any instance where scheduling url is copied
    recordEvent(UserEvents.SchedulingSettingsOpened);
    collapseRightSidebar(true);

    // Update onboarding
    if (onboarding && !onboarding.meetingTimesTabGem) {
      updateOnboarding({
        skipped: false,
        type: { meetingTimesTabGem: true },
      });
    }

    // Update scheduling onboarding
    if (rsbOnboarding && rsbOnboarding === OnboardingType.Scheduling &&
      onboarding && !onboarding?.steps?.scheduling) {
      updateOnboarding({
        skipped: false,
        type: { steps: { scheduling: true } },
      });
    }

    getOnboardingActions().next(OnboardingType.Scheduling);
    dispatchAction(commandControlHide(undefined));

    if (this.ref && document.activeElement === document.body) {
      this.ref.focus();
    }

    logAmplitudeEvent(AmplitudeEvents.ViewMeetingTimes);
  }

  handleRef = (ref: HTMLDivElement) => {
    this.ref = ref;
  }

  handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    const {
      collapseRightSidebar,
      rightSidebarTab,
      dispatchAction,
    } = this.props;
    let handled = false;

    const activeElement = document.activeElement;
    const ignoreInput = activeElement &&
      ['INPUT', 'TEXTAREA'].indexOf(activeElement.tagName) !== -1;

    if (!!ignoreInput) {
      return;
    }

    const hotkey = getHotkey(e);
    switch (hotkey && hotkey.name) {
      case HotkeyAction.NavigationCenter:
        this.props.dispatchAction(commandControlShow(CommandControlType.Navigation));
        handled = true;
        break;
      // Navigation
      case HotkeyAction.NavigateInbox:
      case HotkeyAction.NavigateStarred:
      case HotkeyAction.NavigateReadLater:
      case HotkeyAction.NavigateArchive:
      case HotkeyAction.NavigateTrash:
      case HotkeyAction.NavigateFollowUp:
      case HotkeyAction.NavigateSent:
      case HotkeyAction.NavigateSendLater:
      case HotkeyAction.NavigateDraft:
        if (hotkey && hotkey.route) {
          const newMailbox = { type: MailboxType.Any };
          browserHistory.push(hotkey.route(newMailbox));
          handled = true;
        }
        break;
      case HotkeyAction.KeyboardShortcutGuide:
        if (rightSidebarTab && hotkey) {
          if (rightSidebarTab === RightSidebarTab.KeyboardShortcut) {
            dispatchAction(hotkey.action(rightSidebarTab));
            collapseRightSidebar(true);
            handled = true;
          } else {
            dispatchAction(hotkey.action(rightSidebarTab));
            collapseRightSidebar(false);
            handled = true;
          }
        }
        break;
      case HotkeyAction.ToggleCommandControl:
        dispatchAction(commandControlShow(CommandControlType.Thread));
        handled = true;
        break;
      default:
        break;
    }

    if (handled) {
      e.preventDefault();
      e.stopPropagation();
    }
  }

  handleOnboardingCreate = () => {
    browserHistory.push('/calendars/new');
  }

  // TODO(SHIN): Prevent toggling of double booking if not on Teams+
  render() {
    const {
      selectedMailbox,
      mailCount,
      leftSidebarCount,
    } = this.props;
    const LeftSidebarConnect = LeftSidebar as any;

    // TODO(SHIN): Disable Checkboxes for Double Double Booking when feature bool not present
    return (
      <div
        className="app-manage-scheduling"
        tabIndex={-1}
        onKeyDown={this.handleKeyDown}
        ref={this.handleRef}
      >
        <LeftSidebarConnect
          accounts={[]}
          lists={[]}
          preferences={false}
          selectedTitle="calendar"
          selectedMailbox={selectedMailbox}
          mailCount={mailCount}
          leftSidebarCount={leftSidebarCount}
        />
        <div className="main-container">
          <ContactSupport />
          <NavigationHeader
            title="Meeting Times"
          />
          <div className="body-container">
            <Resizable
                handleStyles={{
                  bottomRight: handleStyles,
                  bottomLeft: handleStyles,
                  topRight: handleStyles,
                  topLeft: handleStyles,
                }}
                enable={{
                  top: false,
                  right: true,
                  bottom: false,
                  left: false,
                  topRight: false,
                  bottomRight: false,
                  bottomLeft: false,
                  topLeft: false,
                }}
                defaultSize={{
                  width: 300,
                  height: 'auto',
                }}
                minWidth={200}
              >
                <SchedulingChooser />
              </Resizable>
              <Switch>
                {routes.map((route: RouteType) => (
                  <Route
                    path={route.pathname}
                    component={route.component}
                    key={route.pathname}
                    exact
                  />
                ))}
                  <Route
                    path={'/calendars'}
                    key={'/calendars'}
                    exact
                  >
                    <FillerDiv onCreate={this.handleOnboardingCreate} />
                  </Route>
              </Switch>
            </div>
          <div className="bottom-container" />
          </div>
          <Toast />
          <RightSidebar page={Page.Threads} />
          <CommandControl default={CommandControlType.System} />
      </div>
    );
  }
}

interface EmptyProps {
  onCreate: () => void;
}

const FillerDiv = (props: EmptyProps) => (
  <div className="filler">
    <SchedulingOnboarding
      onEnable={props.onCreate}
    />
  </div>
);

export default ManageScheduling;
