} from '@src/types';

import { LOCATION_CHANGE, LocationChangeAction } from 'react-router-redux';

import { DB_READY } from '@src/ducks/db';

import {
  DRAFT_SELECT, draftSelect,
  DRAFT_FETCH, draftFetch,
  DRAFT_LIST, draftList,
  DRAFT_UPDATE, draftUpdate,
  DRAFT_CREATE, draftCreate,
  DRAFT_SEND,
  DRAFT_DELETE, draftDelete,
  DRAFT_ATTACHMENT_ADD,
  DRAFT_ATTACHMENT_REMOVE, draftAttachmentRemove,
  DRAFT_CREATE_CONFIGURATION,
  selectDraftForThread,
  selectDraft,
  DraftContactGroupMeta, DRAFT_CONTACT_GROUP_ADD,
  DRAFT_SEND_UNDO, draftSendUndo,
  DRAFT_TEMPLATE_APPLY,
  draftAttachmentsAppend,
  DRAFT_CALENDAR_INVITE_ADD,
  draftCalendarInviteAdd,
  draftCreateConfiguration,
  DraftConfigurationMeta,
  attachmentMetaCreate,
  DRAFT_ATTTACHMENTS_APPEND,
  draftAttachmentAdd,
  selectSelectedDraft,
  selectDraftForOptimisticThread,
  draftUpdateBroadcast,
  draftSend,
  DraftSendMeta,
  selectOptimisticMessagesForThread,
  draftSendNow,
  DRAFT_SEND_NOW,
  draftSendBroadcast,
  selectAttachmentMetasForResource,
  attachmentMetaUpdate,
  ATTACHMENT_META_HYDRATE,
  attachmentMetaHydrate,
} from '@src/ducks/drafts';

import { selectMessageBodies } from '@src/ducks/messageBodies';

import { DraftClient } from '@src/api/drafts';
import {
  createDraft,
  toThread,
  configureRecipients,
  createNewDraft,
  applyTemplateToDraft,
