} from '@src/lib/draft';
import {
  threadSingle,
  threadSelect,
  threadDelete,
  selectThread,
  threadCommitsSubmit,
} from '@src/ducks/threads';
import { toastSet, ConfirmationError, ConfirmationPositon } from '@src/ducks/toasts';
import { contactSelect, contactsAdd } from '@src/ducks/contacts';
import {
  getContactsByContactGroup,
  getContactsByRecipientType,
  defaultContact,
} from '@src/lib/contact';
import { getQueryVariable } from '@src/lib/query';
import { ApiError } from '@src/api/base';
import { recordEvent, UserEvents } from '@src/lib/analytics';
import {
  draftCreateCommit,
  draftDeleteCommit,
  draftUpdateCommit,
  fileUploadMeta,
  draftAttachmentAddCommit,
  draftForwardAttachmentsCommit,
