  submitCommit,
  draftSendNowCommit,
  draftAttachmentDeleteCommit,
} from '@src/lib/commit';
import { onboardingPatch, meFetch, OnboardingPatchMeta } from '@src/ducks/me';
import { RightSidebarMeta } from '@src/ducks/rightSidebar';
import { sharedThreadSelect } from '@src/ducks/sharedThreads';
import { SharedThread } from '@src/types/sharedThreads';
import { MeBody } from '@src/api/me';
import browserHistory from '@src/lib/browserHistory';
import { computeThread } from '@src/lib/thread';
import { routeFromMailbox, isDraftBox } from '@src/lib/mailbox';
