// DB ready triggers list of drafts
const dbReadyTriggerListDraft: Epic<Action<string, Draft[]>, StoreState> = action$ =>
  action$
    .ofType(DB_READY)
    .map(() => draftList(''));

const dbReadyHydrateMetas: Epic<Action<string|AttachmentMeta, AttachmentMeta|null>, StoreState>
= action$ => action$
    .ofType(DB_READY)
    .flatMap(() => Cache.list(
      Stores.Commits,
      (commit: Commit) =>
        [HistoryObject.DraftAttachment, HistoryObject.DraftAttachmentAppend]
          .indexOf(commit.resource) > -1 &&
        [CommitStatus.Pending, CommitStatus.Ready, CommitStatus.Error, CommitStatus.Processing]
          .indexOf(commit.status) > -1,
    ))
    .filter(commits => !!commits.filter(commit => !!commit.rehydrateMeta).length)
    .flatMap((commits) => {
      // Create rehydration metas based off append cases
      const metas: AttachmentMeta[] = flatten(
        compact(
          commits
            .filter((commit: Commit) => commit.status !== CommitStatus.Error)
            .map((commit: Commit) => commit.rehydrateMeta),
        ),
      );

      const failedRehydrateMetas: AttachmentMeta[] = flatten(
        compact(
          commits
            .filter((commit: Commit) => commit.status === CommitStatus.Error)
            .map((commit: Commit) => commit.rehydrateMeta),
        ),
      );

      const failedMetas = failedRehydrateMetas
        .map((rehydrateMeta: any) => {
          // Handle error checks
          let error = rehydrateMeta.error;
          const backupMessage = error && error.description || error.message ||
            'There was an issue attaching this file.' + ' Please remove it and try again!';
          if (!error || !(error instanceof Error)) {
            error = new Error(backupMessage);
          }

          return Observable.of(attachmentMetaCreate(rehydrateMeta, error));
        });

      // Merge all attachments into new metas
      return Observable.merge(
        ...metas.map(meta => Observable.of(attachmentMetaCreate(meta))),
        // tslint:disable-next-line:max-line-length
        ...failedMetas,
      );
    });

// Location change selects draft
const locationChangeSelectDraft:
  Epic<Action<string | DraftConfigurationMeta | Configuration, string|null|undefined>, StoreState> =
  (action$, store) =>
  action$
    .ofType(LOCATION_CHANGE)
    .map((action: any): string => {
      const split = (action as LocationChangeAction).payload.pathname.toLowerCase().split('/');
      if (split[3] === 'compose') {
        const mailto = getQueryVariable(
          'mailto',
          (action as LocationChangeAction).payload.search,
        );

        if (mailto && mailto.match(/^mailto:/)) {
          return mailto;
        }
      }

      if (split[1] === 'popout' && split[2] === 'drafts' && split.length > 3) {
        return split[split.length - 1];
      }

      if (split.length < 5) {
        return '';
      }

      if (['drafts', 'sendlater', 'outbox'].indexOf(split[3]) === -1) {
        return '';
      }

      return split[split.length - 1];
    })
    .filter(id => id !== '')
    .map((id) => {
      if (id.match(/^mailto:/)) {
        return draftCreateConfiguration({
          configuration: Configuration.MailTo,
          recipients: [id.slice('mailto:'.length)],
        });
      }
      return draftSelect(id);
    });

// Situationally hydrates a meta with a new object url if needed for attachment meta
export const hydrateAttachmentMetaEpic: Epic<
  Action<AttachmentMeta|Draft, AttachmentMeta|Draft>, StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(ATTACHMENT_META_HYDRATE)
      .filter(action => action.status === ActionStatus.Start)
      .filter((action: Action<AttachmentMeta, AttachmentMeta>) => !!action.meta.cloudUrl &&
        action.meta.cloudUrl.startsWith('blob:' || false),
      )
      .flatMap((action: Action<AttachmentMeta, AttachmentMeta>) => {
        const meta = action.meta;
        const request = meta.cloudUrl
          ? Observable.fromPromise(fetch(meta.cloudUrl))
          : Observable.of(null);

        // Make request to local fileUrl
        return request
          .catch((e) => {
            return Observable.of(null);
          })
          .filter(r => !r || r.status !== 200) // Do nothing if request was valid
          .flatMap((r) => {
            return Cache.list(
              Stores.FileUploads,
              (fm: FileUploadMeta) => fm.resourceId === meta.resourceId &&
                fm.attachmentId === meta.attachment,
              )
              .filter(fms => !!fms.length && !!fms[0].meta.file)
              .flatMap((fileMetas: FileUploadMeta[]) => {
                const fileMeta = fileMetas[0];
                if (meta.cloudUrl) {
                  URL.revokeObjectURL(meta.cloudUrl);
                }

                const url = URL.createObjectURL(fileMeta.meta.file);
                const updatedMeta: AttachmentMeta = { ...meta, cloudUrl: url };
                const state = store.getState();

                // If draft match and status is sending, send a draft update event
                if (state.selectedDraft === updatedMeta.resourceId) {
                  const draft: Resource<Draft>|undefined = selectSelectedDraft(state);

                  // TODO(SHIN): This currently causes race conditions with sending state reverting
                  if (draft && draft.data && isDraftSending(draft.data)) {
                    const updatedDraft = applyAttachmentToDraft(
                      draft.data,
                      convertToOptimistic(updatedMeta, draft.data.id),
                    );
                    return Observable.merge(
                      Observable.of(attachmentMetaUpdate(updatedMeta)),
                      Observable.of(draftUpdate(updatedDraft, updatedDraft)),
                    );
                  }
                }

                // Send attachment meta update if draft is not status sending
                return Observable.of(attachmentMetaUpdate(updatedMeta));
              });
          });
      });

export const draftSelectHydrateMetas: Epic<
  Action<string | AttachmentMeta, string | null | AttachmentMeta>, StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(DRAFT_SELECT)
      .filter(action => action.status === ActionStatus.Success)
      .flatMap((action: Action<string, string>) => {
        const state = store.getState();
        const draft: Resource<Draft> | undefined = selectSelectedDraft(state);
        const draftId = draft && draft.data && draft.data.id || '';

        const attachmentMetas: Array<Resource<AttachmentMeta>> = selectAttachmentMetasForResource(
          state,
          draftId,
        );

        const compactedMetas = compact(attachmentMetas.map(m => m.data));
        return Observable.merge(
          ...compactedMetas.map(m => Observable.of(attachmentMetaHydrate(m))),
        );
      });

export const selectDraftEpic:
  Epic<Action<string|null, string|Thread|null>, StoreState, Deps> = (action$, store, deps) =>
  action$.ofType(DRAFT_SELECT)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action<string, string|null>) => {
      const state = store.getState();
      const stateDraft = selectDraft(state, action.meta);
      const request: Observable<Draft|undefined> = (!!stateDraft && !!stateDraft.data)
        ? Observable.of(stateDraft.data)
        : Cache.get(Stores.Drafts, action.meta);
      return request
        .flatMap((result) => {
          if (result) {
            return Observable.of(result);
          }
          return deps.drafts.get(action.meta);
        })
        .flatMap((draft: Draft) => {
          const actions: Array<Observable<Action<any, any>>> = [
            Observable.of(draftSelect(draft.id, draft.id)),
          ];

          const mailbox = store.getState().mailbox;
          if (mailbox && isDraftBox(mailbox.type)) {
            actions.push(draft.to.length ?
              Observable.of(contactSelect(draft.to[0])) :
              Observable.of(contactSelect(undefined)));
          }

          // Inserts optimistic thread selectio nif in sending state
          if (!draft.threadV2 && isDraftSending(draft)) {
            const optimistic = toThread(draft, store.getState().accounts);
            actions.push(Observable.of(threadSelect(optimistic.id, optimistic)));
          } else if (draft.threadV2) {
            actions.push(Observable.of(threadSelect(draft.threadV2)));
          } else {
            actions.push(Observable.of(threadSelect('', null)));
          }

          return Observable.merge(...actions);
        });
    });

// Fetch draft fetches a single draft from the server
const fetchDraft: Epic<Action<string, Draft>, StoreState, Deps> = (action$, store, deps) =>
 action$.ofType(DRAFT_FETCH)
  .filter(action => action.status === ActionStatus.Start)
  .filter(action => !selectDraft(store.getState(), action.meta).data)
  .flatMap(action =>
    deps.drafts.get(action.meta)
      .map(draft => draftFetch(action.meta, draft))
      .catch(e => Observable.of(draftFetch(action.meta, e))),
  );

// List draft fetches all drafts from cache
const listDraft: Epic<Action<string, Draft[]>, StoreState> = action$ =>
 action$.ofType(DRAFT_LIST)
  .filter(action => action.status === ActionStatus.Start)
  .flatMap(action =>
    Cache.list<Draft>(Stores.Drafts)
      .map(drafts => draftList(action.meta, drafts))
      .catch(e => Observable.of(draftList(action.meta, e))),
  );

// Updates Draft in commits then broadcasts to worker
export const draftUpdateEpic: Epic<
Action<Draft, Draft|null>, StoreState, Deps
> = (action$, store, deps) =>
 action$.ofType(DRAFT_UPDATE)
  .filter(action => action.status === ActionStatus.Start)
  .flatMap((action) => {
    const updatedDraft: Draft = { ...action.meta, updated: deps.now() };
    return Cache.save(Stores.Commits, draftUpdateCommit(updatedDraft))
      .flatMap(() => {
        return Cache.save(Stores.Drafts, updatedDraft);
      })
      .map(() => draftUpdateBroadcast(updatedDraft))
      .catch((e: Error) => Observable.of(draftUpdate(updatedDraft, e)));
  });

// Appends any forward-attachments that need append after draft has been created
export const draftAttachmentsAppendEpic: Epic<Action<AttachmentMeta, any>, StoreState, Deps>
= (action$, store, deps) =>
  action$.ofType(DRAFT_ATTTACHMENTS_APPEND)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action) => {
      return Cache.save(Stores.Commits, draftForwardAttachmentsCommit(action.meta))
        .flatMap(() => Observable.of(draftAttachmentsAppend(action.meta, action.meta))
        .catch(e => Observable.of(draftAttachmentsAppend(action.meta, e))));
    });

// TODO(SHIN): Will need test for create and select since we lose type safety
// HACK(SHIN): Temporary solution for sending draft when it hasn't been updated to server
const draftCreateEpic: Epic<Action<any, any>, StoreState, Deps> = (action$, store, deps) =>
 action$.ofType(DRAFT_CREATE)
  .filter(action => action.status === ActionStatus.Start)
  .flatMap(action =>
    deps.drafts.create(action.meta)
      .catch(e => Cache.save(Stores.Commits, draftCreateCommit(action.meta)))
      .flatMap(() => Cache.save(Stores.Drafts, action.meta))
      .flatMap(() => {
        // return draftCreate(action.meta, draft);
        // TODO(SHIN): Observable.merge does not work for actions of different types
        // Create draft, select if thread has not been set
        const actions: Array<Action<any, any>> = [
          draftCreate(action.meta, action.meta),
        ];

        if (!action.meta.thread) {
          actions.push(draftSelect(action.meta.id));
        }

        // Merge actions by saving into draftCreateCommit
        return Observable.merge(...actions.map(each => Observable.of(each)));

      })
      .catch(e => Observable.of(draftCreate(action.meta, e))),
  );

export const draftCreateConfigurationEpic: Epic<Action<any, any>, StoreState> = (action$, store) =>
    action$
      .ofType(DRAFT_CREATE_CONFIGURATION)
      .filter(action => action.status === ActionStatus.Start)
      .filter((action) => {
        const state = store.getState();
        if (!state.accounts.length) {
          return false;
        }

        const existingThread = state.selectedThread;
        return action.meta === Configuration.New ||
          action.meta.configuration && action.meta.configuration === Configuration.MailTo ||
          !!(existingThread && selectMessageBodies(state, existingThread.id).length);
      })
      .do((action: Action<DraftConfigurationMeta, any>) => { // Track Move BCC as part of creation
        if (action.meta.configuration === Configuration.MoveBcc) {
          recordEvent(UserEvents.DraftCreated, { type: action.meta.configuration });
        }
      })
      .flatMap((action) => {
        // Identify if existing draft
        // TODO(SHIN): Have to identify existing draft if reclicking onto an optimistic thread
        const state = store.getState();
        const existingDraft = state.selectedDraft &&
          selectDraft(state, state.selectedDraft).data ||
          state.selectedThread && selectDraftForThread(state, state.selectedThread.id).data ||
          state.selectedThread && selectDraftForOptimisticThread(state, state.selectedThread.id);
        const { meta } = action;
        const configuration = typeof meta === 'string' ? meta : action.meta.configuration;

        // Show error prompt if draft new message and optimistic and reply-type configuration
        if (existingDraft && isDraftSending(existingDraft)
          && !existingDraft.threadV2
          && existingDraft.cancelBy && existingDraft.cancelBy > new Date()
          && (configuration === Configuration.Reply || configuration === Configuration.ReplyAll)
        ) {
          const secondsLeft = (existingDraft.cancelBy.getTime() - new Date().getTime()) / 1000;
          const roundedSeconds = Math.floor(secondsLeft);
          return Observable.of(
            toastSet(new ApiError(
              429,
              `Please wait ${roundedSeconds} seconds to reply on a new thread`,
            )),
          );
        }

        const existingThread = state.selectedThread;

        // If existing thread, message, and draft, update draft w. recipients
        if (existingDraft && existingDraft.smtp
          && existingThread && !isDraftSending(existingDraft)) {
          const messageIndex = findIndex(
            existingThread.messages,
            { rfcId: existingDraft.smtp },
          );

          const draftMessage = existingThread.messages[messageIndex];
          if (draftMessage) {
            const newRecipients = configureRecipients(
              configuration,
              draftMessage,
              state.accounts);
            switch (configuration) {
              case Configuration.MoveBcc:
              case Configuration.Reply:
              case Configuration.ReplyAll:
                const recipientsUpdatedDraft = { ...existingDraft, ...newRecipients };
                const description = configuration === Configuration.MoveBcc
                  ? 'Moved Sender to BCC'
                  : 'Updated Recipients';
                const replaceToast = new ConfirmationError(
                  description,
                  true,
                  ConfirmationPositon.BottomCenter,
                );
                return Observable.merge(
                  Observable.of(toastSet(replaceToast)),
                  Observable.of(draftUpdate(recipientsUpdatedDraft)),
                );
              default: break;
            }
          }
        }

        // Otherwise create new draft
        const newDraft = configuration === Configuration.New
           || configuration === Configuration.MailTo;
        const moveBcc = configuration === Configuration.MoveBcc;
        const thread = state.selectedThread;
        const actions = [];
        let selectedDraft: Draft;

        if (newDraft) {
          const defaultAccount = state.preferences.defaultAccount || state.accounts[0].id;
          const selectedAccount = state.mailbox.account || defaultAccount;
          const accountIndex = findIndex(state.accounts, { id: selectedAccount });
          const index = accountIndex !== -1 ? accountIndex : 0;
          selectedDraft = configuration === Configuration.New ? createNewDraft(
            state.accounts[index],
            state.preferences,
            state.features,
            typeof meta === 'string' ? undefined : meta.recipients,
          ) : createMailToDraft(
            state.accounts[index],
            state.preferences,
            state.features,
            typeof meta === 'string' ? '' : meta.recipients[0] || '',
          );
        } else {
          const allMessageBodies: MessageBody[] = selectMessageBodies(state, thread!.id);
          const lastMessage = thread && last(thread.messages);
          const lastMatchedMessageBody = lastMessage && find(
            allMessageBodies,
            body => body.id === lastMessage.id,
          );
          const lastMessageBody = lastMatchedMessageBody ||
            last(selectMessageBodies(state, thread!.id));
          selectedDraft = createDraft(
            thread!,
            configuration,
            typeof meta === 'string' || !meta.body
              ? lastMessageBody
              : meta.body,
            state.preferences,
            state.accounts,
            state.features,
            typeof meta === 'string' ? undefined : meta.recipients,
          );
        }

        if (selectedDraft) {
          actions.push(Observable.of(draftCreate(selectedDraft)));
        }

        // Show toast if sender moved to bcc
        if (moveBcc) {
          actions.push(Observable.of(
            toastSet(new ConfirmationError(
              'Moved Sender to BCC',
              true,
            )),
          ));
        }

        // Handle forwarding attachments by dispatching forward action
        // HACK(SHIN): Set cloudUrl to attachment.url as backup for forward attachment case
        // TODO(SHIN): Resolve potential race condition since even though the action for
        // DRAFT_CREATE && DRAFTATTACHMENTAPPEND is synchronous, the saving to cache may not be
        // TODO(SHIN): Need to figure out dedupe method so multiple append requests do not go out
        if (thread && configuration === Configuration.Forward) {
          const messageIndex = findIndex(thread.messages, { id: (meta.body && meta.body.id) });
          const message = thread.messages[messageIndex] || last(thread.messages);
          if (message && !!message.attachments.length) {
            const forwardAttachmentActions = message.attachments.map(attachment =>
              Observable.of(draftAttachmentsAppend({
                attachment: mongoId(),
                resourceId: selectedDraft.id,
                thread: thread.id,
                message: message.id,
                account: thread.account,
                filename: attachment.filename,
                length: attachment.length,
                cloudUrl: attachment.url,
                inline: attachment.inline,
              })));
            actions.push(...forwardAttachmentActions);
          }
        }

        return Observable.merge(...actions);
      });

const draftDeleteEpic:
  Epic<Action<Error|null|string, string|null|MeBody>, StoreState> = (action$, store) =>
  action$.ofType(DRAFT_DELETE)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action<string, null>) =>
      Cache.save(Stores.Commits, draftDeleteCommit(action.meta))
        .flatMap(() => Cache.deleteSingle(Stores.Drafts, action.meta))
        .flatMap(() => {
          const actions: Array<Observable<Action<any, any>>> = [
            Observable.of(draftDelete(action.meta, action.meta)),
          ];
          const state = store.getState();

          // check if referral has now been redeemed
          if (state.me.data && state.me.data.referrer && !state.me.data.referralRedeemed) {
            actions.push(Observable.of(meFetch('')));
          }

          return Observable.merge(
            ...actions,
          );
        })
        .catch(e => Observable.of(toastSet(e))),
    );

export const draftSendEpic: Epic<
  Action<DraftSendMeta|Draft|Error|null|OnboardingType|string|AttachmentMeta,
  Draft|null|Onboarding|SharedThread>,
  StoreState, Deps
> = (action$, store, deps) =>
  action$.ofType(DRAFT_SEND)
    .filter(action => action.status === ActionStatus.Start)
    // Track Message Sents
    .do((action: Action<DraftSendMeta, Draft>) => {
      recordEvent(UserEvents.MessageSent, {
        later: action.meta.draft.scheduled,
        followUp: !!action.meta.draft.waiting,
        track: action.meta.draft.track,
        calendarInvite: !!action.meta.draft.calendarInvite,
        template: action.meta.draft.template,
      });
    })
    .flatMap((action: Action<DraftSendMeta, Draft>) => {
      // Update send meta with updated delay property
      const isOnboarding = !store.getState().onboarding.sendMessage;
      const delay =  isOnboarding ? 0 : undefined;
      const state = store.getState();

      // Convert found attachment metas to optimistic attachments
      const metas = compact(selectAttachmentMetasForResource(state, action.meta.draft.id)
        .map((r: Resource<AttachmentMeta>) => r.data));
      const optimisticAttachments = metas
        .map((m: AttachmentMeta) => convertToOptimistic(m, action.meta.draft.id)) || [];

      // Create optimistic meta
      const sendMeta = deps.drafts.generateOptimistic(
        { ...action.meta, delay },
        optimisticAttachments,
      );
      const stagedDraft = sendMeta.meta.draft;
      const attachmentRemoveRequests = sendMeta.deletedAttachments
        .map(a => submitCommit(draftAttachmentDeleteCommit(stagedDraft.id, a.attachment)));

      // Start Commit submissions
      return Observable.of(stagedDraft)
        .flatMap(() => {
          const deleteHandler = !!attachmentRemoveRequests.length
            ? Observable.zip(...attachmentRemoveRequests)
            : Observable.of(null);
          return deleteHandler
            .flatMap(() => submitCommit(draftUpdateCommit(stagedDraft)))
            .flatMap(() => submitCommit(draftSendCommit(sendMeta.meta)))
            .flatMap(() => {
              const actions: Array<Observable<
                Action<Draft | DraftSendMeta | null | string | AttachmentMeta,
                Draft | SharedThread | null>
              >> = [
                ...sendMeta.deletedAttachments.map(
                  a => Observable.of(
                    draftAttachmentRemove({
                      resourceId: stagedDraft.id,
                      attachment: a.attachment,
                    }),
                  ),
                ),
                Observable.of(draftSend(
                  sendMeta.meta,
                  stagedDraft,
                )),
              ];

              // Also broadcast draft send events because these can happen from new window
              actions.push(Observable.of(draftSendBroadcast(sendMeta.meta)));

              // Clear shared thread if sending on a new draft
              if (!stagedDraft.threadV2) {
                actions.push(Observable.of(sharedThreadSelect(null)));
              }

              // Async save staged draft
              Cache.save(Stores.Drafts, stagedDraft).subscribe({
                error: (e) => {
                  captureException(e);
                  // tslint:disable-next-line:no-console
                  console.error(e);
                },
              });

              logAmplitudeEvent(AmplitudeEvents.SendEmail);

              // Merge actions and save draft async since we dont want to block on IndexedDB
              // We're losing a guarantee of Cache being consistent here though
              return Observable
                .merge(...actions);
            });
        })
        .catch(e => Observable.of(draftSend(action.meta, e)));
    });

// Handles undo send by emitting success, updating selectedThread and calling threadSingle
export const draftSendUndoEpic: Epic<Action<any, any>, StoreState> = (action$, store, deps) =>
 action$.ofType(DRAFT_SEND_UNDO)
  .filter(action => action.status === ActionStatus.Start)
  .do(() => recordEvent(UserEvents.UndoSend))
  .flatMap((action: Action<Draft, Draft>) => {

    // Optimistic version of draft
    const optimisticUndo: Draft = {
      ...action.meta,
      status: DraftStatus.Draft,
      attachments: action.meta.attachments.filter(a => !a.optimistic),
    };

    // API request if draft is not staged
    const sentRequest = deps.drafts.undoSend(action.meta);

    // Return end request
    return sentRequest
      .catch((e: Error) => {
        // Pass through typeError since we don't have to worry about offline race conditions
        const typeError = e instanceof TypeError;
        if (typeError) {
          return Observable.of(optimisticUndo);
        }

        throw e;
      })
      .do((draft: Draft) => { // Remove all draft send commit
        const commits = Cache.list(
          Stores.Commits,
          (commit: Commit) => commit.resourceId === draft.id && commit.route.endsWith('send'),
        );
        return commits.flatMap((results: Commit[]) => {
          const deleteIds = compact(results.map(r => r.id));
          return Cache.deleteBatch(Stores.Commits, deleteIds);
        }).subscribe({
          error: (e) => {
            captureException(e);
            // tslint:disable-next-line:no-console
            console.error(e);
          },
        });
      })
      .flatMap((d: Draft) => {
        const draft = {
          ...d,
          body: optimisticUndo.body,
          subject: optimisticUndo.subject,
        };
        const actions: Array<Observable<any>> = [
          Observable.of(draftSendUndo(action.meta, draft)),
        ];

        if (draft.threadV2) {
          actions.push(Observable.of(threadSelect(draft.threadV2)));
        }

        if (!draft.threadV2) {
          actions.push(Observable.of(threadDelete(`<${draft.id}@polymail.io>`, null)));
        }

        // Handle Emission of thread update to remove any optimistic messages
        if (draft.threadV2) {
          const existingThread: Thread | undefined = selectThread(store.getState(), draft.threadV2);
          const existingThreadAction = (existingThread
            ? Observable.of(existingThread)
            : Cache.get(Stores.Threads, existingThread))
            .filter(thread => !!thread)
            .flatMap((thread) => {
              const updatedThread = {
                ...thread,
                messages: thread.messages
                  .filter((message: Message) => message.rfcId !== `<${draft.id}@polymail.io>`),
              };

              if (draft.waiting && updatedThread.waiting) {
                delete updatedThread.waiting;
              }

              return Observable.of(threadSingle(updatedThread));
            });
          actions.push(existingThreadAction);
        }

        // HACK(SHIN): Want to persist to cache if no changes made
        // (async to allow UI to be non-blocked)
        Cache.save(Stores.Drafts, draft).subscribe({
          error: (e) => {
            // tslint:disable-next-line:no-console
            console.error(e);
            captureException(e);
          },
        });

        return Observable.merge(...actions);
      })
      .catch((e: Error) => Observable.of(draftSendUndo(action.meta, e)));
  });

// Handle emitting out thread single from optimistic draft send
// TODO(SHIN): Issue with send and archive getting triggered multiple times
// (bc success called off worker) need to identify first
export const draftSendAndArchiveEpic: Epic<
  Action<DraftSendMeta|ThreadCommitMeta, Draft|null>, StoreState, Deps
> = (action$, store, deps) =>
  action$.ofType(DRAFT_SEND)
    .filter(action => action.status === ActionStatus.Success)
    .filter((action: any) => {
      const state = store.getState();
      const stateDraftResource = state.drafts[action.meta.draft.id];
      const incrementCount = stateDraftResource && stateDraftResource.incrementCount;
      const incrementCheck = incrementCount && incrementCount === 1 || false;
      return isDraftSending(action.payload) && incrementCheck;
    })
    .filter((action: Action<DraftSendMeta, Draft>) => {
      const state = store.getState();
      let oddCounter = 0;
      if (action.meta.toggleArchive) {
        oddCounter = 1 + oddCounter;
      }

      if (state.preferences.sendAndArchive) {
        oddCounter = 1 + oddCounter;
      }
      return oddCounter % 2 !== 0;
    })
    .do((action: Action<DraftSendMeta, Draft>) => {
      if (!action.meta.draft || !action.meta.draft.threadV2) {
        const mailbox = store.getState().mailbox;
        // Return to previously selected thread if not in any draft box
        if (!isDraftBox(mailbox.type)) {
          const location = browserHistory.location.pathname;
          browserHistory.push(location);
        } else {
           // If in drafts or send later return back to top of mailbox
           // (fixes draft showing in sending state)
          browserHistory.push(routeFromMailbox(mailbox));
        }
      }
    })
    .filter((action: Action<DraftSendMeta, Draft>) => !!action.meta.draft.threadV2)
    .flatMap((action: any) => {
      return Observable.of(threadCommitsSubmit({
        box: ThreadBox.Archive,
        singleResource: action.meta.draft.threadV2,
      }));
    });

// Handle removing optimistic messages on DRAFT_SEND_FAIL events
export const draftSendFailEpic: Epic<
  Action<DraftSendMeta | ThreadCommitMeta, Draft|null|undefined>, StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(DRAFT_SEND)
      .filter(action => action.status === ActionStatus.Failure)
      .flatMap((action: Action<DraftSendMeta, Draft>) => {
        // Get existing thread id
        const state = store.getState();
        const draft = action.meta.draft;
        const optimisticId = `<${draft.id}@polymail.io>`;
        let threadId = draft.threadV2;

        // If threadId exists, then call threadSingle with the removed optimistic message
        if (threadId) {
          const stateThread: Thread|undefined = selectThread(state, threadId);
          if (stateThread) {
            const toUpdate = {
              ...stateThread,
              messages: stateThread.messages
                .filter(m => m.rfcId !== optimisticId),
            };

            return Observable.of(threadSingle(toUpdate));
          }
        }

        // Remove thread and reselect draft if thread matches
        threadId = toThread(action.meta.draft, state.accounts).id;
        if (state.selectedThread && state.selectedThread.id === optimisticId) {
          return Observable.merge(
            Observable.of(draftSelect(action.meta.draft.id)),
            Observable.of(threadDelete(threadId, null)),
          );
        }

        // Otherwise just delete draft
        return Observable.of(threadDelete(threadId, null));
      });

