// Handle emitting out thread single from optimistic draft send
// TODO(SHIN): We can make this faster if thread matches selected thread
export const draftSendOptimisticEpic: Epic<
  Action<Thread|DraftSendMeta, undefined>, StoreState
> = (action$, store) =>
 action$.ofType(DRAFT_SEND)
  .filter(action => action.status === ActionStatus.Success)
  .filter((action: any) => isDraftSending(action.payload))
  .flatMap((action: any) => {
    if (action.payload.threadV2) {
      const state = store.getState();

      // Try to fetch thread from state first
      let matchedThread = selectThread(state, action.payload.threadV2);
      if (!matchedThread && state.selectedThread &&
        state.selectedThread.id === action.payload.threadV2) {
        matchedThread = state.selectedThread;
      }

      // Turn thread into observable
      const observableThread = !!matchedThread
        ? Observable.of(matchedThread)
        : Cache.get(Stores.Threads, action.payload.threadV2);

      // Apply optimistic handling, clearing out duplicates before hand to have fresh copies of
      // optimistics
      return observableThread
        .map((thread: Thread) => {
          const filteredMessages = thread.messages.filter(m => !m.optimistic);
          const filteredThread = { ...thread, messages: filteredMessages };
          const appended = appendThread(filteredThread, action.payload, store.getState().accounts);
          const uniqueMessages = uniqBy(
            appended.messages.concat(
              selectOptimisticMessagesForThread(store.getState(), thread.id),
            ),
            'rfcId',
          );

          const updated = computeThread({ ...appended, messages: uniqueMessages });
          return threadSingle(updated);
        });
    }

    return Observable.of(
      threadSingle(toThread(action.payload, store.getState().accounts)),
    );
  });

