// Applies attachment replace on successful draft in the background when draft is not selected
const draftAttachmentAddSuccessEpic: Epic<
Action<AttachmentMeta|Draft|Error|null, Attachment| AttachmentMeta|null|Draft>, StoreState>
  = (action$, store) =>
    action$.ofType(DRAFT_ATTACHMENT_ADD)
      .filter(action => action.status === ActionStatus.Success)
      .filter((action: Action<AttachmentMeta, Attachment>) => {
        const state = store.getState();
        const draftResource = selectSelectedDraft(state);
        const draft = draftResource && draftResource.data;
        if (!draft) {
          return true;
        }
        return !!action.meta.inline || !!action.meta.cloud;
      })
      .flatMap((action: Action<AttachmentMeta, Attachment>) => {
        return Cache.get(Stores.Drafts, action.meta.resourceId)
          .flatMap((draft: Draft) => {
            // Reduce through all attachments and apply any cloud/inline
            // NOTE(SHIN): We do all in cases of race condition
            const allAttachments = draft.attachments.concat(action.payload!);
            const updatedDraft = allAttachments.reduce(
              (d, a) => applyAttachmentToDraft(d, a),
              draft,
            );
            return Observable.of(draftUpdate(updatedDraft));
          });
      })
      .catch((e: Error) => Observable.of(toastSet(e)));

export const draftTemplateApplyEpic: Epic<Action<
  Template|Draft|AttachmentMeta, Draft|Attachment
>, StoreState> = (action$, store, deps) =>
 action$.ofType(DRAFT_TEMPLATE_APPLY)
  .filter((action) => {
    const state = store.getState();
    const draft = state.selectedDraft
      ? selectDraft(state, state.selectedDraft).data
      : state.selectedThread && selectDraftForThread(state, state.selectedThread.id).data;
    return action.status === ActionStatus.Start && !!draft;
  })
  .flatMap((action: Action<Template, Draft>) => {
    const state = store.getState();
    const draft = state.selectedDraft
      ? selectDraft(state, state.selectedDraft).data
      : state.selectedThread && selectDraftForThread(state, state.selectedThread.id).data;

    // Remove old template properties from draft
    let updatedDraft = draft;
    if (draft.template) {
      const oldTemplate = selectTemplate(state, draft.template);
      if (oldTemplate) {
        updatedDraft = removeTemplateRecipientsFromDraft(updatedDraft, oldTemplate);
      }
    }

    // Set new template to draft
    updatedDraft = applyTemplateToDraft(updatedDraft, action.meta);
    const actions: Array<Observable<Action<Draft|AttachmentMeta, Draft|Attachment>>> = [
      Observable.of(draftUpdate({ ...updatedDraft })),
    ];

    // Append attachments create commits if necessary, ignoring cloud
    if (action.meta.attachments.length) {
      action.meta.attachments
        .filter(attachment => !attachment.cloud)
        .forEach((attachment) => {
          const attachmentMeta: AttachmentMeta = {
            attachment: attachment.id,
            resourceId: draft.id,
            template: action.meta.id,
            cloudFile: attachment.cloudFile,
            cloudUrl: attachment.cloudUrl,
            filename: attachment.filename,
            length: attachment.length,
            cloud: attachment.cloud,
            inline: attachment.inline,
            file: undefined,
          };

          actions.push(Observable.of(draftAttachmentAdd(attachmentMeta)));
        });
    }

    return Observable.merge(
      ...actions,
    );
  });

