// draftAttachmentCommitRemove Epic deletes any attachmentMeta commits when deleting attachments
const draftAttachmentCommitRemoveEpic: Epic<Action<AttachmentMeta, null>, StoreState> =
(action$, store) =>
  action$.ofType(DRAFT_ATTACHMENT_REMOVE)
    .filter(action => action.status === ActionStatus.Start)
    .filter((action) => {
      const state = store.getState();
      const resourceId = action.meta.resourceId;
      const attachmentId = action.meta.attachment;
      return !!state.attachmentMetas[resourceId] &&
        !!state.attachmentMetas[resourceId][attachmentId];
    })
    .flatMap(action =>
      Cache.list<Commit>(
        Stores.Commits,
        (model => model && model.rehydrateMeta &&
          model.rehydrateMeta.attachment === action.meta.attachment &&
          model.rehydrateMeta.resourceId === action.meta.resourceId),
        )
      .flatMap((commits: Commit[]) => {
        const commit = first(commits);
        if (commit && commit.id) {
          return Cache.deleteSingle(Stores.Commits, commit.id);
        }
        return Observable.empty();
      })
      .flatMap(() => Observable.of(draftAttachmentRemove(action.meta, null)))
      .catch(e => Observable.of(draftAttachmentRemove(action.meta, e))),
  );

