export const draftCalendarInviteAddEpic:
  Epic<Action<TimeBlock|string, TimeBlock|Contact[]>, StoreState> = action$ =>
  action$.ofType(DRAFT_CALENDAR_INVITE_ADD)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap(action =>
      getContactsByRecipientType((action.meta as TimeBlock).event.recipients)
        .flatMap((contacts) => {
          const allContacts = contacts
            .map(c => ({ ...c, type: ContactType.Cache }))
            .concat(
              (action.meta as TimeBlock).event.recipients
                .filter(r => !contacts.find(c => c.email === r.identifier) &&
                  r.type !== EventRecipientType.ContactGroup)
                .map(r => defaultContact(r.identifier)),
            );
          const tb = action.meta as TimeBlock;
          tb.event.recipients = uniqBy(
            allContacts
              .map(c => ({
                type: EventRecipientType.Contact,
                displayName: `${c.fullName} ${c.fullName ? '<' : ''}
                  ${c.email}${c.fullName ? '>' : ''}`,
                identifier: c.email,
              })),
            'identifier',
          );

          return Observable.merge(
            Observable.of(contactsAdd('', allContacts)),
            Observable.of(draftCalendarInviteAdd(action.meta as TimeBlock, tb)),
          );
        }),
    );

