import React from 'react';
import classnames from 'classnames';
import { Link } from 'react-router-dom';
import Alert from '@src/components/Icons/Alert.svg';
import ThreeDotsVertical from '@src/components/LeftSidebar/Icons/ThreeDotsVertical.svg';

import './CollapsedSectionItem.scss';
import { PulseGem } from '@src/components/Base/PulseGem';
import HoverTip from '@src/components/Base/HoverTip';

interface Props {
  name: string;
  id?: string;
  count: number;
  icon?: any;
  selected: boolean;
  link?: string;
  className?: string;
  pillText?: string;
  isOnboarding?: boolean;
  mutedColor?: boolean;
  onClick?: (e: React.MouseEvent<HTMLElement>) => void;
  onMoreClick?: (name: string, e: React.MouseEvent<HTMLElement>) => void;
  errorCopy?: string;
  notificationCount?: boolean;
  highlightMore?: boolean;
  showMore?: boolean;
  hotkey?: string;
}

export const CollapsedSectionItem = (props: Props) => {
  const { onMoreClick, id, highlightMore } = props;
  const showCount = !props.errorCopy;

  const handleDisclosureClick = (e: React.MouseEvent<any>) => {
    e.preventDefault();
    e.stopPropagation();
    if (onMoreClick && id) {
      onMoreClick(id, e);
    }
  };

  // Non linkiefied version
  if (!props.link) {
    return (
      <HoverTip
        copy={props.hotkey}
        left="20px"
      >
        <div
          className={classnames('collapsed-section-item', props.className, {
            selected: props.selected,
            empty: props.count === 0,
            muted: props.mutedColor,
          })}
          onClick={props.onClick}
        >
          <div className="collapsed-container">
            <span className="icon">{props.icon}</span>
            {!!props.pillText &&
              <span className="new-feature">
                {props.pillText}
              </span>
            }
            {!!props.errorCopy &&
              <HoverTip
                top="25px"
                left="-5px"
                copy={props.errorCopy}
                fixedPositioning
              >
                <span className="error"><Alert /></span>
              </HoverTip>}
            {showCount && <span className="count">{props.count}</span>}
            {props.isOnboarding && <PulseGem
              top="-2px"
              right="40px"
              />}
          </div>
        </div>
      </HoverTip>
    );
  }

  return (
    <Link
      className={classnames('collapsed-section-item', props.className, {
        selected: props.selected,
        empty: props.count === 0,
        muted: !!props.mutedColor,
      })}
      to={props.link}
      onClick={props.onClick}
    >
      <div className="collapsed-container">
        <span className="icon">{props.icon}</span>
        {!!props.pillText &&
          <span className="new-feature">
            {props.pillText}
          </span>
        }
        {!!props.errorCopy &&
          <HoverTip
            top="25px"
            left="-5px"
            copy={props.errorCopy}
            fixedPositioning
          >
            <span className="error"><Alert /></span>
          </HoverTip>}
        {showCount &&
          <span
            className={classnames('count', { notification: !!props.notificationCount })}
          >
          {props.count}
          </span>
        }
        {props.isOnboarding && <PulseGem
          top="-2px"
          right="38px"
        />}
      </div>
      {props.showMore && (
        <div
          className={classnames('more-container', { highlight: !!highlightMore })}
          onClick={handleDisclosureClick}
        >
            <ThreeDotsVertical />
        </div>
      )}
    </Link>
  );
};
