import React from 'react';
import ReactDOM from 'react-dom';
import CloudAttachmentCell from '@src/components/Compose/Attachments/CloudAttachmentCell';
import CalendarInvite from '@src/components/Cells/CalendarInvite/CalendarInvite';
import { filenameIconUrl, AttachmentUploadOptions } from '@src/lib/attachment';
import {
  Attachment,
  AttachmentMeta,
  KeyCode,
  DraftCalendarInvite,
  Contact,
  LinkPreviewProps,
} from '@src/types';
import FormattingBar from './FormattingBar';
import VariablePopover from './VariablePopover';

import './ContentEditable.scss';
import { sanitize, FakeDOM } from '@src/lib/html';
import { parseAddress } from '@src/lib/address';
import { debounce, Cancelable, last, compact } from 'lodash';
import { mongoId } from '@src/lib/crypt';
import { jsonRequest } from '@src/api/base';
import LinkPreview, {
  closeButtons,
} from '@src/components/ThreadDetail/components/Composer/components/LinkPreview';
import { isRightToLeftChar } from '@src/lib/utils';
import { isPrimaryModifierKey } from '@src/lib/hotkeys';
import Resizable, { ResizeCallback, ResizeStartCallback } from 're-resizable';
import { generateSentFromPolymailHtmlDiv } from '@src/lib/draft';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

interface Props {
  html: string;
  template?: string;
  draft?: string;
  autofocus?: boolean;
  disabled?: boolean;
  noBranding?: boolean;
  useLinkPreview?: boolean;
  variables?: string[];

  onSelect?: () => void;
  onChange?: (html: string) => void;
  onAttachmentUpload?: (list: File[], options: AttachmentUploadOptions) => void;
  onAttachmentRemove?: (id: string) => void;
  onTabPress?: (event: React.KeyboardEvent<HTMLDivElement>|KeyboardEvent) => boolean;
  getContact?: (email: string) => Contact;
}

interface State {
  lastSelectedRange?: Range;
  forceHyperlinkPopup?: boolean;
  selection: boolean;
  selectionCenter: Point;
  focus: boolean;
  variablePopoverPosition: Point;
  html: string;
  oldContentEditablePosition?: Range;
}

interface CustomWindow extends Window {
  getSelection(): Selection;
}
declare let window: CustomWindow;

export interface Point {
  x: number;
  y: number;
}

// Nesting Level Styles
const ORDERED_NESTING_STYLES = ['decimal', 'lower-alpha', 'lower-roman'];
const UNORDERED_NESTING_STYLES = ['disc', 'circle', 'square'];

// TODO(SHIN): Handle hotkeys & immediate hyperlink
// TODO(SHIN): Implement tabbing, indent and outdent behavior
// TODO(SHIN): Pass in prop to identify when to focus
export default class ContentEditable extends React.Component<Props, State> {
  private body: HTMLDivElement;
  private timeout: NodeJS.Timer;
  private selectedPlaceholderIndex?: number;
  private ref: HTMLDivElement;
  private handleChangeDebounced: ((html: string) => void) & Cancelable;

  constructor() {
    super();

    const initialPosition = {
      x: 0,
      y: 0,
    };

    this.state = {
      selection: false,
      selectionCenter: initialPosition,
      variablePopoverPosition: initialPosition,
      focus: false,
      html: '',
    };

    this.handleChangeDebounced = debounce(this.handleChange, 750, { trailing: true });
  }

  componentDidMount() {
    // tslint:disable-next-line no-unnecessary-type-assertion
    if (this.props.autofocus) {
      this.focus();
    }

    const doc = FakeDOM().createElement('div');
    doc.innerHTML = this.props.html;
    const closeContainer = doc.getElementsByClassName('pm-cal-invite-close-container')[0];
    if (closeContainer && closeContainer.childElementCount === 0) {
      const img = document.createElement('img');
      img.src = `${process.env.API_URL}/v2/img/calendar-invite/close@3x.png`;
      img.alt = 'Close Icon';
      img.setAttribute('data-pm-delete-calendar-invite', 'true');
      img.setAttribute('style', 'width: 12px; height: 12px; float: right; cursor: pointer;');
      closeContainer.appendChild(img);
    }

    this.setState({
      html: doc.innerHTML,
      variablePopoverPosition: {
        x: 0,
        y: this.ref ? this.ref.offsetTop : 0,
      },
    });

    document.addEventListener('mousedown', this.handleMouseDown);
  }

  componentWillUnmount() {
    document.removeEventListener('mousedown', this.handleMouseDown);

    if (this.timeout) {
      clearTimeout(this.timeout);
    }
  }

  UNSAFE_componentWillReceiveProps(nextProps: Props) {
    let closeContainer;
    const img = document.createElement('img');
    img.src = `${process.env.API_URL}/v2/img/calendar-invite/close@3x.png`;
    img.alt = 'Close Icon';
    img.setAttribute('data-pm-delete-calendar-invite', 'true');
    img.setAttribute('style', 'width: 12px; height: 12px; float: right; cursor: pointer;');

    if (this.props.template !== nextProps.template || this.props.draft !== nextProps.draft ||
      nextProps.disabled || !this.state.focus) {
      const doc = FakeDOM().createElement('div');
      doc.innerHTML = nextProps.html;
      closeContainer = doc.getElementsByClassName('pm-cal-invite-close-container')[0];
      if (closeContainer && closeContainer.childElementCount === 0) {
        closeContainer.appendChild(img);
      }

      this.setState({ html: doc.innerHTML });

      // only focus if template or draft has changed
      if (this.props.draft !== nextProps.draft) {
        this.focus();
      }
    } else {
      if (this.ref) {
        closeContainer = this.ref.getElementsByClassName('pm-cal-invite-close-container')[0];
        if (closeContainer && closeContainer.childElementCount === 0) {
          closeContainer.appendChild(img);
        }
      }
    }
  }

  handleMouseDown = (e: MouseEvent) => {
    const target = e.target as HTMLElement;

    if (target && this.ref && !this.ref.contains(target) && this.state.focus) {
      this.handleClickOutside();
    }
  }

  handleRef = (ref: HTMLDivElement) => {
    this.ref = ref;
  }

  cancelOnChange = () => {
    this.handleChangeDebounced.cancel();
  }

  resetOnChangeDebounce = () => {
    this.handleChangeDebounced.cancel();
    this.handleChangeDebounced = debounce(this.handleChange, 750, { trailing: true });
  }

  onChange = (e: React.ChangeEvent<HTMLDivElement>) => {
    const { variablePopoverPosition } = this.state;
    const { variables } = this.props;

    // Check for text-based list insertions when change is not an undo type
    if (e.nativeEvent && (e.nativeEvent as any).inputType !== 'historyUndo') {
      this.checkForUnorderedList();
      this.checkForOrderedList();
    }

    if (this.body) {
      // Pass HTML through callback on changes.
      this.handleChangeDebounced(this.body.innerHTML);
      const newVariablePopoverPosition = this.getPopoverPosition()
        || variablePopoverPosition;

      // Handle updates to variable popover if necessary
      if (variables && newVariablePopoverPosition !== variablePopoverPosition) {
        this.setState({
          variablePopoverPosition: newVariablePopoverPosition,
        });
      }
    }
  }

  // Identifies how many levels deep an nested <LI> tag is
  checkListTagNestingLevel = (element: HTMLElement, tags: string[]): number => {
    let level = -1;
    let walker: HTMLElement|null = element;

    // Start at -1 in order to capture zero index
    while (walker && this.body.contains(walker)) {
      if (tags.indexOf(walker.tagName) > -1) {
        level += 1;
      }
      walker = walker.parentElement;
    }

    // Fallback mechanism to make sure we never return lower than 0
    if (level === -1) {
      return 0;
    }

    return level;
  }

  // Takes current indentation level of element and applies proper styling.
  applyListStyling = () => {
    const selection = document.getSelection();
    if (!selection) {
      return;
    }

    const range = selection.getRangeAt(0);
    const selectionElement = range.startContainer;

    let parent = selectionElement.parentElement;

    // TODO(SHAHAN): We could also run a check where it
    // just keeps walking the parents until we find a UL/OL and
    // bound it by the body containing the element
    // so we don't overflow. Since there could be an edge case of children
    // TEXT nodes?
    if (parent?.tagName === 'LI') {
      parent = parent.parentElement;
    }

    if (!parent) {
      return;
    }

    parent.style.marginTop = '0';
    parent.style.marginBottom = '0';
    const level = this.checkListTagNestingLevel(parent, ['OL', 'UL']);

    parent.style.listStyleType = parent.tagName === 'UL'
      ? UNORDERED_NESTING_STYLES[level % 3]
      : ORDERED_NESTING_STYLES[level % 3];
  }

  checkForUnorderedList = () => {
    const selection = document.getSelection();
    if (!selection) {
      return;
    }
    // Ordered List insertion
    const unorderedListRegexp = new RegExp(
      '^\\*\\s',
      'g',
    );
    const startRange = selection.getRangeAt(0);
    const startContainerValue = startRange
      && startRange.startContainer && startRange.startContainer.nodeValue;
    const commandState = document.queryCommandState('insertUnorderedList');
    if (!commandState && startContainerValue && startRange &&
      startContainerValue.length === 2 && startContainerValue.match(unorderedListRegexp)
    ) {
      startRange.setStart(startRange.startContainer, 0);
      document.execCommand('delete', false, 'false');
      document.execCommand('insertUnorderedList', false, 'false');
      const newSelection = document.getSelection();
      if (newSelection) {
        newSelection.collapseToEnd();
        const elementRange = newSelection.getRangeAt(0);
        const element = elementRange.startContainer;
        if (element && element instanceof HTMLElement) {
          const parent = element.parentElement;
          // Set start value of ordered list insertion
          if (parent && parent.tagName === 'UL') {
            this.applyListStyling();
          }
        }
        startRange.setStartAfter(startRange.startContainer);
      }
    }
  }

  checkForOrderedList = () => {
    const selection = document.getSelection();
    if (!selection) {
      return;
    }
    // Ordered List insertion
    const orderedListRegexp = new RegExp(
      /^(\d+)\.\s/,
    );
    const startRange = selection.getRangeAt(0);
    const startContainerValue = startRange
      && startRange.startContainer && startRange.startContainer.nodeValue;
    const commandState = document.queryCommandState('insertOrderedList');
    if (!commandState && startContainerValue &&
      startContainerValue.length === 3 && startContainerValue.match(orderedListRegexp)
    ) {
      const match = startContainerValue.match(orderedListRegexp);
      const digit = match && match[1];
      startRange.setStart(startRange.startContainer, 0);
      document.execCommand('delete', false, 'false');
      document.execCommand('insertOrderedList', false, 'false');
      selection.collapseToEnd();
      const elementRange = selection.getRangeAt(0);
      const element = elementRange.startContainer;
      if (element && element instanceof HTMLElement) {
        const parent = element.parentElement;
        // Set start value of ordered list insertion
        if (digit && parent && parent.tagName === 'OL') {
          parent.setAttribute('start', digit);
          this.applyListStyling();
        }
      }
      startRange.setStartAfter(startRange.startContainer);
    }
  }

  // Automatically wraps paste in anchor tag and uploads image pastes
  // TODO(SHIN): May want to identify a way we can sanitize on paste
  //  while using system paste handler
  onPaste = (e: React.ClipboardEvent<HTMLDivElement>) => {
    const { onAttachmentUpload, noBranding, useLinkPreview } = this.props;

    // Handle pasting of url
    const pastedText = e.clipboardData.getData('text/plain');

    if (pastedText
        && typeof process.env.SCHEDULING_URL === 'string'
        && pastedText.startsWith(process.env.SCHEDULING_URL)) {
      logAmplitudeEvent(AmplitudeEvents.ShareCalendarMeetingTime);
    }

    // Custom behavior handling when pasting files
    const rtfExists = e.clipboardData.types.indexOf('text/rtf') !== -1;
    if (e.clipboardData.types.indexOf('Files') !== -1 && !rtfExists) {
      e.preventDefault();
      e.stopPropagation();
      const fileList = e.clipboardData.files;
      const files = compact(Array.from(fileList)
        .map((blank, index) => fileList.item(index)));
      if (files && !!files.length && onAttachmentUpload) {
        onAttachmentUpload(files, {});
        return;
      }
    }

    // Handles stripping formatting/color/style when pasting in html content.
    const htmlHandler = () => {
      const pastedHtml = e.clipboardData.getData('text/html');
      if (pastedHtml) {
        const wrapper = FakeDOM().createElement('div');
        wrapper.innerHTML = pastedHtml;

        // Remove formatting when pasting in anything that is not a table
        if (!wrapper.querySelector('table')) {
          e.preventDefault();
          e.stopPropagation();

          const allChildren = wrapper.getElementsByTagName('*');
          Array.from(allChildren).forEach((element) => {
            element.removeAttribute('style');
            element.removeAttribute('color');
            element.removeAttribute('class');

            // Strip out head and meta
            if (['HEAD', 'META', 'STYLE'].indexOf(element.tagName) > -1) {
              element.parentElement?.removeChild(element);
            }

            // HACK(SHIN): Replaces body wrapped elements from Google Docs with proper content
            if (element.tagName === 'B' && !!element.id) {
              const replacement = FakeDOM().createElement('div');
              replacement.innerHTML = element.innerHTML;
              element.parentElement?.replaceChild(replacement, element);
            }
          });
          const range = window.getSelection()?.getRangeAt(0);
          if (range) {
            range.deleteContents();
          }

          const pastedData = wrapper.innerHTML;
          document.execCommand('insertHTML', false, pastedData);
          return;
        }
      }
    };

    // No handling if there is are whitespace characters
    const whiteSpaceRegex = /[\s\n\t\r]/g;
    if (pastedText.match(whiteSpaceRegex)?.length) {
      htmlHandler();
      return;
    }

    // Do not handle when pasting multiple urls
    const regex = new RegExp(/(https?:\/\/[^\s]+)/g);
    const matches = pastedText.match(regex);
    if (matches && matches.length > 1) {
      htmlHandler();
      return;
    }

    // Do not use custom handling if cannot be parsed into a url
    // TODO(SHIN): Pasting url with space
    try {
      const url = new URL(pastedText);
      // TODO(SHIN): Only anchorize pasted content that is of url-based type
      e.preventDefault();

      if (url) {
        const isSelected = !!(window && window.getSelection && window.getSelection().toString());
        const id = mongoId();
        // Create contenteditable false node to wrap <a> tag
        const node = document.createElement('a');
        node.setAttribute('data-pm-link-preview', id);
        node.setAttribute('href', pastedText);
        const innerHtmlText = isSelected ? window.getSelection().toString() : pastedText;
        node.innerHTML = sanitize(innerHtmlText);

        // Get selection
        const sel = window.getSelection();
        if (sel && sel.rangeCount) {
          if (isSelected) {
            // Keep styles of selection
            this.handleHrefSubmit(sel.toString(), pastedText);
          } else {
            // Insert node that will be replaced by the LinkPreview
            const range = sel.getRangeAt(0);

            // Insert node at caret
            document.execCommand(
              'insertHTML',
              false,
              node.outerHTML,
            );

            sel.collapseToEnd();
            this.handleChangeDebounced(this.body.innerHTML);

            // Do not process link preview if part of a HTML list element
            const containerEl = (range.startContainer instanceof HTMLElement) &&
              range.startContainer;
            const parentElement = range.startContainer.parentElement;
            const inList = (containerEl && ['UL', 'LI'].indexOf(containerEl.tagName) > -1) ||
              (parentElement && ['UL', 'LI'].indexOf(parentElement.tagName) > -1);
            if (inList) {
              return;
            }
          }
        }

        // Do not turn into link preview if preference is not turned on
        if (!useLinkPreview) {
          return;
        }

        // we don't need preview when text is selected
        if (isSelected) return;

        // Creates Link Preview for node and replaces contents
        jsonRequest({
          method: 'GET',
          route: '/v1/linkpreview?url=' + encodeURIComponent(pastedText),
        })
          .subscribe((response: LinkPreviewProps) => {
            const linkPreviewContainer = FakeDOM().createElement('div');
            const element = document.querySelector(`[data-pm-link-preview="${id}"]`) as HTMLElement;
            if (!element) {
              return;
            }
            const linkPreviewComponent = (
              <LinkPreview
                title={response.title}
                href={response.url}
                siteName={response.siteName || response.url}
                img={response.imageUrl}
                description={response.description}
                id={id}
                isPro={!!noBranding}
              />
            );

            // Save cursor position in case it needs to be restored
            this.saveCursorPosition();

            // Render link preview and post updates
            ReactDOM.render(linkPreviewComponent, linkPreviewContainer, () => {
              // Replaces HTML
              window.getSelection()!.removeAllRanges();
              window.getSelection()!.addRange(new Range());
              window.getSelection()!.getRangeAt(0).selectNode(element);
              const linkPreviewHTML = linkPreviewContainer.innerHTML;
              document.execCommand(
                'insertHTML',
                false,
                linkPreviewHTML,
              );

              const replacementElement = document
                .querySelector(`[data-pm-link-preview-id="${id}"]`) as HTMLElement;

              if (!replacementElement) {
                return;
              }
              replacementElement.setAttribute('contenteditable', 'false');
              const linkPreviewCloseButtons = closeButtons(id);
              const closeContainer = FakeDOM().createElement('div');

              // Handle insertions of link preview close buttons
              ReactDOM.render(linkPreviewCloseButtons, closeContainer, () => {
                const linkPreviewNode = this.body
                  .querySelector(`[data-pm-link-preview-id="${id}"]`);
                if (linkPreviewNode) {
                  linkPreviewNode.insertBefore(closeContainer, element.firstElementChild);
                  closeContainer.outerHTML = closeContainer.innerHTML;
                  this.handleChangeDebounced(this.body.innerHTML);
                  this.restoreCursorPosition();
                }
              });
            });
          });
      }
    } catch {
      htmlHandler();
    }
  }

  onSelect = (e: React.SyntheticEvent<HTMLDivElement>) => {
    const sel = window.getSelection();
    const { variablePopoverPosition } = this.state;
    const newCollapsedState = !sel!.isCollapsed;
    const forceHyperlinkPopup = false;

    // Only reset selection states if selection or hyperlink has changed
    if (newCollapsedState !== this.state.selection ||
      forceHyperlinkPopup !== (this.state.forceHyperlinkPopup || false)) {
      this.setState({ selection: !sel!.isCollapsed, forceHyperlinkPopup: false });
    }

    if (sel!.rangeCount) {
      this.setState({
        selectionCenter: this.getCenter(),
        lastSelectedRange: sel!.getRangeAt(0),
        variablePopoverPosition: this.getPopoverPosition() || variablePopoverPosition,
      });
    }

    // Delegate for selections
    if (this.props.onSelect) {
      this.props.onSelect();
    }
  }

  handleFormatOption = (key: string) => {
    switch (key) {
      case 'bold':
        this.handleFormattingExecCommand('bold');
        break;
      case 'italics':
        this.handleFormattingExecCommand('italic');
        break;
      case 'underline':
        this.handleFormattingExecCommand('underline');
        break;
      case 'orderedList':
        document.execCommand('insertOrderedList');
        break;
      case 'unorderedList':
        document.execCommand('insertUnorderedList');
        break;
      default:
        break;
    }

    this.focus();
  }

  getCenter = () => {
    // Get selection
    const sel = window.getSelection();
    if (sel && sel.rangeCount) {
      let rect = sel.getRangeAt(0).getBoundingClientRect();

      // HACK(SHIN): If no bounding rect use container edge. Uses startContainer as any
      // since it has property boundingClientRect, but needs typecast
      if (!rect.top && !rect.left) {
        const el: any = sel.getRangeAt(0);
        if (el && el.startContainer && el.startContainer.getBoundingClientRect) {
          rect = el.startContainer.getBoundingClientRect();
          return {
            y: rect.top,
            x: rect.left,
          };
        }
      }

      const selectionCenter = {
        y: rect.top,
        x: rect.left + rect.width / 2,
      };

      return selectionCenter;
    }

    return this.state.selectionCenter;
  }

  getPopoverPosition = () => {
    const sel = window.getSelection();

    // Capture Y position of selection by calculating offsetTop
    if (sel && sel.rangeCount) {
      const rectContainer = sel.getRangeAt(0).startContainer;
      if (rectContainer instanceof HTMLElement) {
        const variablePopoverPosition = {
          y: rectContainer.offsetTop - 5 || this.state.variablePopoverPosition.y,
          x: 0,
        };

        // Only reset if y-position has changed
        if (variablePopoverPosition.y !== this.state.variablePopoverPosition.y) {
          return variablePopoverPosition;
        }
      }
    }

    return null;
  }

  insertTextAtCursor = (text: string) => {
    const textNode = document.createTextNode(text);
    this.insertAtCursor(textNode);
  }

  insertAtCursor = (node: HTMLElement | Text) => {
    const { variablePopoverPosition } = this.state;
    let sel: Selection|null;
    let range;

    if (window.getSelection) {
      sel = window.getSelection();
      if (sel && sel.getRangeAt && sel.rangeCount) {
        range = sel.getRangeAt(0);
        range.deleteContents();
        range.insertNode(node);

        sel.removeAllRanges();
        sel.addRange(range);
        this.setState(
          { variablePopoverPosition: this.getPopoverPosition() || variablePopoverPosition },
          () => {
            sel!.collapseToEnd();
          },
        );

        if (this.body) {
          this.handleChangeDebounced(this.body.innerHTML);
        }
      }
    }
  }

  handleHrefSubmit = (selectedText: string, href?: string) => {
    const sel = window.getSelection();
    if (!sel) {
      return;
    }

    sel.removeAllRanges();

    if (this.state.lastSelectedRange) {
      sel.addRange(this.state.lastSelectedRange);
    }

    if (!href) {
      document.execCommand('unlink', false, 'false');
      return;
    }

    document.execCommand('createLink', false, href);
    sel.collapseToEnd();
  }

  // TODO(SHIN): Handle enter when outdented
  // TODO(SHIN): Handle level indentation type
  handleHotkey = (event: React.KeyboardEvent<HTMLDivElement>|KeyboardEvent) => {
    let handled = false;

    switch (event.keyCode) {
      // HACK(SHIN): Do not pass up navigation hotkey while contenteditable is editing
      case KeyCode.G:
        event.stopPropagation();
        break;
      case KeyCode.Delete:
      case KeyCode.ExtendedDelete:
        // Do not allow delete when resize handler is showing
        const element = document.querySelector('.pm-resizable-image-container');
        if (element && element.parentElement) {
          // return;
          const selection = document.getSelection();
          const range = document.createRange();
          const foundImage = element.querySelector('img');
          const id = foundImage && foundImage.id;
           // Remove the resize handlers and delete the image in question
          this.sanitizeResizeHandlers();
          const elementToDelete = document.querySelector(`#${id}`);
          if (selection && elementToDelete) {
            selection.removeAllRanges();
            range.selectNode(elementToDelete);
            selection.addRange(range);
          }
        }
        break;
      case KeyCode.U:
        if (event.metaKey || event.ctrlKey) {
          this.handleFormattingExecCommand('underline', false, 'false');
          handled = true;
        }
        break;
      case KeyCode.K:
        // Handle hyperlink
        if (isPrimaryModifierKey(event)) {
          const sel = window.getSelection();
          if (sel && sel.rangeCount && !this.state.forceHyperlinkPopup) {
            this.setState({
              selectionCenter: this.getCenter(),
              lastSelectedRange: sel.getRangeAt(0),
              selection: true,
              forceHyperlinkPopup: true,
            });
          } else {
            this.setState({
              selection: false,
              forceHyperlinkPopup: false,
            });
          }
          handled = true;
        }
        break;
      case KeyCode.OpenBracket:
        const checkTabOrderedList = document.queryCommandState('insertOrderedList');
        const checkTabUnorderedList = document.queryCommandState('insertUnorderedList');

        if (isPrimaryModifierKey(event) && (checkTabOrderedList || checkTabUnorderedList)) {
          document.execCommand('outdent');
          this.applyListStyling();
          handled = true;
        }
        break;
      case KeyCode.ClosedBracket:
        const checkBackTabOrderedList = document.queryCommandState('insertOrderedList');
        const checkBackTabUnorderedList = document.queryCommandState('insertUnorderedList');

        if (isPrimaryModifierKey(event) && (checkBackTabOrderedList || checkBackTabUnorderedList)) {
          document.execCommand('indent');
          this.applyListStyling();
          handled = true;
        }
        break;
      case KeyCode.Enter:
        // Override behavior when hitting enter on indented selection.
        // If enter and indent, remove indent as overridden behavior
        const enterSelection = window.getSelection();
        if (enterSelection) {
          const isStart = enterSelection.anchorNode && enterSelection.anchorOffset === 0;
          const isCollapsed = enterSelection.isCollapsed;
          if (this.isSelectionIndented() && isStart && isCollapsed) {
            document.execCommand('outdent', false);
            handled = true;
            break;
          }
        }
        break;
      case KeyCode.Tab:
        event.preventDefault();
        const insertOrderedList = document.queryCommandState('insertOrderedList');
        const insertUnorderedList = document.queryCommandState('insertUnorderedList');
        const listSelection = document.getSelection();
        const parent = listSelection && listSelection.anchorNode &&
          listSelection.anchorNode.parentElement;
        const parentElement = parent && parent.tagName.toLowerCase();
        const listSelected = parentElement && ['li', 'ol', 'ul'].indexOf(parentElement) > -1;
        const templateVariables = this.ref && this.ref.getElementsByClassName('data-pm-var');

        if (insertOrderedList || insertUnorderedList || listSelected) {
          if (event.shiftKey) {
            document.execCommand('outdent');
            this.applyListStyling();
            return;
          }

          document.execCommand('indent');
          this.applyListStyling();
          return;
        }
        if (templateVariables && templateVariables.length) {
          if (this.props.onTabPress && this.props.onTabPress(event)) {
            const sel = window.getSelection();
            if (sel) {
              sel.removeAllRanges();
              this.setState({ selection: false });
            }
          }
          const variables = Array.from(templateVariables) as HTMLElement[];
          if (this.selectedPlaceholderIndex === undefined) {
            this.selectedPlaceholderIndex = event.shiftKey ? variables.length : -1;
          }

          if (event.shiftKey) {
            this.selectedPlaceholderIndex -= 1;
          } else {
            this.selectedPlaceholderIndex += 1;
          }

          if (this.selectedPlaceholderIndex >= 0 &&
            this.selectedPlaceholderIndex < variables.length) {

            this.selectElement(variables[this.selectedPlaceholderIndex]);
            this.focus();
            return;
          }
          if (this.props.onTabPress && this.props.onTabPress(event)) {
            const sel = window.getSelection();
            if (sel) {
              sel.removeAllRanges();
              this.setState({ selection: false, forceHyperlinkPopup: false });
            }
          }
        } else if (!this.props.onTabPress || !this.props.onTabPress(event)) {
          // Handle indents and outdents when selecting words
          const tabSelection = window.getSelection();
          if (tabSelection && !tabSelection.isCollapsed) {
            const action = event.shiftKey ? 'outdent' : 'indent';
            document.execCommand(action, false);
            handled = true;
            break;
          } else if (event.shiftKey) {
            const action = event.shiftKey ? 'outdent' : 'indent';
            document.execCommand(action, false);
            handled = true;
            break;
          }

          // If no selection or not outdenting, just insert a span at selection
          const node = document.createElement('span');
          node.innerHTML = '&#009';
          node.setAttribute('style', 'white-space: pre-wrap');
          this.insertAtCursor(node);
          this.focus();
        }
        handled = true;
        this.selectedPlaceholderIndex = undefined;
        break;
      default:
        break;
    }

    if (!handled) {
      this.selectedPlaceholderIndex = undefined;
    }

    if (handled) {
      event.stopPropagation();
      event.preventDefault();
    }
  }

  // Utility element to determine whether the cursor content is
  // contained within a blockquote element (javascript indent)
  isSelectionIndented = (): boolean => {
    const tabSelection = window.getSelection();
    if (tabSelection && tabSelection.anchorNode && tabSelection.anchorNode.parentElement) {
      const parentElement = tabSelection.anchorNode.parentElement;
      const tagMatches = tabSelection.anchorNode.nodeName.toLowerCase() === 'blockquote';
      const parentMatches = parentElement.tagName.toLowerCase() === 'blockquote';

      // Returns true if selection or cursor element is blockquote
      return parentMatches || tagMatches || !!parentElement.closest('blockquote');
    }

    return false;
  }

  // handleFormattingExecCommand is a shim that temporarily toggles contenteditable for non-editable
  // elements in order to apply consistent formatting options
  // execCommand is deprecated, but there is not direct alternative atm
  // https://stackoverflow.com/a/70831583/13772033
  // todo: should consider in a future using alternatives to execCommand
  handleFormattingExecCommand = (
    command: string,
    showUI: boolean|undefined = undefined,
    value: string|undefined = undefined,
  ) => {
    const items = this.body.querySelectorAll('[contenteditable=false]');
    items.forEach(i => i.setAttribute('contenteditable', 'true'));
    document.execCommand(command, showUI, value);
    items.forEach(i => i.setAttribute('contenteditable', 'false'));
  }

  handleFontSelect = (value: string) => {
    this.handleFormattingExecCommand('fontName', false, value);
    this.handleChangeDebounced(this.body.innerHTML);
    this.focus();
  }

  handleFontSizeSelect = (value: string) => {
    const selection = document.getSelection();
    if (!selection) {
      return;
    }

    const range = selection.getRangeAt(0);
    const startContainer = range.startContainer;

    if (startContainer.parentElement instanceof HTMLElement) {
      this.handleFormattingExecCommand('fontSize', false, '7');
      const fonts = this.body.querySelectorAll('font');
      Array.from(fonts).forEach((font) => {
        if (font.getAttribute('size') === '7') {
          font.removeAttribute('size');
          font.style.fontSize = value !== ''
            ? `${value}px`
            : '';
        }
      });

      this.handleChangeDebounced(this.body.innerHTML);
      this.focus();
    }
  }

  handleFontColorSelect = (value: string) => {
    this.handleFormattingExecCommand('foreColor', false, value);

    this.handleChangeDebounced(this.body.innerHTML);
    this.focus();
  }

  handleFontColorReset = () => {
    this.handleFormattingExecCommand('removeFormat', false, 'foreColor');
    this.handleChangeDebounced(this.body.innerHTML);
    this.focus();
  }

  handleBodyRef = (ref: HTMLDivElement) => {
    this.body = ref;
  }

  focus = (selectFirstPlaceholder?: boolean, selectLastPlaceholder?: boolean) => {
    if (this.body) {
      const setFocusTimeout = (callback?: () => void) => {
        this.timeout = global.setTimeout(
          () => {
            // Selects the end of the document when focusing
            this.body.focus();

            // NOTE(SHIN): Specialized focus handler for move to BCC
            const bccSelector = document.querySelector('.pm-bcc-thx');
            if (bccSelector) {
              document.execCommand('selectAll', false);
              document.getSelection()?.collapseToEnd();
            }

            if (callback) {
              callback();
            }
          },
          0,
        );
      };
      clearTimeout(this.timeout);

      if (selectFirstPlaceholder || selectLastPlaceholder) {
        const templateVariables = this.ref && this.ref.getElementsByClassName('data-pm-var') || [];
        if (selectFirstPlaceholder) {
          this.selectedPlaceholderIndex = 0;
        } else {
          this.selectedPlaceholderIndex = templateVariables.length ?
            templateVariables.length - 1 : undefined;
        }

        if (this.selectedPlaceholderIndex !== undefined) {
          const variables = Array.from(templateVariables) as HTMLElement[];
          setFocusTimeout(() =>
            this.selectElement(variables[this.selectedPlaceholderIndex!]),
          );
        }
      } else {
        setFocusTimeout();
      }
    }
  }

  selectElement = (element: HTMLElement) => {
    const sel = window.getSelection();
    const range = document.createRange();
    if (sel && range) {
      range.setStartBefore(element);
      range.setEndAfter(element);
      sel.removeAllRanges();
      sel.addRange(range);
    }
  }

  // Removes the attachment id from the draft by removing HTML then calls update
  handleCloudAttachmentRemove = (id: string) => {
    const { onAttachmentRemove } = this.props;
    const element = this.body && this.body.querySelector(`div[data-pm-attachment-id="${id}"]`);
    if (element && element.parentElement) {
      element.parentElement.removeChild(element);
      this.handleChangeDebounced(this.body.innerHTML);
      if (onAttachmentRemove) {
        onAttachmentRemove(id);
      }
    }
  }

  handleBlur = () => {
    this.sanitizeResizeHandlers();
  }

  // TODO(SHIN): Clicking outside will clear range unnecessarily
  handleClickOutside = () => {
    this.setState({ focus: false }, () => {

      // Sanitize out any resizing elements
      this.sanitizeResizeHandlers();

      /// Removes selection if still in ContentEditable
      if (window.getSelection) {
        const sel = window.getSelection();
        if (sel && sel.containsNode(this.ref, true)) {
          sel.removeAllRanges();

          // NOTE(SHIN): Blur the body so we get any re-focus events
          // Fixes Grammarly cursor reset issue
          this.body?.blur();
        }
      }

      this.handleHide();
    });
  }

  // Removes resize component upon completion of resize
  handleResizeStop: ResizeCallback = (e: MouseEvent|TouchEvent) => {
    e.stopPropagation();
    e.preventDefault();
    this.sanitizeResizeHandlers();
  }

  // Prevents click handler for firing for resize events
  handleResizeStart: ResizeStartCallback = (e: React.MouseEvent<any>) => {
    e.stopPropagation();
    e.preventDefault();
  }

  // Wraps HTML image element with resize.
  handleWrapImageWithResize = (element: HTMLImageElement) => {
    // Do not wrap if body is not contenteditable
    if (this.body && !this.body.isContentEditable) {
      return;
    }

    // If parent is resize handler, then do not do anything
    if (element.parentElement && element.parentElement.className &&
      element.parentElement.className.startsWith('pm-resizable-image-')) {
      return;
    }

    // If any other resize handlers are shown, then clean those out.
    if (document.querySelector('.pm-resizable-image-container')) {
      this.sanitizeResizeHandlers();
    }

    // Create a wrapped parent that is inline block to contain the image and render comonent
    const wrappedParent = document.createElement('div');
    wrappedParent.style.display = 'inline-block';
    wrappedParent.style.height = element.style.height;
    wrappedParent.style.width = element.style.width;
    wrappedParent.style.maxHeight = element.style.maxHeight;
    wrappedParent.style.maxWidth = element.style.maxWidth;
    if (element.parentElement) {
      element.parentElement.insertBefore(wrappedParent, element);
      wrappedParent.appendChild(element);
    }

    const handleStyles = {
      width: '8px',
      height: '8px',
      borderRadius: '1px',
      backgroundColor: '#00aaff',
    };

    const resizableComponent = (
      <Resizable
        className="pm-resizable-image-container"
        handleClasses={{
          bottomRight: 'pm-resizable-image-handle',
          topLeft: 'pm-resizable-image-handle',
          bottomLeft: 'pm-resizable-image-handle',
          topRight: 'pm-resizable-image-handle',
        }}
        handleStyles={{
          bottomRight: handleStyles,
          bottomLeft: handleStyles,
          topRight: handleStyles,
          topLeft: handleStyles,
        }}
        defaultSize={{
          width: element.width,
          height: element.height,
        }}
        style={{
          display: 'inline-block',
        }}
        lockAspectRatio
        onResizeStart={this.handleResizeStart}
        onResizeStop={this.handleResizeStop}
      >
        <img
          id={element.id}
          src={element.src}
          data-pm-attachment-id={element.getAttribute('data-pm-attachment-id')}
          data-pm-template-id={element.getAttribute('data-pm-template-id')}
          style={{
            width: '100%',
          }}
        />
      </Resizable>
    );

    // Render wrapped resize and remove all selections
    ReactDOM.render(
      resizableComponent,
      wrappedParent,
      () => {
        const selection = document.getSelection();
        if (selection) {
          const resizeElement = document.querySelector('.pm-resizable-image-container');
          if (resizeElement) {
            resizeElement.setAttribute('contenteditable', 'false');
          }
          selection.removeAllRanges();
        }
      },
    );
  }

  // Strips out Resize Component from DOM or passed container element
  sanitizeResizeHandlers = (container?: HTMLElement) => {
    let update = false;
    const elementToUpdate = (container || this.body);
    const allElements = elementToUpdate.querySelectorAll('.pm-resizable-image-container');
    Array.from(allElements).forEach((element) => {
      if (!(element instanceof HTMLElement)) {
        return;
      }

      // Create replacment node for the resizble elements
      // const node = FakeDOM().createElement('div');
      const newImg = FakeDOM().createElement('img');
      const foundImage = element.querySelector('img');
      const parent = element.parentElement;

      // Replace contents with replacement node and remove the resizable component
      if (element && parent) {
        update = true;
        if (foundImage) {
          newImg.src = foundImage.src;
          newImg.id = foundImage.id;
          newImg.setAttribute(
            'data-pm-attachment-id',
            foundImage.getAttribute('data-pm-attachment-id') || '',
          );
          newImg.setAttribute(
            'data-pm-template-id',
            foundImage.getAttribute('data-pm-template-id') || '',
          );
          newImg.style.maxWidth = foundImage.style.maxWidth;
          if (element.style.width && parseInt(element.style.width, 10)) {
            newImg.width = parseInt(element.style.width, 10);
          }

          if (element.style.height && parseInt(element.style.height, 10)) {
            newImg.height = parseInt(element.style.height, 10);
          }
          newImg.style.width = '100%';
          newImg.style.height = '100%';
          newImg.style.maxWidth = element.style.width;
          newImg.style.maxHeight = element.style.height;
        }

        if (element) {
          parent.outerHTML = newImg.outerHTML;
        }
      }
    });

    if (update) {
      this.handleChangeDebounced(elementToUpdate.innerHTML);
    }
  }

  handleClick = (e: React.SyntheticEvent<HTMLElement>) => {
    const { variablePopoverPosition } = this.state;
    const { draft } = this.props;
    const target = e.target as HTMLElement;
    const parentElement = target.parentElement;

    // Wrap resize handler if clicked on an inline image
    if (target && target.id && target.id.startsWith('pm-inline') &&
    target.className !== 'pm-inline-loading') {
      const imageTarget = target as HTMLImageElement;
      this.handleWrapImageWithResize(imageTarget);
      e.stopPropagation();
      e.preventDefault();
      return;
    }

    // Remove handles if clicked on a non image
    if (target && !(target instanceof HTMLImageElement)) {
      this.sanitizeResizeHandlers();
    }

    // Handle attachment Removes on Cloud Attachment
    if (parentElement &&
      [target.className, parentElement.className].indexOf('attachment-remove-button') > -1
    ) {
      // We set the container dependent on which tag has the attachment remove
      // since you can be clicking the SVG instead of the button
      const cloudAttachmentContainer = target.className === 'attachment-remove-button'
        ? parentElement
        : parentElement.parentElement;
      if (cloudAttachmentContainer) {
        const attachmentId = cloudAttachmentContainer.getAttribute('data-pm-attachment-id');
        if (attachmentId) {
          this.handleCloudAttachmentRemove(attachmentId);
        }
      }
    }

    // Handle link preview buttons
    if (target.parentElement && target.parentElement.parentElement) {
      const grandparent = target.parentElement.parentElement;
      const parent = target.parentElement;
      if (grandparent.className === 'pm-lp-close-buttons') {

        // Id of link preview element
        const id = last(parent.className.split('-'));
        const element = id && document.querySelector(`[data-pm-link-preview-id="${id}"]`);

        // Close Buttons
        if (parent.className && parent.className.startsWith('close')) {
          if (element && element.parentElement) {
            element.parentElement.removeChild(element);
            this.handleChangeDebounced(this.body.innerHTML);
          }
        } else if (parent.className && parent.className.startsWith('link-only')) { // Unlinking
          if (element) {
            const href = element.getAttribute('data-pm-link-preview-url');
            const anchorNode = document.createElement('a');
            if (href) {
              anchorNode.setAttribute('href', href);
              anchorNode.innerHTML = href;
              element.outerHTML = anchorNode.outerHTML;
            }
          }
        }
      }
    }

    // HACK(SHIN): Allow for clicking on anchor tags to open in url since standard anchor
    // tag behavior does not work when contenteditable
    const anchorElement = target.tagName === 'A'
      ? target
      : target.parentElement && target.parentElement.tagName === 'A' && target.parentElement;
    if (anchorElement) {
      const url = anchorElement.getAttribute('href');

      // HACK(SHIN): See if we can handle blobs elsewhere
      if (url && !url.startsWith('blob')) {
        window.open(url, '_blank');
        e.preventDefault();
        e.stopPropagation();
      }
      return;
    }

    // If in draft, remove placeholder styling on click
    if (target.className === 'data-pm-var' && draft) {
      target.removeAttribute('class');
      target.removeAttribute('contenteditable');
      target.removeAttribute('style');
      const sel = window.getSelection();
      const range = document.createRange();
      if (sel && range) {
        range.setStartBefore(target);
        range.setEndAfter(target);
        sel.removeAllRanges();
        sel.addRange(range);
      }
    }

    if (target.dataset && target.dataset.pmDeleteCalendarInvite) {
      const node = this.ref && this.ref.getElementsByClassName('pm-calendar-invite')[0];
      if (node && node.parentNode) {
        node.parentNode.removeChild(node);
        this.handleChangeDebounced(this.body.innerHTML);
      }
    }

    this.selectedPlaceholderIndex = undefined;
    this.setState({
      variablePopoverPosition: this.getPopoverPosition() || variablePopoverPosition,
    });
  }

  handleVariableSelect = (value: string) => {
    const style = 'color: #444444; background-color: rgba(255, 223, 50, 0.25); '
    + 'border-radius: 2px;' +
      'padding: 1px 4px; font-weight: 500; cursor: pointer;';
    const spanNode = document.createElement('span');
    spanNode.setAttribute('style', style);
    spanNode.setAttribute('contenteditable', 'false');
    spanNode.className = 'data-pm-var';
    spanNode.innerHTML = value;

    const selection = document.getSelection();
    const range = selection && selection.getRangeAt(0);

    // Insert Node and call save
    if (range) {
      range.insertNode(spanNode);
      if (selection) {
        selection.collapseToEnd();
      }
      this.handleChangeDebounced(this.getBody());
    }
  }

  handleFocus = () => {
    this.setState({ focus: true });
  }

  addCalendarInvite = (event: DraftCalendarInvite) => {
    const { getContact } = this.props;
    const contacts = getContact ? event.recipients.map(r => getContact(parseAddress(r).email)) : [];

    this.injectElement(
      <CalendarInvite event={event} noBranding={!!this.props.noBranding} contacts={contacts} />,
      true,
    );
  }

  replaceCalendarInvite = (event: DraftCalendarInvite) => {
    const { getContact } = this.props;
    const contacts = getContact ? event.recipients.map(r => getContact(parseAddress(r).email)) : [];
    const node = this.ref && this.ref.getElementsByClassName('pm-calendar-invite')[0];

    if (node) {
      ReactDOM.render(
        <CalendarInvite event={event} noBranding={!!this.props.noBranding} contacts={contacts} />,
        node.parentElement,
        () => {
          this.handleChangeDebounced(this.body.innerHTML);
        },
      );
    }
  }

  // Update body directly via DOM and signal onChange event
  updateSignature = (signature: string) => {
    const signatureElement = this.body.querySelector('#psignature');
    if (signatureElement) {
      const fakeNode = FakeDOM().createElement('div');
      fakeNode.innerHTML = signature;

      // Replace signature
      signatureElement.outerHTML = fakeNode.innerHTML;
      const textContent = fakeNode.textContent;

      // Remove cases where sent from polymail is in signature
      if (textContent && textContent.toLowerCase().includes('polymail')) {
        const sfpNode = this.body.querySelector('.pm-sfp');
        if (sfpNode && sfpNode.parentElement) {
          sfpNode.parentElement.removeChild(sfpNode);
        }
      }
    }

    this.handleChangeDebounced(this.body.innerHTML);
  }

  updateSentFromPolymail = (enabled: boolean) => {
    if (!enabled) {
      return;
    }

    if (!this.body) {
      return;
    }

    // If SFP exists don't do anything
    const sfpExists = this.body.querySelector('.pm-sfp');
    if (!!sfpExists) {
      return;
    }

    // Handles proper insertion of sent from polymail
    const accountSignature = this.body.querySelector('#psignature');
    const signatureText = accountSignature && accountSignature.textContent;
    if (signatureText && !signatureText.toLowerCase().includes('polymail')) {
      const sfpDiv = generateSentFromPolymailHtmlDiv();
      if (accountSignature && accountSignature.parentElement) {
        accountSignature.insertAdjacentElement('afterend', sfpDiv);
      } else {
        this.body.appendChild(sfpDiv);
      }
      this.handleChangeDebounced(this.body.innerHTML);
    }
  }

  // Getter to identify if attachment or meta exists in body
  attachmentExistsInBody = (id: string) => {
    return !!this.body.querySelector(`[data-pm-attachment-id="${id}"]`);
  }

  replaceAttachment = (attachment: Attachment) => {
    if (attachment.cloud) {
      const element = (
        <CloudAttachmentCell
          id={attachment.id}
          iconUrl={filenameIconUrl(attachment.filename)}
          cloudUrl={attachment.cloudUrl}
          filename={attachment.filename}
          onDelete={this.handleCloudAttachmentRemove}
          length={attachment.length}
          progress={1}
        />);
      const id = `data-pm-attachment-id-${attachment.id}`;
      this.replaceElement(id, element);
    }

    // Handles replacing inline image
    if (attachment.inline) {
      const image = new Image();
      image.src = attachment.cloudUrl;

      // Handles optimistic forward cases for inline attachments
      if (attachment.originalCid) {
        const strippedCid = attachment.originalCid.replace('<', '').replace('>', '');
        const original = document.querySelector(`[src="cid:${strippedCid}"]`);
        if (original) {
          original.setAttribute('id', `pm-inline-id-${attachment.id}`);
          original.setAttribute('src', attachment.cloudUrl);
          original.setAttribute('data-pm-attachment-id', attachment.id);
        }
      }

      image.onload = () => {
        const id = `pm-inline-id-${attachment.id}`;
        const found = document.getElementById(id);
        const element = (
          <img
            id={`pm-inline-id-${attachment.id}`}
            src={attachment.cloudUrl}
            data-pm-attachment-id={attachment.id}
            style={{
              maxWidth: found && found.style.maxWidth || '100%',
              width: found && found.style.width,
              height: found && found.style.height,
              maxHeight: found && found.style.maxHeight,
            }}
          />
        );

        this.replaceElement(id, element);
      };
    }
  }

  // TODO(SHIN): Implement progress when available
  replaceAttachmentMeta = (meta: AttachmentMeta, error?: Error) => {
    const file: any | undefined = meta.file;
    const src = meta.cloudUrl ||
      file && (file.preview || file.webkitRelativePath) ||
      '';
    const filename = meta.filename || meta.file && meta.file.name || '';
    const length = meta.length || meta.file && meta.file.size || 1000;
    if (meta.cloud) {
      const element = (
        <CloudAttachmentCell
          id={meta.attachment}
          progress={meta.progress || 0}
          iconUrl={filenameIconUrl(filename)}
          cloudUrl={src}
          filename={filename}
          onDelete={this.handleCloudAttachmentRemove}
          length={length}
          error={error}
          isMeta
        />);
      const id = `data-pm-attachment-id-${meta.attachment}`;
      this.replaceElement(id, element);
    }

    // HACK(SHIN): Set explicit width for 100% to prevent composer truncate when scrollbars
    // always showing
    // HACK(SHIN): Only replace attachment meta updates when progress === 1 and image is
    // still loading. fixes issue where replacements were happening to finished attachments
    // in SequenceStageMessage (May want to rethink if we bring progress UI for inline attachments)
    // HACK(SHIN): Replacing when attachmentMeta changes
    if (meta.inline) {
      const image = new Image();
      const uploading = (meta.progress || 0) < 1;
      image.src = src;
      image.onload = () => {
        const element = (
          <img
            id={`pm-inline-id-${meta.attachment}`}
            src={src}
            data-pm-attachment-id={meta.attachment}
            className={uploading ? 'pm-inline-loading' : undefined}
            style={{
              maxWidth: '100%',
            }}
          />
        );
        const id = `pm-inline-id-${meta.attachment}`;
        const check = document.getElementById(id);
        if (!check) {
          return;
        }
        const checkSrc = (check as HTMLImageElement).src;

        // HACK(SHIN): Handler replacing inline image for blob url changes
        if (checkSrc && checkSrc !== src) {
          this.replaceElement(id, element);
        }

        // Do not do anything if progress is not equal to one
        if (meta.progress !== 1) {
          return;
        }

        // Only replace if element is still loading
        if (check && check.className === 'pm-inline-loading') {
          this.replaceElement(id, element);
        }
      };
    }
  }

  // Update body directly via DOM and signal onChange event
  injectCloudAttachmentMeta = (meta: AttachmentMeta, error?: Error) => {
    const file: any | undefined = meta.file;
    const src = meta.cloudUrl ||
      file && (file.preview || file.webkitRelativePath) ||
      '';

    const CloudElement = (
      <CloudAttachmentCell
        id={meta.attachment}
        progress={meta.progress || 0}
        iconUrl={filenameIconUrl(meta.file && meta.file.name || '')}
        cloudUrl={src}
        filename={meta.file && meta.file.name || ''}
        onDelete={this.handleCloudAttachmentRemove}
        length={meta.file && meta.file.size || 1000}
        error={error}
        isMeta
      />);

    this.injectElement(CloudElement);
  }

  // Update body directly via DOM and signal onChange event
  // HACK(SHIN): Set explicit width for 100% to prevent composer truncate when scrollbars
  // always showing
  injectInlineAttachmentMeta = (meta: AttachmentMeta) => {
    const isDraft = this.props.draft !== undefined;
    const file: any | undefined = meta.file;
    const src = meta.cloudUrl ||
      file && (file.preview || file.webkitRelativePath || URL.createObjectURL(file)) ||
      '';
    const InlineImage = (
        <img
          className="pm-inline-loading"
          id={`pm-inline-id-${meta.attachment}`}
          src={src}
          data-pm-attachment-id={meta.attachment}
          data-pm-template-id={isDraft && this.props.template}
          style={{
            maxWidth: '100%',
          }}
        />
    );

    // HACK(SHIN): Sets explicit width to outer container to prevent composer truncate
    this.injectElement(InlineImage);
  }

  // Update body directly via DOM and signal onChange event
  injectSignatureImageAttachmentMeta = (meta: AttachmentMeta) => {
    const file: any | undefined = meta.file;
    const src = meta.cloudUrl ||
      file && (file.preview || file.webkitRelativePath) ||
      '';
    const InlineImage = (
      <img
        id={`pm-inline-id-${meta.attachment}`}
        src={src}
        data-pm-signature-image-id={meta.attachment}
        data-pm-account-id={meta.resourceId}
        style={{
          maxWidth: '100%',
        }}
      />
    );

    this.injectElement(InlineImage);
  }

  // HACK(SHIN): Set explicit width for 100% to prevent composer truncate when scrollbars
  // always showing
  replaceSignatureImageAttachmentMeta = (meta: AttachmentMeta) => {
    const file: any | undefined = meta.file;
    const src = meta.cloudUrl ||
      file && (file.preview || file.webkitRelativePath) ||
      '';
    const element = (
      <img
        id={`pm-inline-id-${meta.attachment}`}
        src={src}
        data-pm-signature-image-id={meta.attachment}
        data-pm-account-id={meta.resourceId}
        style={{
          maxWidth: '100%',
        }}
      />
    );
    const id = `pm-inline-id-${meta.attachment}`;
    this.replaceElement(id, element);
  }

  replaceSignatureImageAttachment = (attachment: Attachment) => {
    const image = new Image();
    image.src = attachment.cloudUrl;
    image.onload = () => {
      const element = (
        <img
          id={`pm-inline-id-${attachment.id}`}
          src={attachment.cloudUrl}
          data-pm-signature-image-id={attachment.id}
          style={{
            maxWidth: '100%',
          }}
        />
      );
      const id = `pm-inline-id-${attachment.id}`;
      this.replaceElement(id, element);

      // Handle attachment replace by updating body
      this.handleChangeDebounced(this.body.innerHTML);
    };
  }

  replaceElement = (id: string, element: JSX.Element) => {
    // Creates node for element renders and replaces
    const containerDiv = FakeDOM().createElement('div');
    ReactDOM.render(
      element,
      containerDiv,
      () => { // Callback to save changes
        // Replaces element
        const found = this.body && this.body.querySelector(`#${id}`);
        if (found && found.parentElement) {
          found.outerHTML = containerDiv.innerHTML;
        }

        ReactDOM.unmountComponentAtNode(containerDiv);
        this.handleChangeDebounced(this.body.innerHTML);
      });
  }

  injectElement = (element: JSX.Element, atEnd: boolean = false) => {
    // Attachment Node
    const node = document.createElement('div');
    const selection = window.getSelection();

    // New line node
    const newLine = document.createElement('div');
    newLine.innerHTML = '<br>';

    ReactDOM.render(element, node, () => {
      const copyNode = node.cloneNode(true);
      const anchorNode = selection!.anchorNode;
      const composeParentSelected = anchorNode && anchorNode === this.ref;
      const rangeExists = selection && selection.rangeCount > 0;

      // Check for range intersections where selection can be inside an attachment
      const elements = document.querySelectorAll('.compose-attachment-cloud-container');
      let intersectedAttachment: HTMLElement|undefined;
      if (elements && rangeExists && elements.length > 0) {
        Array.from(elements).forEach((el: HTMLElement) => {
          if (selection!.containsNode(el, true)) {
            intersectedAttachment = el;
          }
        });
      }

      // Inject cloud attachment
      if (!atEnd &&
        rangeExists &&
        document.activeElement === this.body &&
        !composeParentSelected
      ) {
        const range = selection!.getRangeAt(0);

        if (intersectedAttachment) {
          range.setEndAfter(intersectedAttachment);
          range.setStartAfter(intersectedAttachment);
        }

        range.insertNode(copyNode);
        selection!.collapseToEnd();

      } else {
        const signatureElement = this.body.querySelector('#psignature');
        if (signatureElement && signatureElement.parentElement) {
          signatureElement.parentElement.insertBefore(copyNode, signatureElement);
          signatureElement.parentElement.insertBefore(newLine, signatureElement);
        } else {
          this.body.appendChild(copyNode);
        }
      }

      ReactDOM.unmountComponentAtNode(node);

      this.handleChangeDebounced(this.body.innerHTML);
    });
  }

  handleChange = (html: string) => {
    const { onChange } = this.props;
    if (onChange && this.body) {
      const doc = FakeDOM().createElement('div');
      doc.innerHTML = this.body.innerHTML;
      const closeCont = doc.getElementsByClassName('pm-cal-invite-close-container')[0];
      if (closeCont) {
        closeCont.innerHTML = '';
      }

      // Sanitize any resize elements out
      this.sanitizeResizeHandlers(doc);

      onChange(doc.innerHTML);
    }
  }

  // Returns body of contenteditable
  getBody = () => {
    const doc = FakeDOM().createElement('div');
    doc.innerHTML = this.body.innerHTML;
    const closeCont = doc.getElementsByClassName('pm-cal-invite-close-container')[0];
    if (closeCont) {
      closeCont.innerHTML = '';
    }

    // Sanitize any resize elements out
    this.sanitizeResizeHandlers(doc);
    return doc.innerHTML;
  }

  getComputedStyleProperty = (el: HTMLElement, propName: string): string | undefined => {
    if (window.getComputedStyle) {
      return window.getComputedStyle(el)[propName];
    }

    if (el.style) {
      return el.style[propName];
    }

    return;
  }

  getSelectionStyleProperty = (propName: string): string | undefined => {
    let containerEl;
    if (window.getSelection) {
      const sel = window.getSelection();
      if (sel && sel.rangeCount) {
        const range = sel.getRangeAt(0);
        containerEl = range.commonAncestorContainer;

        // Make sure we have an element rather than a text node
        if (containerEl.nodeType === 3) {
          containerEl = containerEl.parentNode;
        }
      }

      if (containerEl instanceof HTMLElement) {
        return this.getComputedStyleProperty(containerEl, propName);
      }
    }

    return;
  }

  getSelectionStyleColorProperty = (propName: string): string | undefined => {
    let containerEl;
    if (window.getSelection && this.body) {
      const sel = window.getSelection();
      if (sel && sel.rangeCount) {
        const range = sel.getRangeAt(0);
        containerEl = range.commonAncestorContainer;

        // Make sure we have an element rather than a text node
        if (containerEl.nodeType === 3) {
          containerEl = containerEl.parentNode;
        }
      }

      // Try to identify differences in color
      let matchedColor: string|undefined;
      if (containerEl instanceof HTMLElement) {
        matchedColor = this.getComputedStyleProperty(containerEl, propName);
      }
      const bodyDefaults = this.getComputedStyleProperty(this.body, propName);
      if (matchedColor === bodyDefaults) {
        return 'default';
      }

      return matchedColor;
    }

    return;
  }

  pasteCalendarUrl = (url: string) => {
    if (!this.state.selection) {
      const sel = window.getSelection();
      if (sel && sel.rangeCount) {
        this.setState(
          {
            selectionCenter: this.getCenter(),
            lastSelectedRange: sel.getRangeAt(0),
          },
          () => this.handleHrefSubmit('', url),
        );
      }
      return;
    }

    this.handleHrefSubmit('', url);
  }

  handleHide = () => {
    this.setState(
      {
        lastSelectedRange: undefined,
        selection: false,
        forceHyperlinkPopup: false,
      },
    );
  }

  handleRemoveFormatting = () => {
    document.execCommand('removeFormat');
  }

  restoreCursorPosition = () => {
    const selection = window.getSelection();
    if (selection && this.state.oldContentEditablePosition) {
      selection.removeAllRanges();
      selection.addRange(this.state.oldContentEditablePosition);
    }

    this.setState({ oldContentEditablePosition: undefined });
  }

  saveCursorPosition = () => {
    const selection = window.getSelection();
    if (selection && !!selection.rangeCount) {
      this.setState({ oldContentEditablePosition: selection.getRangeAt(0) });
    }
  }

  handleKeyUp = (e: React.KeyboardEvent<HTMLDivElement>) => {
    const compose = e.currentTarget;
    const divs = compose.getElementsByTagName('div');
    let div: HTMLDivElement;
    let text;
    let lastDivRightToLeft = false;

    // tslint:disable-next-line:prefer-for-of
    for (let i = 0; i < divs.length; i += 1) {
      div = divs[i];
      if (div.parentNode !== compose || div.getAttribute('data-pm-history')) {
        continue;
      }

      text = (div.textContent || div.innerText || '').trim();

      if (text && isRightToLeftChar(text.charCodeAt(0))) {
        div.setAttribute('dir', 'rtl');
        lastDivRightToLeft = true;
      } else if (!text && lastDivRightToLeft) {
        div.setAttribute('dir', 'rtl');
        lastDivRightToLeft = true;
      } else {
        div.removeAttribute('dir');
        lastDivRightToLeft = false;
      }
    }
  }

  // Moves cursor to specified mouse position
  moveCursorToPosition = (position: Point) => {

    // Takes element at cursor position and then moves selection to that point if contained in body
    const element = document.elementFromPoint(position.x, position.y);
    if (this.body && element && this.body.contains(element)) {

      // Do not move cursor if point was contained in a signature
      const signature = this.body.querySelector('#psignature');
      if (signature && signature.contains(element)) {
        return;
      }

      const selection = document.getSelection();
      if (selection && !!selection.rangeCount) {
        const range = selection.getRangeAt(0);
        range.selectNode(element);
        range.collapse(true);
      }
    }
  }

  isEditing = () => {
    return this.focus || document.activeElement?.className === 'compose-editable';
  }

  render() {
    const { variables } = this.props;
    const {
      selectionCenter,
      focus,
      variablePopoverPosition,
      html,
      forceHyperlinkPopup,
      lastSelectedRange,
    } = this.state;

    return (
      <div
        className="content-editable"
        tabIndex={1}
        ref={this.handleRef}
        onClick={this.handleClick}
        onBlur={this.handleBlur}
      >
        <FormattingBar
          onOptionSelect={this.handleFormatOption}
          center={selectionCenter}
          onHrefSubmit={this.handleHrefSubmit}
          onFontSelect={this.handleFontSelect}
          onFontSizeSelect={this.handleFontSizeSelect}
          onFontColorSelect={this.handleFontColorSelect}
          onFontColorReset={this.handleFontColorReset}
          selectedText={lastSelectedRange && lastSelectedRange.toString() || ''}
          bolded={focus && !!document.queryCommandState && document.queryCommandState('bold')
            ? true : undefined}
          italicized={focus && !!document.queryCommandState && document.queryCommandState('italic')
            ? true : undefined}
          underlined={focus && !!document.queryCommandState &&
            document.queryCommandState('underline') ? true : undefined}
          orderedList={focus && !!document.queryCommandState
            && document.queryCommandState('insertOrderedList') ? true : undefined}
          unorderedList={focus && !!document.queryCommandState
            && document.queryCommandState('insertUnorderedList') ? true : undefined}
          forceHyperlink={forceHyperlinkPopup}
          selectedFontSize={this.getSelectionStyleProperty('fontSize')}
          selectedFontFamily={this.getSelectionStyleProperty('fontFamily')}
          selectedFontColor={this.getSelectionStyleColorProperty('color')}
          onHide={this.handleHide}
          onRemoveFormatting={this.handleRemoveFormatting}
        />
        {variables && <VariablePopover
          top={variablePopoverPosition.y}
          right={variablePopoverPosition.x}
          variables={variables}
          onSelect={this.handleVariableSelect}
          show={focus}
        />}
        <div
          onKeyDown={this.handleHotkey}
          onKeyUp={this.handleKeyUp}
          className="compose-editable"
          ref={this.handleBodyRef}
          contentEditable={!this.props.disabled}
          dangerouslySetInnerHTML={{ __html: html }}
          onInput={this.onChange}
          onPaste={this.onPaste}
          onSelect={this.onSelect}
          onFocus={this.handleFocus}
        />
      </div>
    );
  }
}
