import Cache, { Stores } from '@src/lib/db';
import { Observable, Observer, Subject, Subscription, AjaxError } from 'rxjs';
import { Combiner, rateLimiter } from '@src/lib/rx';
import { Backoff } from '@src/worker/Backoff';
import { ApiError } from '@src/api/base';
import {
  CommitStatus,
  FileUploadMeta,
  CloudFile,
  Commit,
  AttachmentMeta,
} from '@src/types';
import bus, { getBus, BusEvent, BusEventName } from '@src/worker/bus';
import { captureException } from '@sentry/browser';
import { CloudFileClient, CloudFileAPI } from '@src/api/cloudFile';
import {
  sendAttachmentMetaUpdated,
  sendDraftAttachmentAddFailed,
} from '@src/worker/bridge/workerEvents';

export const resetFileUploadStatuses = (): Observable<null> => {
  return Cache.list<FileUploadMeta>(
    Stores.FileUploads,
    commit => commit.status === CommitStatus.Processing,
  )
    .flatMap((commits: FileUploadMeta[]) => {
      return Cache.load(
        Stores.FileUploads,
        commits.map(c => ({ ...c, status: CommitStatus.Ready })),
      );
    });
};

export const MAX_COMMITS_PER_BATCH = 20;

// FileUploadSync continuously sends idempotent commits to the API.
export class FileUploadSync {
  // State
  readonly workerId: string;
  isRunning: boolean;
  fetchRatelimit: number;
  resolveRatelimit: number;
  private backoff: Backoff;
  private cancelSignal: Subject<null>;
  private retrigger: boolean;

  // Dependencies
  private api: CloudFileAPI;
  private bus: Subject<BusEvent>;
  private sub: Subscription;

  constructor(api: CloudFileAPI = new CloudFileClient()) {
    this.workerId = 'FileUploadSync';
    this.isRunning = false;
    // HACK(SHIN): Set backoff to 30 seconds until we have a better retry mechanism
    this.backoff = new Backoff(this.workerId, 30);
    this.cancelSignal = new Subject<null>();
    this.retrigger = false;

    // how often FileUploadSync fetches from cache
    this.fetchRatelimit = 1000; // in milliseconds\

    // the rate limit how often we make calls to the api
    this.resolveRatelimit = 1000; // in milliseconds

    this.api = api;
    this.bus = getBus();
  }

  init() {
    // Listen for bus events
    if (!this.sub) {
      this.sub = this.bus.subscribe({
        next: (event: BusEvent) => {
          this.handleBusEvent(event);
        },
        error: (e: Error) => {
          captureException(e);
        },
      });
    }
  }

  deinit() {
    if (this.sub) {
      this.sub.unsubscribe();
    }
  }

  // deleteItems is ignored by FileUploadSync as we
  // want to retain commits on clearCache
  clearCache(): Observable<null> {
    if (this.isRunning) {
      this.cancelSignal.next(null);
    }

    // clearCache is triggered by HistorySync
    // Don't want to clear commits this way
    this.backoff.clearErrors();
    return Observable.of(null);
  }

  // reset marks any commits in `Processing` status as `Ready`
  reset(): Observable<null> {
    return Observable.create((runner: Observer<null>) => {
      const combiner = new Combiner<FileUploadMeta[]>();
      combiner.subscribe({
        next: (commits: FileUploadMeta[]) => {
          Cache.load(
            Stores.FileUploads,
            commits
              .filter(commit => commit.status === CommitStatus.Processing)
              .map(commit => ({ ...commit, status: CommitStatus.Ready })),
          ).subscribe({
            error: (e: Error) => {
              combiner.error(e);
            },
            complete: () => {
              combiner.complete();
            },
          });
        },
        error: (e: Error) => {
          runner.error(e);
        },
        complete: () => {
          runner.next(null);
          runner.complete();
        },
      });
      combiner.run(this.fetch());
    });
  }

  run(): Observable<null> {
    return Observable.create((runner: Observer<null>) => {
      if (!this.backoff.isReady()) {
        runner.next(null);
        runner.complete();
        return;
      }
      if (this.isRunning) {
        runner.next(null);
        runner.complete();
        return;
      }
      this.isRunning = true;

      // Allow commits to buffer
      const fetchLimiter = rateLimiter<FileUploadMeta[]>(this.fetchRatelimit);

      // Throttle API calls
      const resolveLimiter = rateLimiter<Error|null>(this.resolveRatelimit);

      // Error aggregator for each batch of CommitSync
      const errors: Error[] = [];

      const combiner = new Combiner<FileUploadMeta[]>();
      combiner.takeUntil(this.cancelSignal).flatMap((commits: FileUploadMeta[]) => {
        if (commits && commits.length === 0) {
          // Trigger Commit Sync when completed
          bus.triggerSync('CommitSync');
          combiner.complete();
          return Observable.empty();
        }

        // Merge commits with the same resource type and id
        return Observable.of(commits);

      })
      .subscribe({
        next: (commits: FileUploadMeta[]) => {
          // Try triggering CommitSync on every next process
          bus.triggerSync('CommitSync');

          if (commits.length <= MAX_COMMITS_PER_BATCH) {
            resolveLimiter(this.resolve(commits.slice(0, MAX_COMMITS_PER_BATCH))).subscribe({
              next: (e: Error|null) => {
                // Push errors to aggregator
                if (e instanceof Error) {
                  errors.push(e);
                }
              },
              error: (e: Error) => combiner.error(e),
              complete: () => {
                // NOTE(SHIN): If we had any errors after finishing the last page, error out
                // to trigger backoff (we only pass through AJAX/or 500s)
                if (!!errors.length) {
                  combiner.error(errors[0]);
                  return;
                }

                // Otherwise run another instance of FileUploadSync (terminates if empty)
                combiner.run(fetchLimiter(this.fetch()));
              },
            });
          } else {
            resolveLimiter(this.resolve(commits.slice(0, MAX_COMMITS_PER_BATCH))).subscribe({
              next: (e: Error | null) => {
                // Push errors to aggregator
                if (e instanceof Error) {
                  errors.push(e);
                }
              },
              error: (e: Error) => {
                combiner.error(e);
              },
              complete: () => { // Recurses the entire set by running again with slice (len-Max)
                return combiner.run(
                  Observable.of(commits.splice(MAX_COMMITS_PER_BATCH)),
                );
              },
            });
          }
        },
        error: (e: Error) => {
          this.isRunning = false;
          this.backoff.setError(e);

          // Try retriggering sync after error
          if (this.backoff.until) {
            const delay = this.backoff.until.getTime() - new Date().getTime() + 100;
            global.setTimeout(
              () => bus.triggerSync(this.workerId),
              delay,
            );
          }

          runner.error(e);
        },
        complete: () => {
          this.isRunning = false;
          this.backoff.clearErrors();
          runner.next(null);
          runner.complete();

          // Handle retrigger
          if (this.retrigger) {
            this.retrigger = false;
            bus.triggerSync(this.workerId);
          }
        },
      });

      // Buffer initial fetch because it's common for commits to be submitted
      // together. And we want to resolve after the batch has been submitted
      // rather than on the first one.
      combiner.run(Observable.of(null)
        .delay(this.fetchRatelimit)
        .flatMap(() => this.fetch()),
      );
    });
  }

  // Resolves resolves a batch of commits with the API. Resolves commit on completion
  private resolve(metas: FileUploadMeta[]): Observable<Error|null> {
    return Observable.zip(
      // Save processing state
      ...metas.map((commit) => {
        return Cache.save(Stores.FileUploads, { ...commit, status: CommitStatus.Processing });
      }),
    )
    .flatMap(() => {
      return Observable.merge(
        ...metas.map((meta): Observable<Error|null> => {
          let cloudFile: CloudFile;
          return this.api.uploadWithProgress(meta.meta.file!)
            .filter((result) => {
              if (typeof result === 'number') {
                const progressMeta: AttachmentMeta = {
                  ...meta.meta,
                  progress: result >= 1 ? 0.99 : result,
                };
                sendAttachmentMetaUpdated(progressMeta);
                return false;
              }

              return true;
            })
            .flatMap((result: CloudFile) => {
              cloudFile = result;

              // Save meta and fetch commit meta
              return Cache.deleteSingle(Stores.FileUploads, meta.id)
                .flatMap(() => this.fetchCommit(meta));
            })
            .flatMap((commits) => {
              const commit = commits[0];
              if (commit && commit.payload && cloudFile) {
                const updatedCommit: Commit = {
                  ...commit,
                  status: CommitStatus.Ready,
                  payload: {
                    ...commit.payload,
                    ...cloudFile,
                  },
                };
                return Cache.save(Stores.Commits, updatedCommit);
              }

              return Observable.of(null);
            })
            .catch((e: Error) => {
              // When error is of type 400..<500, mark as error and do not process
              const apiError = (e as ApiError);

              // Marks ajax errors and type errors similar to 500/connectivity errors to retry
              const ajaxZeroError = (e as AjaxError).status === 0;

              // Set back to ready on 500s and ajaxError zeroes and throw out
              if ((apiError.code && apiError.code >= 500 && apiError.code < 600) || ajaxZeroError) {

                const isDirectory = meta.meta.file &&
                  ['app', ''].indexOf(meta.meta.file.type) > -1;
                const emptyFile = meta.meta.file && meta.meta.file.size <= 0;

                // NOTE(SHIN): Specialized error handler when file is directory/app package
                // to clear out any stuck commits should be safe to remove after March 10, 2020
                if (emptyFile || isDirectory) {
                  return Cache.deleteSingle(Stores.FileUploads, meta.id)
                    .flatMap(() => this.fetchCommit(meta))
                    .flatMap((commits: Commit[]) => {
                      const commit = commits[0];
                      if (commit && commit.rehydrateMeta) {
                        sendDraftAttachmentAddFailed(commit.rehydrateMeta, new Error('Invalid file type. Please try again'));
                      }

                      // Handle updating failed commit, replace payload with error
                      const failedCommit: Commit = {
                        ...commit,
                        status: CommitStatus.Error,
                        rehydrateMeta: {
                          ...commit.rehydrateMeta,
                          error: new Error('Invalid file type. Please try again'),
                        },
                      };
                      return Cache.save(Stores.Commits, failedCommit);
                    });
                }

                // Reset commit to try again with backoff
                return Cache.save(Stores.FileUploads, {
                  ...meta,
                  status: CommitStatus.Ready,
                }).flatMap(() => {
                  // HACK(SHIN): Does not reset unless we do a fetch immediately after.
                  // TODO(SHIN): Resolve this hack later
                  return Cache.get(Stores.FileUploads, meta.id! || '');
                })
                .flatMap((result) => { // Pass through error object to be handled on (next) case
                  return Observable.of(e);
                });
              }

              // On 400 - 500, fetch the corresponding commit and send errored state to app
              if (apiError.code && apiError.code >= 400 && apiError.code < 500 && meta.id) {
                return Cache.deleteSingle(Stores.FileUploads, meta.id)
                  .flatMap(() => this.fetchCommit(meta))
                  .flatMap((commits: Commit[]) => {
                    const commit = commits[0];
                    if (commit && commit.rehydrateMeta) {
                      sendDraftAttachmentAddFailed(commit.rehydrateMeta, e);
                    }

                    // Handle updating failed commit, replace payload with error
                    const failedCommit: Commit = {
                      ...commit,
                      status: CommitStatus.Error,
                      rehydrateMeta: {
                        ...commit.rehydrateMeta,
                        error: e,
                      },
                    };
                    return Cache.save(Stores.Commits, failedCommit);
                  });
              }

              // Otherwise fail with standard message and send failed
              captureException(e);
              // tslint:disable-next-line:no-console
              console.error(e);

              // Fail commit by deleting fileUpload, fetching commit, and sending failed
              return Cache.deleteSingle(Stores.FileUploads, meta.id)
                .flatMap(() => this.fetchCommit(meta))
                .flatMap((commits: Commit[]) => {
                  const commit = commits[0];
                  if (commit && commit.rehydrateMeta) {
                    const message = 'File failed to upload: ' + e.message;

                    // HACK(SHIN): Send Failed message with an object that conforms to error type
                    // since error objects cannot be copied
                    sendDraftAttachmentAddFailed(
                      commit.rehydrateMeta,
                      ({
                        description: message,
                        message,
                      }) as any,
                    );
                  }

                  // Handle updating failed commit
                  const failedCommit: Commit = {
                    ...commit,
                    status: CommitStatus.Error,
                    payload: e,
                    rehydrateMeta: {
                      ...commit.rehydrateMeta,
                      error: e,
                    },
                  };
                  return Cache.save(Stores.Commits, failedCommit);
                });
            });
        }),
      );
    });
  }

  // Returns commit if found for the file upload meta (using combo of resourceId + attachmentId)
  private fetchCommit = (meta: FileUploadMeta): Observable<Commit[]> => {
    return Cache.list(
      Stores.Commits,
      (commit: Commit) => commit.payload &&
        commit.status === CommitStatus.Pending &&
        commit.resourceHash === meta.resourceId + meta.attachmentId,
    );
  }

  // Fetch retrieves a page of commits from the commit log to process
  private fetch(): Observable<FileUploadMeta[]> {
    this.retrigger = false;
    return Cache.list(Stores.FileUploads, (commit: FileUploadMeta) => {
      return commit.status === CommitStatus.Ready;
    })
    .map(results => results.sort((a, b) => b.created.getTime() - a.created.getTime()));
  }

  private handleBusEvent(event: BusEvent) {
    switch (event.name) {
      case BusEventName.ClearErrors:
        this.backoff.clearErrors();
        break;

      case BusEventName.CancelSync:
        if (event.data.workerId && event.data.workerId !== this.workerId) {
          break;
        }
        this.cancelSignal.next(null);
        return;

      case BusEventName.TriggerSync:
        if (event.data.workerId && event.data.workerId !== this.workerId) {
          break;
        }
        if (this.isRunning) {
          this.retrigger = true;
          return;
        }
        this.run().subscribe({
          next: () => { return; },
          error: (error) => { return; },
          complete: () => { return; },
        });
        break;

      case BusEventName.HistoryClearCache:
        this.clearCache().subscribe();
        break;

      default:
        break;
    }
  }
}

export default FileUploadSync;
