import React from 'react';
import { values, debounce, compact, sortBy, uniq } from 'lodash';

import NavigationHeader from '../../components/NavigationHeader';
import ToggleCell from '../../components/ToggleCell';
import Dropdown from '@src/components/Form/Dropdown';
import { WebSafeFonts } from '@src/lib/fonts/WebSafeFonts';
import {
  WebFontSizes,
  matchFontPixelToPoint,
  WebFontSizeConversions,
} from '@src/lib/fonts/WebFontSizes';
import LongRightArrow from './Icons/LongRightArrow.svg';
import UpgradePrompt from '@src/components/Base/UpgradePrompt/UpgradePrompt';

import { Preferences, Me, Features } from '@src/types';

import './Sending.scss';
import { IS_MAC } from '@src/lib/device';
import { isElectronWindows } from '@src/lib/electron';
import { convertLanguageCodes } from '@src/lib/languages/languages';
import MultiSelectDropdown from '@src/components/Form/MultiSelectDropdown';

export interface Props {
  preferences: Preferences;
  features: Features;
  isMobile?: boolean;
  collapsedMobile?: boolean;
}

export interface DispatchProps {
  updateMe: (me: Me) => void;
  updatePreferences: (preferences: Preferences) => void;
  collapseLsb: (collapse: boolean) => void;
}

interface State {
  followUp: number;
  selectedLanguages: string[];
}

class Sending extends React.PureComponent<Props & DispatchProps, State> {
  private attachmentTooltip: UpgradePrompt;
  private linkTooltip: UpgradePrompt;
  private sentFromPolymailTooltip: UpgradePrompt;
  private debounced: any;

  constructor(props: Props & DispatchProps) {
    super();

    this.debounced = debounce(
      props.updatePreferences,
      500,
      );

    this.state = {
      followUp: props.preferences.followUp,
      selectedLanguages: [],
    };
  }

  UNSAFE_componentWillReceiveProps(nextProps: Props & DispatchProps) {
    if (nextProps !== this.props) {
      this.setState({ followUp: nextProps.preferences.followUp });
    }
  }

  componentDidMount() {
      // check for debounced selected languages
    this.handleCurrentSpellcheckerLanguages();
  }

  handleCurrentSpellcheckerLanguages = () => {
    if (window.PMElectronBridge && window.PMElectronBridge.currentSpellcheckerLanguages) {
      let lang: string [];

      // sometimes the BrowserWindow is not ready yet and it could crash the app if not handled
      try {
        lang = window.PMElectronBridge.currentSpellcheckerLanguages();
      } catch (e) {
        lang = [];
      }

      this.setState({
        selectedLanguages: lang,
      });
    }
  }

  handleToggle = (e: React.SyntheticEvent<HTMLElement>) => {
    const { preferences, updatePreferences, features } = this.props;
    const newPreferences = {};
    const id = e.currentTarget.id;

    if (id === 'cloudAttachments' && !features.cloudAttachments) {
      return;
    }

    if (id === 'linkTracking' && !features.trackLinks) {
      return;
    }

    // Disallow toggling of sentFromPolymail when on free plan
    if (id === 'sentFromPolymail' && !features.disableSentFromPolymail) {
      return;
    }

    if (id) {
      newPreferences[id] = !preferences[id];
      updatePreferences({
        ...preferences,
        ...newPreferences,
      });
    }
  }

  handleRemove = (value: string, index: number, id?: string) => {
    switch (id) {
      case 'spellcheck-language':
        const selectedLanguageCode = this.getSortedLanguages()[index];
        this.setState(
          {
            selectedLanguages: this.state.selectedLanguages
              .filter(l => l !== selectedLanguageCode.code),
          },
          () => {
            if (window.PMElectronBridge?.setSpellcheckerLanguages) {
              window.PMElectronBridge.setSpellcheckerLanguages(this.state.selectedLanguages);
            }
          },
        );
        break;
      default:
        return;
    }
  }

  handleSelect = (value: string, index: number, id?: string) => {
    const { updatePreferences, preferences } = this.props;
    const newPreferences = { ...preferences };

    switch (id) {
      case 'composeFontFamily':
        newPreferences.composeFontFamily = WebSafeFonts[value] || '\\';
        break;
      case 'composeFontSize':
        const pixelValue = WebFontSizeConversions[Number(value)] || 0;
        newPreferences.composeFontSize = pixelValue && pixelValue || 0;
        break;
      case 'spellcheck-language':
        const selectedLanguageCode = this.getSortedLanguages()[index];
        const bridge = window.PMElectronBridge;
        if (bridge) {
          const { selectedLanguages } = this.state;
          const newLanguages = uniq([
            ...selectedLanguages,
            selectedLanguageCode.code,
          ]);
          this.setState(
            {
              selectedLanguages: newLanguages,
            },
            () => {
              if (bridge?.setSpellcheckerLanguages) {
                bridge.setSpellcheckerLanguages(newLanguages);
              }
            },
          );
        }
        break;
      default:
        break;
    }

    if (id && newPreferences[id] !== undefined) {
      updatePreferences(newPreferences);
    }
  }

  handleAttachmentRef = (ref: UpgradePrompt) => {
    this.attachmentTooltip = ref;
  }

  handleLinkRef = (ref: UpgradePrompt) => {
    this.linkTooltip = ref;
  }

  handleSentFromRef = (ref: UpgradePrompt) => {
    this.sentFromPolymailTooltip = ref;
  }

  handleAttachmentTooltipShow = () => {
    const { features } = this.props;
    const showAttachmentUpgradePrompt = !features.cloudAttachments;

    if (showAttachmentUpgradePrompt) {
      this.attachmentTooltip.show();
    }
  }

  handleLinkTooltipShow = () => {
    const { features } = this.props;
    const showLinkUpgradePrompt = !features.trackLinks;

    if (showLinkUpgradePrompt) {
      this.linkTooltip.show();
    }
  }

  handleLinkTooltipHide = () => {
    this.linkTooltip.hide();
  }

  handleSentFromTooltipShow = () => {
    const { features } = this.props;
    const showSentFromUpgradePrompt = !features.disableSentFromPolymail;

    if (showSentFromUpgradePrompt) {
      this.sentFromPolymailTooltip.show();
    }
  }

  handleSentFromTooltipHide = () => {
    this.sentFromPolymailTooltip.hide();
  }

  handleAttachmentTooltipHide = () => {
    this.attachmentTooltip.hide();
  }

  handleFollowUpChange = (e: React.SyntheticEvent<HTMLInputElement>) => {
    const { preferences } = this.props;

    if (e.currentTarget.value !== undefined) {
      const value = parseInt(e.currentTarget.value, 10);
      if (value >= 1) {
        this.setState(
          { followUp: value * 24 * 60 * 60 },
          () => {
            this.debounced({
              ...preferences,
              followUp: this.state.followUp,
            });
          },
        );
      }
    }
  }

  handleFollowUpToggle = () => {
    const { updatePreferences, preferences } = this.props;
    const DEFAULT_FOLLOW_UP_DAYS = 3 * 60 * 60 * 24;

    updatePreferences({
      ...preferences,
      followUp: !!preferences.followUp ? 0 : DEFAULT_FOLLOW_UP_DAYS,
    });
  }

  handleSharedDraftToggle = () => {
    const { updatePreferences, preferences } = this.props;

    updatePreferences({
      ...preferences,
      defaultShareDraft: !preferences.defaultShareDraft,
    });
  }

  // Gets sorted available language
  getSortedLanguages = () => {
    // Setting spellchecker only works on Windows, so restricting this feature to Windows only
    if (!isElectronWindows) {
      return [];
    }

    // Set spellchecker language
    const bridge = window.PMElectronBridge;

    if (bridge && bridge.getSpellcheckerAvailableLanguages) {
      const languages = compact(
        convertLanguageCodes(bridge.getSpellcheckerAvailableLanguages())
          .filter(l => !!l.value),
      );
      const sortedLanguages = sortBy(languages, 'value');
      return sortedLanguages;
    }

    return [];
  }

  render() {
    const { preferences, features } = this.props;
    const { followUp, selectedLanguages } = this.state;
    const attachmentDescriptionTooltip =
      'To track when recipients download your attachments, upgrade to a paid plan.';
    const linkDescriptionTooltip =
      'To track when recipients click links sent in your emails, upgrade to a paid plan.';
    const fontSizePixelValue = preferences.composeFontSize &&
      matchFontPixelToPoint(preferences.composeFontSize);
    const followUpOptionOn = !!Math.trunc(preferences.followUp / (60 * 60 * 24));

    // Sent from Polymail preferences
    // tslint:disable-next-line:max-line-length
    const sentFromPolymailDescription = `Upgrade to a paid plan to disable "Sent from Polymail" from being added to the end of your messages`;
    const sentFromPolymailPreference = preferences.sentFromPolymail || false;
    const languages = this.getSortedLanguages();

    return (
      <div
        className="settings-panel sending"
      >
        <NavigationHeader
          title="Sending"
          isMobile={this.props.isMobile}
          collapsedMobile={this.props.collapsedMobile}
          collapseLsb={this.props.collapseLsb}
        />
        <div className="main">
          <ToggleCell
            title="Sent from Polymail"
            subtitle={`Add "Sent from Polymail" to the end of your messages`}
            on={features.disableSentFromPolymail ? sentFromPolymailPreference : true}
            onToggle={this.handleToggle}
            toggleId="sentFromPolymail"
            tooltip={<UpgradePrompt
              className="upgrade-tooltip"
              ref={this.handleSentFromRef}
              title="Sent from Polymail"
              description={sentFromPolymailDescription}
            />}
            onTooltipShow={this.handleSentFromTooltipShow}
            onTooltipHide={this.handleSentFromTooltipHide}
          />
          <ToggleCell
            title="Enable message tracking"
            subtitle="Tracks when recipients read your sent messages."
            on={!preferences.disableTrackByDefault}
            onToggle={this.handleToggle}
            toggleId="disableTrackByDefault"
          />
          <ToggleCell
            title="Enable attachment tracking"
            subtitle={'Tracks when recipients download your sent attachments. ' +
              'Enables cloud attachments.'}
            on={features.cloudAttachments ? preferences.cloudAttachments : false}
            onToggle={this.handleToggle}
            toggleId="cloudAttachments"
            tooltip={<UpgradePrompt
              className="upgrade-tooltip"
              ref={this.handleAttachmentRef}
              title="Attachment tracking"
              description={attachmentDescriptionTooltip}
            />}
            onTooltipShow={this.handleAttachmentTooltipShow}
            onTooltipHide={this.handleAttachmentTooltipHide}
          />
          <ToggleCell
            title="Enable link tracking"
            subtitle="Tracks when recipients click links in your sent messages."
            on={features.trackLinks ? preferences.linkTracking : false}
            onToggle={this.handleToggle}
            toggleId="linkTracking"
            tooltip={<UpgradePrompt
              className="upgrade-tooltip"
              ref={this.handleLinkRef}
              title="Link Tracking"
              description={linkDescriptionTooltip}
            />}
            onTooltipShow={this.handleLinkTooltipShow}
            onTooltipHide={this.handleLinkTooltipHide}
          />
          {false && <ToggleCell
            title="Undo send"
            subtitle="Allows you to unsend a message for a few seconds after sending."
            on={preferences.disableTrackByDefault}
            onToggle={this.handleToggle}
          />}
          <ToggleCell
            title="Automatically archive after sending"
            subtitle={IS_MAC ? 'You can still use ⌘ + ⌥ + Enter to send without archiving.' : 'You can still use ctrl + alt + Enter to send without archiving.'}
            on={preferences.sendAndArchive}
            onToggle={this.handleToggle}
            toggleId="sendAndArchive"
          />
          <ToggleCell
            title="Cancel Scheduled Emails on Reply"
            subtitle="Don't send a scheduled email if you received a reply before the scheduled date."
            on={preferences.cancelOnReply}
            onToggle={this.handleToggle}
            toggleId="cancelOnReply"
          />
          <div className="follow-up">
            <ToggleCell
              title="Send with Follow Up Reminder automatically"
              subtitle="You can change or remove this before sending each message."
              on={followUpOptionOn}
              onToggle={this.handleFollowUpToggle}
              toggleId="followUp"
            />
            {followUpOptionOn && <div className="follow-up-days">
              <span>Remind me if no one replies</span>
              <input
                dir="auto"
                type="number"
                value={Math.trunc(followUp / (60 * 60 * 24)).toString()}
                onChange={this.handleFollowUpChange}
              />
              <span>days after sending</span>
            </div>}
          </div>
          <ToggleCell
            title="Show Drafts on Shared Conversations"
            subtitle="Allow your drafts to be seen on conversation's you've shared"
            on={preferences.defaultShareDraft || false}
            onToggle={this.handleSharedDraftToggle}
            toggleId="defaultShareDraft"
          />
          <ToggleCell
            title="Link Previews"
            subtitle="Turn web links into visual previews of the webpage content."
            on={!preferences.disableLinkPreviews}
            onToggle={this.handleToggle}
            toggleId="disableLinkPreviews"
          />
          <div className="link-preview">
            <div className="link">
              www.twitter.com
          </div>
            <LongRightArrow className="icon" />
            <div className="preview">
              <div className="image" />
              <div className="right-column">
                <h4 className="title">Twitter</h4>
                <h5 className="subtitle">From breaking news and enter…</h5>
              </div>
            </div>
          </div>
          {!!languages.length && (
            <div className="language">
              <div className="title">Spellcheck</div>
              <h5 className="subtitle">
                Choose the languages for Polymail to spellcheck as you type.
              </h5>
              <MultiSelectDropdown
                onSelect={this.handleSelect}
                onRemove={this.handleRemove}
                values={this.getSortedLanguages().map(l => l.value)}
                selectedValues={convertLanguageCodes(selectedLanguages).map(l => l.value)}
                readOnly={false}
                typeahead
                id="spellcheck-language"
              />
            </div>
          )}
          <div className="fonts">
            <h4 className="title">Compose font</h4>
            <div className="font-style">
              <Dropdown
                onSelect={this.handleSelect}
                values={Object.keys(WebSafeFonts)}
                startIndex={values(WebSafeFonts).indexOf(preferences.composeFontFamily)}
                readOnly={false}
                typeahead
                id="composeFontFamily"
              />
              <Dropdown
                onSelect={this.handleSelect}
                values={['Default'].concat(WebFontSizes.map(size => size.toString()))}
                startIndex={fontSizePixelValue && WebFontSizes.indexOf(fontSizePixelValue) + 1 || 0}
                id="composeFontSize"
              />
            </div>
            <div
              className="preview"
              style={{
                fontFamily: preferences.composeFontFamily !== '\\' ?
                  preferences.composeFontFamily : undefined,
                fontSize: preferences.composeFontSize !== 0 ?
                  preferences.composeFontSize + 'px'
                  : undefined,
              }}
            >
              This is what your message font will look like.
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Sending;
