/* INSTRUCTIONS *******

** NECESSARY IMPORTS **
import ReactDOM from 'react-dom';

** HOW TO CREATE **
  const cloudElement = (
    <CloudAttachmentCell
      onDelete={this.handleFormatOption}
      attachment={attachment}
    />);

  const node = document.createElement('div');
  ReactDOM.render(cloudElement, node);

  const range = window.getSelection().getRangeAt(0);
  range.insertNode(node);

  window.getSelection().collapseToEnd();

  const newLine = document.createElement('div');
  newLine.innerHTML = '<br>';

  window.getSelection().getRangeAt(0).insertNode(newLine);

** HOW TO UPDATE **
  const element = document.querySelector(`[data-pm-attachment-id='${this.state.attachment.id}']`);
  const content = (
  <CloudAttachmentCell
    onDelete={this.handleFormatOption}
    attachment={a}
  />);

  ReactDOM.render(content, element!.parentElement!);
*/

import React from 'react';
import { truncate, truncatedProgress } from '@src/lib/attachment';

import './CloudAttachmentCell.scss';
import { ApiError } from '@src/api/base';
import { map } from 'lodash';

interface Props {
  id: string;
  filename: string;
  progress: number;
  iconUrl: string;
  cloudUrl: string;
  length: number;
  onDelete?: (id: string) => void;
  template?: string;

  error?: Error;
  isMeta?: boolean;
}

// TODO(SHIN): See if download property works with cloud attachment tracking
const CloudAttachmentCell = (props: Props) => {
  const handleOnClick = (e: React.MouseEvent<HTMLButtonElement>) => {
    e.preventDefault();
    if (props.onDelete) {
      props.onDelete(props.id);
    }
  };

  const error = props.error;
  let errorString = error && error.message || '';
  if (error && (error as ApiError).errors) {
    errorString = map((error as ApiError).errors, e => e).join(' ');
  }

  // Wrap a in div for progress. With style 100/100% & rgba(0, 170, 255, 0.1);
  // Overlay a cancel button on hover for style
  // HACK(SHIN): Set explicit width for 90% to prevent composer truncate when scrollbars
  // always showing
  return (
    <div
      id={`data-pm-attachment-id-${props.id}`}
      data-pm-attachment-id={props.id}
      data-pm-template-id={props.template}
      className="compose-attachment-cloud-container"
      style={{
        borderRadius: '3px',
        position: 'relative',
        margin: '10px',
        height: '40px',
        maxWidth: '480px',
        width: '90%',
        border: '#f2f5f8 1px solid',
        fontSize: '13px',
        fontWeight: 600,
      }}
      contentEditable={false}
    >
    { props.progress !== 1 &&
      <div
        className="attachment-progress"
        style={{
          position: 'absolute',
          width:`${Math.round(props.progress * 100)}%`,
          height: '100%',
          backgroundColor: 'rgba(0, 170, 255, 0.1)',
          transition: '0.2s width ease-in-out',
        }}
      />
    }
      <button
        className="attachment-remove-button"
        onClick={handleOnClick}
        style={{
          position: 'absolute',
          right: '10px',
          height: '20px',
          width: '20px',
          top: '50%',
          transform: `translateY(-50%)`,
          opacity: 0,
        }}
      >
        <img
          style={{
            width: '10px',
          }}
          src="https://welovepg.polymail.io/v1/img/link-preview/close@3x.png"
        />
      </button>
      <a
        href={props.cloudUrl}
        data-pm-attachment-id={props.id}
        className=""
        style={{
          textDecoration: 'none',
        }}
        download={props.isMeta && props.filename || undefined}
      >
        <table
          style={{
            height: '100%',
            width: '100%',
          }}
        >
          <tbody>
            <tr>
              <td
                className="attachment-image"
                style={{
                  verticalAlign: 'middle',
                  padding: '5px 8px 5px 16px',
                  width: '20px',
                }}
              >
                {props.error &&
                  <span
                    className="error-span"
                    style={{
                      marginRight: '5px',
                    }}
                    title={errorString}
                  >
                    ⚠️
                  </span>
                }
                <a
                  href={props.cloudUrl}
                  data-pm-attachment-id={props.id}
                  className=""
                  style={{
                    textDecoration: 'none',
                  }}
                  download={props.isMeta && props.filename || undefined}
                >
                  <img
                    style={{
                      verticalAlign:'middle',
                      width: '20px',
                    }}
                    src={props.iconUrl}
                  />
                </a>
              </td>
              <td
                style={{
                  verticalAlign: 'middle',
                }}
              >
                <a
                  href={props.cloudUrl}
                  data-pm-attachment-id={props.id}
                  className=""
                  style={{
                    textDecoration: 'none',
                  }}
                  download={props.isMeta && props.filename || undefined}
                >
                  <div
                    className="attachment-filename"
                    style={{
                      verticalAlign:'middle',
                      color: '#444444',
                      textDecoration: 'none',
                    }}
                  >
                    {props.filename}
                  </div>
                </a>
              </td>
              <td
                style={{
                  align: 'right',
                  verticalAlign: 'middle',
                  padding: '0 10px',
                }}
              >
                <a
                  href={props.cloudUrl}
                  data-pm-attachment-id={props.id}
                  download={props.isMeta && props.filename || undefined}
                >
                  <div
                    className="attachment-filesize"
                    style={{
                      textDecoration: 'none',
                      color: '#7e8f9f',
                      verticalAlign:'middle',
                      float: 'right',
                      fontSize: '13px',
                      fontWeight: 600,
                    }}
                    data-pm-complete={props.progress === 1 && 1 || undefined}
                  >
                    { props.progress === 1
                      ? truncate(props.length)
                      : truncatedProgress(props.length, props.progress)
                    }
                  </div>
                </a>
              </td>
            </tr>
          </tbody>
        </table>
      </a>
    </div>
  );
};

export default CloudAttachmentCell;
