import Dexie from 'dexie';
import { captureException } from '@sentry/browser';
import Cache, { Stores, SEARCH_DB_VERSION } from '@src/lib/db';
import { MessageBody, Mailbox, Thread, MailboxType } from '@src/types';
import { Observable } from 'rxjs';
import lunr from 'lunr';
import htmlToText from 'html-to-text';
import { keyBy, size, compact, intersection, flatten, uniq, difference } from 'lodash';
import {
  threadPlainStringValue,
  tokenizeThreads,
  listThreadIdsByEmail,
  countThreadsByEmail,
  listThreadsByEmail,
} from './thread';
import { queryParamsTextValue } from './search';
import { STOP_WORDS } from './searchUtils';
import { getSearchDbConfig } from './device';
import moment from 'moment';

export interface TokenizedMessageBody extends MessageBody {
  tokens: string[];
  updated: Date;
}

export interface TokenizedThread {
  id: string;
  internal: Date;
  tokens: string[];
  hasAttachments?: number;
  hasReminder?: number;
  isOpened?: number;
  isReplied?: number;
  isUnread?: number;
  isUnreplied?: number;
  isUnopened?: number;
}

const MAX_LIMIT = 200;
export const SEARCH_BODIES_EVICTION_DAYS = 3;

// Constants for Message Bodies Database
let searchDb: SearchDb;
const mockIndexer: lunr.Index = lunr(() => { return; });
const punctuationRegex = new RegExp(/[,!"\#$%&'()*+\-./:;<=>?@\[\\\]^_`{|}~\s\n\t\v\f\r\0]/);
lunr.tokenizer.separator = punctuationRegex;
const STOP_WORD_DICT = keyBy(STOP_WORDS);

// IMPORTANT: READ WHEN UPGRDING https://dexie.org/docs/Tutorial/Design#database-versioning
// NOTE(SHIN): Do not match to Polymail database since updating the version causes things to break
// TODO(SHIN): Handle delete and recreate on version update
class SearchDb extends Dexie {
  bodies: Dexie.Table<TokenizedMessageBody, string>;
  threads: Dexie.Table<TokenizedThread, string>;
  constructor() {
    super('polymail-search');
    this.version(SEARCH_DB_VERSION).stores({
      bodies: 'id, thread, *tokens, updated',
      // tslint:disable-next-line:max-line-length
      threads: 'id,hasAttachments,hasReminder,internal,isOpened,isReplied,isUnread,isUnreplied,isUnopened,*tokens',
    });

    this.bodies = this.table('bodies');
    this.threads = this.table('threads');

    this.on.versionchange.subscribe(() => {
      this.close();
      Observable.from(this.open()).subscribe({
        error: (e) => {
          // tslint:disable-next-line: no-console
          console.error(e);
          captureException(e);
        },
      });
    });
  }
}

export function getSearchDb() {
  return searchDb;
}

// limited to 300 deletions at a time
export const evictExpiredSearchCache = (): Observable<null> => {
  // Run TTL clears once every initialization;
  const deleteExpiredBodies = Observable.from(
    searchDb.bodies
      .where('updated')
      .below(moment().subtract(SEARCH_BODIES_EVICTION_DAYS, 'days').toDate())
      .limit(300)
      .delete(),
  );

  // Delete any threads older than TTL
  const deleteExpiredThreads = Observable.from(
    searchDb.threads
      .where('internal')
      .below(moment().subtract(getSearchDbConfig().threadsEvictionDays, 'days').toDate())
      .limit(300)
      .delete(),
  );

  // Delete any threads older than TTL
  // TODO(SHIN): Remove after December
  const deleteInvalidBodies = Observable.from(
    searchDb.bodies
      .where('updated')
      .startsWith('2')
      .limit(300)
      .delete(),
  );

  // Handles logging error to console and sentry on failure
  const logAndPassError = (e: Error): Observable<null> => {
    // tslint:disable-next-line:no-console
    console.error(e);
    captureException(e);
    return Observable.of(null);
  };

  return Observable.from(deleteExpiredBodies)
    .catch(e => logAndPassError(e))
    .flatMap(() => deleteExpiredThreads)
    .catch(e => logAndPassError(e))
    .flatMap(() => deleteInvalidBodies)
    .catch(e => logAndPassError(e))
    .flatMap(() => Observable.of(null));
};

export const limitSearchCacheSize = (maxCount: number) => Observable.from(searchDb.bodies.count())
  .flatMap((currentCount) => {
    if (currentCount < maxCount) {
      return Observable.of(null);
    }

    return Observable.from(
      searchDb.bodies.orderBy('updated').reverse().offset(maxCount).delete(),
    );
  });

// Save Message Bodies into Tokenized Message Bodies for Search
export const saveBodies = (bodies: MessageBody[]): Observable<string> => {
  if (!getSearchDbConfig().enabled || !bodies.length) {
    return Observable.of('');
  }

  const dateLimit = moment().subtract(SEARCH_BODIES_EVICTION_DAYS, 'days');

  // Handle which bodies to save based off eviction criteria
  const filteredBodies = bodies.filter(b => moment(b.updated).isAfter(dateLimit));

  // Does a fetch against cache to determing which bodies to index into tokenized bodies
  const allIds = filteredBodies.map(b => b.id);
  const first = filteredBodies[0];

  // Return empty if we don't want to save anything in search db
  if (!first) {
    return Observable.of('');
  }

  // Request primary keys for any matching for thread id
  const idsRequest = searchDb.bodies.where('thread').equals(first.thread).primaryKeys();
  return Observable.from(idsRequest)
    .flatMap((foundIds) => {
      const toSave = difference(allIds, foundIds);
      const toSaveBodies = bodies.filter(b => toSave.indexOf(b.id) !== -1);
      const indexedBodies = tokenizedMessageBodies(toSaveBodies);
      return Observable.from(searchDb.bodies.bulkPut(indexedBodies));
    });
};

// TODO(SHIN): May want to handle not tokenizing threads that have already been saved into SEARCHDB
export const saveThreadsToSearchDb = (threads: Thread[]): Observable<string> => {
  const config = getSearchDbConfig();
  if (!config.enabled) {
    return Observable.of('');
  }
  const dateLimit = moment().subtract(config.threadsEvictionDays, 'days');
  const toSave = threads.filter(t => moment(t.internal).isAfter(dateLimit));
  const updated = tokenizeThreads(toSave);
  return Observable.from(
    searchDb.threads.bulkPut(updated),
  );
};

// Tokenizes string into constituent parts. If english, uses porter stemmer from lunr for us english
export const tokenizeString = (string: string): string[] => {
  const config = getSearchDbConfig();
  const language = config.language;
  const minLen = 3;
  const maxLen = 64;
  const maxAmount = 512;
  if (config.enabled && language && language.startsWith('en-')) {
    const stemmedTokens = (mockIndexer as any)
      .pipeline.run(lunr.tokenizer(string))
      .map((token: lunr.Token) => {
        const str = token.toString();
        return str.slice(0, Math.min(str.length, maxLen));
      })
      .filter((str: string) =>
        !STOP_WORD_DICT[str] && !str.match(/^\d/) && str.length >= minLen,
      );
    const uniqueEnglishValues: string[] = uniq(stemmedTokens);
    return uniqueEnglishValues.slice(0, Math.min(maxAmount, uniqueEnglishValues.length));
  }

  // Do normal string split for non english languages
  const tokens = string.toLowerCase().split(punctuationRegex)
    .filter((str: string) =>
      !STOP_WORD_DICT[str] && !str.match(/^\d/) && str.length >= minLen,
    );
  const uniqueUnknownValues = uniq(tokens);
  return uniqueUnknownValues.slice(0, Math.min(maxAmount, uniqueUnknownValues.length));
};

// Pipeline to check box values
export const checkBoxResults = (mailbox: Mailbox, threads: Thread[]): Thread[] => {
  const trashOrSpamDict = {
    [MailboxType.Trash.valueOf()]: true,
    [MailboxType.Spam.valueOf()]: true,
  };

  const isTrashOrSpam = [MailboxType.Trash, MailboxType.Spam].indexOf(mailbox.type) !== -1;
  const boxQueryParam = mailbox.queryParams && mailbox.queryParams.box || false;
  if (isTrashOrSpam) {
    return threads.filter(t => t.box.valueOf() === mailbox.type.valueOf());
  }

  // Handle box query param match
  if (boxQueryParam) {
    return threads.filter(t => t.box === boxQueryParam);
  }

  // Otherwise filter out items in the trash or spam folder
  return threads.filter(t => !trashOrSpamDict[t.box]);
};

// Pipeline to check quoted text
export const checkQuotedTextAfter = (
  mailbox: Mailbox,
  threads: Thread[],
  whitelist: {},
): Thread[] => {
  // Handle quoted search
  const isQuoted = mailbox.queryParams && mailbox.queryParams.quoted === true;
  const query = mailbox.queryParams &&
    queryParamsTextValue(mailbox.queryParams).replace(/"/g, '').trim();
  if (!!isQuoted && query) {
    return threads.filter((thread) => {
      // Check whitelist
      if (!!whitelist[thread.id]) {
        return true;
      }

      // Check plain string value
      const plainStringValue = threadPlainStringValue(thread).toLowerCase();
      return plainStringValue.indexOf(query.toLowerCase()) !== -1;
    });
  }

  return threads;
};

// Pipeline to check quoted text
const checkAccountAfter = (mailbox: Mailbox, threads: Thread[]): Thread[] => {
  const accountId = mailbox.account;
  if (!accountId) {
    return threads;
  }
  return threads.filter(t => t.account === accountId);
};

