// Pipeline to check lists
const boxListQueryIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const listId = mailbox.queryParams && mailbox.queryParams.list;
  if (!listId) {
    return Observable.of([]);
  }

  const range = IDBKeyRange.bound([listId, new Date(0)], [listId, Date()]);
  return Cache.listIndexKeys(
      Stores.Threads,
      'list_internal',
      range,
    )
    .map((keys) => {
      if (limit && keys.length > limit) {
        return keys.reverse().splice(0, limit);
      }
      return keys;
    });
};

// Pipeline to check box
const boxQueryIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const box = mailbox.queryParams && mailbox.queryParams.box;
  if (!box) {
    return Observable.of([]);
  }

  return Cache.listIndexKeys(
      Stores.Threads,
      'box_sort',
      IDBKeyRange.bound([box, new Date(0)], [box, Date()]),
    )
    .map((keys) => {
      if (limit && keys.length > limit) {
        return keys.reverse().splice(0, limit);
      }
      return keys;
    });
};

// Pipeline to check starred Index
const starredQueryIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const isStarred = mailbox.queryParams && mailbox.queryParams.isStarred;
  if (!isStarred) {
    return Observable.of([]);
  }

  const range = IDBKeyRange.bound([1, new Date(0)], [1, Date()]);

  const fullThreadIds = Cache.listIndexKeys(
    Stores.Threads,
    'starred_internal',
    range,
  );

  if (limit) {
    const limitRequest: Observable<string[]> = Cache.listIndex(
        Stores.Threads,
        'starred_internal',
        range,
        true,
        limit,
      )
      .map((threads: Thread[]) => threads.map(t => t.id));

    return limitRequest;
  }

  return fullThreadIds;
};

// Returns a limited set of DB results
const limitSearchDbResults = (
  c: Dexie.Collection<TokenizedThread|Thread, string>,
  limit?: number,
): Observable<string[]> => {
  return Observable.from(c.reverse().sortBy('internal'))
  .map(r => r.splice(0, Math.min(r.length, limit || MAX_LIMIT))
    .map(t => t.id),
  );
};

// Pipeline to check attachments
const hasAttachmentsQueryId = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const hasAttachments = mailbox.queryParams && mailbox.queryParams.hasAttachment;
  if (!hasAttachments) {
    return Observable.of([]);
  }

  // Initial db query
  const dbQuery = searchDb.threads
    .where('hasAttachments')
    .equals(1);

  // Return limit if query exists
  if (limit) {
    return limitSearchDbResults(dbQuery, limit);
  }
  // If taking intersection, take all results to run intersection
  return Observable.from(
    dbQuery.primaryKeys(),
  );
};

