// Pipeline to check quoted text
const fromQueryIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const from = mailbox.queryParams && mailbox.queryParams.from;
  const fromDict = from ? keyBy(from) : {};

  // If from not defined, return results
  if (!from || !size(fromDict)) {
    return Observable.of([]);
  }

  return listThreadsByEmail(from, limit, (thread) => {
    const joined = thread.messages.map(m => m.from).join(' ').toLowerCase();
    // TODO: Need to sanitize from
    return joined.search(from) >= 0;
  }).map(threads => threads.map(thread => thread.id));
};

// Returns an AND query of all (with participants)
// TODO(SHIN): Do not do count if with array only has one value
const withQueryIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const withArray = mailbox.queryParams && mailbox.queryParams.with || [];
  if (!withArray.length) {
    return Observable.of([]);
  }

  // Single request if only one with value is specified
  const singleRequest = listThreadIdsByEmail(withArray[0]);

  // Otherwise fetch counts and do optimized query based off best split
  const counts = withArray.map(token =>
    countThreadsByEmail(token)
      .map(count => ({ token, count })),
  );

  let nextWithArray = withArray;
  const multipleRequest = Observable.zip(...counts)
    .flatMap((results) => {
      const highest = results.reduce(
        (prev, next) => next.count < prev.count ? next : prev,
        results[0],
      );

      nextWithArray = nextWithArray.filter(value => value !== highest.token);
      return Observable.of(highest);
    })
    .flatMap(result => listThreadsByEmail(result.token, limit))
    .flatMap((results) => {
      if (withArray.length === 1) {
        return Observable.of(results);
      }

      // Reduction filter for AND query search results
      const updated = nextWithArray.reduce(
        (result: Thread[], key: string) => result
          .filter((doc: Thread) => {
            // Create dict for doc && make sure that the tokens match to complete the and query
            const emailValues = doc.participantEmails.map(e => e.split('|')[0]);
            const tokenDict = keyBy(emailValues);
            return doc.participantEmails && !!tokenDict[key];
          }),
        results,
      );
      return Observable.of(updated);
    })
    .map((results) => {
      // If limit sort by descending date
      if (limit) {
        return results
          .sort((a, b) => b.internal.getTime() - a.internal.getTime())
          .slice(0, Math.min(results.length, limit))
          .map(t => t.id);
      }

      // Otherwise return
      return results.map(t => t.id);
    });

  const dbRequest = withArray.length === 1
    ? singleRequest
    : multipleRequest;

  return dbRequest;
};

// Returns an AND query of all (with participants)
// TODO(SHIN): Have to match on the to array to actually determine to
const toQueryIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const toArray = mailbox.queryParams && mailbox.queryParams.to || [];
  if (!toArray.length) {
    return Observable.of([]);
  }

  // Single request if only one TO value is specified
  const singleRequest = listThreadIdsByEmail(toArray[0]);

  // Otherwise fetch counts and do optimized query based off best split
  const counts = toArray.map(token =>
    countThreadsByEmail(token)
      .map(count => ({ token, count })),
  );

  let nextToArray = toArray;
  const multipleRequest = Observable.zip(...counts)
    .flatMap((results) => {
      const highest = results.reduce(
        (prev, next) => next.count < prev.count ? next : prev,
        results[0],
      );

      nextToArray = nextToArray.filter(value => value !== highest.token);
      return Observable.of(highest);
    })
    .flatMap(result => listThreadsByEmail(result.token, limit))
    .flatMap((results) => {
      if (toArray.length === 1) {
        return Observable.of(results);
      }

      // Reduction filter for AND query search results
      const updated = nextToArray.reduce(
        (result: Thread[], key: string) => result
          .filter((doc: Thread) => {
            // Create dict for doc && make sure that the tokens match to complete the and query
            const emailValues = doc.participantEmails.map(e => e.split('|')[0]);
            const tokenDict = keyBy(emailValues);
            return doc.participantEmails && !!tokenDict[key];
          }),
        results,
      );
      return Observable.of(updated);
    })
    .map((results) => {
      // If limit sort by descending date
      if (limit) {
        return results
          .sort((a, b) => b.internal.getTime() - a.internal.getTime())
          .slice(0, Math.min(results.length, limit))
          .map(t => t.id);
      }

      // Otherwise return
      return results.map(t => t.id);
    });

  const dbRequest = toArray.length === 1
    ? singleRequest
