  return dbRequest;
};

// TODO(SHIN): Sort and filter predicate ids
const generateFilterIdRequests = (mailbox: Mailbox): Array<Observable<string[]>> => {
  let filterIdsRequest: Array<Observable<string[]>> = [];
  const textQueryExists = mailbox.queryParams &&
    queryParamsTextValue(mailbox.queryParams).trim() !== '';
  const limit = !textQueryExists && MAX_LIMIT || undefined;

  // From pipeline request
  if (mailbox.queryParams && mailbox.queryParams.from) {
    const fromRequest = fromQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      fromRequest,
    ]);
  }

  // With pipeline Request
  if (mailbox.queryParams && mailbox.queryParams.with) {
    const withRequest = withQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      withRequest,
    ]);
  }

  // To pipeline Request
  if (mailbox.queryParams && mailbox.queryParams.to) {
    const toRequest = toQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      toRequest,
    ]);
  }

  // Starred pipeline request
  if (mailbox.queryParams && mailbox.queryParams.isStarred) {
    const starredRequest = starredQueryIds(mailbox);
    filterIdsRequest = filterIdsRequest.concat([
      starredRequest,
    ]);
  }

  // Box pipeline request
  if (mailbox.queryParams && mailbox.queryParams.box) {
    if (mailbox.queryParams.list) { // Special handling for lists
      const listRequest = boxListQueryIds(mailbox);
      filterIdsRequest = filterIdsRequest.concat([
        listRequest,
      ]);
    } else { // Normal handling for box
      const boxRequest = boxQueryIds(mailbox);
      filterIdsRequest = filterIdsRequest.concat([
        boxRequest,
      ]);
    }
  }

  // Attachment pipeline request
  if (mailbox.queryParams && mailbox.queryParams.hasAttachment) {
    const attachmentRequest = hasAttachmentsQueryId(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      attachmentRequest,
    ]);
  }

  // Opened pipeline request
  if (mailbox.queryParams && mailbox.queryParams.isOpened) {
    const isOpenedRequest = isOpenedQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      isOpenedRequest,
    ]);
  }

  // Attachment pipeline request
  if (mailbox.queryParams && mailbox.queryParams.isReplied) {
    const isRepliedRequest = isRepliedQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      isRepliedRequest,
    ]);
  }

  // Unread pipeline request
  if (mailbox.queryParams && mailbox.queryParams.isUnread) {
    const isUnreadRequest = isUnreadQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      isUnreadRequest,
    ]);
  }

  // Unreplied pipeline request
  if (mailbox.queryParams && mailbox.queryParams.isUnreplied) {
    const isUnrepliedRequest = isUnrepliedQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      isUnrepliedRequest,
    ]);
  }

  // Attachment pipeline request
  if (mailbox.queryParams && mailbox.queryParams.isUnopened) {
    const isUnopenedRequest = isUnopenedQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      isUnopenedRequest,
    ]);
  }

  // Attachment pipeline request
  if (mailbox.queryParams && mailbox.queryParams.hasFollowUp) {
    const hasReminderRequest = hasReminderQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      hasReminderRequest,
    ]);
  }

  // Before pipeline request
  if (mailbox.queryParams && mailbox.queryParams.before) {
    const beforeRequest = beforeQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      beforeRequest,
    ]);
  }

  // After pipeline request
  if (mailbox.queryParams && mailbox.queryParams.after) {
    const afterRequest = afterQueryIds(mailbox, limit);
    filterIdsRequest = filterIdsRequest.concat([
      afterRequest,
    ]);
  }

  return filterIdsRequest;
};

// Handle local thread search
// TODO(SHIN): Need to handle additional query params for box/from/with/etc
export const newLocalThreadSearch = (mailbox: Mailbox, limit?: number): Observable<Thread[]> => {
  const textSearch = mailbox.queryParams && queryParamsTextValue(mailbox.queryParams);
  let queryIdsRequest: Array<Observable<string[]>> = [];

  // Message Bodies Search, returning values as tokenized threads
  // Probably want a cleaner way of setting quoted matches
  let quotedBodiesMatch = {};
  const messageBodiesRequest = searchBodyThreadIds(mailbox)
    .map((results) => {
      quotedBodiesMatch = keyBy(results);
      return results;
    });

  // Thread Table Search
  const threadMetasRequest = searchThreadIds(mailbox);

  // Query results
  queryIdsRequest = queryIdsRequest.concat([
    messageBodiesRequest,
    threadMetasRequest,
  ]);

  // Generate db requests based off mailboxParams
  const filterIdsRequest = generateFilterIdRequests(mailbox);

  // Handle setting primary and intersection results
  let primaryIdsRequest: Array<Observable<string[]>> = [];
  let intersectionIdsRequest: Array<Observable<string[]>> = [];
  if (textSearch) {
    primaryIdsRequest = queryIdsRequest;
    intersectionIdsRequest = filterIdsRequest;
  } else {
    primaryIdsRequest = filterIdsRequest;
  }

  // Fire all requests
  const zippedRequest = Observable.zip(...primaryIdsRequest)
    .map((results: string[][]) => {
      // Return intersection of requests if no text query
      if (!textSearch) {
        return intersection(...results.map(r => compact(r)));
      }
      // Otherwise combine results
      return uniq(flatten(results));
    })
    // TODO(SHIN): Handle cases where search query returns no results but there is a filter
    .flatMap((primaryResults) => {
      // If no intersections requested, then return results
      if (!intersectionIdsRequest.length) {
        return Observable.of(primaryResults);
      }

      // Do not run intersection if no results for query search
      if (!primaryResults.length && textSearch) {
        return Observable.of(primaryResults);
      }

      // Otherwise run filter requests and intersect results for ids
      return Observable.zip(...intersectionIdsRequest)
        .map(ids => intersection(...ids))
        .map((filterIds) => {

          // If no text search, return filter ids
          if (!textSearch) {
            return filterIds;
          }

          // Otherwise return intersection of query and filters
          const allIds = [
            filterIds,
            primaryResults,
          ];
          return intersection(...allIds);
        });
    })
    // Get full threads from thread ids
    .flatMap(results => Cache.bulkGet<Thread>(Stores.Threads, results))
    // TODO(SHIN): We should handle compaction on bulkGet endpoint. Can remove once implemented
    .map(results => compact(results))
    // Run quoted search filter if search query is quoted
    .map(threads => checkQuotedTextAfter(mailbox, threads, quotedBodiesMatch))
    .map(threads => checkAccountAfter(mailbox, threads))
    // Sort threads by descending
    .map(threads => checkBoxResults(mailbox, threads))
    .map((threads: Thread[]) =>
      threads.sort((a: Thread, b: Thread) => b.sort.getTime() - a.sort.getTime()),
    );

  return zippedRequest;
};

// Searches Thread snippets returning a set of threadIds, sorted by descending date.
const searchThreadIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const textSearch = mailbox.queryParams && queryParamsTextValue(mailbox.queryParams);
  // Return nothing if mailbox is empty
  if (!textSearch) {
    return Observable.of([]);
  }

  const tokenArray = tokenizeString(textSearch).sort((a, b) => b.length - a.length);

  const counts = tokenArray.map(token =>
    Observable.from(searchDb.threads.where('tokens').equals(token).count())
    .map(count => ({ token, count })),
  );

  let nextTokenArray = tokenArray;
  return Observable.zip(...counts)
    .flatMap((results) => {
      const highest = results.reduce(
        (prev, next) => next.count < prev.count ? next : prev,
        results[0],
      );

      nextTokenArray = nextTokenArray.filter(value => value !== highest.token);
      return Observable.of(highest);
    })
    .flatMap(result => searchDb.threads.where('tokens').equals(result.token).toArray())
    .flatMap((results) => {
      if (tokenArray.length === 1) {
        return Observable.of(results);
      }

      // Reduction filter for AND query search results
      const updated = nextTokenArray.reduce(
        (result: TokenizedThread[], key: string) => result
          .filter((doc: TokenizedThread) => {

            // Create dict for doc && make sure that the tokens match to complete the and query
            const tokenDict = keyBy(doc.tokens);
            return doc.tokens && !!tokenDict[key];
          }),
        results,
      );
      return Observable.of(updated);
    })
    .map((results) => {
      return compact(results)
        .sort((a, b) => b.internal.getTime() - a.internal.getTime())
        .slice(0, Math.min(results.length, limit || MAX_LIMIT))
        .map(t => t.id);
    });
};

// Tokenizes the content of message bodies so they can be searched using fts
export const tokenizedMessageBodies = (bodies: MessageBody[]): TokenizedMessageBody[] => {
  // const fakeDiv = FakeDOM().createElement('div');
  const indexedBodies: TokenizedMessageBody[] = bodies.map((body) => {
    const plainText = htmlToText.fromString(
      body.body,
      {
        ignoreHref: true,
        ignoreImage: true,
        wordwrap: false,
      },
    );
    return {
      ...body,
      tokens: tokenizeString(plainText),
    };
  });
  return indexedBodies;
};

// Returns a list of thread ids where there is a fts match on message body
export const searchBodyThreadIds = (mailbox: Mailbox, limit?: number): Observable<string[]> => {
  const textSearch = mailbox.queryParams && queryParamsTextValue(mailbox.queryParams);
  const isQuoted = mailbox.queryParams && mailbox.queryParams.quoted === true;
  const quotedQuery = isQuoted && mailbox.queryParams &&
    queryParamsTextValue(mailbox.queryParams).replace(/"/g, '').trim();
  // Return nothing if mailbox is empty
  if (!textSearch) {
    return Observable.of([]);
  }

  const tokenArray = tokenizeString(textSearch).sort((a, b) => b.length - a.length);

  const counts = tokenArray.map(token =>
    Observable.from(searchDb.bodies.where('tokens').equals(token).count())
    .map(count => ({ token, count })),
  );

  let nextTokenArray = tokenArray;
  return Observable.zip(...counts)
    .flatMap((results) => {
      const highest = results.reduce(
        (prev, next) => next.count < prev.count ? next : prev,
        results[0],
      );

      nextTokenArray = nextTokenArray.filter(value => value !== highest.token);
      return Observable.of(highest);
    })
    .flatMap(result => searchDb.bodies.where('tokens').equals(result.token).toArray())
    .flatMap((results) => {
      if (tokenArray.length === 1) {
        return Observable.of(results);
      }

      // Reduction filter for AND query search results
      const updated = nextTokenArray.reduce(
        (result: TokenizedMessageBody[], key: string) => result
          .filter((doc: TokenizedMessageBody) => {

            // Create dict for doc && make sure that the tokens match to complete the and query
            const tokenDict = keyBy(doc.tokens);
            return doc.tokens && !!tokenDict[key];
          }),
        results,
      );
      return Observable.of(updated);
    })
    .map((results) => {
      // Handle quoted queries
      if (isQuoted && quotedQuery) {
            // TODO: Need to sanitize quotedQuery
        return results.filter(b => b.body.search(quotedQuery) >= 0);
      }
      return results;
    })
    .map((results) => {
      return compact(
        results
        .sort((a, b) => b.updated.getTime() - a.updated.getTime())
        .slice(0, Math.min(results.length, limit || MAX_LIMIT))
        .map(t => t.thread),
      );
    });
};

// initDb must be called by app and worker init once
export function initSearchDb(): Observable<any> {
  if (searchDb !== undefined) {
    searchDb.close();
  }

  const checkDb = Observable.from(new Dexie('polymail-search').open());
  return checkDb
  .catch((e) => {
    return Observable.of(null);
  })
  .flatMap((d: Dexie|null) => {

    // Continue if no db found
    if (!d) {
      return Observable.of(null);
    }

    // Delete DB if version number changed
    // TODO(SHIN): See if we can handle this without having to delete db and see if storage
    // is ok after deleting db
    if (d.verno !== SEARCH_DB_VERSION) {
      const deleteRequests = d.tables.map(t => Observable.from(t.clear()));
      return Observable.zip(...deleteRequests).map(() => {
        d.close();
        return Observable.from(d.delete());
      });
    }

    // If version same, close db connection and load
    d.close();
    return Observable.of(null);
  })
  .flatMap(() => { // Reset DB
    searchDb = new SearchDb();
    return Observable.of(null);
  });
}

