import React from 'react';
import moment from 'moment';
import { values } from 'lodash';
import classnames from 'classnames';
import {
  Resource,
  Attachment,
  AttachmentMeta,
  AttachmentMetaStatus,
  Onboarding,
  TeamMember,
} from '@src/types';
import { SequenceStage, SequenceStageInstance, Sequence, SequenceTab } from '@src/types/sequences';
import Edit from '@src/components/Icons/Edit.svg';
import history from '@src/lib//browserHistory';

import './SequenceStageDetail.scss';
import Dropdown from '@src/components/Form/Dropdown';
import { VictoryPie } from 'victory';
import { Link, RouteComponentProps } from 'react-router-dom';
import SequenceStageMessage from './SequenceStageMessage';
import SequenceStageRecipientsList from './SequenceStageRecipientsList';
import { RecipientCount } from './SequenceStageRecipientsList/SequenceStageRecipientsList';
import Overlay from '@src/components/Base/Overlay';
import SendTestEmail from './SendTestEmail/SendTestEmail';
import {
  SequenceStageInstanceTestMeta, SequenceStageInstanceAttachmentMeta, SequenceStageInstanceMeta,
} from '@src/ducks/sequenceStageInstances';
import { convertToAttachment } from '@src/lib/attachment';
import { stageInstanceAttachmentsToDelete } from '@src/lib/models/sequenceStageInstances';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import { FakeDOM } from '@src/lib/html';
import { DARKMODE_VICTORY_BACKGROUND_COLOR, DEFAULT_VICTORY_BACKGROUND_COLOR } from '@src/lib/colors';
import { OnboardingPatchMeta } from '@src/ducks/me';

export interface StateProps {
  stage?: SequenceStage;
  instances?: {
    [instanceId: string]: Resource<SequenceStageInstance>,
  };
  stageName: string;
  sequence?: Resource<Sequence>;
  selectedTab?: SequenceTab;
  recipientCounts: RecipientCount;
  attachments: Attachment[];
  attachmentMetas: AttachmentMeta[];
  onboarding?: Onboarding;
  user?: TeamMember;
}

export interface DispatchProps {
  updateStageInstance: (instance: SequenceStageInstance) => void;
  updateStage: (stage: SequenceStage) => void;
  sendTestEmail: (meta: SequenceStageInstanceTestMeta) => void;
  deleteStage: (stage: SequenceStage) => void;
  duplicateStage: (stage: SequenceStage) => void;
  removeAttachment: (meta: SequenceStageInstanceAttachmentMeta) => void;
  updateOnboarding: (type: OnboardingPatchMeta) => void;
  refreshStage: (meta: SequenceStageInstanceMeta) => void;
}

export interface State {
  edit: boolean;
  showTestEmailPopover: boolean;
  updatedInstances: {
    [instanceId: string]: SequenceStageInstance,
  };
  confirmProps?: ConfirmProps;
  updatedStage?: SequenceStage;
}

class SequenceStageDetail extends React.PureComponent<
  StateProps & DispatchProps & RouteComponentProps<any>, State
> {
  constructor() {
    super();

    this.state = {
      edit: false,
      showTestEmailPopover: false,
      updatedInstances: {},
    };
  }

  componentDidMount() {
    // Mark any successful onboardings on mount
    this.handleOnboardingUpdates();
  }

  // Marks successful cases for onboading updates
  handleOnboardingUpdates = () => {
    const { onboarding, updateOnboarding } = this.props;
    if (onboarding && !onboarding.editSequenceStage) {
      updateOnboarding({ skipped: false, type: { editSequenceStage: true } });
      return;
    }

    if (onboarding && !onboarding.createAdditionalStage) {
      updateOnboarding({ skipped: false, type: { createAdditionalStage: true } });
      return;
    }
  }

  handleMoreSelect = (value: string, index: number) => {
    const { stage, deleteStage, sequence, duplicateStage } = this.props;

    if (!stage || !sequence || !sequence.data) {
      return;
    }

    switch (value) {
      case 'Duplicate':
        duplicateStage(stage);
        break;
      case 'Delete':

        // Handles delete and reroute, and confirm dismissal
        const deleteHandler = () => {
          if (stage) {
            deleteStage(stage);
          }

          if (sequence && sequence.data) {
            history.push(`/sequences/${sequence.data.id}`);
          }
          this.setState({ confirmProps: undefined });
        };

        // Show confirm dialogue with callback to delete
        const confirmProps: ConfirmProps = {
          title: 'Delete this Stage?',
          message: 'Are you sure you want to delete this Stage?'
            + ' Any analytics and recipients data will be lost forever.',
          confirmCopy: 'Delete',
          cancelCopy: 'Cancel',
          onConfirm: () => deleteHandler(),
          onCancel: () => this.setState({ confirmProps: undefined }),
        };

        this.setState({
          confirmProps,
        });
        break;
      default:
        break;
    }
  }

  handleEdit = () => {
    this.setState({ edit: true });
  }

  handleTestEmailClick = () => {
    if (this.state.edit) {
      this.setState({ showTestEmailPopover: true });
    }
  }

  identifyNumberOfUnmatchedPlaceholders = () => {
    const { sequence } = this.props;

    let unmatchedPlaceholders = 0;
    const headers = sequence && sequence.data && sequence.data.headers || [];
    values(this.state.updatedInstances)
      .forEach((instance: SequenceStageInstance) => {

        // Calculate body placeholders
        const bodyContainer = FakeDOM().createElement('div');
        bodyContainer.innerHTML = instance.body;
        const unmatchedBodyPlaceholders = bodyContainer.querySelectorAll('[data-pm-no-variable]');
        if (unmatchedBodyPlaceholders.length > 0) {
          unmatchedPlaceholders += unmatchedBodyPlaceholders.length;
        }

        // Calculate headers placholders
        const headerPlacholders = instance.subject.match(/{{[\w\s]+}}/g);
        if (headerPlacholders) {
          headerPlacholders
            .map(match => match.replace(/[{}]+/g, ''))
            .forEach((match) => {
              if (headers.indexOf(match) === -1 &&
                match !== 'email' && match !== 'first name' &&
                match !== 'last name') {
                unmatchedPlaceholders += 1;
              }
            });
        }
      });

    return unmatchedPlaceholders;
  }

  handleSave = () => {
    const {
      updateStageInstance,
      attachmentMetas,
      attachments,
      removeAttachment,
      sequence,
      updateStage,
    } = this.props;

    // Blocks any saves when a placeholder exists and does not match variables
    const unmatchedPlaceholders = this.identifyNumberOfUnmatchedPlaceholders();
    if (unmatchedPlaceholders > 0) {
      const confirmProps: ConfirmProps = {
        title: 'Remove Placeholders',
        message: `This message has ${unmatchedPlaceholders} placeholders that do not match`
          + ' any variables. Please remove these placeholders before saving your changes.',
        confirmCopy: 'Ok',
        onConfirm: () => this.setState({ confirmProps: undefined }),
      };
      this.setState({ confirmProps });
      return;
    }

    const saveHandler = () => {
      values(this.state.updatedInstances)
        .forEach((instance: SequenceStageInstance) => {

          // Removes any attachments that were removed from the body on save
          const attachmentsToRemove = stageInstanceAttachmentsToDelete(
            instance,
            attachments,
            attachmentMetas,
          );
          attachmentsToRemove.forEach(meta => removeAttachment(meta));

          // Updates stage instance
          updateStageInstance(instance);
        });

      if (this.state.updatedStage) {
        updateStage(this.state.updatedStage);
      }
      this.setState({ edit: false, confirmProps: undefined });
    };

    if (sequence && sequence.data && sequence.data.paused) {
      saveHandler();
    } else {
      const confirmProps: ConfirmProps = {
        title: 'This message is live',
        message: 'Are you sure you want to save your changes?'
          + ' Any changes you\'ve made will automatically go live to your recipients.',
        confirmCopy: 'Save',
        cancelCopy: 'Cancel',
        onConfirm: () => saveHandler(),
        onCancel: () => this.setState({ confirmProps: undefined }),
      };

      this.setState({ confirmProps });
    }
  }

  handleInstanceEdit = (stageInstance: SequenceStageInstance) => {
    const updatedInstances = {
      ...this.state.updatedInstances,
      [stageInstance.id]: stageInstance,
    };
    this.setState({
      updatedInstances,
    });
  }

  handleStageEdit = (stage: SequenceStage) => {
    this.setState({
      updatedStage: stage,
      edit: true,
    });
  }

  handleStageUpdate = (stage: SequenceStage) => {
    this.props.updateStage(stage);
  }

  handleSendTestEmail = (emails: string[], insertVariables: boolean) => {
    const {
      sendTestEmail,
      sequence,
      stage,
      instances,
      attachmentMetas,
      attachments,
   } = this.props;

    this.setState({
      showTestEmailPopover: false,
    });

    if (stage && instances && sequence && sequence.data) {
      const firstInstance = instances[Object.keys(instances)[0]].data;

      if (firstInstance) {
        sendTestEmail({
          sequence: sequence.data.id,
          stage: stage.id,
          instance: this.state.updatedInstances[firstInstance.id] || firstInstance,
          emails,
          insertVariables,
          attachments: attachments.concat(
            attachmentMetas.filter(meta => meta.status !== AttachmentMetaStatus.Delete)
              .map(convertToAttachment),
          ),
        });
      }
    }
  }

  handleSendTestEmailClose = () => {
    this.setState({
      showTestEmailPopover: false,
    });
  }

  handleCancel = () => {
    const { stage, refreshStage } = this.props;

    const cancelHandler = () => {
      // Revert state and remove confirm dialogue
      this.setState({
        edit: false,
        updatedInstances: {},
        updatedStage: undefined,
        confirmProps: undefined,
      });

      // Handle refreshing stage
      if (stage) {
        refreshStage({
          stage: stage.id,
          sequence: stage.sequence,
        });
      }
    };

    // Show confirm dialogue for cancelling changes
    const confirmProps: ConfirmProps = {
      title: 'Unsaved Changes',
      message: 'You have unsaved changes to this message. '
      + 'Are you sure you want to discard these changes?',
      confirmCopy: 'Continue',
      cancelCopy: 'Cancel',
      onConfirm: () => cancelHandler(),
      onCancel: () => this.setState({ confirmProps: undefined }),
    };

    this.setState({ confirmProps });
  }

  render() {
    const { stage, instances, sequence, stageName, recipientCounts, user } = this.props;
    const { edit, showTestEmailPopover, confirmProps, updatedStage } = this.state;
    const selectedTab = this.props.selectedTab || SequenceTab.Message;

    if (!stage || !instances || !sequence || !sequence.data) {
      return <div />;
    }

    // TODO(SHIN): Abstract to not rely on document.body (preferably using constants)
    const victoryColorBackground = document.body.getAttribute('theme') === 'theme-dark'
      ? DARKMODE_VICTORY_BACKGROUND_COLOR : DEFAULT_VICTORY_BACKGROUND_COLOR;

    const { analytics } = stage;
    const recipientsAnalytics = [
      { name: 'Sent', value: 'sent' },
      { name: 'Scheduled', value: 'scheduled' },
      { name: 'Paused', value: 'paused' },
      { name: 'Bounced', value: 'bounced' },
    ];
    const sequenceAnalytics = [
      { name: 'Delivered', value: 'delivered', color: '#444' },
      { name: 'Opened', value: 'opened', color: '#00aaff' },
      { name: 'Clicked', value: 'clicked', color: '#ffa057' },
      { name: 'Downloaded', value: 'downloaded', color: '#d347ae' },
      { name: 'Replied', value: 'replied', color: '#0ec36b' },
      { name: 'Unsubscribed', value: 'unsubscribed', color: '#444' },
    ];
    const creator = user && `${user.firstName} ${user.lastName}` || '';
    const createdString = moment(stage.created).format('MMM DD, YYYY');
    const total = analytics.delivered;

    return (
      <div className="sequence-stage-detail">
        {confirmProps &&
          <Confirm
            {...confirmProps}
          />
        }
        <div className="navigation-bar">
          <div className="navigation">
            <Link className="sequences-breadcrumb" to={`/sequences`}>Sequences</Link>
            <span className="arrow">&gt;</span>
            <Link className="sequence-breadcrumb" to={`/sequences/${sequence.data.id}`} >
              {sequence.data.name}
            </Link>
            <span className="arrow">&gt;</span>
            <Link
             className="sequence-stage-breadcrumb"
             to={`/sequences/${sequence.data.id}/stages/${stage.id}`}
            >
              {stageName}
            </Link>
          </div>
          <div className="actions">
            <Dropdown
              placeholder="More"
              value=""
              values={['Duplicate', 'Delete']}
              onSelect={this.handleMoreSelect}
            />
            {(!edit && !sequence.data.archived) &&
              <button className="edit-stage" onClick={this.handleEdit}>
                <Edit className="icon" />
                Edit Stage
              </button>}
            {edit && <button className="cancel" onClick={this.handleCancel}>
              Cancel
            </button>}
            {edit && <button className="save" onClick={this.handleSave}>
              Save
            </button>}
          </div>
        </div>
        <div className="main">
          <div className="sequence-stage-analytics">
            <div className="top-row">
              <div className="title">
                <h5>{sequence.data.name}</h5>
                <h1>{stageName}</h1>
              </div>
              <div className="recipients-analytics">
                {recipientsAnalytics.filter(r => !!recipientCounts[r.value]).map(r => (
                  <Link
                    className="link"
                    to={
                      `/sequences/${stage.sequence}/stages/${stage.id}/recipients?filter=${r.name}`}
                    key={r.value}
                  >
                    <div className={classnames('recipients-analytic', r.value)} key={r.value}>
                      <span className="number">{recipientCounts[r.value]}</span>
                      <span>{r.name}</span>
                    </div>
                  </Link>
                ))}
              </div>
            </div>
            <div className="details">
              <div className="created">
                {`Created by ${creator}  ${createdString}`}
              </div>
            </div>
            {!!total && <div className="charts">
              {sequenceAnalytics.map(a => (
                <Link
                  className="link"
                  to={`/sequences/${stage.sequence}/stages/${stage.id}/recipients?filter=${a.name}`}
                  key={a.name}
                >
                  <div className="chart-wrapper" key={a.name}>
                    <div className="chart">
                      <VictoryPie
                        key={a.name}
                        data={[
                          { x: 1, y: analytics[a.value] },
                          { x: 2, y: total - analytics[a.value] },
                        ]}
                        colorScale={[a.color, victoryColorBackground]}
                        innerRadius={130}
                        animate={{
                          duration: 5000,
                        }}
                      />
                      <div className="numbers">
                        <h1 style={{ color: a.color }}>
                          {total ? Math.round((analytics[a.value] / (
                              a.value === 'delivered' ?
                              analytics.delivered + recipientCounts.bounced
                              : total
                            )) * 100) : 0}%
                        </h1>
                        <h4>
                          {analytics[a.value]} of {
                            a.value === 'delivered' ?
                            analytics.delivered + recipientCounts.bounced
                            : total}
                        </h4>
                      </div>
                    </div>
                    <div className="label">
                      {a.name}
                    </div>
                  </div>
                </Link>
              ))}
            </div>}
          </div>
          <div className="drilldowns">
            <div className="tabs">
              <Link to={`/sequences/${sequence.data.id}/stages/${stage.id}`} className="link">
                <div
                  className={classnames('tab', {
                    selected: selectedTab === SequenceTab.Message,
                  })}
                >
                  Message
                </div>
              </Link>
              <Link
                to={`/sequences/${sequence.data.id}/stages/${stage.id}/recipients`}
                className="link"
              >
                <div
                  className={classnames('tab', {
                    selected: selectedTab === SequenceTab.Recipients,
                  })}
                >
                  Recipients
                </div>
              </Link>
            </div>
            <div className="main">
              {selectedTab === SequenceTab.Message &&
                <SequenceStageMessage
                  stage={updatedStage ? updatedStage : stage}
                  instances={instances}
                  edit={edit}
                  onEdit={this.handleInstanceEdit}
                  placeholders={sequence.data.headers}
                  onStageUpdate={this.handleStageUpdate}
                  onStageEdit={this.handleStageEdit}
                  onTestEmailClick={this.handleTestEmailClick}
                  sender={sequence.data.senderEmail}
                  readOnly={sequence.data.archived}
                  enableEdit={this.handleEdit}
                />}
              {selectedTab === SequenceTab.Recipients &&
                <SequenceStageRecipientsList
                  stage={stage}
                  recipientCounts={recipientCounts}
                />}
            </div>
          </div>
        </div>
        {showTestEmailPopover && <Overlay>
          <SendTestEmail
            onSend={this.handleSendTestEmail}
            onClose={this.handleSendTestEmailClose}
            defaultValue={sequence.data.senderEmail}
          />
        </Overlay>}
      </div>
    );
  }
}

export default SequenceStageDetail;
