import React from 'react';
import classnames from 'classnames';
import { Link } from 'react-router-dom';
import CustomScroll from 'react-custom-scroll';
import Resizable, { NumberSize } from 're-resizable';

import { LeftSidebarHeader as Header } from './components/LeftSidebarHeader/LeftSidebarHeader';
// import Loader from '@src/components/Loader/Loader';
import LeftSidebarFooter from './components/LeftSidebarFooter';
import MailTab from './components/MailTab';
import PreferenceTab from './components/PreferenceTab/PreferenceTab';

import { NavbarTab } from '@src/types/leftSidebar';

import {
  AccountStatus,
  AccountType,
  Account,
  List,
  Mailbox,
  MailCount,
  LeftSidebarCount,
  Features,
} from '@src/types';

import { UpgradePrompt } from '@src/types/billing';

import './LeftSidebar.scss';

import { isElectron } from '@src/lib/electron';
import SequencesTab from './components/SequencesTab/SequencesTab';
import AnalyticsTab from '@src/components/LeftSidebar/components/AnalyticsTab/AnalyticsTab';
import { createDeviceTypeNotifications } from '@src/mocks/accounts';
import SyncIndicator from './components/SyncIndicator';
import { Configuration } from '@src/lib/draft';
import browserHistory from '@src/lib/browserHistory';
export interface Props {
  accounts: Account[];
  lists?: List[];
  selectedTab?: NavbarTab;
  selectedMailbox?: Mailbox;
  mailCount?: MailCount;
  leftSidebarCount?: LeftSidebarCount;
  selectedAccount?: string;
  preferences?: boolean;
  selectedTitle?: string;

  defaultAccount?: string;
  incrementalSyncing?: boolean;
  upgradePrompts: UpgradePrompt;
  features?: Features;
  count?: number;
  width?: number;
  collapsed?: boolean;
  collapsedMobile?: boolean;
  isMobile?: boolean;
}

export interface DispatchProps {
  createDraft: () => void;
  dbClear: () => void;
  logout: () => void;
  setWidth: (width: number) => void;
  collapseLsb: (collapse: boolean) => void;
  createDraftToTeam: (configuration: Configuration, recipients: string[]) => void;
  toggleContactSupportPopup: (collapsed: boolean) => void;
}

// TODO(SHIN): Structure for Tabs that can contain All Accounts & Different section types
// TODO(SHIN): Handle selection of different tabs across redux state
// TODO(SHIN): Handle emission of preference actions
// TODO(SHIN): Handle list actions through redux state
// TODO(SHIN): Handle overflow issue with popover component
const LeftSidebar: React.SFC<Props & DispatchProps> = (props) => {
  const {
    lists,
    selectedTab,
    selectedTitle,
    selectedMailbox,
    mailCount,
    leftSidebarCount,
    // incrementalSyncing,
    upgradePrompts,
    width,
    setWidth,
    features,
    collapsed,
    collapseLsb,
    isMobile,
    collapsedMobile,
  } = props;
  const MailTabConnect = MailTab as any;

  let accounts: Account[] = [{
    id: 'all',
    description: 'All Accounts',
    email: 'Unified Inbox',
    signature: '',
    type: AccountType.Alias,
    status: AccountStatus.Ok,
    name: 'Unified Inbox',
    signatureImages: [],

    // mock, do not use for any computation
    notifications: createDeviceTypeNotifications(),
  }];

  if (props.accounts) {
    accounts = accounts.concat(props.accounts);
  }

  const handleClearCache = () => {
    props.dbClear();
  };

  // Creates new draft
  // TODO(SHIN & SHAHAN): Implement default account selector, discuss mapStateToProps
  // without using ownProps viability
  const handleNewDraft = () => {

    const selectedAccount = props.selectedAccount ||
      props.accounts && props.accounts.length && props.accounts[0].id;
    if (selectedAccount && props.accounts && props.accounts.length) {
      props.createDraft();
    }
  };

  const handleResizeStop = (
    event: MouseEvent,
    direction: string,
    refToElement: HTMLElement,
    delta: NumberSize,
  ) => {
    const cWidth = refToElement.clientWidth;

    if (cWidth) {
      setWidth(cWidth);
    }
  };

  const handleResize = (
    event: MouseEvent,
    direction: string,
    refToElement: HTMLElement,
    delta: NumberSize,
  ) => {
    if (!collapsed && event.clientX < 50) {
      collapseLsb(true);
      return;
    }

    if (collapsed && event.clientX > 100) {
      collapseLsb(false);
    }
  };

  const renderSections = () => {
    switch (selectedTab) {
      case NavbarTab.Mentions:
      case NavbarTab.SharedThreads:
      case NavbarTab.Mail:
      case NavbarTab.Scheduling:
      case NavbarTab.Templates:
      case NavbarTab.Unsubscriber:
        return (
          <CustomScroll
            flex="1"
            allowOuterScroll
          >
            <MailTabConnect
              accounts={accounts || []}
              lists={lists || []}
              selectedMailbox={selectedMailbox}
              mailCount={mailCount}
              leftSidebarCount={leftSidebarCount}
              selectedTab={selectedTab}
            />
          </CustomScroll>
        );
      case NavbarTab.Sequences:
        return (
          <SequencesTab
            selectedTitle={selectedTitle}
            showCampaign={!!features && features.campaigns}
          />
        );
      case NavbarTab.Analytics:
        return (
          <AnalyticsTab
            selectedTitle={selectedTitle}
            features={features}
          />
        );
      default:
        return null;
    }
  };

  const handleMenuTogglerTouch = () => {
    collapseLsb(!collapsedMobile);
  };

  // these props don't suit well in the Mail view, so lets hide them
  const isSettings = browserHistory.location.pathname.includes('/settings');

  const mobileSettingsStyle = () => {
    let style = {};

    if (isMobile && isSettings) {
      style = {
        position: 'absolute',
        height: '100vh',
        zIndex: 1,
      };
      if (collapsedMobile) {
        style = { display: 'none' };
      }
    }
    return style;
  };

  const defaultSizeProps = () => {
    let style = {
      width: (collapsed && 100) || (width || 'auto'),
      height: 'auto',
    };

    if (isMobile && isSettings) {
      style = {
        ...style,
        height: '100%',
      };
    }

    return style;
  };

  return (
    <>
      <Resizable
        key={selectedTab}
        className={classnames('left-sidebar', { collapsed })}
        enable={{
          top: false,
          right: true,
          bottom: false,
          left: false,
          topRight: false,
          bottomRight: false,
          bottomLeft: false,
          topLeft: false,
        }}
        defaultSize={defaultSizeProps()}
        style={mobileSettingsStyle()}
        onResize={handleResize}
        onResizeStop={handleResizeStop}
        minWidth={(collapsed && 68) || 180}
        maxWidth={(collapsed && 68) || undefined}
      >
        {isElectron && <div className="electron-spacer" />}

        {!props.preferences && [
          <Header
            key="header"
            onNewDraft={handleNewDraft}
          />,
          renderSections(),
          <div key="sync" className="indicator">
            <SyncIndicator />
            {/* {incrementalSyncing &&
              <div className="incremental-sync">
                <Loader />
                {!collapsed && (
                  <div className="title">
                    Syncing messages...
                  </div>
                )}
              </div>
            } */}
          </div>,
          upgradePrompts.showTrialExpired ?
            <div className="upgrade-button" key="upgrade">
              <Link className="upgrade" to="/settings/billing">
                Upgrade
              </Link>
            </div> :
            undefined,
          <LeftSidebarFooter
            key="footer"
          />,
        ]}
        {props.preferences && props.selectedTitle !== undefined &&
          <PreferenceTab
            selectedTitle={props.selectedTitle}
            createDraft={props.createDraftToTeam}
            toggleContactSupportPopup={props.toggleContactSupportPopup}
            toggleSidebar={handleMenuTogglerTouch}
            onClearCache={handleClearCache}
            isMobile={isMobile}
          />
        }
      </Resizable>
    </>
  );
};

LeftSidebar.defaultProps = {
  selectedTab: NavbarTab.Mail,
};

export default LeftSidebar;
