import React from 'react';
import classnames from 'classnames';
import Autosizer from 'react-virtualized-auto-sizer';
import { FixedSizeList as List } from 'react-window';
import { Resource } from '@src/types';
import { Sequence } from '@src/types/sequences';
import { map, filter, size, find, debounce, orderBy } from 'lodash';

import './SequenceList.scss';
import SequenceCell from './SequenceCell';
import SearchInput from '@src/components/Form/SearchInput/SearchInput';
import { createDefaultSequence } from '@src/lib/models/sequences';
import Loader from '@src/components/Loader/Loader';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import Dropdown from '@src/components/Form/Dropdown';
import browserHistory from '@src/lib/browserHistory';
import { RouteComponentProps } from 'react-router';
import SequenceListHeader, { SequenceListHeaderSortKey } from './SequenceCell/SequenceListHeader/SequenceListHeader';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface StateProps {
  sequences: { [id: string]: Resource<Sequence> };
  account?: string;
  user?: string;
  isArchived: boolean;
  isLoaded: boolean;
}

export interface State {
  checked: { [id: string]: boolean };
  sequences: Sequence[];
  confirmProps?: ConfirmProps;
  sortValue: SequenceListHeaderSortKey;
  sortDirection: SortDirection;
  senderFilter: SequenceSender;
}

export enum SortDirection {
  Descending = 'desc',
  Ascending = 'asc',
}

export interface DispatchProps {
  createSequence: (s: Sequence) => void;
  deleteSequence: (s: Sequence) => void;
  updateSequence: (s: Sequence) => void;
  duplicateSequence: (s: string) => void;
  exportSequence: (s: Sequence) => void;
}

export const enum SequenceFilter {
  Active = 'Active',
  Archived = 'Archived',
}

export const enum SequenceSender {
  Personal = 'My Sequences',
  Team = 'All Sequences',
}

class SequenceList extends React.PureComponent<
  StateProps & DispatchProps & RouteComponentProps<any>, State
> {
  private debouncedHandleSearch: any;

  constructor(props: StateProps & DispatchProps & RouteComponentProps<any>) {
    super(props);

    this.debouncedHandleSearch = debounce(this.handleSearch, 500);

    this.state = {
      checked: {},
      sequences: filter(
        props.sequences, s => !!s.data && s.data.archived === props.isArchived,
      ).map(s => s.data!),
      sortValue: SequenceListHeaderSortKey.Date,
      sortDirection: SortDirection.Descending,
      senderFilter: SequenceSender.Team,
    };
  }

  UNSAFE_componentWillReceiveProps(nextProps: StateProps & DispatchProps) {
    if (nextProps.sequences !== this.props.sequences) {
      const diff = filter(
        this.props.sequences,
        s => !!s.data && !find(
          nextProps.sequences,
          seq => !!seq.data && seq.data.id === s.data!.id,
        ),
      ).map(s => s.data!.id);
      const newChecked = this.state.checked;

      if (diff.length) {
        diff.forEach(d => delete newChecked[d]);
      }
      this.setState({
        sequences: filter(
          nextProps.sequences,
          s => !!s.data && s.data.archived === nextProps.isArchived,
        ).map(s => s.data!),
        checked: newChecked,
      });
    }
  }

  componentWillUnmount() {
    this.debouncedHandleSearch.cancel();
  }

  handleCheckAll = () => {
    const { checked, sequences } = this.state;
    const newChecked = {};

    const allChecked = filter(checked, c => c).length === sequences.length;

    if (allChecked) {
      sequences.forEach(s => newChecked[s.id] = false);
    } else {
      sequences.forEach(s => newChecked[s.id] = true);
    }

    this.setState({
      checked: newChecked,
    });
  }

  handleCheckboxChange = (id: string) => {
    const { checked } = this.state;

    this.setState({
      checked: {
        ...checked,
        [id]: !checked[id],
      },
    });
  }

  handleSearch = (query: string) => {
    const queryLowerCase = query.toLowerCase();

    this.setState({
      checked: {},
      sequences: filter(
        this.props.sequences,
        (s: Resource<Sequence>) => {
          if (!s.data) {
            return false;
          }

          if (s.data.name.toLowerCase().includes(queryLowerCase)) {
            return true;
          }

          return false;
        },
      ).map(s => s.data!),
    });
  }

  handleCreateSequence = () => {

    const { createSequence, account, user } = this.props;

    if (account && user) {
      createSequence(createDefaultSequence(account, user));
    }
    logAmplitudeEvent(AmplitudeEvents.CreateNewSequence);
  }

  handleDelete = () => {
    const { checked } = this.state;
    const { deleteSequence, sequences } = this.props;

    const deleteHandler = () => {
      map(checked, (isChecked: boolean, id: string) => {
        if (isChecked && sequences[id].data) {
          deleteSequence(sequences[id].data!);
        }
      });

      this.setState({ confirmProps: undefined });
    };

    const deleteDescriptor = Object.keys(checked).length > 1
      ? 'these Sequences'
      : 'this Sequence';
    const confirmProps: ConfirmProps = {
      title: `Delete ${deleteDescriptor}?`,
      message: `Are you sure you want to delete ${deleteDescriptor}?`
      + ' Any analytics and recipients data will be lost forever.',
      confirmCopy: 'Delete',
      cancelCopy: 'Cancel',
      onConfirm: () => deleteHandler(),
      onCancel: () => this.setState({ confirmProps: undefined }),
    };

    this.setState({
      confirmProps,
    });
  }

  handleArchive = () => {
    const { checked } = this.state;
    const { updateSequence, sequences } = this.props;

    map(checked, (isChecked: boolean, id: string) => {
      if (isChecked && sequences[id].data) {
        updateSequence({
          ...sequences[id].data!,
          archived: !sequences[id].data!.archived,
          paused: true,
        });
      }
    });
  }

  handleDuplicate = () => {
    const { checked } = this.state;
    const { duplicateSequence } = this.props;
    const numChecked = filter(checked, c => c).length;

    if (numChecked === 1) {
      duplicateSequence(Object.keys(checked)[0]);
    }
  }

  handleExport = () => {
    const { checked } = this.state;
    const { exportSequence, sequences } = this.props;

    map(checked, (isChecked: boolean, id: string) => {
      if (isChecked && sequences[id].data) {
        exportSequence(sequences[id].data!);
      }
    });
  }

  handleFilterSelect = (value: string, index: number, id?: string) => {
    const { isArchived } = this.props;

    if (value === SequenceFilter.Active && isArchived) {
      browserHistory.push('/sequences');
    } else if (value === SequenceFilter.Archived && !isArchived) {
      browserHistory.push('/sequences/archive');
    }
  }

  handleSenderSelect = (value: string, index: number, id?: string) => {
    if (value === SequenceSender.Personal) {
      this.setState({
        senderFilter: SequenceSender.Personal,
      });
    } else if (value === SequenceSender.Team) {
      this.setState({
        senderFilter: SequenceSender.Team,
      });
    }
  }

  renderEmpty() {
    const { isArchived } = this.props;
    const emptyMessage = isArchived ? 'You don\'t have any archived sequences.' :
      'Send personalized messages and automated follow-ups. '
      + 'Track your outreach and prospect engagement.';

    return (
      <div className="empty">
        <div className="emoji">
          ðŸš€
        </div>
        {!isArchived &&
          <div className="title">
            Start your first Sequence
          </div>
        }
        <div className="description">
          {emptyMessage}
        </div>
        <button className="create-button" onClick={this.handleCreateSequence}>
          Create Sequence
        </button>
      </div>
    );
  }

  handleSortClick = (sort: SequenceListHeaderSortKey) => {
    const { sequences, sortValue, sortDirection } = this.state;

    // If sort has changed, or if sort is already ascending, then set sort to descending
    let newSortDirection = SortDirection.Descending;
    if (sortDirection === SortDirection.Descending && sort === sortValue) {
      newSortDirection = SortDirection.Ascending;
    }

    switch (sort) {
      case SequenceListHeaderSortKey.Name: {
        const sorted = orderBy(sequences, (s => s.name), newSortDirection);
        this.setState({
          sortValue: SequenceListHeaderSortKey.Name,
          sequences: sorted,
          sortDirection: newSortDirection,
        });
        return;
      }
      case SequenceListHeaderSortKey.Delivered: {
        const sorted = orderBy(sequences, (s => s.analytics.delivered), newSortDirection);
        this.setState({
          sortValue: SequenceListHeaderSortKey.Delivered,
          sequences: sorted,
          sortDirection: newSortDirection,
        });
        return;
      }
      case SequenceListHeaderSortKey.Opened: {
        const sorted = orderBy(sequences, (s => s.analytics.opened), newSortDirection);
        this.setState({
          sortValue: SequenceListHeaderSortKey.Opened,
          sequences: sorted,
          sortDirection: newSortDirection,
        });
        return;
      }
      case SequenceListHeaderSortKey.Clicked: {
        const sorted = orderBy(sequences, (s => s.analytics.clicked), newSortDirection);
        this.setState({
          sortValue: SequenceListHeaderSortKey.Clicked,
          sequences: sorted,
          sortDirection: newSortDirection,
        });
        return;
      }
      case SequenceListHeaderSortKey.Replied: {
        const sorted = orderBy(sequences, (s => s.analytics.replied), newSortDirection);
        this.setState({
          sortValue: SequenceListHeaderSortKey.Replied,
          sequences: sorted,
          sortDirection: newSortDirection,
        });
        return;
      }
      case SequenceListHeaderSortKey.Date: {
        const sorted = orderBy(sequences, (s => s.created), newSortDirection);
        this.setState({
          sortValue: SequenceListHeaderSortKey.Date,
          sequences: sorted,
          sortDirection: newSortDirection,
        });
        return;
      }
      default:
        return;
    }
  }

  getFilteredSequences = () => {
    const { user } = this.props;
    const { sequences, senderFilter } = this.state;
    if (senderFilter === SequenceSender.Personal) {
      return sequences.filter(s => s.user === user);
    }

    return sequences;
  }

  render() {
    const { isLoaded, isArchived } = this.props;
    const { checked, confirmProps, sortDirection, senderFilter } = this.state;

    const sequences = this.getFilteredSequences();
    const numActives = filter(sequences, s => !s.paused).length;
    const numArchived = filter(sequences, s => s.archived).length;
    const numPaused = size(sequences) - numActives;
    const numChecked = filter(checked, c => c).length;
    const allChecked = !!sequences.length && numChecked === sequences.length;
    let headerTitle = 'Sequences';

    let headerCountDescription: string = `${numActives} active${numPaused ? ` Â· ${numPaused} paused` : ''}`;
    const numSequences = size(sequences);

    if (isArchived) {
      headerTitle = 'Archived Sequences';
      headerCountDescription = `${numArchived} archived`;
    }

    return (
      <div className="sequence-list">
        {confirmProps &&
          <Confirm {...confirmProps} />
        }
        <div className="draggable">
          <div className="heading">
            <div className="left">
              <h1>{headerTitle}</h1>
              <h5>{headerCountDescription}</h5>
            </div>
            <button className="new-sequence" onClick={this.handleCreateSequence}>
              + New Sequence
            </button>
          </div>
        </div>
        <div
          className="list"
        >
          <div className="header">
            <div className="filter">
              <div className="filter-cell">
                <div className="title">
                  STATUS
                </div>
                <Dropdown
                  values={[SequenceFilter.Active, SequenceFilter.Archived]}
                  onSelect={this.handleFilterSelect}
                  startIndex={isArchived ? 1 : 0}
                />
              </div>
              <div className="filter-cell">
                <div className="title">
                  SENDER
                </div>
                <Dropdown
                  values={[SequenceSender.Team, SequenceSender.Personal]}
                  onSelect={this.handleSenderSelect}
                  startIndex={senderFilter === SequenceSender.Personal ? 1 : 0}
                />
              </div>
            </div>
            {!!numChecked && <div className="action-bar">
              <span className="selected-num">
                {`${numChecked} selected`}
              </span>
              <button className="archive" onClick={this.handleArchive}>
                {!!numChecked && !!sequences.length && sequences[0].archived ?
                  'Unarchive' : 'Archive'}
              </button>
              <button
                onClick={this.handleDuplicate}
                className={classnames('duplicate', {
                  disabled: numChecked !== 1,
                })}
              >
                Duplicate
              </button>
              <button onClick={this.handleExport}>Export</button>
              <button className="delete" onClick={this.handleDelete}>Delete</button>
            </div>}
            {!!numSequences && <div className="search">
              <SearchInput
                onSearch={this.debouncedHandleSearch}
                placeholder="Search sequences"
                defaultValue=""
              />
            </div>}
          </div>
          {!numSequences && isLoaded && this.renderEmpty()}
          {!!numSequences && (
            <SequenceListHeader
              sortValue={this.state.sortValue}
              onSortClick={this.handleSortClick}
              sortDirection={sortDirection}
              onCheckToggle={this.handleCheckAll}
              allChecked={allChecked}
              padName={!isArchived}
            />
          )}
          <Autosizer>
            {s => (
              <List
                className="virtual-list"
                height={s.height - 130}
                itemSize={67}
                itemCount={sequences.length}
                width={s.width}
                key="infinite"
              >
                {i => (
                  <div className="container" style={i.style}>
                    <SequenceCell
                      key={sequences[i.index].id}
                      sequence={sequences[i.index]}
                      checked={!!checked[sequences[i.index].id]}
                      onCheckboxChange={this.handleCheckboxChange}
                    />
                  </div>
                )}
              </List>
            )}
          </Autosizer>
          {!numSequences && !isLoaded && <Loader />}
        </div>
      </div>
    );
  }
}

export default SequenceList;
