import { Resource, Me, Team } from '@src/types';

export interface BillingState {
  resource: Resource<Billing>;
  updating: boolean; // used for loading indicator when actions are being performed
  upgradingError?: Error;
  coupons: {
    [meta: string]: Resource<BillingCoupon>;
  };
  upgradeCoupon?: BillingCouponKey;
}

// to do: set real types for these
export interface Coupon {
  [key: string]: string;
}

export const enum CouponType {
  Coupon = 'coupon',
  Promo = 'promo',
  Remove = 'remove',
}

export interface Billing {
  stripeCustomer: string;
  balance: number;
  card?: BillingCard;
  subscription?: BillingSubscription;
  nextInvoice?: BillingInvoice;
}

export interface BillingCard {
  name: string;
  lastFour: string;
  expMonth: string;
  expYear: string;
  brand: BillingCardBrand;
}

export const enum BillingCardBrand {
  AMEX = 'American Express',
  Diners = 'Diners Club',
  Discover = 'Discover',
  JCB = 'JCB',
  MasterCard = 'MasterCard',
  UnionPay = 'UnionPay',
  Visa = 'Visa',
  Unknown = 'Unknown',
}

export enum BillingCycle {
  Monthly = 'monthly',
  Annual = 'annual',
}

export enum BillingTier {
  Pro = 'pro', // Deprecated
  Basic = 'basic',
  Team = 'team', // now called Premium
  Enterprise = 'enterprise',

  // Used for account limit check
  BasicV2 = 'basicV2',
}

export interface BillingInvoice {
  date: Date;
  amount: number;
  attempts: number;
}

