export interface BillingSubscription {
  tier: BillingTier;
  plan: string;
  billingCycle: BillingCycle;
  status: BillingSubscriptionStatus;
  seats: number;
  planStarts: Date;
  trialExpires?: Date;
  planExpires?: Date;
  coupon?: string;
  couponPercent?: number;
  cost: number;
}

export interface BillingCouponKey {
  tier: BillingTier;
  billingCycle: BillingCycle;
  coupon: string;
}

export interface BillingCoupon {
  couponPercent: number;
}

export const enum BillingSubscriptionStatus {
  Trialing = 'trialing',
  Active = 'active',
  PastDue = 'past_due',
  Canceled = 'canceled',
  Unpaid = 'unpaid',
}

export interface UpgradePrompt {
  expirationDate?: Date;
  showTrialExpiring: boolean;
  showTrialExpired: boolean;
  admin: boolean;
  onTrial: boolean;
}

export interface BillingTestCase {
  name: string;
  me: Resource<Me>;
  billing: Resource<Billing>;
  team: Resource<Team>;
}
