import React from 'react';
import classnames from 'classnames';
import moment from 'moment';
import { VictoryPie } from 'victory';
import { Resource, Team, TeamMember, Onboarding } from '@src/types';
import {
  Sequence,
  SequenceTab,
  SequenceStage,
  SequenceStageInstance,
  SequenceRecipientsAddState,
} from '@src/types/sequences';
import history from '@src/lib/browserHistory';
import CenteredLoader from '@src/components/Loader/CenteredLoader';

import './SequenceDetail.scss';
import Dropdown from '@src/components/Form/Dropdown';
import EditableInput from '@src/components/Form/EditableInput/EditableInput';
import StageList from './StageList';
import { Link, RouteComponentProps } from 'react-router-dom';
import SequenceRecipientsList from './SequenceRecipientsList';
import SequenceSettings from './SequenceSettings';
import {
  SequenceRecipientCreateMeta,
  SequenceRecipientHeadersAddMeta,
} from '@src/ducks/sequenceRecipients';
import Overlay from '@src/components/Base/Overlay';
import UploadRecipient from './UploadRecipient';
import CSVHeaderMapping from './CSVHeaderMapping/CSVHeaderMapping';
import Play from '@src/components/Icons/Play.svg';
import Pause from '@src/components/Icons/Pause.svg';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import { PulseGem } from '@src/components/Base/PulseGem';
import OnboardingFooter, {
  Props as OnboardProps,
} from '@src/components/Banner/OnboardingFooter/OnboardingFooter';
import { SequenceStageCreateMeta } from '@src/ducks/sequenceStages';
import { createSequenceStage } from '@src/mocks/sequences';
// tslint:disable-next-line:max-line-length
import UploadRecipientsConfirmation from './UploadRecipientsConfirmation/UploadRecipientsConfirmation';
import { DARKMODE_VICTORY_BACKGROUND_COLOR, DEFAULT_VICTORY_BACKGROUND_COLOR } from '@src/lib/colors';
import { OnboardingPatchMeta } from '@src/ducks/me';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface StateProps {
  sequence?: Resource<Sequence>;
  user?: TeamMember;
  selectedTab?: SequenceTab;
  team?: Team;
  onboarding: Onboarding;
  stages: Array<Resource<SequenceStage>>;
  instances: SequenceStageInstance[];
  sequenceRecipientsAddState?: SequenceRecipientsAddState;
  sequenceRecipientsCount: number;
}

export interface DispatchProps {
  updateSequence: (sequence: Sequence) => void;
  shareSequence: (sequence: Sequence) => void;
  uploadCsv: (meta: File) => void;
  duplicateSequence: (sequence: string) => void;
  deleteSequence: (sequence: Sequence) => void;
  exportSequence: (sequence: Sequence) => void;
  addRecipients: (recipient: SequenceRecipientCreateMeta) => void;
  updateOnboarding: (type: OnboardingPatchMeta) => void;
  createStage: (meta: SequenceStageCreateMeta) => void;
  showSequenceRecipientAdd: (shouldShow: boolean) => void;
  addSequenceRecipientHeaders: (meta: SequenceRecipientHeadersAddMeta) => void;
  clearSequenceRecipientSuccessCount: () => void;
}

interface State {
  confirmProps?: ConfirmProps;
  onboardingProps?: OnboardProps;
}

class SequenceDetail extends React.PureComponent<
  StateProps & DispatchProps & RouteComponentProps<any>, State
> {
  constructor(props: StateProps & DispatchProps & RouteComponentProps<any>) {
    super(props);

    this.state = {};
  }

  handleMoreSelect = (value: string, index: number) => {
    const {
      sequence,
      updateSequence,
      duplicateSequence,
      deleteSequence,
      exportSequence,
    } = this.props;

    if (!sequence || !sequence.data) {
      return;
    }

    switch (value) {
      case 'Archive':
        updateSequence({
          ...sequence.data,
          archived: true,
          paused: true,
        });
        break;
      case 'Unarchive':
        updateSequence({
          ...sequence.data,
          archived: false,
        });
        break;
      case 'Duplicate':
        duplicateSequence(sequence.data.id);
        break;
      case 'Export':
        exportSequence(sequence.data);
        break;
      case 'Delete':
        const deleteHandler = () => {
          if (sequence.data) {
            deleteSequence(sequence.data);
          }
          history.push('/sequences');
          this.setState({ confirmProps: undefined });
        };

        // Show confirm dialogue with callback to delete
        const confirmProps: ConfirmProps = {
          title: 'Delete this Sequence?',
          message: 'Are you sure you want to delete this Sequence?'
            + ' Any analytics and recipients data will be lost forever.',
          confirmCopy: 'Delete',
          cancelCopy: 'Cancel',
          onConfirm: () => deleteHandler(),
          onCancel: () => this.setState({ confirmProps: undefined }),
        };

        this.setState({
          confirmProps,
        });
        break;
      default:
        break;
    }
  }

  componentDidUpdate() {
    this.setNextOnboarding();
  }

  setNextOnboarding = () => {
    const { onboarding, updateOnboarding, createStage, sequence } = this.props;
    let onboardingProps: OnboardProps | undefined;
    if (!onboarding.addSequenceRecipients && !this.state.onboardingProps) {
      onboardingProps = {
        id: 'addSequenceRecipients',
        title: 'Add Recipients',
        message: 'Get started by adding recipients to your Sequence. '
        + 'You\'ll also be able to add more recipients later.',
        buttonCopy: 'Add Recipients',
        onClick: () => {
          this.handleAddRecipientClick();
        },
        onSkip: () => {
          if (!onboarding.addSequenceRecipients) {
            updateOnboarding({ skipped: true, type: { addSequenceRecipients: true } });
            this.setState({ onboardingProps: undefined });
          }
        },
      };
      this.setState({ onboardingProps });

    } else if (!onboarding.editSequenceStage && !this.state.onboardingProps) {
      onboardingProps = {
        id: 'editSequenceStage',
        title: 'Edit Sequence Stages',
        message: 'Each stage represents a message in your Sequence. '
          + 'Edit your first stage to continue',
        buttonCopy: 'Edit Stage',
        onClick: () => {
          // Handle redirect to first sequence when clicked on
          const { stages } = this.props;
          const stage = stages[0];
          const sequenceId = sequence && sequence.data && sequence.data.id;
          const stageId = stage && stage.data && stage.data.id;
          if (sequenceId && stageId) {
            history.push(`/sequences/${sequenceId}/stages/${stageId}`);
          }
        },
        onSkip: () => {
          if (!onboarding.editSequenceStage) {
            updateOnboarding({ skipped: true, type: { editSequenceStage: true } });
            this.setState({ onboardingProps: undefined });
          }
        },
      };
      this.setState({ onboardingProps });

    } else if (!onboarding.createAdditionalStage && !this.state.onboardingProps) {
      onboardingProps = {
        id: 'createAdditionalStage',
        title: 'Create Additional Stages',
        message: 'Recipients will be automatically sent the next stage. '
          + 'after a certain number of days if they haven\'t responded.',
        buttonCopy: 'Add Stage',
        onClick: () => {
          // Creates sequence
          if (sequence && sequence.data) {
            const mockStage = createSequenceStage({
              sequence: sequence && sequence.data && sequence.data.id,
            });
            createStage({ stage: mockStage, navigateTo: true });
          }
        },
        onSkip: () => {
          if (!onboarding.createAdditionalStage) {
            updateOnboarding({ skipped: true, type: { createAdditionalStage: true } });
            this.setState({ onboardingProps: undefined });
          }
        },
      };
      this.setState({ onboardingProps });

    } else if (!onboarding.activateSequence && !this.state.onboardingProps) {

      onboardingProps = {
        id: 'activateSequence',
        title: 'You\'re ready to start sending!',
        message: 'You can check your Settings first to update which days and times '
        + 'your Sequence will send.',
        buttonCopy: 'Activate Sequence',
        onClick: () => {
          // Handles unpausing
          if (sequence && sequence.data && sequence.data.paused) {
            this.handlePausedToggle();
          }
          this.setState({ onboardingProps: undefined });
        },
        onSkip: () => {
          if (!onboarding.activateSequence) {
            updateOnboarding({ skipped: true, type: { activateSequence: true } });
            this.setState({ onboardingProps: undefined });
          }
        },
      };
      this.setState({ onboardingProps });
    }
  }

  // Blocks activating sequence with confirm prompt if any unedited stages or no recipients
  passesActivateRequirements = (): boolean => {
    const { instances, sequenceRecipientsCount } = this.props;
    const uneditedStages = instances.filter(instance => !instance.userEdited);

    if (uneditedStages.length > 0) {
      const firstStage = uneditedStages[0];
      const sequenceId = firstStage.sequence;
      const stageId = firstStage.stage;
      const title = uneditedStages.length > 1
        ? 'Edit Stages'
        : 'Edit Stage';
      const stageDescriptor = uneditedStages.length > 1
        ? `${uneditedStages.length} stages`
        : 'an unedited stage';
      const confirmProps: ConfirmProps = {
        title,
        message: `You have ${stageDescriptor} that need `
        + 'to be updated before activating this sequence.',
        confirmCopy: 'Edit Stage',
        onConfirm: () => {
          this.setState({ confirmProps: undefined });
          history.push(`/sequences/${sequenceId}/stages/${stageId}`);
        },
      };

      this.setState({ confirmProps });
      return false;
    }

    if (sequenceRecipientsCount === 0) {
      const title = 'Add Recipients';
      const message = 'You need to add recipients before activating this Sequence';
      const confirmProps: ConfirmProps = {
        title,
        message,
        confirmCopy: 'Add',
        cancelCopy: 'Cancel',
        onConfirm: () => {
          this.handleAddRecipientClick();
          this.setState({ confirmProps: undefined });
        },
        onCancel: () => this.setState({ confirmProps: undefined }),
      };

      this.setState({ confirmProps });
      return false;
    }

    return true;
  }

  handlePausedToggle = () => {
    const { sequence, updateSequence, updateOnboarding, onboarding } = this.props;
    // Toggle onboarding if activate sequence is on
    if (!onboarding.activateSequence) {
      updateOnboarding({ skipped: false, type: { activateSequence: true } });
      this.setState({ onboardingProps: undefined });
    }

    // Do not allow setting live if stage has not been updated
    if (!this.passesActivateRequirements()) {
      return;
    }

    if (sequence && sequence.data) {
      const togglePause = () => updateSequence({
        ...sequence.data!,
        paused: !sequence.data!.paused,
      });

      // If active, immediately pause
      if (!sequence.data.paused) {
        togglePause();
      }

      // Show confirm prompt if not active yet
      if (sequence.data.paused) {
        const confirmProps: ConfirmProps = {
          title: `Activate ${sequence.data.name}`,
          message: `When activated, this sequence will go `
            + `live to ${sequence.data.recipients.active} recipients.`,
          cancelCopy: 'Cancel',
          confirmCopy: 'Set Live',
          onConfirm: () => {
            togglePause();
            logAmplitudeEvent(AmplitudeEvents.StartNewSequence);
            this.setState({ confirmProps: undefined });
          },
          onCancel: () => {
            this.setState({ confirmProps: undefined });
          },
        };
        this.setState({
          confirmProps,
        });
      }
    }
  }

  handleEditName = (property: string, name: string) => {
    const { sequence, updateSequence } = this.props;

    if (sequence && sequence.data) {
      updateSequence({
        ...sequence.data,
        name,
      });
    }
  }

  handleShare = () => {
    const { sequence, team, shareSequence } = this.props;

    if (sequence && sequence.data && team) {
      shareSequence({
        ...sequence.data,
        team: team.id,
      });
    }
  }

  handleFiles = (files: File[]) => {
    const { uploadCsv } = this.props;
    const file = files[0];

    if (uploadCsv && file) {
      uploadCsv(file);
    }
  }

  renderLoadingState = () => {
    return (
      <div className="sequence-detail">
        <CenteredLoader />
      </div>
    );
  }

  handleAddRecipientClick = () => {
    const { onboarding, updateOnboarding, showSequenceRecipientAdd } = this.props;

    // Handle removing onboarding props
    if (!onboarding.addSequenceRecipients) {
      updateOnboarding({ skipped: false, type: { addSequenceRecipients: true } });
      this.setState({ onboardingProps: undefined });
    }

    showSequenceRecipientAdd(true);
  }

  handleAddRecipientClose = () => {
    this.props.showSequenceRecipientAdd(false);
  }

  handleAddRecipients = (
    addAsPaused: boolean,
  ) => {
    const { addRecipients, sequence } = this.props;

    if (sequence && sequence.data) {
      addRecipients({
        sequence: sequence.data.id,
        addAsPaused,
      });
    }
  }

  handleDone = () => {
    const { sequence, showSequenceRecipientAdd } = this.props;

    if (sequence && sequence.data) {
      showSequenceRecipientAdd(false);
      history.push(`/sequences/${sequence.data.id}/recipients`);
    }
  }

  handleInvalidClick = () => {
    const { clearSequenceRecipientSuccessCount } = this.props;

    clearSequenceRecipientSuccessCount();
  }

  render() {
    const {
      sequence,
      user,
      team,
      stages,
      // uploadedSequenceRecipients,
      onboarding,
      sequenceRecipientsAddState,
      addSequenceRecipientHeaders,
    } = this.props;
    const { confirmProps, onboardingProps } = this.state;
    const selectedTab = this.props.selectedTab || SequenceTab.Stages;
    const showActivateGem = onboarding.createAdditionalStage && onboarding.editSequenceStage &&
      onboarding.addSequenceRecipients && !onboarding.activateSequence;

    if (!sequence || !sequence.data || !stages || !stages.length || !stages[0].data) {
      return this.renderLoadingState();
    }

    const { recipients, analytics } = sequence.data;
    const recipientsNum = recipients.active + recipients.completed +
      recipients.paused + recipients.bounced + recipients.failed;
    const recipientsAnalytics = [
      { name: 'Active', value: 'active' },
      { name: 'Completed', value: 'completed' },
      { name: 'Paused', value: 'paused' },
      { name: 'Bounced', value: 'bounced' },
    ];
    const sequenceAnalytics = [
      {
        name: 'Delivered',
        value: 'delivered',
        color: '#444',
        total: analytics.delivered + recipients.bounced,
      },
      {
        name: 'Opened',
        value: 'opened',
        color: '#00aaff',
        total: sequence.data.analytics.delivered - recipients.bounced,
      },
      {
        name: 'Clicked',
        value: 'clicked',
        color: '#ffa057',
        total: sequence.data.analytics.delivered - recipients.bounced,
      },
      {
        name: 'Downloaded',
        value: 'downloaded',
        color: '#d347ae',
        total: sequence.data.analytics.delivered - recipients.bounced,
      },
      {
        name: 'Replied',
        value: 'replied',
        color: '#0ec36b',
        total: sequence.data.analytics.deliveredUnique,
      },
      {
        name: 'Unsubscribed',
        value: 'unsubscribed',
        color: '#444',
        total: sequence.data.analytics.deliveredUnique,
      },
    ];
    const createdString = moment(sequence.data.created).format('MMM DD, YYYY');
    const total = sequence.data.analytics.delivered - recipients.bounced;
    const dropdownValues = ['Archive', 'Duplicate', 'Export', 'Delete'];

    if (sequence.data.archived) {
      dropdownValues[0] = 'Unarchive';
    }

    // TODO(SHIN): Abstract to not rely on document.body (preferably using constants)
    const victoryColorBackground = document.body.getAttribute('theme') === 'theme-dark'
      ? DARKMODE_VICTORY_BACKGROUND_COLOR : DEFAULT_VICTORY_BACKGROUND_COLOR;

    return (
      <div className="sequence-detail">
        {confirmProps &&
          <Confirm
            {...confirmProps}
          />
        }
        <div className="navigation-bar">
          <div className="navigation">
            <Link
              className="sequences-breadcrumb"
              to={!sequence.data.archived ? `/sequences` : `/sequences/archive`}
            >
              {!sequence.data.archived ? 'Sequences' : 'Archive'}
            </Link>
            <span className="arrow">&gt;</span>
            <Link to={`/sequences/${sequence.data.id}`} className="sequence-breadcrumb">
              {sequence.data.name}
            </Link>
            {!sequence.data.paused && <span className="live-badge">
              Live
            </span>}
          </div>
          <div className="actions">
            {onboarding && !onboarding.addSequenceRecipients &&
              <PulseGem top="-15px" left="-30px" />
            }
            {!sequence.data.archived && <button
              className="add-recipients"
              onClick={this.handleAddRecipientClick}
            >
              + Add Recipients
            </button>}
            <Dropdown
              placeholder="More"
              values={dropdownValues}
              onSelect={this.handleMoreSelect}
              value=""
            />
            {showActivateGem &&
              <PulseGem top="-16px" left="-20px" />
            }
            <button
              className={classnames({
                'set-live': sequence.data.paused,
                pause: !sequence.data.paused,
              })}
              onClick={this.handlePausedToggle}
            >
              {sequence.data.paused ? <Play className="icon" /> : <Pause className="icon" />}
              <span>{sequence.data.paused ? 'Set Live' : 'Pause'}</span>
            </button>
          </div>
        </div>
        <div className="main">
          <div className="sequence-analytics">
            <div className="top-row">
              <div className="title">
                <EditableInput
                  className="name"
                  text={sequence.data.name}
                  onSet={this.handleEditName}
                  property="name"
                />
              </div>
              <div className="recipients-analytics">
                {!!recipientsNum && <Link
                  className="link"
                  to={`/sequences/${sequence.data.id}/recipients?filter=AllRecipients`}
                >
                  <div className="recipients-analytic">
                    <span className="number">{recipientsNum}</span>
                    <span>Recipients</span>
                  </div>
                </Link>}
                {recipientsAnalytics.filter(r => !!recipients[r.value]).map(r => (
                  <Link
                    className="link"
                    to={`/sequences/${sequence.data!.id}/recipients?filter=${r.name}`}
                    key={r.value}
                  >
                    <div className={classnames('recipients-analytic', r.value)}>
                      <span className="number">{recipients[r.value]}</span>
                      <span>{r.name}</span>
                    </div>
                  </Link>
                ))}
              </div>
            </div>
            <div className="details">
              <div className="numbers">
                {`${sequence.data.stages} stages · ${sequence.data.durationDays} days in sequence`}
                {!!recipients.failed && <Link
                  className="error"
                  to={`/sequences/${sequence.data.id}/recipients?filter=Failed`}
                >
                  {`⚠️ ${recipients.failed} recipients failed`}
                </Link>}
              </div>
              <div className="created">
                {`Created by ${user && `${user.firstName} ${user.lastName}`} · ${createdString}`}
                <span
                  className={classnames('tag', {
                    shared: !!sequence.data.team,
                    private: !sequence.data.team,
                  })}
                >
                  {sequence.data.team ? 'Shared' : 'Private'}
                </span>
              </div>
            </div>
            {!!total && <div className="charts">
              {sequenceAnalytics.map(a => (
                <Link
                  className="link"
                  to={`/sequences/${sequence.data!.id}/recipients?filter=${a.name}`}
                  key={a.name}
                >
                  <div className="chart-wrapper">
                    <div className="chart">
                      <VictoryPie
                        key={a.name}
                        data={[
                          { x: 1, y: analytics[a.value] },
                          { x: 2, y: total - analytics[a.value] },
                        ]}
                        colorScale={[a.color, victoryColorBackground]}
                        innerRadius={130}
                        animate={{
                          duration: 5000,
                        }}
                      />
                      <div className="numbers">
                        <h1 style={{ color: a.color }}>
                          {a.total ?
                            Math.round((analytics[a.value] / a.total) * 100)
                            : 0}%
                        </h1>
                        <h4>{analytics[a.value]} of {a.total}</h4>
                      </div>
                    </div>
                    <div className="label">
                      {a.name}
                    </div>
                  </div>
                </Link>
              ))}
            </div>}
          </div>
          <div className="drilldowns">
            <div className="tabs">
              <Link to={`/sequences/${sequence.data.id}`} className="link">
                <div
                  className={classnames('tab', {
                    selected: selectedTab === SequenceTab.Stages,
                  })}
                >
                  Stages
                </div>
              </Link>
              <Link to={`/sequences/${sequence.data.id}/recipients`} className="link">
                <div
                  className={classnames('tab', {
                    selected: selectedTab === SequenceTab.Recipients,
                  })}
                >
                  Recipients
                </div>
              </Link>
              <Link to={`/sequences/${sequence.data.id}/settings`} className="link">
                <div
                  className={classnames('tab', {
                    selected: selectedTab === SequenceTab.Settings,
                  })}
                >
                  Settings
                </div>
              </Link>
              {!sequence.data.archived && <button className="private" onClick={this.handleShare}>
                {team && !sequence.data.team && <span>This sequence is private to you.</span>}
                {team && !sequence.data.team && <span className="underline">Share with team</span>}
              </button>}
              {!!sequence.data.archived && <div className="private">
                <span>This sequence is archived.</span>
              </div>}
            </div>
            {selectedTab === SequenceTab.Stages && <StageList />}
            {selectedTab === SequenceTab.Recipients && <SequenceRecipientsList
              onAddRecipients={this.handleAddRecipientClick}
            />}
            {selectedTab === SequenceTab.Settings && <SequenceSettings />}
          </div>
        </div>
        {sequenceRecipientsAddState && <Overlay>
            <div className="upload-process">
              {!sequenceRecipientsAddState.headersToAdd &&
                sequenceRecipientsAddState.successCount === undefined && <UploadRecipient
                  handleFiles={this.handleFiles}
                  sequenceName={sequence.data.name}
                  onClose={this.handleAddRecipientClose}
                  onAddRecipients={this.handleAddRecipients}
                />}
            {sequenceRecipientsAddState.headersToAdd &&
              <CSVHeaderMapping
                headers={sequenceRecipientsAddState.headersToAdd}
                defaultEmailHeader={sequenceRecipientsAddState.emailHeader}
                defaultFamilyNameHeader={sequenceRecipientsAddState.familyNameHeader}
                defaultGivenNameHeader={sequenceRecipientsAddState.givenNameHeader}
                onClose={this.handleAddRecipientClose}
                addHeaders={addSequenceRecipientHeaders}
              />}
            {sequenceRecipientsAddState.successCount !== undefined &&
              <UploadRecipientsConfirmation
                successCount={sequenceRecipientsAddState.successCount}
                recipients={sequenceRecipientsAddState.recipients}
                sequenceName={sequence.data.name}
                onDone={this.handleDone}
                onInvalidClick={this.handleInvalidClick}
              />}
            </div>
          </Overlay>}
        {onboardingProps && !sequenceRecipientsAddState &&
          <OnboardingFooter
            {...onboardingProps}
          />
        }
      </div>
    );
  }
}

export default SequenceDetail;
