import { RouterState } from 'react-router-redux';
import { Stores } from '@src/lib/db';
import { Moment } from 'moment';

import { SalesforceState } from '@src/types/salesforce';
import { BillingState, Coupon } from '@src/types/billing';

import {
  Sequence, SequenceRecipient, SequenceTab,
  SequenceStage, SequenceStageInstance, SequenceStageRecipient,
  SequenceRecipientsState,
  SequenceRecipientsAddState,
} from '@src/types/sequences';
import { SchedulingState } from '@src/types/scheduling';
import { LeftSidebarState } from '@src/types/leftSidebar';
import { RightSidebarState } from '@src/types/rightSidebar';
import {
  SharedThreadFilter, SharedThread, Comment, SharedThreadActivity,
} from '@src/types/sharedThreads';
import { TeamInviteValidateResponse } from '@src/api/team';
import { SlackState } from '@src/types/slack';
import { AsanaState } from '@src/types/asana';
import { ActivityFeedSearchMeta } from '@src/ducks/activityFeed';
import { Filter } from './filters';
import { ZoomState } from './zoom';
import { StatYearlySummary } from '@src/api/analytics';
import { HubspotState } from './hubspot';
import { StatusPageResult } from '@src/api/app';
import { ExperimentsState } from '@src/lib/unleash-experiments';

export interface StoreState {
  theme: Resource<ThemeSetting>;
  dbReady: boolean;
  appUpdateReady: boolean;
  webappUpdateReady: boolean;
  isOnline: boolean;
  isDefaultClient: boolean;
  clearingCache: boolean;

  loggedIn: boolean;
  authPending: boolean;
  signUpPending: boolean;
  sharedThreadsStatus?: ResourceStatus;
  commandControlType?: CommandControlType;

  isMobile?: boolean;

  contactSupportPopup: {
    collapsed: boolean;
  };

  team: Resource<Team>;

  me: Resource<Me>;
  lists: List[];
  accounts: Account[];
  devices: Device[];
  features: Features;
  preferences: Preferences;
  billing: BillingState;

  mailbox: Mailbox;
  mailboxThreads: Resource<Thread[]>;

  selectedIds: Map<string, boolean>;

  // Used for any multistep thread actions
  threadSwipeId?: string;

  selectedThread?: Thread;
  selectedSharedThread?: string;
  selectedDraft?: string;
  selectedCalendarEvent?: CalendarEvent;

  selectedContact?: string;
  selectedContactNotes: ContactNote[];
  selectedContactThreads: Thread[];
  selectedContactFiles: ContactAttachment[];

  selectedActivity?: Activity;
  selectedContactThread?: Thread;

  messageBodies: {
    [id: string]: Resource<MessageBody[]>;
  };

  contacts: {
    [id: string]: Resource<Contact>;
  };

  sharedThreads: {
    [id: string]: Resource<SharedThread>;
  };

  sharedThreadDrafts: {
    [id: string]: Draft;
  };

  sharedThreadFilter?: SharedThreadFilter;
  sharedThreadUserFilter?: SharedThreadFilter;

  comments: {
    [sharedThread: string]: Comment[];
  };

  // TODO (Cindy): Should not rely on this. Will need to refactor
  threads: {
    [id: string]: Resource<Thread>;
  };

  teamMembers: TeamMembers;
  teamInvites: Resource<TeamInvite[]>;
  teamInvitesValidation: {
    // Key is nonce + payload concatenated together
    [key: string]: Resource<TeamInviteValidateResponse>;
  };

  // Public Team Members
  publicMembers: TeamMembers;
  publicTeams: {
    [id: string]: Team;
  };

  contactGroups: {
    [id: string]: ContactGroup;
  };

  drafts: {
    [id: string]: Resource<Draft>;
  };

  activityFeed: Resource<Activity[]>;
  activityFeedSearchMeta?: ActivityFeedSearchMeta;

  // Resource id -> attachment metas
  // For SequenceStageInstances, the instance id is the resource Id.
  attachmentMetas: {
    [resource: string]: {
      [id: string]: Resource<AttachmentMeta>;
    };
  };

  // Resource id -> attachment
  // For SequenceStageInstances, the instance id is the resource Id.
  attachments: {
    [resource: string]: {
      [id: string]: Attachment;
    },
  };

  attachmentsDownloading: {
    [id: string]: {
      progress: number,
      preview: boolean,
    };
  };

  templates: {
    [template: string]: Template;
  };

  routing: RouterState;

  typeaheadContacts: Contact[];

  mailCounts: {
    [account: string]: MailCount;
  };

  tracks: {
    [thread: string]: {
      [message: string]: Resource<Track>;
    };
  };

  sessionId: number;

  toast?: Error;

  // ShareLink identifier is threadId + messageId
  shareLinks: {
    [id: string]: Resource<ShareLink>;
  };
  selectedShareLink?: string;

  integrations: Integration[];

  calendars: {
    [account: string]: Calendar[];
  };

  calendarEvents: {
    command: {},
    rsb: {},
  };

  permissions: Permission;

  closedBanners: { [banner: string]: boolean };

  onboarding: Onboarding;

  incrementalSyncing: boolean;

  hotkeyActions: { [action: string]: number };

  selectedSequence?: string;
  sequences: { [sequence: string]: Resource<Sequence> };
  sequenceRecipientsState?: SequenceRecipientsState;

  // Use identifier to note if list endpoint for resource has completed
  // SequenceList -> `sequences`
  // TODO(SHIN): SequenceRecipientsList/SequenceStageRecipientsList/SequenceStageList
  sequencePageLoaded: {
    [key: string]: boolean;
  };

  sequenceStages: {
    [sequence: string]: {
      [stage: string]: Resource<SequenceStage>,
    },
  };
  selectedSequenceStage?: string;

  sequenceStageInstances: {
    [sequenceAndSequenceStage: string]: {
      [instanceId: string]: Resource<SequenceStageInstance>,
    },
  };

  sequenceStageRecipients: {
    [sequenceStage: string]: Resource<SequenceStageRecipient[]>,
  };

  sequenceRecipients: {
    [sequence: string]: {
      [recipient: string]: Resource<SequenceRecipient>,
    },
  };

  selectedSequenceTab?: SequenceTab;
  sequenceCount?: number;

  sequenceRecipientsAddState?: SequenceRecipientsAddState;

  salesforce: SalesforceState;
  scheduling: SchedulingState;
  hubspot: HubspotState;
  slack?: SlackState;
  asana: AsanaState;
  zoom: ZoomState;

  leftSidebar: LeftSidebarState;
  rightSidebar: RightSidebarState;
  threadListWidth: number;

  referralInvites: {
    [invite: string]: ReferralInvite,
  };

  sharedThreadActivity: {
    comment: { [user: string]: SharedThreadActivity },
    reply: { [user: string]: SharedThreadActivity },
    view: { [user: string]: SharedThreadActivity },
  };

  // List of blocked users
  filters: {
    [id: string]: Filter;
  };

  // Miscellaneous state to gatekeep user flow
  tfaNeeded: boolean;
  tfaPasswordConfirmed?: boolean;
  banner?: BannerType;
  muteSounds?: boolean;
  showHotkeySuggestions?: boolean;

  // UTM params to pass up to server
  utmParams?: UTMParams;

  // Identifier for special signup source
  unsubscriberSignup?: boolean;

  // Redirect platform (Used when signing in with magic link)
  redirectPlatform?: DevicePlatform;

  // Used for demo video
  demoVideo?: DemoVideoMeta;
  showTeamInviteAcceptedModal?: boolean;

  // Yearly Summaries for year in review
  yearlySummaries?: {
    [key: number]: Resource<StatYearlySummary>;
  };

  status: PolymailStatus;
}

export enum PolymailStatusType {
  Online = 'online',
  Syncing = 'syncing',
}

export interface PolymailStatus {
  statusType: PolymailStatusType;
  commitsRemaining: number;
  statusPage?: StatusPageResult;
}

export interface DemoVideoMeta {
  url: string;
  thumbnailUrl: string;
  captionUrl?: string;
  name?: string;
}

export interface LoginToken {
  public: string;
  private?: string;
  ttl?: number;
  reuseTtl?: number;
  reuseDate?: Date;
}

export interface UTMParams {
  source?: string;
  medium?: string;
  campaign?: string;
}

export interface Permission {
  mailToProtocol: boolean;
  notifications: boolean;
}

export interface ShareLink {
  sharedThread: string;
  message?: string;
  url?: string;
}

export interface AttachmentResource {
  attachment: Attachment;
  uploading: boolean;
  uploadProgress: number;
}

export interface RealtimeEvent {
  name: string;
  data: any;
}

export const enum BannerType {
  AppleSilicon = 'appleSilicon',
  DownloadChrome = 'downloadChrome',
  DefaultMailClient = 'defaultMailClient',
  InvalidAccount = 'invalidAccount',
  NotificationEnable = 'notificationEnable',
  NotificationEnableWarning = 'notificationEnableWarning',
  TrialExpiring = 'trialExpiring',
  UpdateAvailable = 'updateAvailable',
  Offline = 'offline',
  ReferralRedemption = 'referralRedemption',
  YearInReview = 'yearInReview',
  None = 'none',
  Disabled = 'disabled',
}

export const enum CommandControlType {
  ReadLater = 'readlater',
  SendLater = 'sendlater',
  Label = 'label',
  Thread = 'thread',
  Draft = 'draft',
  FollowUp = 'followup',
  Templates = 'templates',
  Calendar = 'calendar',
  Global = 'global',
  Navigation = 'navigation',
  System = 'system',
  SchedulingPages = 'schedulingPages',
}

export const enum TeamPlan {
  Inactive = 'inactive',
  Individual = 'individual',
  Team = 'pro',
  Enterprise = 'enterprise',
}

export interface Team {
  id: string;
  created: Date;
  updated: Date;
  name: string;
  plan: TeamPlan;
  admins: string[];
  trialExpires?: Date;
  planExpires?: Date;
  seatsUsed: number;
  trialPlan?: TeamPlan;
  cost?: number;
  syncExpires?: Date;
}

export interface Features {
  freeTier: boolean;
  messageTemplates: boolean;
  trackLinks: boolean;
  trackRecipients: boolean;
  cloudAttachments: boolean;
  noBranding: boolean;
  activityFeed: boolean;
  autoBcc: boolean;
  sendLater: boolean;
  campaigns: boolean;
  followUp: boolean;
  sequences: boolean;
  sharedTemplates: boolean;
  analytics: boolean;
  unsubscriber: boolean;
  salesforce: boolean;
  scheduling: boolean;
  schedulingDoubleBooking: boolean;
  schedulingNoBranding: boolean;
  comments: boolean;
  disableSentFromPolymail: boolean;
  hubspot: boolean;

  // NOTE(SHIN): Experiment on account number limit
  accounts?: number;
  schedulings?: number;
}

export const enum Swipe {
  Trash = 'trash',
  Archive = 'archive',
  Inbox = 'inbox',
  Unread = 'unread',
  Later = 'later',
  List = 'list',
  Star = 'star',
  None = 'none',
}

export const enum LeftSidebarCount {
  All = 'all',
  Unread = 'unread',
}

export const enum DockCount {
  All = 'all',
  Unread = 'unread',
  Bool = 'bool',
  BoolUnread = 'boolUnread',
  None = 'none',
}

export const enum Hotkeys {
  Default = 'default',
  Gmail = 'gmail',
}

export const enum UnsubscribeBehavior {
  None = 'none',
  Archive = 'archive',
  Trash = 'trash',
}

export const enum DisplayMode {
  Default = 'default',
  Compact = 'compact',
}

export const enum MarkReadOn {
  None = 'none',
  Archive = 'archive',
}

export const enum BlockContactActionType {
  Trash = 'trash',
  Archive = 'archive',
}

export interface Preferences {
  showSocialList: boolean;
  showPromotionsList: boolean;
  showUpdatesList: boolean;

  disableLinkPreviews: boolean;
  disableTrackByDefault: boolean;
  sendAndArchive: boolean;
  cancelOnReply: boolean;

  leftSidebarCount: LeftSidebarCount;
  dockCount: DockCount;
  hotkeys: Hotkeys;
  unsubscribeBehavior: UnsubscribeBehavior;
  blockContactAction: BlockContactActionType;
  defaultAccount: string; // account id, may be incorrect or not set
  displayMode: DisplayMode;
  markReadOn: MarkReadOn;
  blockTrackingPixels: boolean;

  swipeShortRight: Swipe;
  swipeLongRight: Swipe;
  swipeLongLeft: Swipe;
  swipeShortLeft: Swipe;

  laterToday: number;
  laterMorning: number;
  laterEvening: number;

  linkTracking: boolean;
  cloudAttachments: boolean;
  followUp: number;

  composeFontSize: number;
  composeFontFamily: string;
  composeFontColor: string;

  defaultShareContact?: string; // team id
  defaultShareDraft?: boolean; // whether shared drafts are on by default
  syncEmailsToCrm: boolean;
  sentFromPolymail?: boolean;

  weeklyUnsubscriberDigest?: boolean;
}

export const enum SocialNetwork {
  Facebook = 'facebook',
  Twitter = 'twitter',
  AngelList = 'angellist',
  LinkedIn = 'linkedin',
  Instagram = 'instagram',
}

export const enum FilterBox {
  Sent = 'sent',
  Starred = 'starred',
  Unread = 'unread',
}

export enum MailboxType {
  Any = 'any',
  Inbox = 'inbox',
  Trash = 'trash',
  Spam = 'spam',
  Archive = 'archive',
  Later = 'later',
  Drafts = 'drafts',
  SendLater = 'sendLater',
  Outbox = 'outbox',
  None = 'none',
}

// NOTE(SHIN): If modify or add any property to MailBox,
//  need to update ThreadsDataSource.modeHasChanged()
export interface Mailbox {
  type: MailboxType;
  list?: string;
  account?: string;
  starred?: boolean;
  sent?: boolean;
  followUp?: boolean;
  read?: boolean;

  // NOTE(SHIN): Used by ThreadsDataSource for diffing when to clear the threads list
  // Increment to re-hydrate threads list, called on THREAD_LIST_SUCCESS
  hydrateCount?: number;

  // Search Params
  query?: string;
  queryParams?: QueryParams;
  providerSearch?: boolean;
  providerSearchComplete?: boolean;
}

export interface QueryParams {
  with?: string[];
  from?: string;
  to?: string[];
  before?: Date;
  after?: Date;
  isStarred?: boolean;
  isRead?: boolean;
  isUnread?: boolean;
  isUnopened?: boolean;
  isOpened?: boolean;
  isUnreplied?: boolean;
  isReplied?: boolean;
  sent?: boolean;
  hasAttachment?: boolean;
  hasFollowUp?: boolean;
  box?: ThreadBox;
  list?: string;
  text?: string[];
  quoted?: boolean;
}

export interface Social {
  network: SocialNetwork;
  username: string;
  url: string;
}

export const enum ActionStatus {
  Start = 'start',
  Success = 'success',
  Failure = 'failure',
}

export interface Action<Meta, Payload> {
  type: string;
  status: ActionStatus;
  meta: Meta;
  payload?: Payload;
  error?: Error;
}

export const enum ResourceStatus {
  Pending = 'pending',
  Loading = 'loading',
  Refreshing = 'refreshing',
  Ok = 'ok',
  Failure = 'failure',
  Disabled = 'disabled',
}

export interface Resource<T> {
  status: ResourceStatus;
  error?: Error;
  data?: T;

  // Special counter used to identify how often a reducer was called on a resource
  incrementCount?: number;
}

export interface Thread {
  id: string;
  internal: Date;
  sort: Date;
  when?: Date;
  lastSent?: Date;
  box: ThreadBox;
  list?: string;
  account: string;
  messages: Message[];
  waiting?: Waiting;
  apiId?: string;

  // computed properties
  read: boolean;
  sent: boolean;
  starred: boolean;
  hasAttachments: boolean;

  tracked: boolean;
  tracks: number;

  participants: Address[];
  participantEmails: string[];
  subject: string;
  snippet: string;
  sentSnippet: string;

  // Set to 1 if starred is `true`, otherwise `undefined`
  starredIndex?: number;
  waitingIndex?: number;
  listUnreadIndex?: string;
  inboxUnreadIndex?: number;

  // Last performed query result for thread
  query?: string;

  // Muted conversations do not receive notifications
  muted?: boolean;
}

export const enum WaitingType {
  NoReply = 'noReply',
}

export interface Waiting {
  type: WaitingType;
  when: Date;
  whenRelative?: number; // for Drafts only
}

export interface Message {
  id: string;
  internal: Date;
  sent: boolean;
  read: boolean;
  starred: boolean;
  tracked: boolean;
  tracks: number;
  subject: string;
  snippet: string;
  hasAttachments: boolean;
  attachments: MessageAttachment[];
  rfcId?: string;
  calendarEvent?: string;
  optimistic?: DraftStatus;

  from: string;
  bcc: string[];
  cc: string[];
  to: string[];
  replyTo: string[];
}

export interface MessageBody {
  id: string;
  body: string;
  updated: Date;
  history?: string;
  historyMarker?: string;
  unsubscribeLink?: string;
  thread: string;
}

export interface MessageAttachment {
  id: string;
  filename: string;
  type: string;
  length: number;
  cid?: string;
  inline: boolean;
  url: string;
  message: string;
  thread?: string;
  sharedThread?: string;
}

export interface Attachment {
  id: string;
  created: Date;
  updated: Date;
  filename: string;
  type: string;
  length: number;
  cloudFile: string;
  cloudUrl: string;
  cloud: boolean;
  inline: boolean;
  uploading?: boolean;
  template?: string;
  progress: number;
  originalCid?: string;
  optimistic?: boolean;
}

export interface AttachmentMeta {
  resourceId: string;
  attachment: string;
  file?: File;
  inline?: boolean;
  cloud?: boolean;
  template?: string;

  // Display Purposes
  filename?: string;
  length?: number;

  // Forward properties
  thread?: string;
  message?: string;
  account?: string;

  // Sequence Stage Instance properties
  // Template forward properties
  stage?: string;
  sequence?: string;
  status?: AttachmentMetaStatus;
  cloudFile?: string;
  cloudUrl?: string;
  progress?: number;
}

export const enum AttachmentMetaStatus {
  Uploading = 'uploading',
  Create = 'create',
  CreateAfterUpload = 'createAfterUpload',
  Delete = 'delete',
}

export const enum ThreadBox {
  Inbox = 'inbox',
  Trash = 'trash',
  Spam = 'spam',
  Archive = 'archive',
  List = 'list',
  Later = 'later',
}

export const enum TwoFactorAuthenticationStatus {
  Verified = 'verified',
  Unverified = 'unverified',
}

export const enum TwoFactorAuthenticationType {
  Phone = 'phone',
}

export interface TwoFactorAuthentication {
  status: TwoFactorAuthenticationStatus;
  type: TwoFactorAuthenticationType;

  phone?: string;
}

export const enum MeStatus {
  Ok = 'ok',
  Deleted = 'deleted',
}

// new status from the API which would keep backwards compatibility
// with the old status and show more details of user status
// see: https://polymail.atlassian.net/wiki/spaces/POLYMAIL/pages/22642689/Status+improvement
export const enum MeStatusNew {
  Active = 'active',
  Deleted = 'deleted',
  Trial = 'trial',
  TrialSubscribed = 'trial_subscribed',
  TrialExpired = 'disabled_trial_expired',
  Canceled = 'disabled_subscription_canceled',
  Unpaid = 'disabled_payment_failed',
  None = 'none',
}

export interface Me {
  id: string;
  firstName: string;
  middleName: string;
  lastName: string;
  photoUrl: string;
  email: string;
  webappTester?: boolean;
  password?: string;
  plan: MePlan;
  slug: string;
  team: string;
  timeZone?: string;
  photoCloudFile?: string;
  referralUrl: string;
  referrer?: string;
  referralRedeemed?: Date;
  intercomIdentity: string;
  coupon?: Coupon;

  status: MeStatus;
  // new status from the API which would keep backwards compatibility
  // with the old status and show more details of user status
  statusNew?: MeStatusNew;
  trialExpires?: Date;

  tfa?: TwoFactorAuthentication;
  onboardingSkippedTeam?: boolean;

  created: Date;
  debugUntil?: Date;

  failMessage?: string; // payment failure message

  experiments?: ExperimentsState;
}

export interface DeviceTypeNotifications {
  desktop: DeviceNotifications;
  mobile: DeviceNotifications;
  tablet: DeviceNotifications;
}

export const enum MePlan {
  Free = 'free',
  Pro = 'pro',
}

export const enum AccountType {
  Google = 'google',
  Alias = 'alias',
  iCloud = 'icloud',
  MicrosoftLegacy = 'microsoft',
  Microsoft = 'microsoftGraph',
  IMAP = 'imap',
}

export const enum AccountStatus {
  Ok = 'ok',
  Invalid = 'invalid',
  Deleting = 'deleting',
}

export interface Account {
  id: string;
  name: string;
  description: string;
  email: string;
  signature: string;
  autoBcc?: string|null;
  type: AccountType;
  status: AccountStatus;
  account?: string;
  team?: string;
  signatureImages: Attachment[];
  mailInitSynced?: string;
  autoReply?: AutoReply;

  // For IMAP Accounts
  imap?: Imap;
  smtp?: Smtp;
  preset?: AccountPreset;

  // For IMAP/iCloud Accounts
  password?: string;

  notifications: DeviceTypeNotifications;
}

export interface AutoReply {
  updated?: Date;
  enabled: boolean;
  body: string;
}

export interface Imap {
  username: string;
  password: string;
  ssl: boolean;
  insecureSsl: boolean;
  host: string;
  port: number;
  idle: boolean;
  folders: ImapFolders;
}

export interface ImapFolders {
  inbox: string;
  sent: string;
  archive: string;
  trash: string;
  spam: string;
  readLater: string;
}

export interface Smtp {
  username: string;
  password: string;
  ssl: boolean;
  starttls: boolean;
  insecureSsl: boolean;
  host: string;
  port: number;
}

export const enum AccountPreset {
  Fastmail = 'fastmail',
  Yahoo = 'yahoo',
}

export interface Tab {
  key: string;
  name: string;
  count: number;
  errorCopy?: string;
  sortIdentifier?: string;

  link?: string;
  icon?: JSX.Element;
  onClick?: () => void;
  hidden?: boolean;
  isOnboarding?: boolean;
  notificationCount?: boolean;

  // List tabs
  listLevel?: number;
  list?: List;
  parentList?: string;
  descendants?: boolean;
  showMore?: boolean;
  hideCollapsed?: boolean;
}

export interface List {
  id: string;
  // created: Date;
  // updated: Date;
  status: ListStatus;
  name: string;
}

export const enum ListStatus {
  Ok = 'ok',
  Updating = 'updating',
  Deleting = 'deleting',
}

export interface Address {
  fullName: string;
  givenName: string;
  familyName: string;
  email: string;
}

export const enum ThreadActionColor {
  Archive = '#87ecbb',
  List = '#D14CAD',
  Snooze = '#FDA05E',
  Trash = '#FD6D7E',
  Inbox = '#47dff5',
  Unread = '#63A0F6',
  Star = '#ffdf32',
  None = '#ffffff',
}

export const enum PhoneType {
  Personal = 'personal',
  Mobile = 'mobile',
  Pager = 'pager',
  Main = 'main',
  HomeFax = 'home_fax',
  WorkFax = 'work_fax',
  Home = 'home',
  Work = 'work',
  Other = 'other',
}

export interface Phone {
  type: PhoneType;
  number: string;
}

export interface ContactGroup {
  id: string;
  created: Date;
  updated: Date;
  name: string;
  team?: string;
}

export const enum ContactType {
  Default = 'default',
  Cache = 'cache',
  Enriched = 'enriched',
}

export interface Contact {
  id: string;
  created: Date;
  updated: Date;
  user: string;
  team?: string;
  email: string;
  salutation: string;
  givenName: string;
  familyName: string;
  fullName: string;
  gender: string;
  photoUrl: string;
  firstMet: string;
  bio: string;
  birthdate?: Date;
  location: string;
  organizationTitle: string;
  organization: string;
  website: string;
  addresses: PhysicalAddress[];
  phoneNumbers: Phone[];
  socialMedia: Social[];
  groups: string[];
  company?: Company;
  type: ContactType;
  lastSent?: Date;

  searchIndex: string[];
}

// API docs calls this Address
export interface PhysicalAddress {
  type: string;
  street: string;
  city: string;
  region: string;
  postcode: string;
  country: string;
}

export const enum DraftStatus {
  Draft = 'draft',
  Scheduled = 'scheduled',
  Sending = 'sending',
  Failed = 'failed',
  Staged = 'staged',
}

export interface Draft {
  id: string;
  created: Date;
  updated: Date;
  status: DraftStatus;
  scheduled?: Date | null;
  cancelBy?: Date;
  cancelByTtl?: number;
  error?: string;
  account: string;
  to: string[];
  cc: string[];
  bcc: string[];
  subject: string;
  threadV2?: string;
  message?: string;
  split: boolean;
  thread?: string;
  smtp?: string;
  body: string;
  track: boolean;
  trackLinks: boolean;
  attachments: Attachment[];
  template?: string;
  waiting?: Waiting | null;
  sharedThread?: string;
  calendarInvite?: DraftCalendarInvite | null;
  version: number;

  // Identifies whether to cancel scheduled on reply
  cancelOnReply: boolean;
}

export interface DraftCalendarInvite {
