// Flags for Development
// NOTE: Must be false when merging
const enableDbLogger = false; // output db op stats to console
const disableApiCorsOptimization = false; // disable CORS optimization for API requests
const realtimeTokenDebug = false; // Allow realtime token to be used locally

// Polyfills
import 'whatwg-fetch';

// Enable Sentry for production environments
import { init, setUser, captureException } from '@sentry/browser';

if (process.env.NODE_ENV === 'production' && process.env.SENTRY_DSN !== '') {
  init({
    dsn: process.env.SENTRY_DSN,
    release: process.env.SOURCE_VERSION,
    environment: process.env.SENTRY_ENV,
  });
}

// Imports
import { setToken, AUTH_TOKEN_NONE, isLoggedIn, disableCorsOptimization } from './api/base';
import {
  initDb, resetDbIfNeeded, ClearCacheMechanism, enableUserDebug,
  setClearCacheMechanism, setAllowReconnect, getClearCacheMechanism,
  disableUserDebug, enableForceDebug,
} from '@src/lib/db';

if (disableApiCorsOptimization) {
  disableCorsOptimization();
}

// Used to enable IndexedDB calls for debugging
if (enableDbLogger) {
  enableForceDebug();
}

// Identify if we want to enable realtime token debugging
if (realtimeTokenDebug) {
  enableRealtimeDebug();
}

import ActivityFeedSync from './worker/sync/ActivityFeedSync';
import ActivityFeedIncrSync from './worker/sync/ActivityFeedIncrSync';
import SyncOverwatch from './worker/sync/SyncOverwatch';
import DraftSync from './worker/sync/DraftSync';
import DraftIncrSync from './worker/sync/DraftIncrSync';
import CalendarEventSync from './worker/sync/CalendarEventSync';
import ContactSync from './worker/sync/ContactSync';
import ContactGroupSync from './worker/sync/ContactGroupSync';
import ContactNoteSync from './worker/sync/ContactNoteSync';
import SharedThreadSync from './worker/sync/SharedThreadSync';
import TeamMemberSync from './worker/sync/TeamMemberSync';
import TemplateSync from './worker/sync/TemplateSync';
import TemplateIncrSync from './worker/sync/TemplateIncrSync';
import ThreadSync from './worker/sync/ThreadSync';
import Realtime, { enableRealtimeDebug } from './worker/Realtime';
import HistorySync from './worker/HistorySync';
import CommitSync, { resetCommitStatuses } from './worker/commit/CommitSync';
import MessageBodiesIncrSync from './worker/sync/MessageBodiesIncrSync';
import MessageBodiesSync from './worker/sync/MessageBodiesSync';

import Bridge from './worker/bridge/Bridge';
import { AppEvent, AppEventName } from './worker/bridge/appEvents';
import {
  sendWorkerReady,
  sendMeUpdated,
  sendTeamUpdated,
  sendNotification,
  setConnections,
  sendDraftUpdated,
  sendDraftSent,
  sendSharedThreadActivity,
  sendRequestConfig,
} from './worker/bridge/workerEvents';
import { Account, RealtimeEvent, AccountType, AccountStatus, List } from './types';
import bus from './worker/bus';
import { MailCountBot } from '@src/worker/mailCountBot';
import { FileUploadSync, resetFileUploadStatuses } from '@src/worker/commit/FileUploadSync';
import { saveBodies } from './lib/searchDb';
import { enableSearchDb } from './lib/device';
import { MaintenanceBot } from './worker/maintenanceBot';
import moment from 'moment';
import { recordLogWorker } from './lib/log_worker';

const overwatch = new SyncOverwatch();

const realtime = new Realtime();
realtime.persist(5);

const connections: MessagePort[] = [];
let currentTeam: string|undefined;
let activityFeedSyncEnabled = false;
let mailCountBot: MailCountBot|undefined;

self.addEventListener('connect', (e: any) => {
  const port = e.ports[0];

  connections.push(port);
  setConnections(connections);

  initWorker();
  port.start();
});

declare global {
  var SharedWorkerGlobalScope: any;
}

// Detect SharedWorker
let sharedWorker = false;
try {
  // HACK(SHAHAN): SharedWorkerGlobalScope should be available in sharedworker only
  if (self instanceof SharedWorkerGlobalScope) {
    sharedWorker = true;
  }
} catch (e) {
  // SharedWorker not detected, must be a Worker
}

// Init Worker starts the bridge and waits for
const initWorker = () => {
  // Don't start worker if not yet ready
  // SharedWorker must setup connection to the web thread first
  if (sharedWorker && !connections.length) {
    return;
  }

  // Start bridge
  // Handle app events
  const bridge = new Bridge(sharedWorker ? connections : undefined);
  bridge.subscribe({
    next: (appEvent: AppEvent) => {
      handleAppEvent(appEvent);
    },
  });

  // Request config
  sendRequestConfig();
};

initWorker();

// startWorker is called after init db happens
// db is initialized by the Config appEvent
let workerReady = false;
let startWorkerCalled = false;
const startWorker = () => {
  // Don't startWorker until ready
  if (!workerReady) {
    return;
  }

  // Prevent duplicate setup of worker
  if (startWorkerCalled) {
    return;
  }

  startWorkerCalled = true;

  // Handle realtime events
  realtime.subscribe((event: RealtimeEvent) => {
    switch (event.name) {
      case 'MeUpdated':
        sendMeUpdated();
        break;

      case 'TeamUpdated':
        sendTeamUpdated();
        break;

      case 'Notification':
        sendNotification(event.data);
        break;

      case 'SharedThreadActivity':
        sendSharedThreadActivity(event.data.activity);
        break;

      default:
        break;
    }
  });

  // Trigger backup sync every minute
  overwatch.persist(60);

  // Handle Mail Count Updates
  mailCountBot = new MailCountBot();
  mailCountBot.start();

  // Handle maintenance
  const maintenanceBot = new MaintenanceBot();
  maintenanceBot.start();
};

let tryDbResetRetries = 0;

const tryDbReset = () => {
  initDb(true)
    .subscribe({
      next: () => {
        tryDbResetRetries = 0;
        setAllowReconnect(true);

        // Restart worker to initiate background syncs
        sendWorkerReady();
      },
      error: (initError) => {
        captureException(initError);

        // tslint:disable-next-line: no-console
        console.error(initError);

        if (tryDbResetRetries < 2) {
          tryDbResetRetries += 1;
          tryDbReset();
        } else {
          throw new Error('programmer: exhausted tryDbReset');
        }
      },
    });
};

// Handle events sent from the app
const handleAppEvent = (e: AppEvent) => {
  // Handle special events that work before the worker is ready
  switch (e.name) {
    case AppEventName.Config:
      // Setup auth token, clear cache mechanism
      setToken(e.data.token);
      realtime.setToken(e.data.token);

      if (e.data.electronBridgeClearCache) {
        setClearCacheMechanism(ClearCacheMechanism.WorkerElectronBridge);
      }

      setAllowReconnect(e.data.allowReconnect);

      // Handle initialization tasks and then start worker
      initDb()
        .flatMap(() => resetCommitStatuses())
        .flatMap(() => resetFileUploadStatuses())
        .subscribe({
          complete: () => {
            workerReady = true;
            startWorker();
            sendWorkerReady();
          },
          error: (initErr) => {
            // tslint:disable-next-line: no-console
            console.error(initErr);
            captureException(initErr);
          },
        });

      return;

    case AppEventName.AllowDbReconnect:
      if (!e.data.allowReconnect) {
        currentTeam = undefined;
        activityFeedSyncEnabled = false;
        mailCountBot?.reset();

        setAllowReconnect(false);

        overwatch.syncers.forEach((_, key) => {
          overwatch.remove(key);
        });
        return;
      }

      tryDbReset();
      return;

    default:
      break;
  }

  // Handle regular events if worker is ready
  if (!workerReady) {
    return;
  }

  switch (e.name) {

    case AppEventName.ListsUpdated:
      // Sync account base resources
      mailCountBot?.setLists(e.data.lists.map((l: List) => l.id));
      return;

    case AppEventName.SearchDbEnable:
      if (e.data.config.enabled) {
        enableSearchDb(e.data.config);
        overwatch.add(new MessageBodiesSync());
        overwatch.add(new MessageBodiesIncrSync());
      }
      break;

    case AppEventName.AuthUpdated:
      setToken(e.data.token);
      realtime.setToken(e.data.token);

      // Want to start workers after any db resets are complete
      resetDbIfNeeded().subscribe({
        next: () => {

          // Don't want to do anything if not logged in
          if (!isLoggedIn()) {
            return;
          }

          // Syncers use AuthUpdated to clear errors
          bus.clearErrors();

          // Add Syncers
          // check if user is not disabled
          if (!e.data.disabled) {
            overwatch.add(new HistorySync(realtime));
            overwatch.add(new FileUploadSync());
            overwatch.add(new CommitSync());
            overwatch.add(new ContactSync());
            overwatch.add(new ContactNoteSync());
            overwatch.add(new DraftSync());
            overwatch.add(new DraftIncrSync(realtime));
            overwatch.add(new ContactGroupSync());
            overwatch.add(new SharedThreadSync());
            overwatch.add(new TemplateSync());
            overwatch.add(new TemplateIncrSync(realtime));
          }

          // Trigger Sync
          bus.triggerSync(undefined, true);
        },
        error: (resetErr) => {
          // tslint:disable-next-line: no-console
          console.error(resetErr);
          captureException(resetErr);
        },
      });
      break;

    case AppEventName.AccountsUpdated:
      // Sync account base resources
      e.data.accounts
        .filter((account: Account) =>
          // don't want to sync aliases or accounts in deleting state
          account.type !== AccountType.Alias && account.status !== AccountStatus.Deleting,
        )
        .forEach((account: Account) => {

          // Calendars
          if (account.type === AccountType.Microsoft || account.type === AccountType.Google) {
            overwatch.add(new CalendarEventSync(account.id));
          }

          // Mail Sync
          overwatch.add(new ThreadSync(account.id));

          // Initial Mail Counts
          bus.updateMailCount(account.id);
        });
      break;

    case AppEventName.PreferencesUpdated:
      // Update count preferences
      mailCountBot?.updatePreferences(
        e.data.preferences.leftSidebarCount, e.data.preferences.dockCount,
      );

      // Update mail counts when accounts are provided
      if (e.data.accounts) {
        e.data.accounts.forEach((account: Account) => bus.updateMailCount(account.id));
      }
      break;

    case AppEventName.CommitSubmitted:
      bus.triggerSync('CommitSync');
      bus.triggerSync('FileUploadSync');
      break;

    case AppEventName.DraftUpdated:
      const updatedDraft = e.data && e.data.draft;
      if (updatedDraft) {
        sendDraftUpdated(updatedDraft);
      }
      break;

    case AppEventName.DraftSent:
      const sentDraft = e.data && e.data.draft;
      if (sentDraft) {
        sendDraftSent({ ...e.data });
      }
      break;

    case AppEventName.FileUploadSubmitted:
      bus.triggerSync('FileUploadSync');
      break;

    case AppEventName.DraftUndoSent:
      bus.cancelSync('CommitSync');
      bus.triggerSync('CommitSync');
      break;

    case AppEventName.UndoMove:
      if (e.data.box) {
        bus.undoMove(e.data.box);
      }
      break;

    case AppEventName.FeaturesUpdated:
      // Handle turning activity feed sync on and off
      if (e.data.features.activityFeed !== activityFeedSyncEnabled) {
        if (e.data.features.activityFeed) {
          overwatch.add(new ActivityFeedSync());
          overwatch.add(new ActivityFeedIncrSync(realtime));
          bus.triggerSync('ActivityFeedSync');
          bus.triggerSync('ActivityFeedIncrSync');
        } else {
          overwatch.remove('ActivityFeedSync');
          overwatch.remove('ActivityFeedIncrSync');
        }

        // update enabled state
        activityFeedSyncEnabled = e.data.features.activityFeed;
      }
      break;

    case AppEventName.ClearCache:
      bus.cancelSync();

      resetDbIfNeeded().subscribe({
        next: () => {
          // We only want to send worker ready for the default case,
          // as when we are using electron based clear cache, we want
          // to wait for the completion of IndexedDB folder deletion
          if (getClearCacheMechanism() === ClearCacheMechanism.Default) {
            sendWorkerReady();
          }
        },
        error: (resetErr) => {
          // tslint:disable-next-line: no-console
          console.error(resetErr);
          captureException(resetErr);
        },
      });
      break;

    case AppEventName.Logout:
      setToken(AUTH_TOKEN_NONE);
      realtime.setToken(AUTH_TOKEN_NONE);
      currentTeam = undefined;
      // Clear cache not needed here, as its handled on main thread
      break;

    case AppEventName.MailCountUpdated:
      bus.updateMailCount(e.data.accountId);

      // Handle notification of unread list change if unreadList was provided
      if (e.data.unreadList) {
        bus.updateListUnreadCount(e.data.accountId, e.data.unreadList);
      }
      break;

    case AppEventName.MeUpdated:
      setUser({
        email: e.data.me.email,
        id: e.data.me.id,
      });

      // Handle debug
      if (e.data.me.debugUntil && moment(e.data.me.debugUntil).isAfter(moment())) {
        enableUserDebug('WORKER', recordLogWorker);
      } else {
        disableUserDebug();
      }

      // Sync shared resources
      const team = e.data.me.team;
      if (currentTeam !== team) {
        currentTeam = team;
        bus.updateTeam(team);

        overwatch.add(new TeamMemberSync(team));
        overwatch.add(new ContactSync(team));
        overwatch.add(new ContactGroupSync(team));
        overwatch.add(new ContactNoteSync(team));
        overwatch.add(new TemplateSync(team));
        overwatch.add(new HistorySync(realtime, team));
        overwatch.add(new TemplateIncrSync(realtime, team));

        bus.triggerSync(undefined, true);
      }
      break;

    case AppEventName.Online:
      bus.clearErrors();
      bus.triggerSync(undefined, true);
      break;

    case AppEventName.SaveMessageBodies:
      const { bodies, threadId } = e.data;
      if (!bodies && !threadId) {
        break;
      }

      // Handle save of message bodies into separate database
      saveBodies(bodies).subscribe({
        error: (saveError) => {
          captureException(saveError);
          // tslint:disable-next-line:no-console
          console.error(saveError);
        },
      });
      break;

    default:
      const err = new Error(`unhandled event from Bridge ${e.name}`);
      captureException(err);
      // tslint:disable-next-line:no-console
      console.error(err);
  }
};
