import moment from 'moment';
import { last, sortBy, findIndex, flatten, padStart } from 'lodash';
import { parseAddress } from './address';
import { ThreadBox, Thread, Message, Commit, Mailbox, MailboxType } from '@src/types';
import { Observable } from 'rxjs';
import Cache, { Stores } from './db';
import { tokenizeString, TokenizedThread } from './searchDb';

export const threadPlainStringValue = (thread: Thread): string => {
  const subject = thread.subject;
  const spacer = ' ';
  const attachmentNames = thread.messages
    .map(message => message.attachments.map(attachment => attachment.filename));
  const participantsArray = thread.participants
    .map(participant => participant.email + spacer + participant.fullName);
  const snippetsArray = thread.messages.map(message => message.snippet);
  const flattendParticipants = flatten(participantsArray);
  const flattenedAttachmentNames = flatten(attachmentNames, true);
  const flattenedSnippets = flatten(snippetsArray);

  // Create string of subject, attachments, participants and snippets
  const finalString = [
    subject, flattenedAttachmentNames.join(' '), flattendParticipants, flattenedSnippets,
  ].join(' ').toLowerCase();

  return finalString.toLowerCase();
};

// Tokenize threads with attachments participants, subject, and snippet
export const tokenizeThreads = (threads: Thread[]): TokenizedThread[] => {
  const updated = threads.map((thread) => {

    const finalString = threadPlainStringValue(thread);
    // Create tokens array for threads
    const tokenizedArray = tokenizeString(finalString);
    const tokenized: TokenizedThread = {
      id: thread.id,
      internal: thread.internal,
      tokens: tokenizedArray,
      hasAttachments: thread.hasAttachments ? 1 : undefined,
      hasReminder: !!thread.waiting ? 1 : undefined,
      isUnread: !thread.read ? 1 : undefined,
      isOpened: !!thread.tracks && thread.tracked ? 1 : undefined,
      isUnopened: !thread.tracks && thread.tracked ? 1 : undefined,
      isReplied: thread.messages.length > 1 &&
        thread.lastSent && thread.lastSent !== thread.internal ? 1 : undefined,
      isUnreplied: thread.lastSent &&
        moment(thread.lastSent).isSame(thread.internal) ? 1 : undefined,
    };
    return tokenized;
  });
  return updated;
};

export function computeThread(thread: Thread, sharedThread?: string): Thread {
  const computed: Thread = {
    // ensure default properties are set
    messages: [],

    ...thread,

    // reset computed values
    read: true,
    sent: false,
    starred: false,
    hasAttachments: false,
    tracked: false,
    tracks: 0,
    participants: [],
    participantEmails: [],
    subject: '',
    snippet: '',
    sentSnippet: '',
  };

  // sort by internal date
  computed.messages = sortBy(
    // make sure default properties are set
    computed.messages.map(message => ({
      to: [],
      cc: [],
      bcc: [],
      from: '',
      subject: '(No Subject)',
      snippet: '',
      tracked: false,
      tracks: 0,
      sent: false,
      read: false,
      starred: false,
      attachments: [],
      hasAttachments: false,
      ...message,
    })),
    ['internal'],
  );

  if (!(computed.internal instanceof Date) && (typeof computed.internal === 'string')) {
    computed.internal = new Date(computed.internal);
  }

  if (!(computed.sort instanceof Date) && (typeof computed.sort === 'string')) {
    computed.sort = new Date(computed.sort);
  }

  let lastSent: Message | undefined;
  let addresses: string[] = [];

  computed.messages.forEach((message) => {
    if (!message.internal) {
      message.internal = new Date('1970-01-01T00:00:00.000Z');
    }

    if (!(message.internal instanceof Date)) {
      message.internal = new Date(message.internal);
    }

    if (!message.read) {
      computed.read = false;
    }

    if (message.sent) {
      computed.sent = true;
      lastSent = message;
    }

    if (message.starred) {
      computed.starred = true;
    }

    // Handle setting attachment url based off if shared thread fetch or not
    message.attachments = message.attachments.map((att) => {
      const threadsSection = !!sharedThread
        ? '/v1/sharedThreads/' + encodeURIComponent(sharedThread)
        : '/v2/threads/' + encodeURIComponent(thread.id);
      return ({
        ...att,
        message: message.id,
        thread: computed.id,
        url: att.url && att.url !== '' ? att.url : process.env.API_URL +
          threadsSection
          + '/messages/' + encodeURIComponent(message.id)
          + '/attachments/' + encodeURIComponent(att.id),
        sharedThread,
      });
    });

    if (message.attachments.filter(a => !a.inline).length > 0) {
      message.hasAttachments = true;
    }

    if (message.hasAttachments) {
      computed.hasAttachments = true;
    }

    if (computed.subject === '') {
      computed.subject = message.subject;
    }

    addresses = [...addresses, ...message.to, ...message.cc, ...message.bcc];
    if (message.from !== '') {
      addresses.push(message.from);
    }

    if (message.replyTo) {
      addresses.concat(message.replyTo);
    } else {
      message.replyTo = [];
    }
  });

  // snippet
  if (computed.messages.length > 0) {
    const lastMessage = last(computed.messages)!;
    computed.snippet = lastMessage.snippet;
  }

  // no subject
  if (computed.subject === '') {
    computed.subject = '(No Subject)';
  }

  // handle last sent message
  if (lastSent) {
    computed.sentSnippet = lastSent.snippet;
    computed.tracked = lastSent.tracked;
    computed.tracks = lastSent.tracks;
    computed.lastSent = lastSent.internal;
  }

  // handle participants
  const added: { [email: string]: boolean } = {};
  addresses.forEach((address) => {
    const parsed = parseAddress(address);
    if (!added[parsed.email]) {
      computed.participants.push(parsed);
    }
    added[parsed.email] = true;
  });

  if (computed.participants.length > 0) {
    computed.participantEmails = computed.participants.map(
      p => p.email.toLowerCase() + '|'
        + padStart(moment(computed.internal).unix().toString(), 10, '0'),
    );
  }

  // Handle index properties
  computed.starredIndex = undefined;
  if (computed.starred) {
    computed.starredIndex = 1;
  }

  computed.waitingIndex = undefined;
  if (computed.waiting && moment(computed.waiting.when).isAfter()) {
    computed.waitingIndex = 1;
  }

  computed.listUnreadIndex = undefined;
  if (!computed.read && computed.list) {
    computed.listUnreadIndex = computed.list;
  }

  computed.inboxUnreadIndex = undefined;
  if (computed.box === ThreadBox.Inbox) {
    computed.inboxUnreadIndex = computed.read ? 0 : 1;
  }

  if (computed.when && !(computed.when instanceof Date)) {
    computed.when = new Date(computed.when);
  }

  if (computed.waiting && computed.waiting.when && !(computed.waiting.when instanceof Date)) {
    computed.waiting.when = new Date(computed.waiting.when);
  }

  return computed;
}

export const patchedNoCompute = (thread: Thread, commit: Commit): Thread => {
  const updated = { ...thread };

  // Replace box if exists
  if (commit.payload.box) {
    updated.box = commit.payload.box;

    // Set when date if provided
    updated.when = commit.payload.when;
    updated.list = commit.payload.list;
  }

  // Update read/starred if needed
  if (commit.payload.messages) {
    commit.payload.messages.forEach((message: Message) => {
      const index = findIndex(updated.messages, { id: message.id });
      if (index !== -1) {

        if (message.read !== undefined) {
          updated.messages[index].read = message.read;
        }

        if (message.starred !== undefined) {
          updated.messages[index].starred = message.starred;
        }
      }
    });
  }

  if (commit.payload.waiting !== undefined) {
    updated.waiting = commit.payload.waiting === null ? undefined : commit.payload.waiting;
  }

  if (commit.payload.muted !== undefined) {
    updated.muted = commit.payload.muted;
  }

  return updated;
};

export const patched = (thread: Thread, commit: Commit): Thread => {
  return computeThread(patchedNoCompute(thread, commit));
};

// Identifies if next thread matches the id of the old thread
export const matchesThread = (old: Thread, next: Thread) => {
  if (old.id === next.id) {
    return true;
  }

  if (next.messages.length === 1 && next.messages[0].rfcId === old.id) {
    return true;
  }

  return false;
};

// mailboxThreadsPredicate applies mailbox filter on thread
export function mailboxThreadsPredicate(
  mailbox: Mailbox,
  thread: Thread,
  ignoreFlags: boolean = false,
): boolean {

  if (mailbox.account && thread.account !== mailbox.account) {
    return false;
  }

  if (mailbox.list && thread.list !== mailbox.list) {
    return false;
  }

  if (mailbox.starred !== undefined && !thread.starred && !ignoreFlags) {
    return false;
  }

  if (mailbox.followUp !== undefined && !thread.waiting) {
    return false;
  }

  if (mailbox.sent !== undefined && !thread.sent && !ignoreFlags) {
    return false;
  }

  if (mailbox.read !== undefined && mailbox.read !== thread.read && !ignoreFlags) {
    return false;
  }

  // Handle mailbox filters
  switch (mailbox.type) {
    case MailboxType.SendLater:
    case MailboxType.Drafts:
    case MailboxType.Outbox:
      return false;
    case MailboxType.Archive:
      return thread.box === ThreadBox.Archive;
    case MailboxType.Inbox:
      return thread.box === ThreadBox.Inbox;
    case MailboxType.Later:
      return thread.box === ThreadBox.Later;
    case MailboxType.Spam:
      return thread.box === ThreadBox.Spam;
    case MailboxType.Trash:
      return thread.box === ThreadBox.Trash;
    case MailboxType.Any:
      return true;
    default:
      return false;
  }
}

const descending = true;
export const THREAD_LIST_SIZE = 1000;
type ThreadFilter = (thread: Thread) => boolean;

export function listThreadsByBox(
  box: ThreadBox, offset?: Date, filter?: ThreadFilter,
): Observable<Thread[]> {
  const lessThan = offset ? offset : Date();
  return Cache.listIndex<Thread>(
    Stores.Threads,
    'box_sort',
    IDBKeyRange.bound([box, new Date(0)], [box, lessThan]),
    !(box === ThreadBox.Later),
    THREAD_LIST_SIZE,
    filter,
  );
}

export function listThreadsByAccountBox(
  account: string, box: ThreadBox, offset?: Date, filter?: ThreadFilter,
): Observable<Thread[]> {
  const lessThan = offset ? offset : Date();
  return Cache.listIndex<Thread>(
    Stores.Threads,
    'account_box_sort',
    IDBKeyRange.bound([account, box, new Date(0)], [account, box, lessThan]),
    !(box === ThreadBox.Later),
    THREAD_LIST_SIZE,
    filter,
  );
}

export function listThreadsSent(offset?: Date, filter?: ThreadFilter): Observable<Thread[]> {
  const range = offset ? IDBKeyRange.upperBound([offset]) : undefined;

  return Cache.listIndex<Thread>(
    Stores.Threads,
    'lastsent',
    range,
    descending,
    THREAD_LIST_SIZE,
    filter,
  );
}

