    THREAD_LIST_SIZE,
    filter,
  );
}

export function listThreadsByEmail(
  email: string, limit: number|undefined, filter?: ThreadFilter,
): Observable<Thread[]> {
  const key = email.toLowerCase() + '|';

  return Cache.listIndex<Thread>(
    Stores.Threads,
    'participantemails',
    Cache.startsWith(key),
    descending,
    limit,
    filter,
  );
}

export function listThreadIdsByEmail(email: string): Observable<string[]> {
  const key = email.toLowerCase() + '|';
  return Cache.listIndexKeys(
    Stores.Threads,
    'participantemails',
    Cache.startsWith(key),
  );
}

export function countThreadsByEmail(email: string): Observable<number> {
  const key = email.toLowerCase() + '|';
  return Cache.countIndex(
    Stores.Threads,
    'participantemails',
    Cache.startsWith(key),
  );
}

// Warning: Only used for search
export function listAllThreadsByAccount(
  account: string, limit: number, filter?: ThreadFilter,
): Observable<Thread[]> {
  return Cache.listIndex<Thread>(
    Stores.Threads,
    'account_internal',
    IDBKeyRange.bound([account, new Date(0)], [account, Date()]),
    descending,
    limit,
    filter,
  );
}

export function listThreadsStarred(filter?: ThreadFilter): Observable<Thread[]> {
  return Cache.listIndex<Thread>(
    Stores.Threads,
    'starred_internal',
    IDBKeyRange.bound([1, new Date(0)], [1, Date()]),
    descending,
    THREAD_LIST_SIZE,
    filter,
  );
}

export function listThreadsAccountStarred(
  account: string, filter?: ThreadFilter,
): Observable<Thread[]> {
  return Cache.listIndex<Thread>(
    Stores.Threads,
    'starred_account_internal',
    IDBKeyRange.bound([1, account, new Date(0)], [1, account, Date()]),
    descending,
    THREAD_LIST_SIZE,
    filter,
  );
}

export function listThreadsFollowUp(filter?: ThreadFilter): Observable<Thread[]> {
  return Cache.listIndex<Thread>(
    Stores.Threads,
    'waiting',
    IDBKeyRange.only(1),
    descending,
    THREAD_LIST_SIZE,
    filter,
  );
}

export function isThreadInitSyncCompleted(accounts: string[]): Observable<boolean> {
  if (accounts.length === 0) {
    return Observable.of(true);
  }
  const checks = accounts.map(account =>
    Cache.get(Stores.Workers, 'ThreadSync-' + account)
      .map(state => !!(state && state.completed)));
  return Observable.zip(...checks)
    .map(statuses =>
      (statuses.reduce((state, status) => state && status)));
}

export const getFollowUpReminderDisplayString = (thread: Thread) => {
  const now = new Date();
  const followUp = thread.waiting && thread.waiting.when && thread.waiting.when > now ?
    moment(thread.waiting.when).format(
      now.getFullYear() === thread.waiting.when.getFullYear() ?
        'ddd, MMM D [at] h:mm a' : 'ddd, MMM D YYYY [at] h:mm a',
    ) : undefined;

  return followUp;
};
