import { last, compact, values, uniq } from 'lodash';
import { Base64 } from 'js-base64';

import Cache, { Stores } from '@src/lib/db';
import { BasicAPI, BasicClient } from '@src/api/basic';
import { Observable, Observer, Subject, Subscription } from 'rxjs';
import { Combiner, rateLimiter } from '@src/lib/rx';
import { Backoff } from '@src/worker/Backoff';
import { ApiError, BatchResponseParameters } from '@src/api/base';
import {
  HistoryObject,
  Commit,
  CommitStatus,
  Thread,
  Mailbox,
  AttachmentMeta,
  Attachment,
  Draft,
  DraftStatus,
  PolymailStatusType,
} from '@src/types';
import { computeThread, mailboxThreadsPredicate, patched } from '@src/lib/thread';
import { defaultDraft, failedDraft, patchedDraft } from '@src/lib/draft';
import {
  sendDraftCreated,
  sendDraftDeleted,
  sendThreadUpdated,
  sendThreadDeleted,
  sendDraftAttachmentAdded,
  sendDraftAttachmentAddFailed,
  sendDraftSent,
  sendDraftUpdated,
  sendDraftSendFailed,
  sendThreadMoveUndoCompleted,
  sendMeUpdated,
  sendAppStatusUpdate,
} from '@src/worker/bridge/workerEvents';
import bus, { getBus, BusEvent, BusEventName } from '@src/worker/bus';
import { withScope, captureMessage, captureException } from '@sentry/browser';
import { DraftSendMeta } from '@src/ducks/drafts';
import { ProviderErrors } from '@src/lib/errors';

export const resetCommitStatuses = (): Observable<null> => {
  return Cache.list<Commit>(Stores.Commits, commit => commit.status === CommitStatus.Processing)
    .flatMap((commits: Commit[]) => {
      return Cache.load(Stores.Commits, commits.map(c => ({ ...c, status: CommitStatus.Ready })));
    });
};

export const MAX_COMMITS_PER_BATCH = 20;

// CommitSync continuously sends idempotent commits to the API.
export class CommitSync {
  // State
  readonly workerId: string;
  isRunning: boolean;
  fetchRatelimit: number;
  resolveRatelimit: number;
  private backoff: Backoff;
  private cancelSignal: Subject<null>;
  private retrigger: boolean;
  private resourceBackoffs: {[key: string]: Backoff} = {};

  // Dependencies
  private api: BasicAPI;
  private bus: Subject<BusEvent>;
  private sub: Subscription;

  constructor(api: BasicAPI = new BasicClient()) {
    this.workerId = 'CommitSync';
    this.isRunning = false;
    // HACK(SHIN): Set backoff to 30 seconds until we have a better retry mechanism
    this.backoff = new Backoff(this.workerId, 30);
    this.cancelSignal = new Subject<null>();
    this.retrigger = false;

    // how often CommitSync fetches from cache
    this.fetchRatelimit = 1500; // in milliseconds\

    // the rate limit how often we make calls to the api
    this.resolveRatelimit = 1000; // in milliseconds

    this.api = api;
    this.bus = getBus();
  }

  init() {
    // Listen for bus events
    if (!this.sub) {
      this.sub = this.bus.subscribe({
        next: (event: BusEvent) => {
          this.handleBusEvent(event);
        },
        error: (e: Error) => {
          captureException(e);
        },
      });
    }
  }

  deinit() {
    if (this.sub) {
      this.sub.unsubscribe();
    }
  }

  // deleteItems is ignored by CommitSync as we want to retain
  // commits on clearCache
  clearCache(deleteItems: boolean): Observable<null> {
    if (this.isRunning) {
      this.cancelSignal.next(null);
    }

    // clearCache is triggered by HistorySync
    // Don't want to clear commits this way
    this.backoff.clearErrors();
    return Observable.of(null);
  }

  // reset marks any commits in `Processing` status as `Ready`
  reset(): Observable<null> {
    return Observable.create((runner: Observer<null>) => {
      const combiner = new Combiner<Commit[]>();
      combiner.subscribe({
        next: (commits: Commit[]) => {
          Cache.load(
            Stores.Commits,
            commits
              .filter(commit => commit.status === CommitStatus.Processing)
              .map(commit => ({ ...commit, status: CommitStatus.Ready })),
          ).subscribe({
            error: (e: Error) => {
              combiner.error(e);
            },
            complete: () => {
              combiner.complete();
            },
          });
        },
        error: (e: Error) => {
          runner.error(e);
        },
        complete: () => {
          runner.next(null);
          runner.complete();
        },
      });
      combiner.run(
        this.resolvePendingCommits()
        .flatMap(() => this.fetch()),
      );
    });
  }

  run(): Observable<null> {
    return Observable.create((runner: Observer<null>) => {
      if (!this.backoff.isReady()) {
        runner.next(null);
        runner.complete();
        return;
      }
      if (this.isRunning) {
        runner.next(null);
        runner.complete();
        return;
      }
      this.isRunning = true;

      // Throttle API calls
      const resolveLimiter = rateLimiter<Commit[]>(this.resolveRatelimit);

      const combiner = new Combiner<Commit[]>();
      combiner.takeUntil(this.cancelSignal)
        .flatMap((commits: Commit[]) => {

          if (commits && commits.length === 0) {
            // Status update false
            sendAppStatusUpdate({
              statusType: PolymailStatusType.Online,
              commitsRemaining: commits.length,
            });

            combiner.complete();
            return Observable.empty();
          }

          // Merge commits with the same resource type and id
          return this.merge(commits);
        })
        .map((commits: Commit[]) => {
          // Status update false
          sendAppStatusUpdate({
            statusType: PolymailStatusType.Syncing,
            commitsRemaining: commits.length,
          });

          // Prioritize draft only commits
          const draftTypeCommits = commits
            .filter(c =>
              [
                HistoryObject.Draft,
                HistoryObject.DraftAttachment,
                HistoryObject.DraftAttachmentAppend,
              ].indexOf(c.resource) > -1,
            );

          if (draftTypeCommits.length) {
            return draftTypeCommits;
          }

          return commits;
        })
        .subscribe({
          next: (commits: Commit[]) => {

            // Handle processing all ready commits
            if (commits.length <= MAX_COMMITS_PER_BATCH) {

              resolveLimiter(this.resolve(commits.slice(0, MAX_COMMITS_PER_BATCH))).subscribe({
                error: (e: Error) => {
                  combiner.error(e);
                },
                complete: () => combiner.run(
                  Observable.of(null)
                    .delay(this.fetchRatelimit)
                    .flatMap(() => this.resolvePendingCommits())
                    .flatMap(() => this.fetch()),
                ),
              });
            } else {

              resolveLimiter(this.resolve(commits.slice(0, MAX_COMMITS_PER_BATCH))).subscribe({
                error: (e: Error) => {
                  combiner.error(e);
                },
                complete: () => {
                  // Recurse
                  return combiner.run(
                    Observable.of(commits.splice(MAX_COMMITS_PER_BATCH)),
                  );
                },
              });
            }
          },
          error: (e: Error) => {

            this.isRunning = false;

            if ((e as ApiError).code === 401) {
              sendMeUpdated();
            }

            this.backoff.setError(e);
            runner.error(e);
          },
          complete: () => {
            this.isRunning = false;

            this.backoff.clearErrors();
            runner.next(null);
            runner.complete();

            // Handle retrigger
            if (this.retrigger) {
              this.retrigger = false;
              bus.triggerSync(this.workerId);
            }
          },
        });

      // Buffer initial fetch because it's common for commits to be submitted
      // together. And we want to resolve after the batch has been submitted
      // rather than on the first one.
      combiner.run(Observable.of(null)
        .delay(this.fetchRatelimit)
        .flatMap(() => this.resolvePendingCommits())
        .flatMap(() => this.fetch()),
      );
    });
  }

  // TODO(SHIN): Existing: DELETE -> POST/PATCH not being handled (resolved on 404 anyways)
  merge(commits: Commit[]): Observable<Commit[]> {
    const toSave: Commit[] = [];
    const toDelete: Commit[] = [];
    const unique = {};

    commits.forEach((commit) => {
      const key = commit.resource + commit.resourceHash;

      // Merge multiple commits for the same resource
      if (key in unique) {
        const existing = unique[key];

        if ((existing.method === 'POST' || existing.method === 'PATCH') &&
             commit.method === 'PATCH'
        ) {

          // If create -> update or update -> update, then merge into existing commit
          existing.payload = {
            ...existing.payload,
            ...commit.payload,
          };

          // Handles undo payload merges if undoPayload was specified
          if (commit.undoPayload) {
            existing.undoPayload = {
              ...commit.undoPayload,
            };
          }

          // Let undoable if commit should be undoable
          if (commit.undoable) {
            existing.undoable = commit.undoable;
          }

          toSave.push(existing);
          toDelete.push(commit);

          unique[key] = existing;

        } else if (existing.method === 'PATCH' && commit.method === 'POST') {

          // Handles draft save coming after DRAFT_UPDATE
          // HACK(SHIN): Temporary solution for sending draft when it hasn't been updated to server
          existing.method = 'POST';

          toSave.push(existing);
          toDelete.push(commit);

          unique[key] = commit;

        } else if (existing.method === 'PATCH' && commit.method === 'DELETE') {

          // If create -> delete or update -> delete, then merge into new commit
          toDelete.push(existing);
          toSave.push(commit);

          unique[key] = commit;

        } else if (existing.method === 'POST' && commit.method === 'DELETE') {

          // If create -> delete, then delete
          toDelete.push(existing);
          toDelete.push(commit);

          delete unique[key];

        } else {
          // Do not merge forward events
          const forwardHandler = existing.method === 'POST' && commit.method === existing.method &&
          existing.resource === HistoryObject.DraftAttachmentAppend &&
          commit.resource === existing.resource;
          if (forwardHandler) {
            return;
          }

          // Delete -> Delete, Delete -> Update, Delete -> Create cases should never happen
          withScope((scope) => {
            // Sanitization for Draft Commits
            if (commit.store === Stores.Drafts) {
              const limitedExisting = {
                ...existing,
                payload: {
                  ...existing.payload,
                  to: [],
                  cc: [],
                  bcc: [],
                  body: '',
                  attachments: [],
                },
              };

              const limitedCommit = {
                ...commit,
                payload: {
                  ...commit.payload,
                  to: [],
                  cc: [],
                  bcc: [],
                  body: '',
                  attachments: [],
                },
              };
              scope.setExtras({ existing: limitedExisting, commit: limitedCommit });
            } else {
              scope.setExtras({ existing, commit });
            }

            captureMessage('programmer: invalid commit merge');
          });
        }
        return;
      }

      // Add to set if this commit is not already found
      unique[key] = commit;
    });

    // Convert object into an array
    const processedCommits: Commit[] = [];
    Object.keys(unique).forEach(key => processedCommits.push(unique[key]));

    const toSaveHandler = () => toSave.length
      ? Cache.load(Stores.Commits, toSave)
      : Observable.of(null);

    const toDeleteKeys = compact(toDelete.map(commit => commit.id));
    const toDeleteHandler = () => toDeleteKeys.length
      ? Cache.deleteBatch(Stores.Commits, toDeleteKeys)
      : Observable.of(null);

    // Return processedCommits commits only after we finish saving to cache
    return toSaveHandler()
      .flatMap(() => toDeleteHandler())
      .flatMap(() => Observable.of(processedCommits));
  }

  // UndoMove creates a reversed, not-undoable commit, and resolves the undoable comit.
  undoMove = (box: Mailbox): Observable<any> => {
    // Cancel any existing resolves before processing undo.
    if (this.isRunning) {
      this.cancelSignal.next(null);
    }

    return Cache.list(Stores.Commits, (commit: Commit) => {
      return commit.undoable
        && commit.undoPayload
        && mailboxThreadsPredicate(box, { ...commit.undoPayload, account: commit.account });
    })
    .filter((commits: Commit[]) => commits.length > 0)
    .flatMap((commits: Commit[]) => {
      const lastCommit = last(commits);
      if (!lastCommit) {
        return Observable.of(null);
      }
      const undoCommit = {
        ...lastCommit,
        undoable: false,
        status: CommitStatus.Ready,
        payload: lastCommit.undoPayload,
        created: new Date(),
      };
      lastCommit.undoable = false;
      lastCommit.status = CommitStatus.Complete;
      delete undoCommit.id;

      let updatedThread: Thread;

      return Cache
        .save(Stores.Commits, lastCommit)
        .flatMap(() => Cache.get(Stores.Threads, lastCommit.resourceId))
        .flatMap((thread: Thread) => {
          updatedThread = patched(thread, undoCommit);
          return Cache.save(Stores.Commits, undoCommit);
        })
        .flatMap(() => Cache.save(Stores.Threads, updatedThread))
        .do(() => {
          sendThreadMoveUndoCompleted(box, updatedThread);
          sendThreadUpdated(updatedThread);
          bus.updateMailCount(updatedThread.account);

          // Emit list unread if the updated thread is unread and in a list
          if (updatedThread.list && !updatedThread.read) {
            bus.updateListUnreadCount(updatedThread.account, updatedThread.list);
          }
        });
    });
  }

  // Resolves any pending draft send commits into ready if no other commits for draft
  resolvePendingCommits(): Observable<null> {
    // Check for matching draft send requests
    let pendingSendCommits: Commit[] = [];
    let pendingSendNowCommits: Commit[] = [];
    return this.fetchPendingCommits().flatMap((commits) => {

      // Set pending draft commits first then fetch all unresolved
      pendingSendCommits = commits.filter(commit =>
        commit.resource === HistoryObject.Draft && commit.route.endsWith('send'));

      // Set pending send now commits
      pendingSendNowCommits = commits.filter(commit =>
        commit.resource === HistoryObject.Draft && commit.resourceHash.endsWith('-send-now'),
      );
      return this.fetchUnresolvedCommits();
    })
      .flatMap((unresolvedCommits) => {
        // Check for any ongoing or failed requests
        const unresolvedCommitsMap: { [key: string]: number } = unresolvedCommits.reduce(
          (dict, next) => {

            if (next.resourceHash.endsWith('send-now')) {
              return dict;
            }

            const matchInt = dict[next.resourceId];
            dict[next.resourceId] = matchInt && matchInt + 1 || 1;
            return dict;
          },
          {},
        );

        // Check for failed commits
        const failedCommitsCount: { [key: string]: number } = {};
        const failedCommitsMap: { [key: string]: Commit } = unresolvedCommits.reduce(
          (dict, next) => {
            if (next.status !== CommitStatus.Error) {
              return dict;
            }

            // Add any failed commits
            const matchInt = failedCommitsCount[next.resourceId];
            failedCommitsCount[next.resourceId] = matchInt && matchInt + 1 || 1;
            dict[next.resourceId] = next;
            return dict;
          },
          {},
        );

        // Handle Saving update requests to either failed or ready
        const updateCommits: {[ resourceId: string]: Commit } = {};
        let deleteIds: number[] = [];

        // Removes any duplicate pending draft commits so no limbo state occurs
        const pendingSendCommitsDict: {
          [resourceId: string]: Commit,
        } = pendingSendCommits.reduce(
          (dict, c) => {
            const found = dict[c.resourceId];
            if (found && c.id) {
              deleteIds = deleteIds.concat([c.id]);
              return dict;
            }

            dict[c.resourceId] = c;
            return dict;
          },
          {},
        );

        // Removes any duplicate pending draft commits so no limbo state occurs
        const pendingSendNowCommitsDict: {
          [resourceId: string]: Commit,
        } = pendingSendNowCommits.reduce(
          (dict, c) => {
            const found = dict[c.resourceId];
            if (found && c.id) {
              deleteIds = deleteIds.concat([c.id]);
              return dict;
            }

            dict[c.resourceId] = c;
            return dict;
          },
          {},
        );

        // Loop through pending draft commits to see which ones we should turn off or delete
        values(pendingSendCommitsDict)
          .filter((each) => {
            // We only want to process send handler
            // when we have a single pending commit (unresolved - failed)
            const unresolvedCommitsCount = unresolvedCommitsMap[each.resourceId] || 0;
            const failedCount = failedCommitsCount[each.resourceId] || 0;
            return unresolvedCommitsCount - failedCount === 1;
          })
          .forEach((commit) => {
            const failed = failedCommitsMap[commit.resourceId];
            const baseDraft = commit.rehydrateMeta && commit.rehydrateMeta.draft as Draft;

            // For Failed draft send out a draft failed event
            if (!!failed && baseDraft) {

              // Generate error for commit
              let error = failed.rehydrateMeta && failed.rehydrateMeta.error;
              const backupMessage = error && (error.description || error.message) || undefined;
              if ((!error || !(error instanceof Error) && backupMessage)) {
                error = new Error(backupMessage);
              }

              // Handle draft failure error
              this.handleDraftFailure(baseDraft, commit, error);

              // Submit commit id for deletion
              if (commit.id) {
                deleteIds = deleteIds.concat([commit.id]);
              }
            }

            // Otherwise update commit to ready state
            const updated = ({
              ...commit,
              status: failed ? CommitStatus.Error : CommitStatus.Ready,
            });
            updateCommits[updated.resourceId] = updated;
          });

        // Loop through pending send now commits
        values(pendingSendNowCommitsDict)
          .forEach((commit) => {

            // NOTE(SHIN): Using similar behavior as Draft send,
            // testing outbox performance w/ changes for draftfailure error
            // proper rehydrate meta access
            const failed = failedCommitsMap[commit.resourceId];
            const baseDraft = commit.rehydrateMeta && commit.rehydrateMeta as Draft;

            // For Failed draft send out a draft failed event
            if (!!failed && baseDraft) {

              // Generate error for commit
              let error = failed.rehydrateMeta && failed.rehydrateMeta.error;
              const backupMessage = error && (error.description || error.message) || undefined;
              if ((!error || !(error instanceof Error) && backupMessage)) {
                error = new Error(backupMessage);
              }

              // Handle draft failure error
              this.handleDraftFailure(baseDraft, commit, error);

              // Submit commit id for deletion
              if (commit.id) {
                deleteIds = deleteIds.concat([commit.id]);
              }
            }

            // Turn Ready
            const canTurnReady = !(unresolvedCommitsMap[commit.resourceId] > 1)
               || failedCommitsMap[commit.resourceId];

            // Check for match in update commits or in pending send commits
            const foundUpdateCommit = updateCommits[commit.resourceId] ||
              pendingSendCommitsDict[commit.resourceId];
            if (foundUpdateCommit) {
              const modifiedPayload = {
                ...foundUpdateCommit,
                payload: {
                  ...foundUpdateCommit.payload,
                  delay: 0,
                },
              };

              updateCommits[foundUpdateCommit.resourceId] = modifiedPayload;
              if (commit.id) {
                deleteIds = deleteIds.concat([commit.id]);
              }
            } else if (canTurnReady) { // If no match then turn send now commit status to ready
              const sendNowReadyCommit = {
                ...commit,
                status: CommitStatus.Ready,
              };
              updateCommits[commit.resourceId] = sendNowReadyCommit;
            }
          });

        const updateLength = Object.keys(updateCommits).length;

        // Continue if no update requests
        if (!updateLength && !deleteIds.length) {
          return Observable.of(null);
        }

        // Retrigger since we made some commits ready state
        if (!!updateLength) {
          this.retrigger = true;
        }

        // Turn into requests to pass through Observable.zip
        const updateRequests = !!updateLength
          ? Cache.load(Stores.Commits, values(updateCommits))
          : Observable.of(null);
        const deleteRequests = uniq(deleteIds).length
          ? Cache.deleteBatch(Stores.Commits, deleteIds)
          : Observable.of(null);

        // Handle loading update requests
        return Observable.zip(
          updateRequests,
          deleteRequests,
        ).flatMap(() => Observable.of(null));
      });
  }

  // Resolves resolves a batch of commits with the API
  private resolve(commits: Commit[]): Observable<any[]> {
    return Observable.zip(

      // Save processing state
      ...commits.map((commit) => {
        return Cache.save(Stores.Commits, { ...commit, status: CommitStatus.Processing });
      }),
    )
    .flatMap(() => {
      // HACK(SHIN): Use old batch request when only thread PATCH commits since this runs in
      // parallel and causes HistorySync to receive responses with more batched pagination results
      const threadCommitLength = commits
        .filter(commit =>
          commit.resource === HistoryObject.Thread && commit.method !== 'DELETE',
        ).length;
      const isOnlyThreadCommits = threadCommitLength === commits.length;
      if (isOnlyThreadCommits) {
        const threadRequests = commits.map((commit) => {
          return {
            method: commit.method,
            url: commit.route,
            body: Base64.encode(JSON.stringify(commit.payload)),
          };
        });
        const threadBatchRequest = { requests: threadRequests };
        return this.api.concurrentBatchRequest(threadBatchRequest);
      }

      // Make batch request
      const requests = commits.map((commit) => {
        return { method: commit.method, url: commit.route, body: JSON.stringify(commit.payload) };
      });
      const batchRequest = { requests };
      return this.api.batchRequest(batchRequest);
    })
    .catch((e: Error) => { // Handle type error
      // On batch request error, reset commits to try again.
      const readyCommits = commits
        .map(commit => Cache.save(Stores.Commits, { ...commit, status: CommitStatus.Ready }));
      return Observable
        .zip(...readyCommits)
        .flatMap(() => Cache.get(Stores.Commits, commits[0].id))
        .flatMap(() => Observable.throw(e));
    })
    .flatMap((responses) => {
      return Observable.zip(...commits.map((commit, i) => {
        return this.resolveLocal(commit, responses[i]);
      }));
    });
  }

  private resolveLocal(commit: Commit, response: BatchResponseParameters): Observable<null> {
    switch (response.code) {
      case 200:
      case 201:

        // Reset any backoffs for provider thread ops
        if (commit.resource === HistoryObject.Thread) {
          const { account, resourceHash } = commit;
          if (account) {
            delete this.resourceBackoffs[account];
          }

          delete this.resourceBackoffs[resourceHash];
        }

        // Handle Attachment Metas for append case
        // HACK(SHIN): Sets cloudUrl to backup to the meta cloudUrl as a backup src
        if (commit.resource === HistoryObject.DraftAttachmentAppend) {
          if (commit.method === 'POST' && response.body && response.body.length) {
            const attachments: Attachment[] = response.body;
            const skeletonMeta: AttachmentMeta = commit.rehydrateMeta;
            if (skeletonMeta) {
              attachments.forEach(attachment =>
                sendDraftAttachmentAdded(
                  skeletonMeta,
                  {
                    ...attachment,
                    cloudUrl: attachment.cloudUrl || skeletonMeta.cloudUrl || '',
                  },
                ),
              );

              return Cache.deleteSingle(Stores.Commits, commit.id);
            }
          }
        }

        // Specialized handling for DraftAttachments since there is a parent resource
        if (commit.resource === HistoryObject.DraftAttachment) {
          // If deleted then just process delete
          // TODO(SHIN): Maybe push out a redux event
          if (commit.method === 'DELETE' && commit.id) {
            return Cache.deleteSingle(Stores.Commits, commit.id);
          }

          // On success emit out action completed
          if (commit.method === 'POST' && response.body && commit.id) {
            const skeletonMeta: AttachmentMeta = commit.rehydrateMeta;
            if (skeletonMeta) {
              // Sends attachment added completion
              sendDraftAttachmentAdded(skeletonMeta, response.body);
            }

            // Delete commit once completed
            return Cache.deleteSingle(Stores.Commits, commit.id);
          }
        }

        // Handle successful deletions
        if (commit.method === 'DELETE' && commit.id) {
          return Cache.deleteSingle(commit.store, commit.resourceId)
            .flatMap(() => {
              switch (commit.resource) {
                case HistoryObject.Draft:
                  sendDraftDeleted(commit.resourceId);
                  break;
                case HistoryObject.Thread:
                  sendThreadDeleted(commit.resourceId);
                  // TODO(Shahan): Should call bus.updateMailCount
                  // once we figure out how to get the account id
                  // for the thread here.
                  break;
                default:
              }
              return Cache.deleteSingle(Stores.Commits, commit.id);
            });
        }

        // Apply defaults
        let patchedResponse = response.body;
        switch (commit.resource) {
          case HistoryObject.Draft:
            patchedResponse = defaultDraft(response.body);
            break;

          case HistoryObject.Thread:
            patchedResponse = computeThread(response.body);
            break;

          default:
        }

        // We probably want to abstract the entire draft commit handling outside
        // On draft update requests, we query the cache to apply any optimistic properties needed
        // TODO(SHIN): We MAY need some other optimistic properties
        // (attachments, later date, follow up, etc)
        const isDraftUpdate = commit.resource === HistoryObject.Draft && commit.method === 'PATCH';
        const isDraftCreate = commit.resource === HistoryObject.Draft && commit.method === 'POST' &&
          commit.resourceHash === commit.resourceId;
        const isDraftSend = commit.resource === HistoryObject.Draft &&
          commit.resourceHash.endsWith('send');

        const patchOptimistic: Observable<null> = Cache.get(
          commit.store,
          patchedResponse.id,
        ).flatMap((d: Draft|undefined) => {
          if (!d) {
            return Cache.save(commit.store, patchedResponse);
          }

          // Drop requests with older version
          if (d.version > patchedResponse.version) {
            return Observable.of(null);
          }

          // Save last sent draft if version is higher or matches
          if (isDraftSend) {
            return Cache.save(commit.store, patchedResponse);
          }

          // Always overwrite and save into indexeddb if version is greater
          if (patchedResponse.version > d.version) {
            return Cache.save(commit.store, patchedResponse);
          }

          // Scheduled can also get overridden
          // TODO(SHIN): May need to handle other properties as well
          if (d.status === DraftStatus.Staged || d.status === DraftStatus.Scheduled) {
            patchedResponse = patchedDraft(patchedResponse, d);
          }

          return Cache.save(commit.store, patchedResponse);
        });
        let patchSaveRequest = Cache.save(commit.store, patchedResponse);
        if (isDraftUpdate || isDraftCreate || isDraftSend) {
          patchSaveRequest = patchOptimistic;
        }

        // Handle successful create / updates
        return patchSaveRequest
          .flatMap(() => {

            const draftPostRequest = commit.resource === HistoryObject.Draft &&
              commit.method === 'POST';

            // Draft Created Emission
            if (draftPostRequest && !commit.resourceHash.endsWith('send')) {
              sendDraftCreated(patchedResponse);
            }

            // Draft Send Emission
            if (draftPostRequest && commit.resourceHash.endsWith('send')) {
              sendDraftSent({ draft: patchedResponse, toggleArchive: false });
              sendDraftUpdated(patchedResponse);
            }

            // Handle patching send nows
            if (commit.resourceHash.endsWith('send-now')) {
              sendDraftUpdated(patchedResponse);
            }

            // Delete Draft Commits when completed
            if (commit.resource === HistoryObject.Draft && commit.id) {
              return Cache.deleteSingle(Stores.Commits, commit.id);
            }

            return Cache.save(Stores.Commits, {
              ...commit,
              status: CommitStatus.Complete,
            });
          });

      case 404:

        return Cache.deleteSingle(commit.store, commit.resourceId)
          .flatMap(() => {
            switch (commit.resource) {
              case HistoryObject.Draft:
                sendDraftDeleted(commit.resourceId);

                // Return delte commit on 404
                return Cache.deleteSingle(Stores.Commits, commit.id);

              case HistoryObject.Thread:
                sendThreadDeleted(commit.resourceId);
                break;

              default:
            }
            return Cache.save(Stores.Commits, {
              ...commit,
              status: CommitStatus.Complete,
            });
          });

      case 409: // For 409, version was not up to date so we drop the commit.

        return Cache.deleteSingle(Stores.Commits, commit.id);

      case 400:

        const errorBody = response.body;

        // When Attachment/Append failed emit attachment failed event
        if (
          [HistoryObject.DraftAttachment, HistoryObject.DraftAttachmentAppend]
          .indexOf(commit.resource) >= 0
        ) {
          const meta = commit.rehydrateMeta;
          if (meta) {
            return Cache.save(Stores.Commits, {
              ...commit,
              status: CommitStatus.Error,
              undoable: false,
              rehydrateMeta: {
                ...commit.rehydrateMeta,
                error: errorBody,
              },
            }).flatMap(() => {
              // Handle emission of failed attachment
              sendDraftAttachmentAddFailed(meta, errorBody);
              return Observable.of(null);
            });
          }
        }

        // Submit Draft Send Fail
        if (commit.resource === HistoryObject.Draft) {

          // Send draft failed if send case
          if (commit.resourceHash.endsWith('send')) {
            const meta = commit.rehydrateMeta as DraftSendMeta;
            if (meta) {
              // Handle failure and delete commit
              this.handleDraftFailure(meta.draft, commit, errorBody);
            }
          }

          // Delete all other draft commits on error
          return Cache.deleteSingle(Stores.Commits, commit.id);
        }

        // TODO(Jeff): Handle undo logic
        return Cache.save(Stores.Commits, {
          ...commit,
          status: CommitStatus.Error,
          undoable: false,
          rehydrateMeta: {
            ...commit.rehydrateMeta,
            error: errorBody,
          },
        });

      default: {
        const errBody = response.body as ApiError;
        let isProviderError = false;

        // Handle increments for expo backoff mechanism
        switch (errBody.description) {
          case ProviderErrors.ProviderError: {
            const { resourceHash } = commit;
            const backoff = this.resourceBackoffs[resourceHash] || new Backoff(resourceHash);
            backoff.minPower = 8;
            backoff.minSecs = 120;
            backoff.maxSecs = 1800;
            backoff.setError(new ApiError(response.code, errBody.description));
            this.resourceBackoffs[resourceHash] = backoff;
            isProviderError = true;

            // If received more than 3 errors delete on provider error fail
            // HACK(SHIN): Currently deleting vs undoing provider 500 errors on > 4x attempts.
            // This separates concerns between provider/API and allows API to power through.
            // Previously CommitSync was being completely blocked. We delete instead of undo bc
            // of potentially large volume of undos and bringing server side instead
            if (backoff.numErrors > 4) {

              // Remove resource backoff
              delete this.resourceBackoffs[resourceHash];

              // Delete commit
              return Cache.deleteSingle(Stores.Commits, commit.id);
            }
            break;
          }
          case ProviderErrors.ProviderRateLimit: {
            const { account } = commit;
            if (account) {
              const backoff = this.resourceBackoffs[account] || new Backoff(account);
              backoff.minPower = 7;
              backoff.minSecs = 60;
              backoff.maxSecs = 1800;
              backoff.setError(new ApiError(response.code, errBody.description));
              this.resourceBackoffs[account] = backoff;
              isProviderError = true;
            }
            break;
          }
          default: break;
        }

        // TODO(SHIN): Handle removal of commit if backoffs exceed max limit
        return Cache.save(Stores.Commits, {
          ...commit,
          status: CommitStatus.Ready,
        })
        .flatMap((r) => {
          // Capture upstream if not provider error
          if (!isProviderError) {
            throw new Error('Commit failed ' + response.code + response.body);
          }

          return Observable.of(r);
        });
      }
    }
  }

  // Fetch retrieves a page of commits from the commit log to process
  private fetch(): Observable<Commit[]> {
    this.retrigger = false;
    return Cache.list(Stores.Commits, (commit: Commit) => {
      return commit.status === CommitStatus.Ready;
    }).map((fetchedCommits) => {
      const commits = fetchedCommits.filter((c) => {

        // NOTE(SHIN): We filter out any Thread Operations that communicate with provider
        // This allows us to continue Running CommitSync while handling API commits
        // We only filter out commits that are for Thread Resources
        if (c.resource === HistoryObject.Thread) {
          const resourceBackoff = this.resourceBackoffs[c.resourceHash];
          if (resourceBackoff) {
            if (!resourceBackoff.isReady()) {
              return false;
            }
          }

          if (c.account) {
            const accountBackoff = this.resourceBackoffs[c.account];
            if (accountBackoff) {
              if (!accountBackoff.isReady()) {
                return false;
              }
            }
          }
        }

        return true;
      });

      return commits;
    });
  }

  // Fetch retrieves a page of commits from the commit log to process
  private fetchPendingCommits(): Observable<Commit[]> {
    const pendingCommits = Cache.list(Stores.Commits, (commit: Commit) => {
      return commit.status === CommitStatus.Pending;
    });

    return pendingCommits;
  }

  // Fetch all unresolved commits
  private fetchUnresolvedCommits(): Observable<Commit[]> {
    const allUnresolvedCommits = Cache.list(Stores.Commits, (commit: Commit) => {
      return [CommitStatus.Complete].indexOf(commit.status) === -1;
    });
    return allUnresolvedCommits;
  }

  // handleDraftFailure handles side effects for draft save and worker events for failed draft
  private handleDraftFailure = (start: Draft, commit: Commit, error: Error|undefined) => {

    // Set error or create if doesn't exist
    const finalError = error || new Error(
      'We couldn\'t send this draft. Please fix the errors and try again.',
    );

    // Convert necessary properties for failed drafts
    const converted = failedDraft(commit, defaultDraft(start), finalError);

    // Save into cache and emit worker events
    Cache.save(Stores.Drafts, converted).subscribe();
    sendDraftUpdated(converted);
    sendDraftSendFailed(
      { draft: converted, toggleArchive: false },
      finalError,
    );
  }

  private handleBusEvent(event: BusEvent) {
    switch (event.name) {
      case BusEventName.ClearErrors:
        this.backoff.clearErrors();
        break;

      case BusEventName.CancelSync:
        if (event.data.workerId && event.data.workerId !== this.workerId) {
          break;
        }
        this.cancelSignal.next(null);
        return;

      case BusEventName.TriggerSync:
        if (event.data.workerId && event.data.workerId !== this.workerId) {
          break;
        }
        if (this.isRunning) {
          this.retrigger = true;
          return;
        }
        this.run().subscribe({
          error: (e) => {
            // tslint:disable-next-line:no-console
            console.error(e);
          },
        });
        break;

      case BusEventName.UndoMove:
        if (event.data.box) {
          this.undoMove(event.data.box)
            .flatMap(() => this.run())
            .subscribe();
        }
        break;

      case BusEventName.HistoryClearCache:
        this.clearCache(false).subscribe();
        break;

      default:
        break;
    }
  }
}

export default CommitSync;
