import {
  Sequence,
  SequenceStage,
  SequenceStageInstance,
  SequenceStageRecipient,
  SequenceStageRecipientStatus,
  SequenceRecipient,
  SequenceRecipientCount,
  SequenceAnalytics,
  SequenceStageRecipientCount,
  SequenceRecipientStatus,
  SequenceRecipientsState,
  SequenceRecipientFilter,
  SequenceRecipientSort,
  SequenceStopCondition,
} from '@src/types/sequences';
import {
  SequenceRecipientUploadMeta,
  SequenceRecipientUploadPayload,
  SequenceRecipientCreateRecipient,
} from '@src/ducks/sequenceRecipients';
import { SequenceStageInstanceAttachmentMeta } from '@src/ducks/sequenceStageInstances';
import { createFile } from '@src/mocks/attachments';
import { AttachmentMetaStatus } from '@src/types';

export const createSequenceRecipientCount = (count?: Partial<SequenceRecipientCount>) => {
  return {
    active: 1,
    completed: 2,
    paused: 3,
    bounced: 4,
    failed: 5,

    ...count,
  };
};

export const createSequenceStageRecipientCount = (count?: Partial<SequenceStageRecipientCount>) => {
  return {
    scheduled: 1,
    bounced: 2,
    paused: 3,

    ...count,
  };
};

export const createSequenceAnalytics = (analytics?: Partial<SequenceAnalytics>) => {
  return {
    delivered: 9,
    deliveredUnique: 7,
    opened: 2,
    clicked: 3,
    downloaded: 4,
    replied: 5,
    unsubscribed: 6,

    ...analytics,
  };
};

export const createSequence = (sequence?: Partial<Sequence>): Sequence => {
  return {
    id: 'id',
    created: new Date('2017-09-27T05:20:31.216Z'),
    updated: new Date('2017-09-27T05:20:31.216Z'),
    team: 'teamId',
    user: 'userId',
    account: 'accountId',
    senderEmail: 'doge@polymail.io',

    archived: false,
    paused: false,
    name: 'sequence1',
    headers: [],
    durationDays: 0,
    stages: 4,

    afterSecondsSinceMidnight: 0,
    beforeSecondsSinceMidnight: 86400,
    daysOfWeek: [0, 1, 2, 3, 4, 5, 6],
    timeZone: 'America/Los_Angeles',
    dailyLimit: 500,

    recipients: createSequenceRecipientCount(),
    analytics: createSequenceAnalytics(),
    stopCondition: SequenceStopCondition.Reply,
    ...sequence,
  };
};

export const createSequenceStage = (sequenceStage?: Partial<SequenceStage>): SequenceStage => {
  return {
    id: 'id',
    stage: 'stage',
    created: new Date('2017-09-27T05:20:31.216Z'),
    updated: new Date('2017-09-27T05:20:31.216Z'),
    user: 'id',
    intervalDays: 2,
    newThread: false,
    sequence: 'testSequence',
    order: 0,

    recipients: createSequenceStageRecipientCount(),
    analytics: createSequenceAnalytics(),

    ...sequenceStage,
  };
};

export const createSequenceStageInstance =
  (sequenceStageInstance?: Partial<SequenceStageInstance>): SequenceStageInstance => {
    return {
      id: 'id',
      stage: 'stage',
      sequence: 'sequence',
      created: new Date('2017-09-27T05:20:31.216Z'),
      updated: new Date('2017-09-27T05:20:31.216Z'),
      user: 'user',
      unsubscribeLink: true,

      custom: false,
      subject: 'subject',
      body: 'body',
      userEdited: false,

      ...sequenceStageInstance,
    };
  };

export const createSequenceStageRecipient =
  (sequenceStageRecipient?: Partial<SequenceStageRecipient>): SequenceStageRecipient => {
    return {
      id: 'id',
      stage: 'stage',
      sequence: 'sequence',
      created: new Date('2017-09-27T05:20:31.216Z'),
      updated: new Date('2017-09-27T05:20:31.216Z'),
      user: 'user',
      instance: 'instance',

      status: SequenceStageRecipientStatus.Complete,
      sequenceStatus: SequenceRecipientStatus.Active,

      draft: 'draft',
      thread: 'thread',
      message: 'message',

      analytics: createSequenceAnalytics(),

      email: 'doge@polymail.io',
      fullName: 'Doge Foo',
      paused: false,

      ...sequenceStageRecipient,
    };
  };

export const createSequenceRecipient = (recipient?: Partial<SequenceRecipient>) => {
  return {
    id: 'id',
    email: 'email',
    sequence: 'sequence',
    created: new Date('2017-09-27T05:20:31.216Z'),
    updated: new Date('2017-09-27T05:20:31.216Z'),
    user: 'user',
    familyName: 'familyName',
    givenName: 'givenName',

    status: SequenceRecipientStatus.Complete,
    paused: false,
    lastStage: 'stage',

    analytics: createSequenceAnalytics(),
    ...recipient,
  };
};

export const createUploadedSequenceRecipients = (uploadedSequenceRecipients?: Partial<{
  recipients: SequenceRecipient[],
  csv: string,
}>) => {
  return {
    recipientsCount: 5,
    invalidRecipients: [
      createSequenceRecipient({
        email: 'doge@polymail.io',
        status: SequenceRecipientStatus.Bounced,
      }),
    ],
    csv: '',
    ...uploadedSequenceRecipients,
  };
};

export const createSequenceRecipientUploadMeta = (meta?: Partial<SequenceRecipientUploadMeta>)
: SequenceRecipientUploadMeta => {
  return {
    sequence: 'test-sequence',
    cloudFile: 'test-cloudFile',
    emailHeader: 'emailHeader',
    givenNameHeader: 'givenNameHeader',
    familyNameHeader: 'familyNameHeader',
  };
};

export const createSequenceInstanceAttachmentUploadMeta =
(meta?: Partial<SequenceStageInstanceAttachmentMeta>): SequenceStageInstanceAttachmentMeta => {
  return {
    sequence: 'test-sequence',
    stage: 'test-stage',
    attachment: 'test-attachment',
    resourceId: 'test-resource',
    file: createFile(),
    status: AttachmentMetaStatus.Create,
    ...meta,
  };
};

export const createSequenceRecipientMeta =
(partial?: Partial<SequenceRecipientUploadMeta>): SequenceRecipientUploadMeta => ({
  sequence: 'sequence',
  cloudFile: 'fake-cloud',
  emailHeader: 'test',
  givenNameHeader: 'givenName-header',
  familyNameHeader: 'familyName-header',

  ...partial,
});

export const createSequenceRecipientState = (partial?: Partial<SequenceRecipientsState>) => ({
  filter: SequenceRecipientFilter.All,
  sort: SequenceRecipientSort.Name,
  query: '',
  ...partial,
});

export const createSequenceRecipientUploadPayload =
  (partial?: SequenceRecipientUploadPayload): SequenceRecipientUploadPayload => {
    return {
      invalidRecipients: [
        createSequenceRecipient({
          status: SequenceRecipientStatus.Failed,
        }),
      ],
      recipients: [createSequenceRecipient()],
      ...partial,
    };
  };

export const createSequenceRecipientCreateRecipient =
  (partial?: Partial<SequenceRecipientCreateRecipient>) => {
    return {
      email: 'doge@polymail.io',
      sequence: 'sequenceId',
      variables: {
        email: 'doge@polymail.io',
      },

      ...partial,
    };
  };
