import {
  ActionStatus,
  Action,
  StoreState,
  Attachment,
  AttachmentMetaStatus,
  AttachmentMeta,
  Template,
  Resource,
} from '@src/types';
import { SequenceStageInstance } from '@src/types/sequences';
import { Epic } from 'redux-observable';
import { Deps } from '@src/lib/epic';
import { Observable } from 'rxjs';
import {
  SEQUENCE_STAGE_INSTANCE_LIST, sequenceStageInstanceList,
  SEQUENCE_STAGE_INSTANCE_DELETE, sequenceStageInstanceDelete,
  SEQUENCE_STAGE_INSTANCE_UPDATE, sequenceStageInstanceUpdate,
  sequenceStageInstanceCreate, SEQUENCE_STAGE_INSTANCE_CREATE,
  SequenceStageInstanceMeta,
  SequenceStageInstanceCreateMeta,
  SequenceStageInstanceUpdateMeta,
  SequenceStageInstanceTestMeta,
  SEQUENCE_STAGE_INSTANCE_TEST,
  sequenceStageInstanceTest,
  SequenceStageInstanceAttachmentMeta,
  SEQUENCE_STAGE_INSTANCE_ATTACHMENT_ADD, sequenceStageInstanceAttachmentAdd,
  sequenceStageInstanceAttachmentRemove,
  sequenceStageInstanceAttachmentsList, SEQUENCE_STAGE_INSTANCE_ATTACHMENTS_LIST,
  SEQUENCE_STAGE_INSTANCE_ATTACHMENTS_APPLY, sequenceStageInstanceAttachmentsApply,
  SEQUENCE_STAGE_INSTANCE_TEMPLATE_APPLY,
} from '@src/ducks/sequenceStageInstances';
import { toastSet } from '@src/ducks/toasts';
import { ApiError } from '@src/api/base';
import { selectAttachmentMetasForResource, attachmentMetaCreate } from '@src/ducks/drafts';
import { values, compact } from 'lodash';
import { applyTemplateToStageInstance } from '@src/lib/models/sequenceStageInstances';
import { convertToMeta } from '@src/lib/attachment';
import { sequenceStageList } from '@src/ducks/sequenceStages';

const sequenceStageInstanceListEpic: Epic<
  Action<
    SequenceStageInstanceMeta | SequenceStageInstance,
    SequenceStageInstance[] | Attachment[]
  >,
  StoreState,
  Deps
> = (action$, store, deps) =>
  action$.ofType(SEQUENCE_STAGE_INSTANCE_LIST)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action<SequenceStageInstanceMeta, SequenceStageInstance[]>) =>
      deps.sequenceStageInstances.list(action.meta.sequence, action.meta.stage)
        .flatMap((result) => {
          const firstResult = result[0];
          const actions: Array<Observable<Action<any, any>>> = [
            Observable.of(sequenceStageInstanceList(action.meta, result)),
          ];
          if (firstResult) {
            actions.push(Observable.of(sequenceStageInstanceAttachmentsList(firstResult)));
          }
          return Observable.merge(
            ...actions,
          );
        })
        .catch((e: Error) => Observable.of(sequenceStageInstanceList(action.meta, e))),
  );

const sequenceStageInstanceCreateEpic: Epic<
  Action<
    SequenceStageInstanceCreateMeta | SequenceStageInstanceMeta | Error | null,
    SequenceStageInstance | SequenceStageInstance[] | null
  >, StoreState, Deps
  > = (action$, store, deps) =>
    action$.ofType(SEQUENCE_STAGE_INSTANCE_CREATE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<SequenceStageInstance, SequenceStageInstance>) =>
        deps.sequenceStageInstances.create(action.meta)
          .flatMap(result => Observable.merge(
            Observable.of(sequenceStageInstanceCreate(action.meta, result)),
            Observable.of(sequenceStageInstanceList({
              sequence: action.meta.sequence,
              stage: action.meta.stage,
            })),
          ))
          .catch((e: Error) => Observable.merge(
            Observable.of(toastSet(e)),
            Observable.of(sequenceStageInstanceCreate(action.meta, e)),
          )),
    );

const sequenceStageInstanceUpdateEpic: Epic<
  Action<
    SequenceStageInstanceUpdateMeta | SequenceStageInstance |  Error | null,
    SequenceStageInstance | null | Attachment[]
  >, StoreState, Deps
  > = (action$, store, deps) =>
    action$.ofType(SEQUENCE_STAGE_INSTANCE_UPDATE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<SequenceStageInstance, SequenceStageInstance>) =>
        deps.sequenceStageInstances.update(action.meta)
          .flatMap(result =>
            Observable.merge(
              Observable.of(sequenceStageInstanceUpdate(action.meta, result)),
              Observable.of(sequenceStageInstanceAttachmentsApply(result)),
            ),
          )
          .catch((e: Error) => Observable.merge(
            Observable.of(toastSet(e)),
            Observable.of(sequenceStageInstanceUpdate(action.meta, e)),
          )),
    );

const sequenceStageInstanceDeleteEpic: Epic<
  Action<any, any>, StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(SEQUENCE_STAGE_INSTANCE_DELETE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<SequenceStageInstance, null>) =>
        deps.sequenceStageInstances.delete(action.meta)
          .flatMap(result =>
            Observable.concat(
              Observable.of(sequenceStageInstanceDelete(action.meta, null)),
              Observable.of(sequenceStageList(action.meta.sequence)),
            ))
          .catch((e) => {
            // handle 404 as completion
            if (e as ApiError && e.code === 404) {
              return Observable.of(sequenceStageInstanceDelete(action.meta, null));
            }

            throw e;
          })
          .catch((e: Error) => Observable.merge(
            Observable.of(toastSet(e)),
            Observable.of(sequenceStageInstanceDelete(action.meta, e)),
          )),
    );

const sequenceStageInstanceTestEpic: Epic<Action<
  SequenceStageInstanceTestMeta|Error|null, null
>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_STAGE_INSTANCE_TEST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequenceStageInstances.test(action.meta as SequenceStageInstanceTestMeta)
          .map(result =>
            sequenceStageInstanceTest(action.meta as SequenceStageInstanceTestMeta, null))
          .catch(e => Observable.of(toastSet(e))),
      );

const sequenceStageInstanceAttachmentAddEpic: Epic<Action<
    SequenceStageInstanceAttachmentMeta | SequenceStageInstance | AttachmentMeta,
    SequenceStageInstanceAttachmentMeta | Attachment[] | AttachmentMeta>,
  StoreState,
  Deps
> = (action$, store, deps) =>
  action$.ofType(SEQUENCE_STAGE_INSTANCE_ATTACHMENT_ADD)
  .filter(action => action.status === ActionStatus.Start)
  .flatMap((action: Action<
    SequenceStageInstanceAttachmentMeta,
    SequenceStageInstanceAttachmentMeta>) =>
    deps.sequenceStageInstances.uploadCloudFile(action.meta)
      .flatMap((cloudFile) => {
        // Handle any progress events
        if (typeof cloudFile === 'number') {
          return Observable.of(attachmentMetaCreate({
            ...action.meta,
            progress: cloudFile,
          }));
        }

        const state = store.getState();
        const { stage, sequence, resourceId } = action.meta;
        const uploadedMeta = {
          ...action.meta,
          cloudFile: cloudFile.cloudFile,
          cloudUrl: cloudFile.cloudUrl,
          status: AttachmentMetaStatus.Create,
          progress: 1,
        };
        // Check state meta
        const metaResources: Array<Resource<SequenceStageInstanceAttachmentMeta>> =
          selectAttachmentMetasForResource(
          store.getState(),
          action.meta.resourceId,
        );

        const metas = compact(values(metaResources.map(resource => resource.data)));

        // Grab fetched meta and staged instance. If staged for create after upload process
        // Apply to instance immediately and call apply success handler
        const fetchedMeta = metas.filter(meta => meta.attachment === action.meta.attachment)[0];
        const stageInstance = state.sequenceStageInstances[sequence + stage] &&
          state.sequenceStageInstances[sequence + stage][resourceId] &&
          state.sequenceStageInstances[sequence + stage][resourceId].data;
        if (fetchedMeta && fetchedMeta.status === AttachmentMetaStatus.CreateAfterUpload &&
          stageInstance
        ) {
          return deps.sequenceStageInstances
            .uploadAttachment(uploadedMeta)
            .map(attachment => sequenceStageInstanceAttachmentsApply(
              stageInstance,
              [attachment],
            ));
        }

        // Otherwise mark for creation by calling success handler
        return Observable.of(
          sequenceStageInstanceAttachmentAdd(action.meta, uploadedMeta),
        );
      })
      .catch(e => Observable.of(sequenceStageInstanceAttachmentAdd(action.meta, e))),
  );

