const sequenceStageInstanceAttachmentsApplyEpic: Epic<Action<
  SequenceStageInstance | Error | null | AttachmentMeta,
  Attachment[] | AttachmentMeta | null
>, StoreState, Deps> = (action$, store, deps) =>
  action$.ofType(SEQUENCE_STAGE_INSTANCE_ATTACHMENTS_APPLY)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action<SequenceStageInstance, Attachment[]>) => {
      const state = store.getState();
      const metaResources: Array<Resource<SequenceStageInstanceAttachmentMeta>> =
        selectAttachmentMetasForResource(
          state,
          action.meta.id,
        );
      const metas = compact(values(metaResources.map(resource => resource.data)));
      const addMetas = metas.filter(meta => meta.status === AttachmentMetaStatus.Create);
      const deleteMetas = metas.filter(meta => meta.status === AttachmentMetaStatus.Delete);
      const uploadingMetas = metas.filter(meta => meta.status === AttachmentMetaStatus.Uploading);

      // Turns all add metas into upload requests and calls apply success handler
      const addRequests = addMetas
        .map(meta => deps.sequenceStageInstances.uploadAttachment(meta));
      const addHandler = Observable.zip(...addRequests)
        .map(result => sequenceStageInstanceAttachmentsApply(action.meta, result));

      // Turns all delete metas into remove requests and call remove success handler
      const deleteHandler = deleteMetas
        .map(meta => deps.sequenceStageInstances
          .removeAttachment(meta)
          .map(payload => sequenceStageInstanceAttachmentRemove(meta, null)));

      // Turns all uploading to create after upload
      const stillUploadingHandler = uploadingMetas
          .map(meta => Observable.of(sequenceStageInstanceAttachmentAdd(
            meta,
            { ...meta, status: AttachmentMetaStatus.CreateAfterUpload },
          )));

      // Merge both calls
      return Observable.merge(
        ...stillUploadingHandler,
        addHandler,
        ...deleteHandler,
      ).catch((e: Error) => Observable.merge(
        Observable.of(toastSet(e)),
        Observable.of(sequenceStageInstanceAttachmentsApply(action.meta, e)),
      ));
    });

const sequenceStageInstanceAttachmentsListEpic: Epic<Action<
  SequenceStageInstance, Attachment[]
  >, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_STAGE_INSTANCE_ATTACHMENTS_LIST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequenceStageInstances
          .listAttachments(
            action.meta.sequence,
            action.meta.stage,
            action.meta.id,
        )
          .map(attachments => sequenceStageInstanceAttachmentsList(action.meta, attachments))
          .catch(e => Observable.of(sequenceStageInstanceAttachmentsList(action.meta, e))),
    );

// Base handling for applying sequenceStagInstance update
const sequenceStageInstanceTemplateApplyEpic: Epic<
  Action<
    Template | SequenceStageInstanceUpdateMeta | SequenceStageInstanceAttachmentMeta,
    SequenceStageInstance | AttachmentMeta | Error | null>,
  StoreState,
  Deps
> = (action$, store, deps) =>
  action$.ofType(SEQUENCE_STAGE_INSTANCE_TEMPLATE_APPLY)
    .filter(action => action.status === ActionStatus.Start)
    .filter(() => {
      const state = store.getState();
      const sequenceId = state.selectedSequence;
      const stageId = state.selectedSequenceStage;
      if (!(sequenceId && stageId && state.sequenceStageInstances[sequenceId + stageId])) {
        return false;
      }
      const stageInstance = values(state.sequenceStageInstances[sequenceId + stageId])[0];
      return stageInstance && !!stageInstance.data;
    })
    .flatMap((action: Action<Template, SequenceStageInstance>) => {
      const state = store.getState();
      const sequenceId = state.selectedSequence!;
      const stageId = state.selectedSequenceStage;
      const stageInstance = values(state.sequenceStageInstances[sequenceId + stageId])[0].data!;
      const sequence = sequenceId && state.sequences[sequenceId] &&
        state.sequences[sequenceId].data;
      const stage = stageId && state.sequenceStages[sequenceId] &&
        state.sequenceStages[sequenceId][stageId] && state.sequenceStages[sequenceId][stageId].data;
      const headers = sequence && sequence.headers || [];
      const templateAppliedInstance = applyTemplateToStageInstance(
        stageInstance,
        action.meta,
        headers,
        stage && stage.newThread || false,
      );
      const templateAttachments = action.meta.attachments;
      const templateAttachmentActions = templateAttachments
        .map(attachment => ({
          ...convertToMeta(attachment, stageInstance.id),
          attachment: attachment.id,
          sequence: stageInstance.sequence,
          stage: stageInstance.stage,
          resourceId: stageInstance.id,
          template: action.meta.id,
          status: AttachmentMetaStatus.Create,
          cloudUrl: attachment.cloudUrl,
          progress: 1,
        }))
      .map(meta => Observable.of(sequenceStageInstanceAttachmentAdd(meta, meta)));

      // Remove any old metas for attachment metas
      const oldMetaResources: Array<Resource<AttachmentMeta>> = selectAttachmentMetasForResource(
        state,
        stageInstance.id,
      );
      const oldMetas = compact(values(oldMetaResources.map(resource => resource.data)));
      const removeActions = oldMetas
        .filter(meta => !!meta.template)
        .filter(meta => meta.template !== action.meta.id)
        .map(meta => sequenceStageInstanceAttachmentRemove({
          ...meta,
          sequence: stageInstance.sequence,
          stage: stageInstance.stage,
          status: AttachmentMetaStatus.Delete,
        }))
        .map(removeAction => Observable.of(removeAction));

      return Observable.merge(
        ...removeActions,
        ...templateAttachmentActions,
        Observable.of(sequenceStageInstanceUpdate(stageInstance, templateAppliedInstance)),
      );
    });

export default [
  sequenceStageInstanceCreateEpic,
  sequenceStageInstanceListEpic,
  sequenceStageInstanceDeleteEpic,
  sequenceStageInstanceUpdateEpic,
  sequenceStageInstanceTestEpic,
  sequenceStageInstanceAttachmentAddEpic,
  // sequenceStageInstanceAttachmentRemoveEpic,
  sequenceStageInstanceAttachmentsListEpic,
  sequenceStageInstanceAttachmentsApplyEpic,
  sequenceStageInstanceTemplateApplyEpic,
];
