import React from 'react';
import moment from 'moment';
import classnames from 'classnames';
import { last, map, throttle, compact, keyBy } from 'lodash';

// TODO(SHIN): Beta - re-implement when we can attach before created draft
// import Attachment from '@src/components/Icons/Attachment.svg';
import RecipientsPane, { RecipientEditorType } from './components/RecipientsPane/RecipientsPane';
import CommentPane from './components/CommentPane';
import ComposePane from '@src/components/Compose/ComposePane';

import {
  Me,
  Account,
  Draft,
  Thread,
  AttachmentMeta,
  FileType,
  CommandControlType,
  Action,
  ContactGroup,
  Contact,
  DraftStatus,
  Features,
  ThreadCommitMeta,
  Resource,
  Preferences,
  OnboardingType,
  Attachment as AttachmentType,
  Template,
  TemplateType,
} from '@src/types';

import { Configuration, replaceDraftSender, bodyWithoutSignatureAndHistory,
  isDraftSending,
  attachmentsToDelete,
  plainTextDraftBodyContents,
} from '@src/lib/draft';
import { fileFormat, AttachmentUploadOptions, convertToOptimistic } from '@src/lib/attachment';
import { mongoId } from '@src/lib/crypt';

import { getHotkey, HotkeyAction, checkNextHotkeyChange } from '@src/lib/hotkeys';
import './Composer.scss';
import { FakeDOM } from '@src/lib/html';
import SubjectPane from '@src/components/Compose/SubjectPane/SubjectPane';
import browserHistory from '@src/lib/browserHistory';
import { hotkeyUpdate, hotkeySuggest } from '@src/ducks/hotkeys';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import { ApiError } from '@src/api/base';
import Reply from '@src/components/Icons/Reply.svg';
import ReplyAll from '@src/components/Icons/ReplyAll.svg';
import Forward from '@src/components/Icons/Forward.svg';
import CommentEditor from './components/CommentEditor';
import {
  SharedThreadCreateMeta, PING_INTERVAL, SharedThreadActivityCreateMeta,
} from '@src/ducks/sharedThreads';
import { CommentCreateMeta } from '@src/ducks/comments';
import Attachment from '@src/components/Icons/Attachment.svg';
import OnboardingTip, { AnchorPosition } from '@src/components/Onboarding/components/OnboardingTip/OnboardingTip';
import { DraftSendMeta, draftCreateConfiguration } from '@src/ducks/drafts';
import { SharedThreadActivityType, SharedThreadPermissionScope } from '@src/types/sharedThreads';
import { ConfirmationError, ConfirmationPositon } from '@src/ducks/toasts';
import { onboardingPatch } from '@src/ducks/me';
import { getOnboardingActions } from '@src/lib/OnboardingDispatcher';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface Props {
  me: Me;
  accounts: Account[];

  className?: string;
  draft?: Draft;
  thread?: Thread;
  onExpand?: (message?: string) => void;
  showFullHeight?: boolean;
  hidePopout?: boolean;
  onDelete?: () => void;
  onSend?: () => void;
  hideActions?: boolean;
  hideCommentEditor?: boolean;
  rsbOnboarding?: OnboardingType;
}

export interface StateProps {
  cloudAttachments: boolean;
  noBranding: boolean;
  getContact: (email: string) => Contact;
  features: Features;
  hotkeyActions: { [action: string]: number };
  schedulingUrl?: string;
  commandControlType?: CommandControlType;
  attachmentMetas?: Array<Resource<AttachmentMeta>>;
  showSendOnboarding?: boolean;
  preferences: Preferences;
  selectedSharedThread?: string;
  rsbOnboarding?: OnboardingType;
  showCommentOnboarding?: boolean;
  autofocus?: boolean;
  draftAccount?: Account;
  draftParentAccount?: Account;

  // Used for comment creation identification
  sharedThreadDraft?: string;
}

export interface DispatchProps {
  update: (draft: Draft) => void;
  send: (draft: DraftSendMeta) => void;
  upload: (attachment: AttachmentMeta) => void;
  delete: (draft: Draft) => void;
  removeAttachment: (attachment: AttachmentMeta) => void;
  createDraftWithConfiguration: (configuration: Configuration) => void;
  commandControlShow: (type: CommandControlType) => void;
  dispatchAction: (action: Action<any, any>) => void;
  addContactGroup: (draft: Draft, list: string, contactGroup: string) => void;
  updateHotkey: (hotkeyAction: HotkeyAction) => void;
  submitCommit: (meta: ThreadCommitMeta) => void;
  setToast: (error: Error) => void;
  createSharedThread: (meta: SharedThreadCreateMeta) => void;
  createComment: (meta: CommentCreateMeta) => void;
  updateOnboarding: (meta: OnboardingType) => void;
  createSharedThreadActivity: (meta: SharedThreadActivityCreateMeta) => void;
  saveAsTemplate: (meta: Template) => void;
}

interface State {
  expanded: boolean;
  showHistory: boolean;
  showComment: boolean;
  recipientsExpanded: boolean;

  oldContentEditablePosition?: Range;
  confirmProps?: ConfirmProps;
  focused: boolean;

  // Used to show onboarding tip
  commentFocused?: boolean;
}

// Identify when to show
class Composer extends React.PureComponent<Props & StateProps & DispatchProps, State>  {
  private recipientsPane: RecipientsPane;
  private composePane: ComposePane;
  private subjectPane: SubjectPane;
  private ref: HTMLDivElement;
  private commentEditor: any;
  private recordTyping: any;

  constructor(props: Props & StateProps & DispatchProps) {
    super();

    const newDraft = (props.draft && !props.draft.threadV2) || false;
    this.recordTyping = throttle(
      () => {
        const { selectedSharedThread, createSharedThreadActivity } = this.props;

        if (selectedSharedThread) {
          createSharedThreadActivity({
            type: SharedThreadActivityType.Reply,
            sharedThread: selectedSharedThread,
          });
        }
      },
      PING_INTERVAL,
    );

    this.state = {
      showHistory: false,
      expanded: props.draft !== undefined,
      showComment: false,
      recipientsExpanded: newDraft,
      focused: false,
      commentFocused: false,
    };
  }

  componentDidMount() {
    const newDraft = this.props.draft;
    if (newDraft && newDraft.created === newDraft.updated) {
      if (newDraft.to.length === 0) {
        this.focusRecipientEditor();
      } else {
        this.composePane.contentEditable.focus();
      }
    }
  }

  UNSAFE_componentWillReceiveProps(nextProps: Props & StateProps & DispatchProps) {
    const { draft, onExpand, onSend } = this.props;

    if (!nextProps.draft) {
      this.setState({ expanded: false });
      return;
    }

    // HACK(SHIN): This is here to close popped out window and guarantee that
    // action has run and been broadcast
    if (onSend && draft && isDraftSending(draft)) {
      onSend();
    }

    if (nextProps.draft && (!draft || draft.id !== nextProps.draft.id)) {
      // Expands
      this.setState(
        {
          expanded: true,
          showHistory: false,
          recipientsExpanded: !nextProps.draft.thread,
        },
        () => {
          if (onExpand && nextProps.draft) {
            onExpand(nextProps.draft.message);
          }
        },
      );
    }
  }

  componentDidUpdate(oldProps: StateProps & Props, oldState: State) {
    if (!!oldProps.commandControlType && !this.props.commandControlType &&
      !this.ref.contains(document.activeElement)
    ) {

      // Restore cursor position if focus is not on shared thread input
      if (document.activeElement?.className !== 'url-input') {
        this.restoreCursorPosition();
      }
    }

    // Handle hotkeys for cc and bcc
    const newActions = this.props.hotkeyActions;
    const oldActions = oldProps.hotkeyActions;
    if (oldActions !== newActions) {
      this.handleHotkeyActionsChange(oldActions, newActions);
    }

    if (oldState.expanded && !this.state.expanded) {
      document.body.focus();
    }

    // Handle initial focus events
    const oldDraft = oldProps.draft;
    const newDraft = this.props.draft;
    const draftChanged = !!newDraft && (!oldDraft || oldDraft && oldDraft.id !== newDraft.id);
    if (newDraft && newDraft.created === newDraft.updated && draftChanged) {
      if (newDraft.to.length === 0)  {
        this.focusRecipientEditor();
      } else {
        this.composePane.contentEditable.focus();
      }
    }

    // Stop previous on change
    // TOOD(SHIN): Investigate issue for draft sending as empty culprit?
    if (draftChanged && this.composePane && this.composePane.contentEditable) {
      this.composePane.contentEditable.cancelOnChange();
    }
  }

  // HACK(SHIN): Uses getWrappedInstance until we can upgrade to > React 16.3
  handleCommentEditorRef = (ref: any) => {
    if (ref) {
      this.commentEditor = ref.getWrappedInstance();
    }
  }

  restoreCursorPosition = () => {
    const selection = window.getSelection();
    if (selection && this.state.oldContentEditablePosition) {
      selection.removeAllRanges();
      selection.addRange(this.state.oldContentEditablePosition);
    }

    this.setState({ oldContentEditablePosition: undefined });
  }

  saveCursorPosition = () => {
    const selection = window.getSelection();
    if (!selection) {
      return;
    }
    if (!!selection.rangeCount) {
      this.setState({ oldContentEditablePosition: selection.getRangeAt(0) });
    }
  }

  onConfigureClick = (e: React.MouseEvent<HTMLElement>) => {
    const target = e.currentTarget;
    const action = target && target.getAttribute('data-action') ||
      (e.target as HTMLElement).id || Configuration.ReplyAll;

    if (action) {
      this.configureComposer(action as Configuration);
    }
  }

  configureComposer = (configuration: Configuration) => {
    const { createDraftWithConfiguration, dispatchAction, draft } = this.props;
    if (!draft) {
      switch (configuration) {
        case Configuration.Reply:
          dispatchAction(hotkeySuggest(HotkeyAction.Reply));
          break;
        case Configuration.ReplyAll:
          dispatchAction(hotkeySuggest(HotkeyAction.ReplyAll));
          break;
        case Configuration.Forward:
          dispatchAction(hotkeySuggest(HotkeyAction.Forward));
          break;
        default:
          break;
      }
    }

    switch (configuration) {
      case Configuration.Reply:
      case Configuration.ReplyAll:
      case Configuration.Forward:
        this.setState({ expanded: true });
        createDraftWithConfiguration(configuration);
        break;
      case Configuration.Comment:
        this.setState({
          showComment: true,
        });
        return;
      default:
        return;
    }
  }

  handleHotkeyActionsChange = (
    oldActions: {[action: string]: number},
    newActions: {[action: string]: number},
  ) => {
    const { recipientsExpanded } = this.state;
    const { preferences, draft, thread, dispatchAction, commandControlShow } = this.props;

    // Handle CC toggle
    if (checkNextHotkeyChange(HotkeyAction.Cc, newActions, oldActions)) {
      const expandCc = () => this.recipientsPane.setState(
        { showCc: true },
        () => { this.recipientsPane.focus(RecipientEditorType.Cc); },
      );

      // Handle expansion and focus
      if (!recipientsExpanded) {
        this.setState({ recipientsExpanded: true }, () => expandCc());
      } else {
        expandCc();
      }
    }

    // Handle BCC toggle
    if (checkNextHotkeyChange(HotkeyAction.Bcc, newActions, oldActions)) {
      // Call recipients pane bcc
      const expandBcc = () => this.recipientsPane.setState(
        { showBcc: true },
        () => { this.recipientsPane.focus(RecipientEditorType.Bcc); },
      );

      // Handle expansion and focus
      if (!recipientsExpanded) {
        this.setState({ recipientsExpanded: true }, () => expandBcc());
      } else {
        expandBcc();
      }
    }

    // Handle BCC toggle
    if (checkNextHotkeyChange(HotkeyAction.ReplyBCC, newActions, oldActions)) {
      if (!thread) {
        return;
      }

      dispatchAction(draftCreateConfiguration(Configuration.MoveBcc));
    }

    // Handle Send hotkey
    if (checkNextHotkeyChange(HotkeyAction.Send, newActions, oldActions)) {
      // Send hotkey is the secondary  +  +  when preference is not on
      const sendToggled = preferences.sendAndArchive;

      this.handleSend(sendToggled);
    }

    // Handle Send and Archive hotkey
    if (checkNextHotkeyChange(HotkeyAction.SendAndArchive, newActions, oldActions)) {
      // Send and archive is the secondary  +  +  when preference is not on
      const sendAndArchiveToggled = !preferences.sendAndArchive;

      this.handleSend(sendAndArchiveToggled);
    }

    // Handle Send hotkey
    if (checkNextHotkeyChange(HotkeyAction.Discard, newActions, oldActions)) {
      this.handleDelete(true);
    }

    // Restore cursor position if possible
    if (checkNextHotkeyChange(HotkeyAction.CopySchedulingUrl, newActions, oldActions)) {
      // Show Scheduling Pages
      commandControlShow(CommandControlType.SchedulingPages);
      return;
    }

    // Handle comment hotkey
    if (checkNextHotkeyChange(HotkeyAction.Comment, newActions, oldActions)) {
      if (this.commentEditor) {
        this.commentEditor.focus();
      }
    }

    // Handle template save hotkey
    if (checkNextHotkeyChange(HotkeyAction.SaveAsTemplate, newActions, oldActions)) {
      this.handleTemplateSave();
    }

    // Handle upload hotkey
    if (checkNextHotkeyChange(HotkeyAction.AddAttachment, newActions, oldActions)) {
      const uploadButton = this.composePane.uploadButton;
      if (uploadButton) {
        uploadButton.input.click();
      }
    }

    // Handle from address hotkey
    if (checkNextHotkeyChange(HotkeyAction.ChangeFromAddress, newActions, oldActions)) {
      this.setState(
        { recipientsExpanded: true },
        () => {
          const fromDropdown = this.recipientsPane.fromDropdown;
          if (fromDropdown) {
            fromDropdown.handleOpen();
          }
        },
      );
    }

    // Handle popout draft
    if (checkNextHotkeyChange(HotkeyAction.PopoutDraft, newActions, oldActions)) {
      if (draft) {
        window.open(`/popout/drafts/${draft.id}`, '_blank', 'height=500,width=800');
      }
    }
  }

  handleRecipientsChange = (list: string, recipients: string[]) => {
    const newState = {};
    newState[list] = recipients;
    this.setState(newState);

    if (this.props.draft) {
      // Update recipients
      const draft = {
        ...this.props.draft,
        ...newState,
      };

      // Update draft
      this.props.update(draft);
    }
  }

  handleContactGroupAdd = (list: string, contactGroup: ContactGroup) => {
    const { addContactGroup, draft } = this.props;
    if (draft) {
      addContactGroup(draft, list, contactGroup.id);
    }
  }

  handleSenderSelect = (email: string) => {
    const { draft, update, accounts } = this.props;
    // Update draft from address

    if (draft) {
      // Update draft with new sender/recipient information
      const updated = replaceDraftSender(draft, accounts, email);
      update(updated);

      // Handle restoring contenteditable position if triggered from hotkey
      if (this.state.oldContentEditablePosition) {
        this.restoreCursorPosition();
      }
    }
  }

  handleSubjectChange = (subject: string) => {
    if (this.props.draft && this.props.draft.subject !== subject) {
      const draft = {
        ...this.props.draft,
        subject,
      };
      this.props.update(draft);
    }
  }

  // Updates draft immediately
  handleBodyChange = (html: string) => {
    if (this.props.draft) {
      this.recordTyping();

      const draft = {
        ...this.props.draft,
        body: html,
      };

      // Removes calendar invite when deleted from the draft
      // TODO(SHIN): Figure out how to delete calendarInvite. Send null?
      // Abstract to lib level
      if (this.ref && !this.ref.getElementsByClassName('pm-calendar-invite').length &&
        draft.calendarInvite) {
        draft.calendarInvite = null;
      }

      this.props.update(draft);
    }
  }

  // Figure out which send clicking the send button triggers
  handlePrimarySendClick = () => {
    const { preferences, updateHotkey, dispatchAction } = this.props;
    dispatchAction(hotkeySuggest(HotkeyAction.Send));

    if (preferences.sendAndArchive) {
      updateHotkey(HotkeyAction.SendAndArchive);
    } else {
      updateHotkey(HotkeyAction.Send);
    }
  }

  // Sends draft
  // TODO(SHIN): Abstract to epic if possible
  handleSend = (toggleArchive: boolean = false) => {
    const { attachmentMetas, setToast, onSend, rsbOnboarding } = this.props;
    const draft = this.props.draft;
    if (!draft) {
      return;
    }

    if (rsbOnboarding === OnboardingType.SendMessage) {
      logAmplitudeEvent(AmplitudeEvents.TourStepOneSend);
    }

    // Update draft body right before sending
    const contentEditable = this.composePane && this.composePane.contentEditable;
    if (contentEditable) {
      contentEditable.resetOnChangeDebounce();
      draft.body = contentEditable.getBody();
    }

    // Show warning and prevent send if message template placeholders exist
    const templateVariables = this.ref && this.ref.getElementsByClassName('data-pm-var');
    if (this.ref && templateVariables.length > 0) {
      const placeholdersCopy = 'placeholder' + (templateVariables.length > 1 ? 's' : '');
      const message = `Your draft contains ${templateVariables.length} unfilled ` +
        placeholdersCopy +
        ' left in your message.' +
        ` Please replace or remove the ${placeholdersCopy} before sending.`;
      setToast(new Error(message));
      return;
    }

    // Add check for send later
    if (draft.status === DraftStatus.Scheduled && draft.scheduled) {
      const scheduledDate = moment(draft.scheduled).calendar(new Date(), {
        sameDay: '[today] [at] h:mm A',
        nextDay: '[tomorrow] [at] h:mm A',
        nextWeek: 'dddd [at] h:mm A',
        sameElse: 'mmm, ddd [at] h:mm A',
      });
      const message = 'This draft is scheduled to send ' +
        scheduledDate +
        '.\n\nWould you like to send this draft now?';
      const confirmProps: ConfirmProps = {
        title: 'Scheduled Draft',
        message,
        confirmCopy: 'Send Now',
        onConfirm: () => {
          this.setState({ confirmProps: undefined });
          this.props.send({ draft: { ...draft, scheduled: null }, toggleArchive: false });

          // Handle send to dismiss popout window
          if (onSend) {
            onSend();
          }
        },
        onCancel: () => this.setState({ confirmProps: undefined }),
      };

      this.setState({ confirmProps });
      return;
    }

    // TODO(SHIN: Handling cloud attachments
    const attachmentErrors = attachmentMetas &&
      attachmentMetas.filter(resource => !!resource.error);
    // Handle any attachment errors before sending
    if (attachmentErrors && attachmentErrors.length) {
      // This identifies attachment metas that will be deleted downstream so we dont handle errors
      const optimisticMetas = compact(attachmentErrors.map(a => a.data))
        .map(a => convertToOptimistic(a, draft.id));
      const toDelete = attachmentsToDelete({
        ...draft,
        attachments: draft.attachments.concat(optimisticMetas),
      });
      const deleteMap = keyBy(toDelete, 'attachment');
      const filteredErrors = attachmentErrors.filter(a => a.data && !deleteMap[a.data.attachment]);

      // Grab first Error that wasn't removed from draft to block UI
      const first = filteredErrors[0];
      if (first) {
        const attachmentName = first.data && first.data.filename || '';
        const message = `Your attachment "${attachmentName}" failed to attach to this message. ` +
          'Please remove or try to attach this file before sending!';
        const firstError = first && first.error;
        const mainErrorPrefix = message + '\nError: ';

        // Handle additional content to display for the error
        if (firstError) {
          if ((firstError as ApiError).errors) {
            const suffix = map((firstError as ApiError).errors, e => e).join(' ');
            setToast(new Error(mainErrorPrefix + suffix));
            return;
          }

          const error = new Error(mainErrorPrefix + firstError.message);
          setToast(error);

          return;
        }
      }
    }

    // Make sure that there is at least on recipient
    const recipients = draft.to.concat(draft.cc).concat(draft.bcc);
    if (!recipients.length) {
      const error = 'You must add a recipient before sending a message.';
      setToast(new Error(error));
      return;
    }

    // Recipients length check blocks over 100 recipients
    if (recipients.length > 100) {
      const error = 'You cannot have more than 100 recipients in a message.';
      setToast(new Error(error));
      return;
    }

    // Validator to check attachment size for draft
    let totalAttachmentSize = 0;
    draft.attachments.forEach((a) => {
      totalAttachmentSize += a.length;
    });
    if (attachmentMetas) {
      attachmentMetas.forEach((a) => {
        const size = a.data && a.data.file && a.data.file.size;
        if (size) {
          totalAttachmentSize += size;
        }
      });
    }
    if (totalAttachmentSize > 25000000) {
      const error = 'You cannot send a draft with more than 25MB.';
      setToast(new Error(error));
      return;
    }

    // Show warning if no attached
    const bodyString = this.composePane.contentEditable.getBody();
    const mainBodyString = plainTextDraftBodyContents(bodyString, draft.id);
    const regex = /(attached|attachment|attach)/;
    const metaLength = this.props.attachmentMetas?.length || 0;
    const attachmentsLength = draft.attachments.length;
    const allAttachmentLength = metaLength + attachmentsLength;
    const matches = mainBodyString?.match(regex);
    if (allAttachmentLength === 0 && !!matches?.length) {
      const confirmProps: ConfirmProps = {
        title: 'No attachments',
        message: `You wrote the word "${matches[0]}" but you did not attach any files on this message. Would you like to send anyway?`,
        confirmCopy: 'Ok',
        cancelCopy: 'Cancel',
        onConfirm: () => {
          this.setState({ confirmProps: undefined });
          this.props.send({ draft: { ...draft, scheduled: null }, toggleArchive: false });

          // Handle send to dismiss popout window
          if (onSend) {
            onSend();
          }
        },
        onCancel: () => this.setState({ confirmProps: undefined }),
      };

      this.setState({ confirmProps });
      return;
    }

    // Show warning if no subject
    if (draft.subject === '') {
      const confirmProps: ConfirmProps = {
        title: 'No Subject',
        message: 'You did not include a subject in this message. Would you like to send anyway?',
        confirmCopy: 'Ok',
        cancelCopy: 'Cancel',
        onConfirm: () => {
          this.setState({ confirmProps: undefined });
          this.props.send({ draft: { ...draft, scheduled: null }, toggleArchive: false });

          // Handle send to dismiss popout window
          if (onSend) {
            onSend();
          }
        },
        onCancel: () => this.setState({ confirmProps: undefined }),
      };

      this.setState({ confirmProps });
      return;
    }

    // Send if all checks pass and notify parent
    if (draft
        && draft.calendarInvite
        && draft.calendarInvite.recipients
        && draft.calendarInvite.recipients.length) {
      logAmplitudeEvent(AmplitudeEvents.SendCalendarInvite);
    }
    this.props.send({ draft, toggleArchive });
  }

  handleTemplateSave = () => {
    const { draft, saveAsTemplate } = this.props;
    if (!draft) {
      return;
    }

    // Extract Body from draft
    const bodyString = this.composePane.contentEditable.getBody();
    const mainBodyString = bodyWithoutSignatureAndHistory(bodyString, draft.id);

    // Save as New Template
    const newTemplate: Template = {
      id: '',
      name: draft.subject,
      subject: draft.subject,
      body: mainBodyString,
      user: '',
      attachments: draft.attachments,
      created: new Date(),
      updated: new Date(),
      type: TemplateType.Message,
    };

    saveAsTemplate(newTemplate);

    return;
  }

  handleSendLater = () => {
    const { rsbOnboarding } = this.props;

    this.props.commandControlShow(CommandControlType.SendLater);
    this.props.dispatchAction(hotkeySuggest(HotkeyAction.SendLater));

    if (rsbOnboarding === OnboardingType.SendLater) {
      logAmplitudeEvent(AmplitudeEvents.TourStepTwo, { skipped: false });
    }
  }

  handleFollowUp = () => {
    this.props.dispatchAction(hotkeySuggest(HotkeyAction.FollowUpReminder));
    this.props.commandControlShow(CommandControlType.FollowUp);
  }

  handleReplyClick = () => {
    this.configureComposer(Configuration.ReplyAll);
  }

  handleCommentClick = () => {
    this.configureComposer(Configuration.Comment);
  }

  // TODO(SHIN): Figure out where to identify cloud attachment
  handleUpload = (files: File[], options: AttachmentUploadOptions) => {
    const { draft, upload } = this.props;

    // Block ability to attach files larger than 25 MB.
    if (files.filter(file => file.size > 25000000).length) {
      const confirmProps: ConfirmProps = {
        title: 'Error Uploading Attachment',
        message: 'You cannot upload an attachment greater than 25 MB.',
        confirmCopy: 'Ok',
        onConfirm: () => {
          this.setState({ confirmProps: undefined });
        },
      };

      this.setState({ confirmProps });
      return;
    }

    if (draft) {
      files.forEach((file) => {
        // Create meta
        const inline = fileFormat(file.type) === FileType.Image;
        const attachment = {
          file,
          resourceId: draft.id,
          attachment: mongoId(),
          inline: !options.shiftKey && !options.ignoreInline && inline,
          cloud: !options.shiftKey && draft.track && draft.trackLinks
            ? !inline && this.props.cloudAttachments
            : false,
          cloudUrl: URL.createObjectURL(file),
        };

        upload(attachment);
      });
    }
  }

  handleAttachmentRemove = (id: string) => {
    if (this.props.draft) {
      const attachment = {
        resourceId: this.props.draft.id,
        attachment: id,
        file: undefined,
      };

      this.props.removeAttachment(attachment);
    }
  }

  handleRecipientsPaneRef = (ref: RecipientsPane) => {
    this.recipientsPane = ref;
  }

  handleRef = (ref: HTMLDivElement) => {
    this.ref = ref;
  }

  handleDelete = (shortcut?: any) => {
    // Show suggestion if deleted without hotkey
    if (shortcut !== true) {
      this.props.dispatchAction(hotkeySuggest(HotkeyAction.Discard));
    }

    if (this.props.draft) {
      const confirmProps: ConfirmProps = {
        title: 'Deleting Draft',
        message: 'Are you sure you want to delete this draft?',
        confirmCopy: 'Delete',
        onConfirm: () => {
          this.setState({ confirmProps: undefined });
          if (this.props.draft) {
            this.props.delete(this.props.draft);
            if (this.props.onDelete) {
              this.props.onDelete();
            }
            this.setState({ expanded: false });
          }
        },
        onCancel: () => this.setState({ confirmProps: undefined }),
      };

      this.setState({ confirmProps });
    }
  }

  // TODO(SHIN: See if we can remember cursor position using CommandControl component
  handleKeyDown = (event: React.KeyboardEvent<HTMLElement>) => {
    const { commandControlShow, dispatchAction,
      features, updateHotkey, draft, setToast, update } = this.props;
    let handled = false;

    // NOTE(SHIN): We stop propagation since any keystroke could
    // result in a bunch of unintended hotkeyss getting triggered (New Draft, Delete, Archive)
    event.stopPropagation();

    const hotkey = getHotkey(event);
    switch ((hotkey && hotkey.name) || event.keyCode) {
      case HotkeyAction.Discard:
        handled = true;
        this.handleDelete(true);
        break;
      case HotkeyAction.SendLater:
        if (hotkey && hotkey.action && features.sendLater) {
          this.saveCursorPosition();
          handled = true;
          dispatchAction(hotkey.action);
        }
        break;
      case HotkeyAction.CancelFollowup: // Cancels Followup reminder
        if (draft && draft.waiting) {
          update({
            ...draft,
            waiting: null,
          });
          setToast(new ConfirmationError(
            'Your followup has been canceled!',
            true,
            ConfirmationPositon.TopCenter,
          ));
          handled = true;
        }
        break;
      case HotkeyAction.FollowUpReminder:
        if (hotkey && hotkey.action && features.followUp) {
          this.saveCursorPosition();
          handled = true;
          dispatchAction(hotkey.action);
        }
        break;
      case HotkeyAction.CalendarInvite:
        if (hotkey && hotkey.action) {
          this.saveCursorPosition();
          dispatchAction(hotkey.action);
          handled = true;
        }
        break;
      case HotkeyAction.Send:
        if (hotkey && hotkey.name) {
          updateHotkey(HotkeyAction.Send);
          handled = true;
        }
        break;
      case HotkeyAction.SendAndArchive:
        if (hotkey && hotkey.name) {
          updateHotkey(HotkeyAction.SendAndArchive);
          handled = true;
        }
        break;
      case HotkeyAction.MessageTemplates:
        if (features.messageTemplates) {
          this.saveCursorPosition();
          handled = true;
          commandControlShow(CommandControlType.Templates);
        }
        break;
      case HotkeyAction.ToggleCommandControl:
        commandControlShow(CommandControlType.Draft);
        this.saveCursorPosition();
        handled = true;
        break;
      case HotkeyAction.ReplyBCC:
        if (hotkey && hotkey.name) {
          updateHotkey(HotkeyAction.ReplyBCC);
          handled = true;
        }
        break;
      case HotkeyAction.Cc:
        if (hotkey && hotkey.name) {
          updateHotkey(HotkeyAction.Cc);
          handled = true;
        }
        break;
      case HotkeyAction.Bcc:
        if (hotkey && hotkey.name) {
          updateHotkey(HotkeyAction.Bcc);
          handled = true;
        }
        break;
      case HotkeyAction.CopySchedulingUrl:
        this.saveCursorPosition();
        commandControlShow(CommandControlType.SchedulingPages);
        break;
      default:
        break;
    }

    // Do not handle key if handled by hotkey
    if (handled) {
      event.preventDefault();
    }
  }

  handlePreviewClick = () => {
    this.setState({ recipientsExpanded: true });
  }

  // Only toggles recipientsExpanded when not expanded to prevent re-renders of ContentEditable
  handleComposeSelect = () => {
    const { showFullHeight } = this.props;
    if (this.state.recipientsExpanded && !showFullHeight) {
      this.setState({ recipientsExpanded: false });
    }
  }

  handleShowHistory = () => {
    this.setState({ showHistory: true });
  }

  handleComposePaneRef = (ref: ComposePane) => {
    this.composePane = ref;
  }

  handleSubjectPaneRef = (ref: SubjectPane) => {
    this.subjectPane = ref;
  }

  focusContentEditable = (selectFirstPlaceholder?: boolean, selectLastPlaceholder?: boolean) => {
    if (this.composePane) {
      this.composePane.focusContentEditable(selectFirstPlaceholder, selectLastPlaceholder);
    }
  }

  focusSubjectPane = (selectFirstPlaceholder?: boolean, selectLastPlaceholder?: boolean) => {
    if (this.subjectPane) {
      this.subjectPane.focus(selectFirstPlaceholder, selectLastPlaceholder);
    }
  }

  handleRecipientsPaneTabPress = () => {
    const { draft } = this.props;
    const {
      showComment, recipientsExpanded,
    } = this.state;

    const newDraft = !draft || !draft.thread;
    const subject = this.props.draft && this.props.draft.subject;
    const showSubject = !showComment && (!newDraft && recipientsExpanded || newDraft);
    const variables = subject && subject.match(/{{[^{}]+}}/g);

    if (showSubject) {
      if (variables) {
        this.focusSubjectPane(true, false);
      } else {
        this.focusSubjectPane();
      }

      return;
    }

    this.focusContentEditable();
  }

  focusRecipientEditor = () => {
    if (this.recipientsPane) {
      this.recipientsPane.focus();
    }
  }

  handleContentEditableTabPress = (event: React.KeyboardEvent<HTMLDivElement> | KeyboardEvent) => {
    const subject = this.props.draft && this.props.draft.subject;
    const variables = subject && subject.match(/{{[^{}]+}}/g);

    if (event.shiftKey && variables) {
      this.focusSubjectPane(false, true);
      return true;
    }

    if (variables) {
      this.focusSubjectPane(true, false);
      return true;
    }

    if (event.shiftKey) {
      // Override handler to subject pane if in indented block
      if (this.composePane && this.composePane.contentEditable &&
        this.composePane.contentEditable.isSelectionIndented()) {
        return false;
      }

      this.focusSubjectPane();
      return true;
    }

    return false;
  }

  handleSubjectTabPress = (e: React.KeyboardEvent<HTMLElement>) => {
    const { recipientsExpanded } = this.state;
    const body = this.props.draft && this.props.draft.body;
    const doc = FakeDOM().createElement('div');
    doc.innerHTML = body || '';
    const variables = doc.getElementsByClassName('data-pm-var');

    if (e.shiftKey && variables.length) {
      this.focusContentEditable(false, true);
      return;
    }

    if (variables.length) {
      this.focusContentEditable(true);
      return;
    }

    if (e.shiftKey) {
      if (recipientsExpanded) {
        this.focusRecipientEditor();
        return;
      }

      this.setState({ recipientsExpanded: true }, () => {
        this.focusRecipientEditor();
      });

      return;
    }

    this.focusContentEditable();
  }

  handleSchedulingUrl = () => {
    const { schedulingUrl } = this.props;
    if (schedulingUrl) {
      this.props.dispatchAction(hotkeyUpdate(HotkeyAction.CopySchedulingUrl));
    } else {
      browserHistory.push('/settings/calendar');
    }
  }

  handleTrack = () => {
    const { draft, update, preferences } = this.props;
    if (draft) {
      const newTrackValue = !draft?.track;
      const newTrackLinksValue = preferences.linkTracking && newTrackValue;

      update({
        ...draft,
        track: newTrackValue,
        trackLinks: newTrackLinksValue,
      });
    }
  }

  // TODO(SHIN): Move pending comments to redux state level
    // with a reference id to sharedthread/thread
  handleComment = (body: string, attachments: AttachmentType[]) => {
    const {
      thread,
      createComment,
      selectedSharedThread,
      createSharedThread,
      setToast,
      rsbOnboarding,
      dispatchAction,
      sharedThreadDraft,
    } = this.props;
    const lastMessage = thread && last(thread.messages);

    // Block comment when last message is still optimistic
    if (lastMessage && !!lastMessage.optimistic) {
      setToast(
        new Error('Please wait for your last messsage to finish sending' +
        ' before commenting on this conversation.'),
      );
      return;
    }

    // Stop comment upload when still uploading attachments
    if (attachments.filter(attachment => attachment.progress < 1).length  > 0) {
      setToast(new Error('Please wait for your attachments '
      + 'to finish uploading before posting your comment.'));
      return;
    }

    // Update onboarding for comment
    if (rsbOnboarding && rsbOnboarding === OnboardingType.Comment) {
      dispatchAction(onboardingPatch({
        skipped: false,
        type: { steps: { [OnboardingType.Comment]: true } },
      }));
      logAmplitudeEvent(AmplitudeEvents.TourStepEleven, { skipped: false });
    }

    getOnboardingActions().next(OnboardingType.Comment);

    if (lastMessage && selectedSharedThread) {
      const lastMessageRfcId = lastMessage.rfcId || lastMessage.id;
      const computedRfcId = sharedThreadDraft && `<${sharedThreadDraft}@polymail.io>` ||
        lastMessageRfcId;
      createComment({
        thread: selectedSharedThread,
        rfcId: computedRfcId,
        draft: sharedThreadDraft,
        body,
        attachments,
      });
    } else if (lastMessage && thread) {
      const computedRfcId = lastMessage.rfcId || lastMessage.id;
      createSharedThread({
        thread: thread.id,
        permissions: {
          scope: SharedThreadPermissionScope.Team,
        },
        comments: [{
          rfcId: computedRfcId,
          body,
          attachments,
        }],
      });
    }
  }

  handleCommentFocused = () => {
    this.props.updateOnboarding(OnboardingType.CreateComment);
    this.setState({ commentFocused: true });
  }

  handleCommentFocus = () => {
    if (this.commentEditor) {
      this.commentEditor.focus();
    }
  }

  handleErrorDismiss = () => {
    // Extract propeties
    const { draft, update } = this.props;
    if (!draft) {
      return;
    }

    // Remove error from draft and reset status to draft
    const updated = {
      ...draft,
      error: undefined,
    };

    update(updated);
  }

  render() {
    const {
      expanded, showComment,
      recipientsExpanded, showHistory, confirmProps,
      commentFocused,
    } = this.state;
    const {
      accounts,
      draft,
      thread,
      showFullHeight,
      dispatchAction,
      noBranding,
      getContact,
      features,
      showSendOnboarding,
      preferences,
      hidePopout,
      hideActions,
      showCommentOnboarding,
      hideCommentEditor,
      autofocus,
      draftAccount,
      draftParentAccount,
      className,
    } = this.props;

    const useLinkPreview = !preferences.disableLinkPreviews;
    const showHistoryButton = draft && !showHistory && draft.body
      && draft.body.includes('gmail_quote');
    const to = (draft && draft.to) || [];
    const cc = (draft && draft.cc) || [];
    const bcc = (draft && draft.bcc) || [];
    const subject = (draft && draft.subject) || '';
    const sender = (draftAccount && draftAccount.email) || '';
    const lastMessage = thread && last(thread.messages.filter(message => !message.sent));
    const showReplyAll =
      lastMessage && lastMessage.to.concat(lastMessage.cc.concat(lastMessage.bcc)).length > 1;

    const newDraft = !draft || !draft.thread;
    const showSubject = !showComment && (!newDraft && recipientsExpanded || newDraft);
    let popoutLink;
    const onboardingMentionCopy = (
      <span>
        Give context for the future by leaving comments on a conversation.
        You can also start internal discussions by @mentioning teammates in a comment.
        <br/>
        <br />
        Try posting a comment for yourself below
      </span>
    );

    if (draft) {
      popoutLink = `/popout/drafts/${draft.id}`;
    }

    // TODO(SHIN): Disable hide add-ons for RecipientsPane for beta
    return (
      <div
        className={classnames('composer', {
          expanded,
          'hide-history': !showHistory && !!showHistoryButton,
          // make signature always visible
          // https://polymail.atlassian.net/browse/PM-244
          'hide-signature': false,
          'full-height': showFullHeight,
        },                    className)}
        onKeyDown={this.handleKeyDown}
        ref={this.handleRef}
      >
        {confirmProps && <Confirm {...confirmProps} />}
        {!expanded && !features.comments && <div className="preview-actions">
          <button className="options" onClick={this.onConfigureClick}>
            Click Here to
            <span id={Configuration.Reply}>Reply</span>,
            <span id={Configuration.ReplyAll}>Reply All</span>, or
            <span id={Configuration.Forward}>Forward</span>
          </button>
          <Attachment />
        </div>}
        {!hideActions && features.comments && <div className="actions">
          <div className="buttons">
            <button data-action={Configuration.Reply} onClick={this.onConfigureClick}>
              <Reply />
            </button>
            {showReplyAll && <button
              data-action={Configuration.ReplyAll}
              onClick={this.onConfigureClick}
            >
              <ReplyAll />
            </button>}
            <button  data-action={Configuration.Forward} onClick={this.onConfigureClick}>
              <Forward />
            </button>
          </div>
        </div>}
        {!expanded && features.comments && !hideCommentEditor && (
          <div className="preview">
            {showCommentOnboarding && commentFocused &&
              <OnboardingTip
                title="Comments & Mentions"
                message={onboardingMentionCopy}
                top="-164px"
                anchorPosition={AnchorPosition.Bottom}
              />
            }
            <CommentEditor
              ref={this.handleCommentEditorRef}
              comment={this.handleComment}
              onFocus={this.handleCommentFocused}
            />
          </div>
        )}
        <div className="main">
          {showComment && false &&
            <CommentPane
              onReplyClick={this.handleReplyClick}
            />
          }
          {!showComment &&
            <RecipientsPane
              ref={this.handleRecipientsPaneRef}
              onRecipientsChange={this.handleRecipientsChange}
              draftKey={draft && draft.id}
              to={to}
              bcc={bcc}
              cc={cc}
              sender={sender}
              senderEmails={accounts.map(account => account.email)}
              onSenderSelect={this.handleSenderSelect}
              hideAddons={true}
              onCommentClick={this.handleCommentClick}
              expanded={recipientsExpanded}
              onPreviewClick={this.handlePreviewClick}
              onContactGroupAdd={this.handleContactGroupAdd}
              onTabPress={this.handleRecipientsPaneTabPress}
              showPopout={!showSubject && !hidePopout}
              newDraft={newDraft ? newDraft : undefined}
              popoutLink={popoutLink}
              autofocus={autofocus}
            />
          }
          {showSubject && <SubjectPane
            onChange={this.handleSubjectChange}
            subject={subject}
            onTabPress={this.handleSubjectTabPress}
            ref={this.handleSubjectPaneRef}
            onDeletePress={this.handleDelete}
            showPopout={!hidePopout}
            popoutLink={popoutLink}
          />}
          {expanded &&
            <ComposePane
              ref={this.handleComposePaneRef}
              onSend={this.handlePrimarySendClick}
              onSendLater={this.handleSendLater}
              onFollowUp={this.handleFollowUp}
              onChange={this.handleBodyChange}
              onUpload={this.handleUpload}
              onRemoveAttachment={this.handleAttachmentRemove}
              onTabPress={this.handleContentEditableTabPress}
              draft={draft}
              accounts={accounts}
              html={draft && draft.body}
              onSelect={this.handleComposeSelect}
              onShowHistory={showHistoryButton ? this.handleShowHistory : undefined}
              autofocus={false}
              attachments={draft && draft.attachments}
              attachmentMetas={this.props.attachmentMetas || []}
              dispatchAction={dispatchAction}
              noBranding={noBranding}
              getContact={getContact}
              draftAccount={draftAccount}
              draftParentAccount={draftParentAccount}
              features={features}
              onSchedulingUrlSelect={this.handleSchedulingUrl}
              useLinkPreview={useLinkPreview}
              showSendOnboarding={showSendOnboarding}
              onDelete={this.handleDelete}
              onTrack={this.handleTrack}
              showSentFromPolymail={
                !features.disableSentFromPolymail || !!preferences.sentFromPolymail
              }
              onErrorDismiss={this.handleErrorDismiss}
            />
          }
        </div>
      </div>
    );
  }
}

