import { create } from '@src/lib/redux';
import { resourceFailed, resourceOk, resourcePending } from '@src/lib/resource';
import { HubspotContactProfile } from '@src/types/hubspot';
import { RightSidebarTab } from '@src/types/rightSidebar';
import update from 'immutability-helper';

export const {
  type: HUBSPOT_DIRECT_OPEN,
  action: hubspotDirectOpen,
  reducer: hubspotDirectOpenReducer,
} = create<string, HubspotContactProfile>('HUBSPOT_DIRECT_OPEN', {
  setup: (state, meta) => {
    const setupModel = state.hubspot.profiles[meta] || resourcePending();
    return update(state, {
      rightSidebar: {
        tab: { $set: RightSidebarTab.Hubspot },
      },
      hubspot: {
        profiles: {
          [meta]: { $set: setupModel },
        },
      },
    });
  },
  success: (state, meta, payload) => {
    return update(state, {
      hubspot: {
        profiles: {
          [meta]: { $set: resourceOk(payload) },
        },
      },
    });
  },
  failure: (state, meta, error) => {
    return update(state, {
      hubspot: {
        profiles: {
          [meta]: { $set: resourceFailed(error) },
        },
      },
    });
  },
});

export const {
  type: HUBSPOT_CONTACT_FETCH,
  action: hubspotContactFetch,
  reducer: hubspotContactFetchReducer,
} = create<string, HubspotContactProfile>('HUBSPOT_CONTACT_FETCH', {
  setup: (state, meta) => {
    const setupModel = state.hubspot.profiles[meta] || resourcePending();
    return update(state, {
      hubspot: {
        profiles: {
          [meta]: { $set: setupModel },
        },
      },
    });
  },
  success: (state, meta, payload) => {
    return update(state, {
      hubspot: {
        profiles: {
          [meta]: { $set: resourceOk(payload) },
        },
      },
    });
  },
  failure: (state, meta, error) => {
    return update(state, {
      hubspot: {
        profiles: {
          [meta]: { $set: resourceFailed(error) },
        },
      },
    });
  },
});

export default [hubspotContactFetchReducer, hubspotDirectOpenReducer];
