import React from 'react';

interface Props {
  title?: string;
  img?: string;
  description: string;
  siteName: string;
  href: string;
  id: string;
  isPro: boolean;
}

export const closeButtons = (id: string) => {
  return (
    <div
      className="pm-lp-close-buttons"
      style={{
        position: 'absolute',
        top: '-8px',
        right: '-8px',
        opacity: 0,
      }}
    >
      <a className={`close-${id}`}>
        <img
          style={{
            width: '10px',
            position: 'absolute',
            right: '15px',
            top: '15px',
          }}
          src="https://welovepg.polymail.io/v1/img/link-preview/close@3x.png"
        />
      </a>
      <a className={`link-only-${id}`}>
        <img
          style={{
            width: '13px',
            position: 'absolute',
            right: '14px',
            top: '36px',
          }}
          src="https://welovepg.polymail.io/v1/img/link-preview/link@3x.png"
        />
      </a>
    </div>
  );
};

const LinkPreview = (props: Props) => {
  return (
    <div
      className="link-preview-container"
      data-pm-link-preview-id={props.id}
      data-pm-link-preview-url={props.href}
      style={{
        borderRadius: '3px',
        margin: '10px',
        height: '80px',
        maxWidth: '500px',
        border: '#f2f5f8 1px solid',
        overflow: 'hidden',
        position: 'relative',
        textDecoration: 'none',
      }}
    >
      <a
        className=""
        href={props.href}
        style={{
          textDecoration: 'none',
        }}
      >
        <table
          style={{
            padding: 0,
            borderSpacing: 0,
            border: 0,
            display: 'block',
            tableLayout: 'fixed',
          }}
          cellPadding={0}
          cellSpacing={0}
        >
          <tbody>
            <tr>
              {props.img &&
                <td
                  className="link-preview-image"
                  style={{
                    verticalAlign: 'middle',
                    width: '90px',
                    whiteSpace: 'nowrap',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                  }}
                >
                  <a
                    href={props.href}
                    style={{
                      textDecoration: 'none',
                    }}
                  >
                    <div
                      style={{
                        verticalAlign:'middle',
                        width: '90px',
                        height: '80px',
                        background:`transparent url(${props.img}) center/cover`,
                        borderTopLeftRadius: '2px',
                        borderBottomLeftRadius: '2px',
                        border: `0px`,
                      }}
                    />
                  </a>
                </td>
              }
              <td
                style={{
                  width: '100%',
                  marginLeft: '10px',
                  height: '100%',
                }}
              >
                <table
                  style={{
                    marginLeft: '10px',
                    height: '80px',
                    width: '96%',
                    tableLayout: 'fixed',
                  }}
                >
                  <tbody>
                    <tr className="link-preview-title">
                      <td
                        style={{
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          fontSize: '14px',
                          lineHeight: '16px',
                          marginTop: '8px',
                          verticalAlign: 'bottom',
                          fontWeight: 600,
                          width: '96%',
                        }}
                      >
                        <a
                          href={props.href}
                          style={{
                            color: '#444444',
                            textDecoration: 'none',
                          }}
                        >
                        {props.title}
                        </a>
                      </td>
                    </tr>
                    <tr className="link-preview-description">
                      <td
                        style={{
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          fontSize: '12px',
                          lineHeight: '14px',
                          marginTop: '4px',
                          fontWeight: 500,
                        }}
                      >
                        <a
                          href={props.href}
                          style={{
                            color: '#444444',
                            textDecoration: 'none',
                          }}
                        >
                        {props.description}
                        </a>
                      </td>
                    </tr>
                    <tr className="link-preview-description">
                      <td
                        style={{
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          fontSize: '11px',
                          fontWeight: 500,
                          marginTop: '5px',
                        }}
                      >
                        <a
                          href={props.href}
                          style={{
                            color: '#7e8f9f',
                            textDecoration: 'none',
                          }}
                        >
                          {props.siteName}
                        </a>
                      </td>
                      {!props.isPro &&
                        <td>
                          <a
                            href={'https://polymail.io/?utm_source=po' +
                            'lymail&utm_medium=referral&utm_campaign=link-preview'}
                          >
                            <img
                              style={{
                                width: '15px',
                                marginRight: '5px',
                                verticalAlign: 'bottom',
                              }}
                              src="https://welovepg.polymail.io/v1/img/link-preview/logo@3x.png"
                            />
                          </a>
                        </td>
                      }
                    </tr>
                  </tbody>
                </table>
              </td>
            </tr>
          </tbody>
        </table>
      </a>
    </div>
  );
};

export default LinkPreview;
