import {
  Thread,
  Draft,
  Activity,
  RealtimeNotification,
  Template,
  MailCount,
  AttachmentMeta,
  Attachment,
  Mailbox,
  PolymailStatus,
} from '@src/types';
import {
  SharedThread, SharedThreadActivity,
} from '@src/types/sharedThreads';
import { DraftSendMeta } from '@src/ducks/drafts';
import { Filter } from '@src/types/filters';
import { serializeError } from '@src/lib/errors';

// WorkerEvent defines events sent from the Worker to the App.
export interface WorkerEvent {
  name: WorkerEventName;
  data?: any;
}

export enum WorkerEventName {
  ActivityUpdated = 'activityUpdated',
  CalendarsSynced = 'calendarSynced',
  DraftCreated = 'draftCreated',
  DraftDeleted = 'draftDeleted',
  DraftUpdated = 'draftUpdated',
  DraftBatchReceived = 'draftBatchReceived',
  DraftSyncCompleted = 'draftSyncCompleted',
  DraftSent = 'draftSent',
  DraftSendFailed = 'DraftSendFailed',
  FilterDeleted = 'filterDeleted',
  FilterUpdated = 'filterUpdated',
  MailCountUpdated = 'mailCountUpdated',
  MeUpdated = 'meUpdated',
  Notification = 'notification',
  IncrementalSyncStarted = 'incrementalSyncStarted',
  IncrementalSyncFinished = 'incrementalSyncFinished',
  SharedThreadUpdated = 'sharedThreadUpdated',
  SharedThreadDeleted = 'sharedThreadDeleted',
  SharedThreadActivity = 'sharedThreadActivity',
  TeamUpdated = 'teamUpdated',
  TeamMemberUpdated = 'teamMemberUpdated',
  TemplateUpdated = 'templateUpdated',
  ThreadDeleted = 'threadDeleted',
  ThreadUpdated = 'threadUpdated',
  ThreadsRefreshed = 'threadsRefreshed',
  ThreadMoveUndoCompleted = 'threadMoveUndoCompleted',
  WorkerReady = 'workerReady',
  AttachmentMetaUpdated = 'attachmentMetaUpdated',
  DraftAttachmentAdded = 'draftAttachmentAdded',
  DraftAttachmentAddFailed = 'draftAttachmentAddFailed',
  TriggerElectronClearCache = 'electronClearCache',
  RequestConfig = 'requestConfig',

  ServerClearCacheStarted = 'serverClearCacheStarted',
  AppStatusUpdate = 'appStatusUpdate',
}

let connections: MessagePort[] | undefined;
export function setConnections(c: MessagePort[]) {
  connections = c;
}

function workerPostMessage(event: WorkerEvent) {
  if (connections) {
    connections.forEach((c) => {
      c.postMessage(event);
    });
  } else {
    (postMessage as any)(event);
  }
}

export function sendRequestConfig() {
  workerPostMessage({
    name: WorkerEventName.RequestConfig,
  });
}

export function sendTriggerElectronClearCache() {
  workerPostMessage({
    name: WorkerEventName.TriggerElectronClearCache,
  });
}

export function sendDraftAttachmentAddFailed(meta: AttachmentMeta, error: Error) {
  workerPostMessage(
    {
      name: WorkerEventName.DraftAttachmentAddFailed,
      data: { meta, error: serializeError(error) },
    },
  );
}

export function sendDraftAttachmentAdded(meta: AttachmentMeta, attachment: Attachment) {
  workerPostMessage(
    { name: WorkerEventName.DraftAttachmentAdded, data: { meta, attachment } },
  );
}

export function sendAttachmentMetaUpdated(meta: AttachmentMeta) {
  workerPostMessage(
    { name: WorkerEventName.AttachmentMetaUpdated, data: { meta } },
  );
}

export function sendActivityUpdated(activity: Activity) {
  workerPostMessage(
    { name: WorkerEventName.ActivityUpdated, data: { activity } },
  );
}

export function sendCalendarsSynced() {
  workerPostMessage(
    { name: WorkerEventName.CalendarsSynced },
  );
}

export function sendDraftCreated(draft: Draft) {
  workerPostMessage(
    { name: WorkerEventName.DraftCreated, data: { draft } },
  );
}

export function sendDraftDeleted(draftId: string) {
  workerPostMessage(
    { name: WorkerEventName.DraftDeleted, data: { draftId } },
  );
}

export function sendFilterDeleted(filterId: string) {
  workerPostMessage(
    { name: WorkerEventName.FilterDeleted, data: { filterId } },
  );
}

export function sendFilterUpdated(filter: Filter) {
  workerPostMessage(
    { name: WorkerEventName.FilterUpdated, data: { filter } },
  );
}

export function sendDraftSyncComplete() {
  workerPostMessage(
    { name: WorkerEventName.DraftSyncCompleted },
  );
}

export function sendDraftUpdated(draft: Draft) {
  workerPostMessage(
    { name: WorkerEventName.DraftUpdated, data: { draft } },
  );
}

export function sendDraftBatchReceived(drafts: Draft[]) {
  workerPostMessage(
    { name: WorkerEventName.DraftBatchReceived, data: { drafts } },
  );
}

export function sendDraftSent(meta: DraftSendMeta) {
  workerPostMessage(
    { name: WorkerEventName.DraftSent, data: meta },
  );
}

export function sendDraftSendFailed(meta: DraftSendMeta, error: Error) {
  workerPostMessage(
    { name: WorkerEventName.DraftSendFailed, data: { meta, error: serializeError(error) } },
  );
}

export function sendIncrementalSyncStarted() {
  workerPostMessage({
    name: WorkerEventName.IncrementalSyncStarted,
  });
}

export function sendIncrementalSyncFinished() {
  workerPostMessage({
    name: WorkerEventName.IncrementalSyncFinished,
  });
}

export function sendMailCountUpdated(accountId: string, count: Partial<MailCount>) {
  workerPostMessage(
    { name: WorkerEventName.MailCountUpdated, data: { accountId, count } },
  );
}

export function sendMeUpdated() {
  workerPostMessage(
    { name: WorkerEventName.MeUpdated },
  );
}

export function sendNotification(data: RealtimeNotification) {
  workerPostMessage(
    { name: WorkerEventName.Notification, data },
  );
}

export function sendServerClearCacheStarted() {
  workerPostMessage(
    { name: WorkerEventName.ServerClearCacheStarted },
  );
}

export function sendSharedThreadDeleted(sharedThreadId: string) {
  workerPostMessage(
    { name: WorkerEventName.SharedThreadDeleted, data: { sharedThreadId } },
  );
}

export function sendSharedThreadUpdated(sharedThread: SharedThread) {
  workerPostMessage(
    { name: WorkerEventName.SharedThreadUpdated, data: { sharedThread } },
  );
}

export function sendSharedThreadActivity(data: SharedThreadActivity) {
  workerPostMessage(
    { name: WorkerEventName.SharedThreadActivity, data },
  );
}

export function sendTeamUpdated() {
  workerPostMessage(
    { name: WorkerEventName.TeamUpdated },
  );
}

export function sendTeamMemberUpdated() {
  workerPostMessage(
    { name: WorkerEventName.TeamMemberUpdated },
  );
}

export function sendTemplateUpdated(data: Template) {
  workerPostMessage(
    { name: WorkerEventName.TemplateUpdated, data },
  );
}

export function sendThreadDeleted(threadId: string) {
  workerPostMessage(
    { name: WorkerEventName.ThreadDeleted, data: { threadId } },
  );
}

export function sendThreadUpdated(thread: Thread) {
  workerPostMessage(
    { name: WorkerEventName.ThreadUpdated, data: { thread } },
  );
}

export function sendThreadMoveUndoCompleted(box: Mailbox, thread: Thread) {
  workerPostMessage(
    { name: WorkerEventName.ThreadMoveUndoCompleted, data: { thread, box } },
  );
}

export function sendThreadsRefreshed(account: string) {
  workerPostMessage(
    { name: WorkerEventName.ThreadsRefreshed, data: { account } },
  );
}

export function sendAppStatusUpdate(meta: Partial<PolymailStatus>) {
  workerPostMessage(
    { name: WorkerEventName.AppStatusUpdate, data: meta },
  );
}

export function sendWorkerReady() {
  workerPostMessage(
    { name: WorkerEventName.WorkerReady },
  );
}
