import { create } from '@src/lib/redux';
import { createSelector } from '@src/lib/selector';
import { Integration, StoreState, IntegrationStatus, IntegrationType } from '@src/types';

export const {
  type: INTEGRATION_LIST,
  action: integrationList,
  reducer: integrationListReducer,
} = create<null, Integration[]>('INTEGRATION_LIST', {
  success: (state, meta, payload) => {
    // save to local storage
    localStorage.setItem('integrations', JSON.stringify(payload));
    return { ...state, integrations: payload };
  },
});

export const {
  type: INTEGRATION_SALESFORCE_LOGIN,
  action: integrationSalesforceLogin,
  reducer: integrationSalesforceLoginReducer,
} = create<string, string>('INTEGRATION_SALESFORCE_LOGIN', {});

