export const {
  type: INTEGRATION_ASANA_UPDATE,
  action: integrationAsanaUpdate,
  reducer: integrationAsanaUpdateReducer,
} = create<string, null>('INTEGRATION_ASANA_UPDATE', {});

export const selectSalesforceEnabled = createSelector<undefined, Integration[], boolean>(
  [(state: StoreState) =>
    state.integrations],
  (integrations: Integration[]) =>
    integrations
      .filter(int => int.status === IntegrationStatus.Ok
        && int.type === IntegrationType.Salesforce).length > 0,
  undefined,
  ['integrations'],
);

