import React from 'react';
import classnames from 'classnames';
import Overlay from '@src/components/Base/Overlay';
import AppleIconWhite from '@src/components/Icons/AppleIconWhite.svg';
import WindowsIcon from '@src/components/Icons/WindowsIcon.svg';

import './GettingStarted.scss';
import { Me } from '@src/types';
import { isElectron } from '@src/lib/electron';
import { IS_MAC, IS_WINDOWS } from '@src/lib/device';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface StateProps {
  me?: Me;
}

interface Props {
  onNext: () => void;
  className?: string;
}

export interface DispatchProps {
  skipOnboarding: () => void;
}

const Onboarding = (props: Props & DispatchProps & StateProps) => {
  const handleClick = () => {
    logAmplitudeEvent(AmplitudeEvents.TourStepOne, { skipped: false, details: 'Clicked on the "Get Started" button' });
    if (props.onNext) {

      props.onNext();
    }
    return;
  };

  const downloadIOSClick = () => {
    logAmplitudeEvent(AmplitudeEvents.TourStepOne, { skipped: false, details: 'click iOS' });
    window.open('https://itunes.apple.com/us/app/polymail/id1082058386?mt=8', '_blank');
  };

  const downloadDesktopClick = () => {
    if (IS_MAC) {
      // from Ryan: 'Let's track either action as the same event and save what they do as a property
      // (Make Default true or false is fine)'
      logAmplitudeEvent(AmplitudeEvents.DownloadNativeApp, { details: 'mac' });
      logAmplitudeEvent(AmplitudeEvents.TourStepOne, { skipped: false, details: 'click Mac' });
    } else {
      logAmplitudeEvent(AmplitudeEvents.DownloadNativeApp, { details: 'windows' });
      logAmplitudeEvent(AmplitudeEvents.TourStepOne, { skipped: false, details: 'click Windows' });
    }

    window.open(
      'https://polymail.io/download?download=1&utm_source=webapp',
      '_blank',
    );
  };

  const title = '👋 Welcome' + (props.me && `, ${props.me.firstName}` || '') +  '!';
  const description = 'Polymail helps you manage your emails quickly and stay on top of your important conversations';
  return (
    <Overlay
      className={classnames('overlay', props.className)}
    >
      <div className="getting-started-main">
        <div className="header">
          <div className="title">
            {title}
          </div>
          <div className="description">
            {description}
          </div>
        </div>
        <button
          onClick={handleClick}
          className="cta-button"
        >
          Get Started
        </button>
        <div className="download-native-section">
          Want the best experience? Download Polymail for:
          <div className="info-section">
            {!isElectron &&
              <button
                className="download-button"
                onClick={downloadDesktopClick}
              >
                <div className="icon">
                  {IS_MAC && (
                    <AppleIconWhite height="15px" width="15px" className="icon" />
                  )}
                  {IS_WINDOWS && (
                    <WindowsIcon height="15px" width="15px" className="icon" />
                  )}
                </div>
                <div className="copy">
                  {IS_MAC ? 'Mac' : 'Windows'}
                </div>
              </button>
            }
            <button
              onClick={downloadIOSClick}
              className="download-button"
            >
              <div className="icon">
                <AppleIconWhite height="15px" width="15px" className="icon" />
              </div>
              <div className="copy">
                iOS
              </div>
            </button>
          </div>
        </div>
        {/* <div className="skip-section">
          Not your first time with Polymail?
          <button
            className="skip-button"
            onClick={handleSkip}
          >
            Skip tutorial
          </button>
        </div> */}
      </div>
    </Overlay>
  );
};

export default Onboarding;
