import React from 'react';
import Infinite from 'react-infinite';
import { range, last, indexOf, cloneDeep } from 'lodash';
import { History, Location } from 'history';
import {
  Thread,
  ThreadBox,
  Mailbox,
  Commit,
  Draft,
  MailboxType,
  KeyCode,
  ThreadCommitMeta,
  CommandControlType,
  Swipe,
  OnboardingType,
} from '@src/types';
import { routeFromMailbox, isDraftBox } from '@src/lib/mailbox';
import { getHotkey, HotkeyAction, isPrimaryModifierKey } from '@src/lib/hotkeys';

import ThreadCell from './ThreadCell';
import { threadCellIdentifier } from './ThreadCell/ThreadCell';
import DraftCell from './DraftCell/DraftCell';
import EmptyCell from './EmptyCell/EmptyCell';
import ThreadsDataSource from './ThreadsDataSource';
import { threadDataSourceItem, draftDataSourceItem } from './ThreadsDataSourceItem';
import EmptySearchCell from './EmptySearchCell';
import ProviderSearchCell from './ProviderSearchCell';
import { isAnimated } from '@src/lib/commit';
import { OnboardingPatchMeta } from '@src/ducks/me';
import { getOnboardingActions } from '@src/lib/OnboardingDispatcher';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface Props {
  actions: ThreadBox[];
  listHeight: number;
  selectedIds: Map<string, boolean>;
  threads: Thread[];
  drafts: Draft[];
  onThreadMove?: (commit: Commit) => void;
  loading: boolean;
}

export interface StateProps {
  selectedMailbox?: Mailbox;
  selectedDraft?: string;
  selectedThread?: string;
  history?: History;
  location?: Location;
  commandControlType?: CommandControlType;

  lastSwipeId?: string;
  selectedAccount?: string;
  rsbOnboardingType?: OnboardingType;
  cellHeight: number;
}

export interface DispatchProps {
  updateThread: (commit: ThreadCommitMeta) => void;
  updateSelections: (selections: Map<string, boolean>) => void;
  showCommandControl: (type: CommandControlType) => void;
  setSwipeId: (id: string|null) => void;
  refreshThreads: () => void;
  updateOnboarding: (meta: OnboardingPatchMeta) => void;
}

interface State {
  commits: { [id: string]: Commit; };
  resetCounter: { [id: string]: number };
}

export enum Direction {
  Up = 'up',
  Down = 'down',
}

export enum FillBehavior {
  Fill = 'fill',
  Deselect = 'deselect',
  None = 'none',
}

const PROVIDER_SEARCH_CELL_HEIGHT = 60;

class InfiniteThreadList extends React.PureComponent<Props & StateProps & DispatchProps, State> {
  dataSource: ThreadsDataSource;
  private list: HTMLDivElement;

  constructor() {
    super();
    this.state = { commits: {}, resetCounter: {} };
  }

  // HACK(SHIN): Force a threadList refetch on mount since box could have been active
  // but mailboxThreads not updated bc user may have left InfiniteThreadList page
  componentDidMount() {
    this.props.refreshThreads();
  }

  // Clear commits when mode changes
  UNSAFE_componentWillReceiveProps(nextProps: Props & StateProps) {
    if (nextProps.selectedMailbox !== this.props.selectedMailbox) {
      this.setState({ commits: {} });
    }
  }

  // Calculates scrollTop height
  componentDidUpdate(oldProps: Props & StateProps) {
    const { selectedThread, selectedDraft, lastSwipeId, cellHeight } = this.props;
    const selectedId = selectedThread || selectedDraft || undefined;

    // Handles resetting swipes
    const { resetCounter } = this.state;
    if (oldProps.commandControlType && !this.props.commandControlType) {
      let newResetCounter = cloneDeep(resetCounter);

      if (selectedId && oldProps.commandControlType && !this.props.commandControlType) {
        const threadResetCounter = resetCounter[selectedId] || 0;
        newResetCounter = { ...resetCounter, [selectedId]: threadResetCounter + 1 };
        this.setState({ resetCounter: newResetCounter });
      }

      // Handle swipe reset
      if (oldProps.lastSwipeId && !lastSwipeId) {
        const swipeResetCounter = newResetCounter[oldProps.lastSwipeId] || 0;
        newResetCounter = { ...newResetCounter, [oldProps.lastSwipeId]: swipeResetCounter + 1 };
      }

      this.setState({ resetCounter: newResetCounter });
    }

    if (oldProps.selectedIds !== this.props.selectedIds) {
      const singleSelection = last(Array.from(this.props.selectedIds.keys()));
      if (singleSelection) {

        // Grab list element
        const element = document.getElementsByClassName('list')[0];
        if (element instanceof HTMLElement) {
          const location = this.dataSource.getLocation(singleSelection);
          const listHeight = this.list.offsetHeight;

          // Calculate cell and list positions
          const cellTop = cellHeight * (location);
          const cellBottom = cellHeight + (cellHeight) * location;
          const listTop = element.scrollTop;
          const listBottom = element.scrollTop + listHeight;

          // If outside of top range, then set scrollTop to cell top
          if (cellTop < listTop) {
            element.scrollTop = cellTop;
            return;
          }

          // If bottom outside of list bottom range then reset scrollTop
          if (cellBottom > listBottom) {
            element.scrollTop = cellBottom - listHeight;
            return;
          }
        }
      }
    }
  }

  handleDataSourceRef = (ref: ThreadsDataSource) => {
    this.dataSource = ref;
  }

  handleRef = (ref: HTMLDivElement) => {
    this.list = ref;
  }

  handleCommit = (commit: Commit) => {
    const commits = cloneDeep(this.state.commits);
    const { selectedIds } = this.props;
    const foundDomMatch = document.getElementById(threadCellIdentifier(commit.resourceId));
    const isAnimatable = isAnimated(commit);
    const inSelectedIds = selectedIds.get(commit.resourceId);

    // Handler for we'll use the dataStore and infiniteThreadlist. What sets state for animation
    // or for removing instantly from datasource
    const dataHandler = () => {
      // HACK(SHIN): HTML ID used to check if ThreadCell is rendered in DOM
      // Use commit animation if rendered on doc. Otherwise immediately remove from Datasource
      if (foundDomMatch) {
        commits[commit.resourceId] = commit;
        this.setState({ commits });
      } else {
        this.dataSource.remove(commit.resourceId);
      }
    };

    // Handler for next state selection and undo
    const stateHandler = () => {
      if (!inSelectedIds) {
        return;
      }

      this.handleNextSelect();

      // Handle commit
      if (this.props.onThreadMove) {
        this.props.onThreadMove(commit);
      }
    };

    // HACK(SHIN): We process the changes of how the next selection is made based off
    // the backing store, so for instant animations, we want to update data then state
    // Otherwise, we want to handle updates to state, then data (as state selection depends on data)
    if (isAnimatable) {
      dataHandler();
      stateHandler();
    } else {
      stateHandler();
      dataHandler();
    }
  }

  // Determines fill behavior
  calculateFillType = (current: number, direction: Direction, indexes: number[]) => {
    const stepper = direction === Direction.Down ? 1 : -1;
    const nextPosition = current + stepper;

    // If next available, fill
    if (indexOf(indexes, nextPosition) === -1) {
      return FillBehavior.Fill;
    }

    // If at edge, then deselect
    if (indexOf(indexes, current - stepper) === -1) {
      return FillBehavior.Deselect;
    }

    return FillBehavior.None;
  }

  // Determines the next reference point for shift indexes
  calculateNextEdge = (current: number, direction: Direction, indexes: number[]) => {
    const stepper = direction === Direction.Down ? 1 : -1;
    let edgeIndex = current + (stepper * 2);
    let isEdge = indexOf(indexes, edgeIndex) === -1;
    while (!isEdge) {
      edgeIndex += stepper;
      isEdge = indexOf(indexes, edgeIndex) === -1;
    }

    return edgeIndex - stepper;
  }

  // selectedIds calculates edge using last entry of selected Ids
  handleShiftKeySelect = (current: number, direction: Direction, indexes: number[]) => {
    const { updateSelections, selectedIds } = this.props;
    const newMap = new Map(selectedIds);
    const stepper = direction === Direction.Down ? 1 : -1;

    const fillType = this.calculateFillType(current, direction, indexes);
    switch (fillType) {
      case FillBehavior.Fill:
        const fillIndex = current + stepper;
        const edge = this.calculateNextEdge(current, direction, indexes);

        // Set next index in selected ids
        newMap.set(this.dataSource.getItem(fillIndex).identifier, true);

        // Reset edge
        if (edge !== fillIndex) {
          newMap.delete(this.dataSource.getItem(edge).identifier);
          newMap.set(this.dataSource.getItem(edge).identifier, true);
        }
        break;
      case FillBehavior.Deselect:
        newMap.delete(this.dataSource.getItem(current).identifier);
        break;
      default:
        const newEdge = this.calculateNextEdge(
          current,
          Direction.Down,
          indexes,
        );
        newMap.delete(this.dataSource.getItem(newEdge).identifier);
        newMap.set(this.dataSource.getItem(newEdge).identifier, true);
    }

    updateSelections(newMap);
  }

  selectIndexes = (fromIndex: number, toIndex: number) => {
    const { selectedIds, updateSelections } = this.props;
    const inclusiveToIndex = toIndex > fromIndex
      ? toIndex + 1
      : toIndex - 1;

    const currentSelections = new Map(selectedIds);
    const indexes = range(fromIndex, inclusiveToIndex);
    const newSelections = indexes.reduce(
      (newIds, index) => {

        // Get items
        const item = this.dataSource.getItem(index);
        if (!item) {
          return newIds;
        }

        // Remove if item is currently selected
        if (currentSelections[item.identifier]) {
          const removedSelection = new Map(newIds);
          removedSelection.delete(item.identifier);
          return removedSelection;
        }

        // Otherwise set to be selected
        newIds.set(item.identifier, true);
        return newIds;
      },
      currentSelections,
    );

    updateSelections(newSelections);
  }

  // Identifies next thread to select after item has been removed from mode
  handleNextSelect = () => {
    const {
      selectedThread,
      selectedDraft,
      selectedMailbox,
      history,
      updateSelections,
      selectedIds,
      selectedAccount,
    } = this.props;
    const selectedId = selectedThread || selectedDraft;

    if (selectedId && selectedMailbox && history) {
      const indexes = Array.from(selectedIds.keys())
        .map(key => this.dataSource.getLocation(key));
      const lastSelectedIndex = last(indexes) || this.dataSource.getLocation(selectedId);

      // Handles selecting next thread if action performed on that thread
      const nextIndex = lastSelectedIndex + 1;
      const nextItem = this.dataSource.getItem(nextIndex);
      const previousItem = this.dataSource.getItem(lastSelectedIndex - 1);
      const newSelections = new Map();

      if (nextItem) {
        history.push(routeFromMailbox(
          selectedMailbox,
          nextItem.resource.id,
          selectedAccount,
        ));
        newSelections.set(nextItem.identifier, true);
        updateSelections(newSelections);
        return;
      }

      if (previousItem && !selectedIds.get(previousItem.identifier)) {
        history.push(routeFromMailbox(
          selectedMailbox,
          previousItem.resource.id,
          selectedAccount,
        ));
        newSelections.set(previousItem.identifier, true);
        updateSelections(newSelections);
        return;
      }

      updateSelections(newSelections);
      history.push(routeFromMailbox(
        selectedMailbox,
        undefined,
        selectedAccount,
      ));
    }
  }

  // Handles moving immediately
  handleThreadMove = (thread: Thread, action: Swipe) => {
    const { showCommandControl, setSwipeId } = this.props;
    const { resetCounter } = this.state;
    const actionValue = action.valueOf();

    // Do not do anything when action is not set
    if (!actionValue) {
      return;
    }

    switch (actionValue) {
      case Swipe.None:
        const beforeNoneCounter = resetCounter[thread.id] || 0;
        const afterNoneCounter = { ...resetCounter, [thread.id]: beforeNoneCounter + 1 };
        this.setState({ resetCounter: afterNoneCounter });
        return;
      case ThreadBox.Later:
        showCommandControl(CommandControlType.ReadLater);
        setSwipeId(thread.id);
        return;
      case ThreadBox.List:
        showCommandControl(CommandControlType.Label);
        setSwipeId(thread.id);
        return;
      case Swipe.Star:
        this.props.updateThread({
          starred: !thread.starred,
          singleResource: thread.id,
        });
        const beforeStarCounter = resetCounter[thread.id] || 0;
        const afterStarCounter = { ...resetCounter, [thread.id]: beforeStarCounter + 1 };
        this.setState({ resetCounter: afterStarCounter });
        return;
      case Swipe.Unread:
        this.props.updateThread({
          read: !thread.read,
          singleResource: thread.id,
        });
        const beforeReadCounter = resetCounter[thread.id] || 0;
        const afterReadCounter = { ...resetCounter, [thread.id]: beforeReadCounter + 1 };
        this.setState({ resetCounter: afterReadCounter });
        return;
      default:
        break;
    }
    this.props.updateThread({
      box: actionValue as ThreadBox,
      singleResource: thread.id,
    });
  }

  handleAnimationComplete = (id: string) => {
    const commits = { ...this.state.commits };
    if (commits[id]) {
      delete commits[id];
      this.dataSource.remove(id);
      this.setState({ commits });
    }
  }

  handleThreadClick = (e: React.MouseEvent<HTMLElement>, thread: Thread)  => {
    const {
      history,
      selectedMailbox,
      selectedThread,
      location,
      updateSelections,
      selectedIds,
      selectedAccount,
    } = this.props;
    const currentSelections = new Map(selectedIds);

    const primaryModifierKeySelected = isPrimaryModifierKey(e);

    // Remove multiple selections if non-modified click
    if (!(primaryModifierKeySelected || e.shiftKey)) {
      const singleSelect = new Map();
      singleSelect.set(thread.id, true);
      updateSelections(singleSelect);
    }

    // Handle meta selection
    if (selectedThread && primaryModifierKeySelected) {
      const shouldSelect = currentSelections.get(thread.id) === undefined;
      const newMap = new Map(selectedIds);
      newMap.set(thread.id, shouldSelect);

      // Remove selection
      if (!shouldSelect) {
        newMap.delete(thread.id);
      }

      updateSelections(newMap);

      // If selections now empty, clear.
      if (newMap.size === 0 && selectedMailbox && history) {
        history.push(routeFromMailbox(selectedMailbox, undefined, selectedAccount));
      }
      return;
    }

    // Handle shift selection
    if (selectedThread && e.shiftKey) {
      const toIndex = this.dataSource.getLocation(thread.id);
      const fromIndex = this.dataSource.getLocation(selectedThread);
      this.selectIndexes(fromIndex, toIndex);
    }

    // Set selection by updating history
    if (history && location && selectedMailbox) {
      history.push(routeFromMailbox(selectedMailbox, thread.id, selectedAccount) + location.search);
    }
  }

  renderThreadCell = (thread: Thread) => {
    const { selectedIds, selectedThread } = this.props;
    const { resetCounter } = this.state;

    // Determine if item is selected
    const isSelected = selectedIds.size > 0
      ? selectedIds.get(thread.id) !== undefined
      : selectedThread && selectedThread === thread.id || false;

    // Pass in commit
    const commit = this.state.commits[thread.id];

    return (
      <ThreadCell
        thread={thread}
        key={thread.id}
        selected={isSelected}
        onClick={this.handleThreadClick}
        onThreadMove={this.handleThreadMove}
        onAnimationComplete={this.handleAnimationComplete}
        actionToPerform={commit}
        resetCounter={resetCounter[thread.id]}
      />
    );
  }

  handleDraftClick = (e: React.MouseEvent<HTMLElement>, draft: Draft) => {
    const { updateSelections, selectedDraft, selectedIds, history, selectedMailbox } = this.props;
    const currentSelections = new Map(selectedIds);

    const primaryModifier = isPrimaryModifierKey(e);
    // Remove multiple selections if non-modified click
    if (!(primaryModifier || e.shiftKey)) {
      const singleSelect = new Map();
      singleSelect.set(draft.id, true);
      updateSelections(singleSelect);
    }

    // Handle meta selection
    if (selectedDraft && primaryModifier) {
      const shouldSelect = currentSelections.get(draft.id) === undefined;
      const newMap = new Map(selectedIds);
      newMap.set(draft.id, shouldSelect);

      // Remove selection
      if (!shouldSelect) {
        newMap.delete(draft.id);
      }

      updateSelections(newMap);

      // If selections now empty, clear.
      if (newMap.size === 0 && selectedMailbox && history) {
        history.push(routeFromMailbox(selectedMailbox, draft.id));
      }
      return;
    }

    // Handle shift selection
    if (selectedDraft && e.shiftKey) {
      const toIndex = this.dataSource.getLocation(draft.id);
      const fromIndex = this.dataSource.getLocation(selectedDraft);
      this.selectIndexes(fromIndex, toIndex);
    }

    // Set selection by updating history
    if (history && location && selectedMailbox) {
      history.push(routeFromMailbox(selectedMailbox, draft.id));
    }
  }

  renderDraftCell = (draft: Draft) => {
    const { selectedDraft, selectedIds } = this.props;

    // Determine if item is selected
    const isSelected = selectedIds.size > 0
      ? selectedIds.get(draft.id) !== undefined
      : selectedDraft && selectedDraft === draft.id || false;

    return (
      <DraftCell
        draft={draft}
        key={draft.id + draft.updated}
        selected={isSelected}
        onClick={this.handleDraftClick}
      />
    );
  }

  renderDraftCells = () => {
    const { drafts } = this.props;
    return drafts ?
      drafts.map((draft: Draft) => this.renderDraftCell(draft)) : [];
  }

  // selectedIds calculates edge using last entry of selected Ids
  handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    const {
      history,
      selectedMailbox,
      selectedThread,
      selectedIds,
      updateSelections,
      selectedAccount,
      selectedDraft,
      rsbOnboardingType,
      updateOnboarding,
    } = this.props;
    let handled = false;

    if (document.activeElement && (document.activeElement.tagName === 'INPUT' ||
      document.activeElement.tagName === 'TEXTAREA')) {
      return;
    }

    // Check if primary modifier was selected
    const primaryModifierKeySelected = isPrimaryModifierKey(e);

    // Handles select all
    if (primaryModifierKeySelected && e.keyCode === KeyCode.A) {
      this.selectIndexes(0, this.dataSource.getLength());
      handled = true;
    }

    const selectedId = selectedThread || selectedDraft;

    const hotkey = getHotkey(e);
    switch (hotkey && hotkey.name || e.keyCode) {
      case KeyCode.Up:
      case KeyCode.K:
      case HotkeyAction.PreviousThread:
        if (rsbOnboardingType === OnboardingType.NextThread) {
          updateOnboarding({
            skipped: false,
            type: { steps: { [OnboardingType.NextThread]: true } },
          });
          logAmplitudeEvent(AmplitudeEvents.TourStepSix, { skipped: false, details: 'up' });
        }
        getOnboardingActions().next(OnboardingType.PreviousThread);

        if (selectedMailbox) {
          const indexes = Array.from(selectedIds.keys())
            .map(key => this.dataSource.getLocation(key));
          let lastSelectedIndex: string|number|undefined = last(indexes);
          if (typeof lastSelectedIndex !== 'number' && selectedId) {
            lastSelectedIndex = selectedThread &&
              this.dataSource.getLocation(selectedId);
          }
          if (typeof lastSelectedIndex !== 'number') {
            lastSelectedIndex = -1;
          }
          const previousIndex = lastSelectedIndex - 1;
          const previousItem = this.dataSource.getItem(previousIndex);

          // Do not move selection if no previous item
          if (!previousItem) {
            return;
          }

          // Remove selections on non-modified keypress
          if (!e.shiftKey) {
            const singleSelect = new Map();
            singleSelect.set(previousItem.identifier, true);
            updateSelections(singleSelect);
          }

          // Update selections
          if (e.shiftKey && previousItem) {
            this.handleShiftKeySelect(lastSelectedIndex, Direction.Up, indexes);
          }

          if (previousIndex > -1 && history) {
            history.push(routeFromMailbox(
              selectedMailbox,
              previousItem.identifier,
              selectedAccount,
            ));
          }

          handled = true;
        }
        break;
      case KeyCode.Down:
      case KeyCode.J:
      case HotkeyAction.NextThread:
        if (rsbOnboardingType === OnboardingType.NextThread) {
          updateOnboarding({
            skipped: false,
            type: { steps: { [OnboardingType.NextThread]: true } },
          });
          logAmplitudeEvent(AmplitudeEvents.TourStepSix, { skipped: false, details: 'down' });
        }

        getOnboardingActions().next(OnboardingType.NextThread);

        if (selectedMailbox) {
          const indexes = Array.from(selectedIds.keys())
            .map(key => this.dataSource.getLocation(key));
          let lastSelectedIndex: string | number | undefined = last(indexes);
          if (typeof lastSelectedIndex !== 'number' && selectedId) {
            lastSelectedIndex = selectedThread &&
              this.dataSource.getLocation(selectedId);
          }
          if (typeof lastSelectedIndex !== 'number') {
            lastSelectedIndex = -1;
          }
          const nextIndex = lastSelectedIndex + 1;
          let nextItem = this.dataSource.getItem(nextIndex);

          // Do not move selection if no previous item
          if (!nextItem) {
            if (lastSelectedIndex === -1 && this.dataSource.getLength() > 0) {
              nextItem = this.dataSource.getItem(0);
              if (!nextItem) {
                return;
              }
            } else {
              return;
            }
          }

          // Remove selections on non-modified keypress
          if (!e.shiftKey) {
            const singleSelect = new Map();
            singleSelect.set(nextItem.identifier, true);
            updateSelections(singleSelect);
          }

          // Update selections
          if (e.shiftKey && nextItem) {
            this.handleShiftKeySelect(lastSelectedIndex, Direction.Down, indexes);
          }

          if (nextItem && history) {
            history.push(routeFromMailbox(
              selectedMailbox,
              nextItem.identifier,
              selectedAccount,
            ));
            handled = true;
          }
        }
        break;
      default:
        break;
    }

    if (handled) {
      e.preventDefault();
      e.stopPropagation();
    }
  }

  // Callback when datasource has updated to force rerender
  handleForceUpdate = () => {
    this.forceUpdate();
  }

  // Handles clearing out a commit on a successful undo
  handleUndoCompletion = (t: Thread) => {
    const { commits } = this.state;
    if (commits[t.id]) {
      delete commits[t.id];
      this.setState({ commits });
    }
  }

  renderEmptyCells = () => {
    const numOfEmptyCells = 3;
    const cells = [];

    let i;
    for (i = 0; i < numOfEmptyCells; i += 1) {
      cells.push(<EmptyCell key={i} />);
    }

    return cells;
  }

  render() {
    const { selectedMailbox, listHeight, threads, drafts, loading, cellHeight } = this.props;
    const showDrafts = selectedMailbox && isDraftBox(selectedMailbox.type);
    const mode = selectedMailbox || { type: MailboxType.Any };
    const items = showDrafts
      ? drafts.map(draft => draftDataSourceItem(draft))
      : threads.map(thread => threadDataSourceItem(thread, mode));

    const itemsEmpty = !!this.dataSource && !this.dataSource.getLength();
    const isEmpty = !threads.length && itemsEmpty && selectedMailbox && !loading &&
    !isDraftBox(selectedMailbox.type);
    const isSearch = selectedMailbox && !!selectedMailbox.query;

    // Adjust height of list when showing the provider search disclaimer
    const showProviderDisclaimer = isSearch &&
      selectedMailbox && !selectedMailbox.providerSearchComplete && !selectedMailbox.providerSearch;
    let calculatedListHeight = listHeight;
    if (showProviderDisclaimer) {
      calculatedListHeight = calculatedListHeight - PROVIDER_SEARCH_CELL_HEIGHT;
    }

    return (
      <div
        className="infinite-thread-list"
        style={{ height: listHeight, overflow: 'hidden' }}
        ref={this.handleRef}
        tabIndex={-1}
        onKeyDown={this.handleKeyDown}
      >
        {isEmpty && isSearch && selectedMailbox && selectedMailbox.providerSearchComplete &&
          <EmptySearchCell
            selectedMailbox={selectedMailbox}
          />
        }
        {!loading && <ThreadsDataSource
          ref={this.handleDataSourceRef}
          onCommit={this.handleCommit}
          mode={mode}
          items={items}
          onUpdate={this.handleForceUpdate}
          onUndoComplete={this.handleUndoCompletion}
        >
          {dataSourceItems =>
            <Infinite
              className="list"
              elementHeight={cellHeight}
              containerHeight={calculatedListHeight}
              key="infinite"
              // TODO(SHAHAN): Re-enable infinite loading when we need it
              // loadingSpinnerDelegate={<Loader />}
              // onInfiniteLoad={this.handleInfiniteLoad}
              // isInfiniteLoading={this.state.loading}
              // infiniteLoadBeginEdgeOffset={50}
            >
              {
                showDrafts
                  ? dataSourceItems.map(item => this.renderDraftCell(item))
                  : dataSourceItems.map(item => this.renderThreadCell(item))
              }
            </Infinite>
          }
        </ThreadsDataSource>}
        {isEmpty && isSearch && selectedMailbox && selectedMailbox.providerSearchComplete &&
          <EmptySearchCell
            selectedMailbox={selectedMailbox}
          />
        }
        {loading && this.renderEmptyCells()}
        {!loading && !!showProviderDisclaimer && selectedMailbox &&
          <ProviderSearchCell
            query={selectedMailbox.query}
            selectedMailbox={selectedMailbox}
          />
        }
      </div>
    );
  }
}

export default InfiniteThreadList;
