import React from 'react';

import NavigationHeader from '@src/routes/Settings/components/NavigationHeader';
import BillingPlanSection from './BillingPlanSection';
import Toggle from '@src/components/Form/Toggle';
import { BillingTier, BILLING_PLANS, BillingSubscription, BillingCycle, BillingCard } from '@src/types/billing';
import browserHistory from '@src/lib/browserHistory';
import { formatCurrencyCents } from '@src/lib/utils';
import LoadingErrorState from '@src/routes/Settings/routes/Billing/components/LoadingErrorState';
// tslint:disable-next-line:max-line-length
import BillingStatusBanner from '@src/routes/Settings/routes/Billing/components/BillingStatusBanner';
import { Account, MeStatusNew, TeamPlan } from '@src/types';

import './BillingPlans.scss';
import { isDisabledState } from '@src/lib/banner';
import { trialDaysLeft } from '@src/lib/models/billing';

export interface Props {
  loading: boolean;
  loadingError: boolean;
  isAdmin: boolean;

  balance: number;
  seatsUsed: number;
  activeSubscription: boolean;
  subscription?: BillingSubscription;
  trialExpires?: Date;
  planExpires?: Date;
  trialPlan?: TeamPlan;
  isMobile?: boolean;
  collapsedMobile?: boolean;
  statusNew?: MeStatusNew;
  card?: BillingCard;
  accounts: Account[];
}

export interface DispatchProps {
  setToast(error: Error): void;
  collapseLsb: (collapse: boolean) => void;
}

interface State {
  annualToggle: boolean;
}

class BillingPlans extends React.PureComponent<Props & DispatchProps, State> {
  constructor(props: Props & DispatchProps) {
    super(props);
    this.state = {
      annualToggle: props.subscription &&
         props.subscription.billingCycle === BillingCycle.Monthly ? false : true,
    };
  }

  handleAnnualToggle = () => {
    this.setState({
      annualToggle: !this.state.annualToggle,
    });
  }

  handleCtaClick = (tier: BillingTier) => {
    // redirect to correct page
    const cycle = this.state.annualToggle ? 'annual' : 'monthly';
    browserHistory.push(`/settings/billing/upgrade/${tier}/${cycle}`);
  }

  handleBack = () => {
    browserHistory.push(`/settings/billing`);
  }

  render() {
    const {
      subscription, trialExpires, balance, loading, loadingError,
      isAdmin, activeSubscription, seatsUsed, trialPlan, isMobile, statusNew,
      collapseLsb, collapsedMobile, planExpires, accounts,
    } = this.props;

    const currentTier = subscription && subscription.tier;
    // failed payment users shouldn't see the BillingStatusBanner in /settings/billing
    // https://polymail.atlassian.net/browse/PM-227
    const hasPaymentFailed = statusNew === MeStatusNew.Unpaid;
    const hasTrialExpired =
      statusNew === MeStatusNew.TrialExpired
      // plan takes precedence over trial for cancelled accounts
      || trialDaysLeft(planExpires || trialExpires) <= 0;

    if (loading || loadingError || !isAdmin) {
      return (
        <LoadingErrorState
          loading={loading}
          loadingError={loadingError}
          requireAdmin={!isAdmin}
        />
      );
    }

    return (
      <div className="settings-panel preferences-billing">
        <NavigationHeader
          title="Plans & Billing"
          onBack={activeSubscription && currentTier && this.handleBack  || undefined}
          contextButton={
            activeSubscription && currentTier && <h5>Change Plan</h5> || undefined
          }
          isMobile={isMobile}
          collapsedMobile={collapsedMobile}
          collapseLsb={collapseLsb}
        />
        <div className="billing-plans">
          {(!hasPaymentFailed && !hasTrialExpired) && (
            <BillingStatusBanner
              subscription={subscription}
              trialExpires={trialExpires}
              trialPlan={trialPlan}
              isAdmin={isAdmin}
              isDisabled={isDisabledState(statusNew)}
              card={this.props.card}
            />
          )}
          <div className="toggle-header">
            {balance < 0  && <div className="account-credit">
              <span className="emoji">ðŸ’¸</span>
              You have {formatCurrencyCents(-1 * balance)} in account credit
            </div>}
            <div className="label">Monthly</div>
            <Toggle
              on={this.state.annualToggle}
              onChange={this.handleAnnualToggle}
            />
            <div className="label">Annual</div>
          </div>
          <div className="plan-sections">
            {BILLING_PLANS
              .filter(plan => !plan.deprecated)
              .map(plan =>
                <BillingPlanSection
                  title={plan.title}
                  key={plan.tier}
                  onCtaClick={this.handleCtaClick}
                  tier={plan.tier}
                  cost={this.state.annualToggle ? plan.annualCost : plan.monthlyCost}
                  description={plan.description}
                  ctaCopy={plan.ctaCopy}
                  features={plan.features}
                  existingTier={activeSubscription && currentTier || undefined}
                  seatsUsed={seatsUsed}
                  isMobile={isMobile}
                  accounts={accounts.length}
                  accountsLimit={plan.accountsLimit}
                />,
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default BillingPlans;
