import {
  Mailbox,
  QueryParams,
  Thread,
  ThreadBox,
  List,
  MailboxType,
} from '@src/types';
import Cache, { Stores } from '@src/lib/db';
import { ThreadAPI } from '@src/api/threads';
import {
  computeThread,
  listThreadsSent,
  listThreadsAccountSent,
  listThreadsByBox,
  listThreadsByAccountBox,
  listThreadsByList,
  listThreadsByAccountList,
  listThreadsByEmail,
  listThreadsAccountStarred,
  listAllThreadsByAccount,
  THREAD_LIST_SIZE,
 } from '@src/lib/thread';
import { Observable } from 'rxjs';
import { orderBy, keyBy } from 'lodash';
import moment from 'moment';

const isWhitespace = (c: string): boolean => {
  return c === ' ' || c === '\t' || c === '\r' || c === '\n';
};

const emailRegex = new RegExp(`\\S+@\\S+\\.\\S+`, 'gi');
export const isEmail = (t: string): boolean  => {
  return !!t.match(emailRegex);
};

const dateRegex = new RegExp('(\\d{2}-\\d{2}-(\\d{4}|\\d{2}))|(\\d{4}/\\d{2}/\\d{2})', 'gi');
const isDate = (t: string): boolean => {
  return !!t.match(dateRegex);
};

export const queryParamsTextValue = (params: QueryParams) => {
  return params.text && params.text.join(' ') || '';
};

const removeQuotes = (s: string): string => {
  let removed = s;
  if (removed.startsWith('"') && removed.endsWith('"')) {
    removed = removed.slice(1);
    removed = removed.slice(0, -1);
  }
  return removed;
};

export const parseQueryParams = (s: string, lists: List[]): QueryParams => {
  const params: QueryParams = {};
  const tokens = tokenize(s).map(t => t.toLowerCase());

  // Identify if we can use preix matching
  tokens.forEach((t) => {
    if (t.startsWith('"')) {
      params.quoted = true;
    }

    if (t.startsWith('with:') && isEmail(t)) {
      if (!params.with) {
        params.with = [];
      }
      params.with.push(t.slice(5));
      return;
    }

    if (t.startsWith('from:') && isEmail(t)) {
      params.from = t.slice(5);
      return;
    }

    if (t.startsWith('to:') && isEmail(t)) {
      if (!params.to) {
        params.to = [];
      }
      params.to.push(t.slice(3));
      return;
    }

    if (t.startsWith('before:') && isDate(t)) {
      const m = moment(t.slice(7), ['MM-DD-YY', 'MM-DD-YYYY', 'YYYY/MM/DD']);
      if (m.isValid()) {
        params.before = m.startOf('day').toDate();
        return;
      }
    }
    if (t.startsWith('after:') && isDate(t)) {
      const m = moment(t.slice(6), ['MM-DD-YY', 'MM-DD-YYYY', 'YYYY/MM/DD']);
      if (m.isValid()) {
        params.after = m.startOf('day').toDate();
        return;
      }
    }

    // TOOD(SHN): Use prefix matching for in: items
    if (t.startsWith('in:')) {
      let location = t.slice(3);
      switch (location) {
        case 'inbox':
          params.box = ThreadBox.Inbox;
          return;
        case 'archive':
          params.box = ThreadBox.Archive;
          return;
        case 'trash':
          params.box = ThreadBox.Trash;
          return;
        case 'spam':
          params.box = ThreadBox.Spam;
          return;
        case 'later':
          params.box = ThreadBox.Later;
          return;
        default:
          location = removeQuotes(location);
          const listDict = keyBy(lists, (list => list.name.toLowerCase()));
          const l = listDict[location.toLowerCase()];
          if (!!l) {
            params.box = ThreadBox.List;
            params.list = l.id;
            return;
          }
      }
    }

    // TODO(SHIN): Use prefix matching for is: items
    if (t === 'is:starred') {
      params.isStarred = true;
      return;
    }
    if (t === 'is:read') {
      params.isRead = true;
      params.isUnread = false;
      return;
    }
    if (t === 'is:unread') {
      params.isUnread = true;
      params.isRead = false;
      return;
    }
    if (t === 'is:opened') {
      params.isOpened = true;
      params.isUnopened = false;
      params.sent = true;
      return;
    }
    if (t === 'is:unopened') {
      params.isUnopened = true;
      params.isOpened = false;
      params.sent = true;
      return;
    }
    if (t === 'is:replied') {
      params.isReplied = true;
      params.isUnreplied = false;
      params.sent = true;
      return;
    }
    if (t === 'is:unreplied') {
      params.isUnreplied = true;
      params.isReplied = false;
      params.sent = true;
      return;
    }
    if (t === 'has:attachment') {
      params.hasAttachment = true;
      return;
    }
    if (t === 'has:followup') {
      params.hasFollowUp = true;
      return;
    }

    // Aggregate search query
    if (!params.text) {
      params.text = [];
    }
    params.text.push(removeQuotes(t.toLowerCase()));
  });
  return params;
};

export const tokenize = (input: string): string[] => {
  const s = input.trim();

  let inQuote = false;
  let start;

  const tokens: string[] = [];

  // tslint:disable-next-line
  for (let i = 0; i < s.length; i++) {
    const c = s[i];

    // If in quote, continue until we find another quote
    if (inQuote) {
      if (c === '"') {
        inQuote = false;
      }
    }

    // If not in quote start on the first non-whitespace character
    if (!isWhitespace(c) && start === undefined) {
      start = i;
    }

    // If this is the last character or the next chracter is a white space
    // then append the current set to tokens.
    const isLastChar = (i === s.length - 1);
    if (start !== undefined && (isLastChar || (!inQuote && isWhitespace(s[i + 1])))) {
      const token = s.substr(start, i - start + 1);
      tokens.push(token);
      start = undefined;
      continue;
    }

    if (!isWhitespace(c) && !inQuote && c === '\"') {
      inQuote = true;
    }

    // If whitespace just contine...
  }

  return tokens;
};

// threadsSearchFilter should be applies after both local and provider search queries.
export const threadSearchFilter = (mailbox: Mailbox, thread: Thread): boolean => {

  // Account filter
  if (mailbox.account && thread.account !== mailbox.account) {
    return false;
  }

  const p = mailbox.queryParams;
  if (!p) {
    return true;
  }

  // Box filter
  if (p.box && thread.box !== p.box) {
    return false;
  }

  // List filter
  if (p.list && thread.box === ThreadBox.List && thread.list !== p.list) {
    return false;
  }

  // After filter
  if (p.after && thread.internal < p.after) {
    return false;
  }

  // Before filter
  if (p.before && thread.internal > p.before) {
    return false;
  }

  // With filter
  if (p.with) {
    for (const email of p.with) {
      let found = false;
      for (const participant of thread.participantEmails) {
        if (participant.startsWith(email)) {
          found = true;
        }
      }
      if (!found) {
        return false;
      }
    }
  }

  // From filter
  // Check if this email exists in the `from` field of any message
  if (p.from) {
    let found = false;
    for (const message of thread.messages) {
      if (message.from.includes(p.from)) {
        found = true;
        break;
      }
    }
    if (!found) {
      return false;
    }
  }

  // To filter
  // Check if all these emails exists in the to field of any message.
  if (p.to) {
    for (const email of p.to) {
      let found = false;
      for (const message of thread.messages) {
        for (const to of message.to) {
          if (to.includes(email)) {
            found = true;
            break;
          }
        }
        if (found) {
          break;
        }
      }
      if (!found) {
        return false;
      }
    }
  }

  // Starred filter
  if (p.isStarred && !thread.starred) {
    return false;
  }

  // Read filter
  if (p.isRead && !thread.read) {
    return false;
  }

  // Unread filter
  if (p.isUnread && thread.read) {
    return false;
  }

  // Opened filter
  if (p.isOpened) {
    if (!thread.lastSent) {
      return false;
    }
    for (let i = thread.messages.length - 1; i >= 0; i -= 1) {
      const m = thread.messages[i];
      if (m.sent) {
        if (!m.tracked || m.tracks === 0) {
          return false;
        }
        break;
      }
    }
  }

  // Unopened filter
  if (p.isUnopened) {
    if (!thread.lastSent) {
      return false;
    }
    for (let i = thread.messages.length - 1; i >= 0; i -= 1) {
      const m = thread.messages[i];
      if (m.sent) {
        if (!m.tracked || m.tracks > 0) {
          return false;
        }
        break;
      }
    }
  }

  const lastMessage = thread.messages[thread.messages.length - 1];

  // Replied filter
  if (p.isReplied && (!thread.lastSent || lastMessage.sent)) {
    return false;
  }

  // Unreplied filter
  if (p.isUnreplied && !lastMessage.sent) {
    return false;
  }

  // Attachment filter
  if (p.hasAttachment && !thread.hasAttachments) {
    return false;
  }

  // Followup filter
  if (p.hasFollowUp && !thread.waiting) {
    return false;
  }

  return true;
};

// localThreadSearchPredicate is used to filter threads when we're running local search.
export const localThreadSearchPredicate = (mailbox: Mailbox, thread: Thread): boolean => {
  const p = mailbox.queryParams;
  if (!p) {
    return true;
  }

  // Apply search filters
  if (!threadSearchFilter(mailbox, thread)) {
    return false;
  }

  // Query subject, snippet and participants.
  // Make sure all text params match somewhere in the thread.
  if (p.text) {

    let allMatched = true;
    for (const q of p.text) {

      // match subject
      // TODO: Need to sanitize q
      if (thread.subject.toLowerCase().search(q) >= 0) {
        continue;
      }

      // match snippet
      let snippetMatched = false;
      for (const message of thread.messages) {
        // TODO: Need to sanitize q
        if (message.snippet.toLowerCase().search(q) >= 0) {
          snippetMatched = true;
          break;
        }
      }
      if (snippetMatched) {
        continue;
      }

      // match participants
      let participantMatched = false;
      for (const participant of thread.participants) {
        // TODO: Need to sanitize q
        if (participant.email.toLowerCase().search(q) >= 0) {
          participantMatched = true;
          break;
        }
        // TODO: Need to sanitize q
        if (participant.fullName.toLowerCase().search(q) >= 0) {
          participantMatched = true;
          break;
        }
      }
      if (participantMatched) {
        continue;
      }
      allMatched = false;
    }
    if (!allMatched) {
      return false;
    }
  }

  return true;
};

// TODO(SHIN): Cacche provider thread search results
export const providerThreadSearch = (api: ThreadAPI, mailbox: Mailbox): Observable<Thread[]> => {
  if (!mailbox.query) {
    return Observable.of([]);
  }

  return api.search(mailbox.query, mailbox.account)
    .map(threads => threads.filter(thread => threadSearchFilter(mailbox, thread)))
    .flatMap(threads => Cache.load(Stores.Threads, threads).map(() => threads));
};

export const localThreadSearch = (mailbox: Mailbox): Observable<Thread[]> => {
  const params = mailbox.queryParams;
  const account = mailbox.account;

  // Do not allow old provider searches for IMAP accounts
  if (!account) {
    return Observable.throw(
      new Error('programmer: localThreadSearch only supports account based search'),
    );
  }

  // // Default to full scan
  // let results = Cache.list<Thread>(
  //   Stores.Threads,
  //   thread => localThreadSearchPredicate(mailbox, thread),
  // );

  // Fetch all threads from the account
  let results = listAllThreadsByAccount(
    account,
    50,
    thread => localThreadSearchPredicate(mailbox, thread),
  );

  // Use optimized query if possible
  if (params && params.isStarred) {
    // If `starred` filter use star index
    results = listThreadsAccountStarred(
      account,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (params && params.with && params.with.length > 0) {
    // If `with` filter use email index
    results = listThreadsByEmail(
      params.with[0],
      THREAD_LIST_SIZE,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (params && params.sent && mailbox.account) {
    // If `account` and `sent` filter use account sent index
    results = listThreadsAccountSent(
      mailbox.account,
      undefined,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (params && params.box === ThreadBox.List && params.list && mailbox.account) {
    // If `account` and `list` filter use account list index
    results = listThreadsByAccountList(
      mailbox.account,
      params.list,
      undefined,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (params && params.box && mailbox.account) {
    // If `account` and `box` filter use account box index
    results = listThreadsByAccountBox(
      mailbox.account,
      params.box,
      undefined,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (params && params.sent) {
    // If `sent` filter use account sent index
    results = listThreadsSent(
      undefined,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (params && params.box === ThreadBox.List && params.list) {
    // If `list` filter use list index
    results = listThreadsByList(
      params.list,
      undefined,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (params && params.box) {
    // If `box` filter use box index
    results = listThreadsByBox(
      params.box,
      undefined,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  } else if (mailbox.account) {
    // If `account` filter use account box index w/ range for box
    results = listAllThreadsByAccount(
      mailbox.account,
      50,
      thread => localThreadSearchPredicate(mailbox, thread),
    );
  }
  return results.map(threads =>
    orderBy(threads.map(thread => computeThread(thread)), t => t.internal, 'desc'));
};

const DEFAULT_TEXT_SEARCH_OPTIONS = [
  'in:', 'is:', 'has:', 'with:', 'from:', 'to:', 'before:', 'after:',
];
const DEFAULT_IS_SEARCH_OPTIONS = [
  'read',
  'unread',
  'replied',
  'unreplied',
  'opened',
  'unopened',
  'starred',
];
const DEFAULT_HAS_SEARCH_OPTIONS = [
  'reminder',
  'attachment',
];
const DEFAULT_BOX_SEARCH_OPTIONS = [
  ThreadBox.Inbox,
  ThreadBox.Archive,
  ThreadBox.Trash,
  ThreadBox.Spam,
  MailboxType.Later,
];

export const getDefaultOptions = (component: string, isOptions?: boolean) => {
  switch (component) {
    case 'text':
      return DEFAULT_TEXT_SEARCH_OPTIONS;
    case 'modifier':
      return isOptions ? DEFAULT_IS_SEARCH_OPTIONS : DEFAULT_HAS_SEARCH_OPTIONS;
    case 'box':
      return DEFAULT_BOX_SEARCH_OPTIONS;
    default:
      return;
  }
};
