import React from 'react';
import moment, { Moment } from 'moment';

import ControlPopover from '../components/ControlPopover';
import MultiDayView from './components/MultiDayView/MultiDayView';
import EventDetail from './components/EventDetail/EventDetail';
import CalendarList from './components/CalendarList/CalendarList';
import CalendarHeader from './components/CalendarHeader/CalendarHeader';

import {
  CommandControlType,
  TimeBlock,
  Event as EventType,
  Thread,
  EventRecipientType,
  EventRecipient,
  Draft,
  DraftCalendarInvite,
  Calendar as CalendarType,
  CalendarEvent,
} from '@src/types';
import CalendarIcon from '@src/components/Icons/Calendar.svg';

import './Calendar.scss';
import { stringToTimeBlock } from '@src/lib/datetime';
import { parseAddress } from '@src/lib/address';
import { isEqual, isEmpty, map, flatten, forEach } from 'lodash';
import { getEventsForDate } from '@src/lib/event';
import KeyboardSymbol from '@src/components/Base/KeyboardSymbol/KeyboardSymbol';
import { recordEvent, UserEvents } from '@src/lib/analytics';
import { logAmplitudeEvent, AmplitudeEvents } from '@src/lib/amplitude-analytics';
import { CalendarEventRangeMeta, CalendarEventDisplayScope } from '@src/ducks/calendars';

export interface Props {
  isDefault: boolean;
  onHide: () => void;
}

export interface StateProps {
  selectedThread?: Thread;
  draft?: Draft;
  account?: string;
  calendars: {
    [account: string]: CalendarType[];
  };
  calendarEvents: {
    [account: string]: CalendarEvent[];
  };
  readOnly?: boolean;
}

export interface DispatchProps {
  addCalendarInviteToDraft: (timeblock: TimeBlock) => void;
  listCalendars: (accounts?: string) => void;
  toggleCalendarHide: (calendar: string) => void;
  listCalendarEvents: (range: CalendarEventRangeMeta) => void;
}

interface State {
  startDate: Moment;
  endDate: Moment;
  selectedTimeBlock?: TimeBlock;
  showEventDetail: boolean;
  hiddenCalendars: string[];
  calendarEvents: { [date: string]: CalendarEvent[] };
  calendars?: CalendarType[];
  columnWidth: number;
  windowWidth: number;
  windowHeight: number;
  dateRange: Moment[];
}

const LEFT_SIDE_WIDTH = 300;
const RIGHT_PADDING = 20 + 70;
const MIN_POPOVER_WIDTH = 800;
const START_HOUR = 0;
const END_HOUR = 24;

class Calendar extends React.PureComponent<Props & StateProps & DispatchProps, State> {
  private popover: ControlPopover;
  private input: HTMLInputElement;
  private timeout: NodeJS.Timer;
  private calendarNode: HTMLDivElement;

  constructor(props: Props & StateProps & DispatchProps) {
    super(props);

    const startDate = props.draft?.calendarInvite ?
      moment(props.draft.calendarInvite.start) : moment().startOf('day');
    const endDate = startDate.clone().add(4, 'days');
    const dateRange = this.generateDateRange(startDate, endDate);
    const calendars = props.account ? props.calendars[props.account] :
      flatten(map(props.calendars, c => c));
    const hiddenCalendars = calendars ? calendars.filter(c => c.hidden).map(c => c.id) : [];

    this.state = {
      startDate,
      endDate,
      showEventDetail: props.draft && props.draft.calendarInvite ? true : false,
      selectedTimeBlock: props.draft
        ? this.convertCalendarInviteToTimeBlock(props.draft.calendarInvite)
        : undefined,
      hiddenCalendars,
      calendarEvents: this.generateCalendarEvents(dateRange, props, hiddenCalendars),
      columnWidth: Math.floor(((Math.max(window.innerWidth, MIN_POPOVER_WIDTH) * .8)
        - LEFT_SIDE_WIDTH - RIGHT_PADDING) / 5),
      windowWidth: window.innerWidth,
      windowHeight: window.innerHeight,
      dateRange,
      calendars,
    };
  }

  componentDidMount() {
    const { listCalendars, account, listCalendarEvents } = this.props;
    const { startDate, endDate } = this.state;

    // Always fetch calendars on mount
    listCalendars(account);

    listCalendarEvents({
      start: startDate.toDate(),
      end: endDate.toDate(),
      scope: CalendarEventDisplayScope.Command,
    });

    // Track Calendar Open
    recordEvent(UserEvents.CalendarViewed);

    this.timeout = global.setTimeout(
      () => {
        const node = this.calendarNode &&
          this.calendarNode.getElementsByClassName('selected-event')[0];
        if (node) {
          node.scrollIntoView();
        }

        if (this.input) {
          this.input.focus();
        }
      },
      0,
    );
    window.addEventListener('resize', this.handleResize);
  }

  componentWillUnmount() {
    if (this.timeout) {
      clearTimeout(this.timeout);
    }

    window.removeEventListener('resize', this.handleResize);
  }

  UNSAFE_componentWillReceiveProps(nextProps: Props & StateProps & DispatchProps) {
    const newState: any = {};
    const { dateRange } = this.state;

    const nextDraft = nextProps.draft;
    const thisDraft = this.props.draft;

    if (!!nextDraft && !!thisDraft &&
      thisDraft !== nextDraft &&
      isEqual(nextDraft?.calendarInvite, thisDraft?.calendarInvite)
    ) {
      newState.selectedTimeBlock = nextProps.draft
        ? this.convertCalendarInviteToTimeBlock(nextProps.draft.calendarInvite)
        : undefined;
    }

    if (nextProps.calendars && nextProps.calendars !== this.props.calendars) {
      let calendars;
      if (nextProps.account) {
        calendars = nextProps.calendars[nextProps.account];
      } else {
        calendars = Object.keys(nextProps.calendars).length
          ? flatten(map(nextProps.calendars, c => c))
          : undefined;
      }
      newState.calendars = calendars;
      newState.hiddenCalendars = calendars ?
        calendars.filter(c => !!c.hidden).map(c => c.id) : undefined;
    }

    if ((nextProps.calendarEvents && this.props.calendarEvents !== nextProps.calendarEvents) ||
      newState.hiddenCalendars) {
      newState.calendarEvents = this.generateCalendarEvents(
        dateRange,
        nextProps,
        newState.hiddenCalendars,
      );
    }

    if (!isEmpty(newState)) {
      this.setState(newState);
    }
  }

  handleResize = () => {
    this.setState({
      columnWidth: Math.floor(((Math.max(window.innerWidth, MIN_POPOVER_WIDTH) * .8)
        - LEFT_SIDE_WIDTH - RIGHT_PADDING) / 5),
      windowWidth: window.innerWidth,
      windowHeight: window.innerHeight,
    });
  }

  convertCalendarInviteToTimeBlock = (
    calInvite?: DraftCalendarInvite | null,
  ): TimeBlock | undefined => {
    if (!calInvite) {
      return undefined;
    }

    return {
      startTime: moment(calInvite.start),
      endTime: moment(calInvite.end),
      event: {
        name: calInvite.name,
        notes: calInvite.notes,
        location: calInvite.location,
        recipients: calInvite.recipients.map(r => ({
          type: EventRecipientType.Contact,
          displayName: r,
          identifier: parseAddress(r).email,
        })),
      },
    };
  }

  handleRef = (ref: ControlPopover) => {
    this.popover = ref;
  }

  handleUpdateTimeBlock = (tb: TimeBlock) => {
    const { selectedTimeBlock } = this.state;
    const { draft, readOnly } = this.props;
    const timeblock = tb;

    if (readOnly) {
      return;
    }

    // first time setting selectedTimeBlock
    if (!selectedTimeBlock && draft) {
      timeblock.event.recipients = draft.cc.concat(draft.to).map(e => ({
        type: EventRecipientType.Contact,
        displayName: e,
        identifier: e,
      }));
    }

    this.setState({ selectedTimeBlock: timeblock, showEventDetail: true });
  }

  generateCalendarEvents = (
    dateRange: Moment[],
    props: Props & StateProps & DispatchProps,
    forceHiddenCalendars?: string[],
  ) => {
    const events = {};
    const hiddenCalendars = forceHiddenCalendars || this.state.hiddenCalendars;
    const { calendarEvents } = props;

    let accountEvents: CalendarEvent[] = [];
    forEach(calendarEvents, (val: CalendarEvent[]) => {
      accountEvents = accountEvents.concat(val);
    });

    dateRange.forEach((d) => {
      events[d.format('MM-DD-YYYY')] = getEventsForDate(
        d,
        accountEvents,
        START_HOUR,
        END_HOUR,
        hiddenCalendars,
      );
    });

    return events;
  }

  renderFooter = () => {
    const { readOnly } = this.props;

    let description = 'Click on your calendar to select a meeting time';
    if (readOnly) {
      description  = 'View the events that are on your calendar';
    }

    return (
      <div className="control-footer">
        <div>
          {description}
        </div>
        <div>
          <KeyboardSymbol
            character="âŽ‹ (esc)"
          />
          <span>to dismiss</span>
        </div>
      </div>
    );
  }

  handleStartDateChange = (newStartDate: Moment) => {
    const { listCalendarEvents } = this.props;
    const startDate = newStartDate.clone();
    const endDate = newStartDate.clone().add(4, 'days');
    const dateRange = this.generateDateRange(startDate, endDate);

    listCalendarEvents({
      start: startDate.toDate(),
      end: endDate.toDate(),
      scope: CalendarEventDisplayScope.Command,
    });

    this.setState({
      startDate,
      endDate,
      calendarEvents: this.generateCalendarEvents(dateRange, this.props),
      dateRange,
    });
  }

  handleEventUpdate = (property: string, value: string | Moment | EventRecipient[]) => {
    const { selectedTimeBlock, startDate, endDate } = this.state;
    if (selectedTimeBlock) {
      const event: EventType = selectedTimeBlock.event;

      if (typeof value === 'string') {
        event[property] = value;
      } else if (property === 'recipients') {
        event[property] = value as EventRecipient[];
      }

      this.setState({
        selectedTimeBlock: {
          startTime:
          property === 'startTime' &&
            startDate
              .clone()
              .startOf('day')
              .isSameOrBefore(value as Moment) &&
            endDate
              .clone()
              .endOf('day')
              .isSameOrAfter(value as Moment) &&
            selectedTimeBlock.endTime.isAfter(value as Moment)
            ? (value as Moment)
            : selectedTimeBlock.startTime,
          endTime:
          property === 'endTime' &&
            startDate
              .clone()
              .startOf('day')
              .isSameOrBefore(value as Moment) &&
            endDate
              .clone()
              .endOf('day')
              .isSameOrAfter(value as Moment) &&
            selectedTimeBlock.startTime.isBefore(value as Moment)
            ? (value as Moment)
            : selectedTimeBlock.endTime,
          event,
        },
      });
    }
  }

  handleAdd = () => {
    if (this.state.selectedTimeBlock) {
      this.props.addCalendarInviteToDraft(this.state.selectedTimeBlock);
      this.popover.hide();
      this.props.onHide();
      logAmplitudeEvent(AmplitudeEvents.SendCalendarInvite);
    }
  }

  handleCalendarClick = (calendar: string) => {
    const { toggleCalendarHide } = this.props;

    toggleCalendarHide(calendar);
  }

  handleInputRef = (ref: HTMLInputElement) => {
    this.input = ref;
  }

  handleCalendarNodeRef = (ref: HTMLDivElement) => {
    this.calendarNode = ref;
  }

  handleInputSubmit = () => {
    const { draft } = this.props;
    const selectedTimeBlock = stringToTimeBlock(this.input.value);

    if (draft) {
      selectedTimeBlock.event.recipients = draft.cc.concat(draft.to).map(e => ({
        type: EventRecipientType.Contact,
        displayName: e,
        identifier: e,
      }));
    }

    const startDate = selectedTimeBlock.startTime.clone();
    const endDate = selectedTimeBlock.startTime.clone().add(4, 'days');
    const dateRange = this.generateDateRange(startDate, endDate);
    this.setState(
      {
        selectedTimeBlock,
        showEventDetail: true,
        startDate,
        endDate,
        dateRange,
        calendarEvents: this.generateCalendarEvents(dateRange, this.props),
      },
      () => {
        if (this.calendarNode) {
          const node = this.calendarNode.getElementsByClassName('selected-event')[0];
          if (node) {
            node.scrollIntoView();
          }
        }
      },
    );
  }

  generateDateRange = (startDate: Moment, endDate: Moment) => {
    const start = startDate.clone().startOf('day');
    const end = endDate.clone().startOf('day');
    const dateRange = [];

    while (start.isSameOrBefore(end)) {
      dateRange.push(start.clone());
      start.add(1, 'day');
    }

    return dateRange;
  }

  renderMain = () => {
    const { draft, readOnly } = this.props;
    const {
      startDate,
      endDate,
      selectedTimeBlock,
      showEventDetail,
      hiddenCalendars,
      calendars,
      columnWidth,
      calendarEvents,
      dateRange,
    } = this.state;

    let title = !!draft?.calendarInvite ? 'Update Calendar Invite' : 'Add Calendar Invite';
    if (readOnly) {
      title = 'Your Calendar Events';
    }

    return (
      <div className="main">
        <div className="left" style={{ width: LEFT_SIDE_WIDTH }}>
          {(!selectedTimeBlock || !showEventDetail) && (
            <div className="month-calendars">
              {!readOnly && (
                <form
                  onSubmit={this.handleInputSubmit}
                >
                  <input
                    dir="auto"
                    className="control-input"
                    placeholder="Meeting at 11am next Friday"
                    ref={this.handleInputRef}
                    autoFocus
                  />
                </form>
              )}
              {calendars && (
                <CalendarList
                  calendars={calendars}
                  onCalendarClick={this.handleCalendarClick}
                  hiddenCalendars={hiddenCalendars}
                />
              )}
            </div>
          )}
          {selectedTimeBlock &&
            showEventDetail && (
              <EventDetail
                name={selectedTimeBlock.event.name}
                recipients={selectedTimeBlock.event.recipients}
                notes={selectedTimeBlock.event.notes}
                onUpdate={this.handleEventUpdate}
                startTime={selectedTimeBlock.startTime}
                endTime={selectedTimeBlock.endTime}
                onAdd={this.handleAdd}
                ctaText={title}
                location={selectedTimeBlock.event.location}
              />
            )}
        </div>
        <div className="right" ref={this.handleCalendarNodeRef}>
          <CalendarHeader
            startDate={startDate}
            endDate={endDate}
            onStartDateChange={this.handleStartDateChange}
            columnWidth={columnWidth}
            dateRange={dateRange}
            events={calendarEvents}
          />
          <MultiDayView
            startDate={startDate}
            endDate={endDate}
            startHour={Math.min(startDate.hour(), START_HOUR)}
            endHour={END_HOUR}
            selectedTimeBlock={selectedTimeBlock}
            onUpdateTimeBlock={this.handleUpdateTimeBlock}
            hiddenCalendars={hiddenCalendars}
            columnWidth={columnWidth}
            dateRange={dateRange}
            events={calendarEvents}
            readOnly={!draft}
          />
        </div>
      </div>
    );
  }

  render() {
    const { onHide, readOnly, draft } = this.props;
    const { windowWidth, windowHeight } = this.state;

    let title = !!draft?.calendarInvite ? 'Update Calendar Invite' : 'Add Calendar Invite';
    if (readOnly) {
      title = 'Your Calendar Events';
    }

    return (
      <ControlPopover
        className="calendar-popover"
        type={CommandControlType.Calendar}
        icon={<CalendarIcon width="14px" height="14px" />}
        heading={title}
        options={[]}
        onHide={onHide}
        ref={this.handleRef}
        renderMain={this.renderMain}
        renderFooter={this.renderFooter}
        placeholder="Standup at 11pm on Friday"
        style={{
          minWidth: MIN_POPOVER_WIDTH,
          width: windowWidth * .8,
          height: windowHeight * .8,
          top: (windowHeight * .5) - (windowHeight * .4),
        }}
        showOnMount
        hideInput
      />
    );
  }
}

export default Calendar;
