import React from 'react';
import { History } from 'history';
import { last, filter, startCase } from 'lodash';

import {
  Thread,
  ContactGroup,
  CommandControlType,
  MessageBody,
  MailboxType,
  Mailbox,
  Account,
  ThreadBox,
  Draft,
  Action,
  ThreadCommitMeta,
  Resource,
  MailCount,
  Preferences,
  UnsubscribeBehavior,
  Me,
  ShareLink,
  KeyCode,
  BlockContactActionType,
  Features,
  OnboardingType,
  ThemeSetting,
} from '@src/types';
import {
  RightSidebarTab, Page,
} from '@src/types/rightSidebar';

import { HotkeyAction, getHotkey, checkNextHotkeyChange } from '@src/lib/hotkeys';
import { parseAddress, addressToEmail } from '@src/lib/address';
import { threadMoveUndo, threadCommitsSubmit, threadDisableTrack } from '@src/ducks/threads';

import './Threads.scss';

import ThreadList from './components/ThreadList';
import ThreadDetail from '@src/components/ThreadDetail';
import LeftSidebar from '@src/components/LeftSidebar';
import RightSidebar from '@src/components/RightSidebar';
import CommandControl from '@src/components/CommandControl';
import InboxZero from './components/InboxZero/InboxZero';
import Toast from '@src/components/Base/Toast';
import { recordEvent, UserEvents } from '@src/lib/analytics';
import { UpgradePrompt } from '@src/types/billing';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
// tslint:disable-next-line:max-line-length
import BlockConfirm, { Props as BlockConfirmProps } from '@src/components/Base/BlockConfirm/BlockConfirm';
import OnboardingFooter from '@src/components/Banner/OnboardingFooter/OnboardingFooter';
import { commandControlShow } from '@src/ducks/commandControl';
import browserHistory from '@src/lib/browserHistory';
import { onboardingPatch } from '@src/ducks/me';
import { draftCreate, draftDelete, draftSendNow, draftUpdate } from '@src/ducks/drafts';
import { createMailToDraft, isDraftSending } from '@src/lib/draft';
import GettingStarted from '@src/components/Onboarding/components/GettingStarted';
import KeyboardSymbol from '@src/components/Base/KeyboardSymbol/KeyboardSymbol';
import { isElectronMac } from '@src/lib/electron';
import AppleIconWhite from '@src/components/Icons/AppleIconWhite.svg';
import { hotkeyUpdate } from '@src/ducks/hotkeys';
import { filterDelete, filterSet, filterBlockCreate } from '@src/ducks/filters';
import { Filter } from '@src/types/filters';
import { isDraftBox } from '@src/lib/mailbox';
import { toastSet, ConfirmationError } from '@src/ducks/toasts';
import { getOnboardingActions } from '@src/lib/OnboardingDispatcher';
import { appThemeSet } from '@src/ducks/app';
import { leftSidebarToggle } from '@src/ducks/leftSidebar';
import TeamJoined from '@src/components/Onboarding/components/TeamJoined';
import { rightSidebarTabSelect } from '@src/ducks/rightSidebar';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';
import ContactSupport from '@src/components/ContactSupport';

export interface Props {
  history?: History;
  contactGroups: { [id: string]: ContactGroup; };
  selectedThread?: Thread;
  selectedIds: Map<string, boolean>;
  bodies: MessageBody[];
  sessionId: number;
  selectedMailbox: Mailbox;
  accounts: Account[];
  threads: Resource<Thread[]>;
  selectedContact?: string;
  commandControlType?: CommandControlType;
  draft?: Draft;
  sendingDraft?: Draft;
  mailCount?: MailCount;
  unsubscribeLink?: string;
  preferences?: Preferences;
  me: Resource<Me>;
  shareLink?: ShareLink;
  initialSyncing?: boolean;
  upgradePrompts: UpgradePrompt;
  closedBanners: { [id: string]: boolean };
  rightSidebarCollapsed: boolean;
  rightSidebarTab?: RightSidebarTab;
  selectedAccount?: string;
  schedulingUrl?: string;
  hotkeyActions?: { [key: string]: number };
  blockFilter?: Filter;
  features: Features;

  showCommandCenterOnboarding?: boolean;
  showCompleteOnboarding?: boolean;
  showCreateDraftOnboarding?: boolean;
  showExploreModal?: boolean;
  showInviteTeamOnboarding?: boolean;
  showMentionsTabOnboarding?: boolean;
  rsbOnboardingType?: OnboardingType;
  showTeamInviteJoinedPrompt?: boolean;
}

export interface DispatchProps {
  dispatchAction: (action: Action<any, any>) => void;
  selectContact: (email: string) => void;
  submitCommitMeta: (commitMeta: ThreadCommitMeta) => void;
  updateSelections: (selections: Map<string, boolean>) => void;
  closeBanner: (banner: string) => void;
  collapseRightSidebar: (collapse: boolean) => void;
  updateHotkey: (hotkey: HotkeyAction) => void;
}

export interface State {
  confirmProps?: ConfirmProps;
  blockConfirmProps?: BlockConfirmProps;
}

class Threads extends React.PureComponent<Props & DispatchProps, State> {
  constructor(props: Props & DispatchProps) {
    super();

    const { selectedThread } = props;
    const lastMessage = selectedThread && selectedThread.messages.length ?
      last(filter(selectedThread.messages, message => !message.sent)) : undefined;
    const sender = lastMessage && lastMessage.from;
    if (sender) {
      props.selectContact(parseAddress(sender).email);
    }

    this.state = {};
  }

  UNSAFE_componentWillMount() {
    window.addEventListener('focus', this.handleFocus);
  }

  componentWillUnmount() {
    window.removeEventListener('focus', this.handleFocus);
  }

  componentDidUpdate(oldProps: Props & DispatchProps) {
    const oldActions = oldProps.hotkeyActions || {};
    const newActions = this.props.hotkeyActions || {};
    this.handleHotkeyActionsChange(oldActions, newActions);

    if (document.activeElement && document.activeElement.tagName === 'BODY') {
      this.handleFocus();
    }

    const { selectedThread, selectContact, selectedContact } = oldProps;
    const lastMessage = selectedThread && selectedThread.messages.length ?
      last(filter(selectedThread.messages, message => !message.sent)) : undefined;
    const sender = lastMessage && lastMessage.from;
    const email = sender && parseAddress(sender).email;
    if (email && (!selectedContact || selectedThread !== this.props.selectedThread)) {
      selectContact(email);
    }

    // If CommandControl was dismissed, then reset focus back to threads, unless draft is set
    if (!this.props.commandControlType && oldProps.commandControlType) {
        // HACK(SHIN): Do not do anything if contenteditable or search has focus
      if (document.activeElement) {
        if (document.activeElement.getAttribute('contenteditable')) {
          return;
        }
        if (document.activeElement.getAttribute('type') === 'search') {
          return;
        }
      }

      if (this.props.draft) {
        const contentEditableElement = document.querySelector('.compose-editable');
        const composeElement = contentEditableElement && contentEditableElement.parentElement;
        if (contentEditableElement instanceof HTMLElement && composeElement) {
          return;
        }

        return;
      }
      this.handleFocus();
    }

    // Identify if box type changed to refocus threads list
    const oldMailbox = this.props.selectedMailbox;
    const newMailbox = oldProps.selectedMailbox;
    const boxTypeChanged = (
      newMailbox && (!oldMailbox || oldMailbox && oldMailbox.type !== newMailbox.type));

    // Return focus on draft delete or mailbox change
    if (boxTypeChanged || (!this.props.draft && oldProps.draft)) {
      this.handleFocus();
    }
  }

  handleHotkeyActionsChange = (
    oldActions: { [action: string]: number },
    newActions: { [action: string]: number },
  ) => {
    const {
      dispatchAction,
      selectedThread,
      preferences,
      submitCommitMeta,
      blockFilter,
      unsubscribeLink,
    } = this.props;

    // Handle DarkMode toggle
    if (checkNextHotkeyChange(HotkeyAction.ToggleDarkMode, newActions, oldActions)) {
      const theme = document.body.getAttribute('theme') === 'theme-dark'
        ? ThemeSetting.Light
        : ThemeSetting.Dark;
      dispatchAction(appThemeSet(theme));
      return;
    }

    // Handle Unblock toggle
    if (checkNextHotkeyChange(HotkeyAction.UnblockSender, newActions, oldActions)) {
      if (blockFilter) {
        dispatchAction(filterDelete(blockFilter.id));
        return;
      }
    }

    // Handle Disable Tracks by first showing confirm modal
    if (checkNextHotkeyChange(HotkeyAction.DisableTracks, newActions, oldActions)) {
      if (selectedThread) {
        const disableTracksConfirmProps: ConfirmProps = {
          title: 'Disable Tracking for this Conversation?',
          message: 'Tracking will be turned off this conversation and no new activity will be recorded. This is not reversible. Would you like to continue?',
          confirmCopy: 'Ok',
          cancelCopy: 'Cancel',
          onConfirm: () => {
            this.setState({ confirmProps: undefined }, () => {
              this.props.dispatchAction(threadDisableTrack(selectedThread.id));
            });
          },
          onCancel: () => {
            this.setState({ confirmProps: undefined });
          },
        };

        this.setState({ confirmProps: disableTracksConfirmProps });
        return;
      }
    }

    // Handle Block toggle
    if (checkNextHotkeyChange(HotkeyAction.BlockSender, newActions, oldActions)) {
      if (selectedThread) {
        const senderLastMessage = last(selectedThread.messages);
        if (!senderLastMessage) {
          return;
        }

        if (!preferences) {
          return;
        }

        // Handle setting block
        const sender = addressToEmail(senderLastMessage.from).toLowerCase();
        const blockConfirmProps: BlockConfirmProps = {
          title: `Block ${sender}`,
          // tslint:disable-next-line:max-line-length
          message: `Messages received from this sender will be blocked and automatically moved to ${startCase(preferences.blockContactAction)}.`,
          hasUnsubscribe: !!unsubscribeLink,
          onUnsubscribeClick: () => {
            // Handle unsubscribe
            this.setState(
              { blockConfirmProps: undefined },
              () => this.handleUnsubscribe(),
            );
            logAmplitudeEvent(AmplitudeEvents.Unsubscribe);
          },
          onConfirm: () => {
            // Block filter
            dispatchAction(filterSet(filterBlockCreate(sender)));

            // Handle sending to proper destination box
            switch (preferences.blockContactAction) {
              case BlockContactActionType.Archive:
                const blockArchiveCommit: ThreadCommitMeta = {
                  singleResource: selectedThread.id,
                  box: ThreadBox.Archive,
                };
                submitCommitMeta(blockArchiveCommit);
                break;
              case BlockContactActionType.Trash:
                const blockTrashCommit: ThreadCommitMeta = {
                  singleResource: selectedThread.id,
                  box: ThreadBox.Trash,
                };
                submitCommitMeta(blockTrashCommit);
                break;
              default: break;
            }
            logAmplitudeEvent(AmplitudeEvents.BlockSender);

            // Remove confirmProps
            this.setState({ blockConfirmProps: undefined });
          },
          onCancel: () => {
            this.setState({ blockConfirmProps: undefined });
          },
        };

        // Set ConfirmProps to confirm block
        this.setState({ blockConfirmProps });
      }
    }
  }

  handleFocus = () => {
    if (document.activeElement && document.activeElement.tagName.toLowerCase() !== 'body') {
      return;
    }

    const { selectedThread } = this.props;
    const container = document.getElementsByClassName('infinite-thread-list');
    const element = selectedThread && document.getElementById(selectedThread.id);

    if (element) {
      element.focus();
    } else if (container && container.length) {
      (container[0] as HTMLElement).focus();
    }
  }

  // Ability to handle moves for all threads
  handleThreadMove = (box: ThreadBox) => {
    this.props.submitCommitMeta({ box });
  }

  handleTrialExpiredClose = () => {
    this.props.closeBanner('TrialExpired');
  }

  handleThreadDelete = () => {
    const confirmProps: ConfirmProps = {
      title: 'Permanently delete this thread?',
      message: 'Are you sure? This cannot be undone.',
      confirmCopy: 'Delete',
      cancelCopy: 'Cancel',
      onConfirm: () => {
        this.handleThreadMove(ThreadBox.Trash);
        this.setState({ confirmProps: undefined });
      },
      onCancel: () => this.setState({ confirmProps: undefined }),
    };

    this.setState({ confirmProps });
  }

  handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    const {
      history,
      selectedMailbox,
      selectedThread,
      dispatchAction,
      draft,
      submitCommitMeta,
      unsubscribeLink,
      preferences,
      rightSidebarCollapsed,
      collapseRightSidebar,
      selectedAccount,
      rightSidebarTab,
      blockFilter,
    } = this.props;
    let handled = false;

    if (document.activeElement && (document.activeElement.tagName === 'INPUT' ||
      document.activeElement.tagName === 'TEXTAREA')) {
      return;
    }

    const hotkey = getHotkey(e);
    switch (hotkey && hotkey.name || e.keyCode) {
      case HotkeyAction.Search:
        handled = true;
        dispatchAction(hotkeyUpdate(HotkeyAction.Search));
        break;
      case HotkeyAction.ShowLinks:
        if (!!selectedThread) {
          dispatchAction(rightSidebarTabSelect({
            rsbTab: RightSidebarTab.LinkExtractor,
          }));
          handled = true;
        }
        break;
      case HotkeyAction.KeyboardShortcutGuide:
        if (hotkey && hotkey.action && rightSidebarTab) {
          dispatchAction(hotkey.action(rightSidebarTab));
          handled = true;
        }
        break;
      case HotkeyAction.NewDraft:
        if (hotkey && hotkey.action) {
          dispatchAction(hotkey.action);
          handled = true;
        }
        break;
      // UI Toggle
      case HotkeyAction.ToggleLSB:
        handled = true;
        dispatchAction(leftSidebarToggle(true));
        break;
      // UI Toggle
      case HotkeyAction.ToggleRSB:
        handled = true;
        collapseRightSidebar(!rightSidebarCollapsed);
        break;

      // Drafts
      case HotkeyAction.Reply:
      case HotkeyAction.ReplyAll:
      case HotkeyAction.Forward:
      case HotkeyAction.ReadLater:
      case HotkeyAction.Label:
      case HotkeyAction.ReplyBCC: {

        // Do not trigger if last message recipients is less than two
        if (hotkey?.name === HotkeyAction.ReplyBCC) {
          const lastMessage = selectedThread && selectedThread.messages.length ?
            last(filter(selectedThread.messages, message => !message.sent)) : undefined;
          if (!lastMessage || lastMessage.to.concat(lastMessage.cc).length < 2) {
            return;
          }
        }

        if (hotkey && hotkey.action && selectedThread) {
          dispatchAction(hotkey.action);
          handled = true;
        }
        break;
      }

      // Navigation
      case HotkeyAction.NavigateInbox:
      case HotkeyAction.NavigateStarred:
      case HotkeyAction.NavigateReadLater:
      case HotkeyAction.NavigateArchive:
      case HotkeyAction.NavigateTrash:
      case HotkeyAction.NavigateFollowUp:
      case HotkeyAction.NavigateSent:
      case HotkeyAction.NavigateSendLater:
      case HotkeyAction.NavigateDraft:
        if (history && selectedMailbox && hotkey && hotkey.route) {
          history.push(hotkey.route(selectedMailbox, selectedAccount));
          handled = true;
        }
        break;
      case HotkeyAction.MessageTemplates:
      case HotkeyAction.CalendarInvite:
        handled = true;
        if (hotkey && hotkey.action && draft) {
          dispatchAction(hotkey.action);
          handled = true;
        }
        break;
      case HotkeyAction.Inbox:
        if (selectedThread) {
          this.handleThreadMove(ThreadBox.Inbox);
          handled = true;
        }
        break;
      case HotkeyAction.Archive:
        if (this.props.rsbOnboardingType === OnboardingType.Archive) {
          dispatchAction(onboardingPatch({
            skipped: false,
            type: { steps: { [OnboardingType.Archive]: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepSeven, { skipped: false });
        }
        getOnboardingActions().next(OnboardingType.Archive);
        if (selectedThread) {
          this.handleThreadMove(ThreadBox.Archive);
        }
        break;
      case HotkeyAction.CancelFollowup: // Handles cancelling follow up reminders via hotkey
        if (draft && draft.waiting) {
          dispatchAction(draftUpdate({ ...draft, waiting: null }));
          handled = true;
        } else if (selectedThread && selectedThread.waiting) {
          const commitMeta = { singleResource: selectedThread.id, waiting: null };
          dispatchAction(threadCommitsSubmit(commitMeta));
          handled = true;
        }

        // Show followup removal
        if (handled) {
          dispatchAction(
            toastSet(new ConfirmationError(
              'Your followup has been canceled!',
              true,
            )),
          );
        }
        break;
      case HotkeyAction.Trash:
      case KeyCode.ExtendedDelete:
      case KeyCode.Delete:
        // Enable deletions of drafts using normal delete when mailbox is draft type
        if (selectedMailbox && isDraftBox(selectedMailbox.type) && draft) {
          const discardDraftIds = Array.from(this.props.selectedIds.keys());
          if (this.props.selectedMailbox &&
            this.props.selectedMailbox.type === MailboxType.Drafts &&
            discardDraftIds.length
          ) {
            this.handleSelectedDraftsDelete(discardDraftIds);
          }
          handled = true;
          break;
        }
        if (selectedMailbox && selectedMailbox.type === MailboxType.Trash &&
          selectedThread && selectedThread.box === ThreadBox.Trash) {
          this.handleThreadDelete();
          handled = true;
        } else if (selectedThread && selectedThread.box !== ThreadBox.Trash) {
          this.handleThreadMove(ThreadBox.Trash);
          handled = true;
        }

        break;
      case HotkeyAction.Discard:
        // Enable deletions of drafts using normal delete when mailbox is draft type
        if (
          selectedMailbox &&
          [MailboxType.Drafts, MailboxType.SendLater].indexOf(selectedMailbox.type) > -1 &&
          draft
        ) {
          const deleteDraftIds = Array.from(this.props.selectedIds.keys());
          if (this.props.selectedMailbox &&
            this.props.selectedMailbox.type === MailboxType.Drafts &&
            deleteDraftIds.length
          ) {
            handled = true;
            this.handleSelectedDraftsDelete(deleteDraftIds);
          }
        } else if (draft && !isDraftSending(draft)) {
          handled = true;
          this.handleSelectedDraftsDelete([draft.id]);
        }

        break;
      case HotkeyAction.Comment:
        this.props.updateHotkey(HotkeyAction.Comment);
        handled = true;
        break;
      case HotkeyAction.Undo:
        if (this.props.rsbOnboardingType === OnboardingType.Undo) {
          dispatchAction(onboardingPatch({
            skipped: false,
            type: { steps: { [OnboardingType.Undo]: true } },
          }));
          logAmplitudeEvent(AmplitudeEvents.TourStepEight, { skipped: false });
        }
        getOnboardingActions().next(OnboardingType.Undo);
        if (selectedMailbox) {
          this.props.dispatchAction(threadMoveUndo(selectedMailbox));
        }
        break;
      case HotkeyAction.ToggleUnread:
        if (selectedThread) {
          submitCommitMeta({ read: !selectedThread.read });
        }
        handled = true;
        break;
      case HotkeyAction.ToggleStar:
        if (selectedThread) {
          submitCommitMeta({ starred: !selectedThread.starred });
        }
        handled = true;
        break;
      case HotkeyAction.NavigationCenter:
        this.props.dispatchAction(commandControlShow(CommandControlType.Navigation));
        handled = true;
        break;
      case HotkeyAction.ToggleCommandControl:
        if (hotkey && hotkey.action) {
          const type = draft && !isDraftSending(draft)
            ? CommandControlType.Draft
            : CommandControlType.Thread;
          this.props.dispatchAction(hotkey.action(type));
          handled = true;
        }
        break;
      case HotkeyAction.UnblockSender:
      case HotkeyAction.BlockSender:
        if (this.props.rsbOnboardingType === OnboardingType.Block) {
          this.props.dispatchAction(onboardingPatch({
            skipped: false,
            type: { steps: { [OnboardingType.Block]: true }  },
          }));
        }
        getOnboardingActions().next(OnboardingType.Block);
        if (blockFilter) {
          dispatchAction(hotkeyUpdate(HotkeyAction.UnblockSender));
        } else {
          dispatchAction(hotkeyUpdate(HotkeyAction.BlockSender));
        }
        return;

      case HotkeyAction.MuteConversation:
      case HotkeyAction.UnmuteConversation:
        if (selectedThread) {
          const muteCommitMeta = {
            singleResource: selectedThread.id,
            muted: !selectedThread.muted,
          };
          submitCommitMeta(muteCommitMeta);
        }
        return;

      case HotkeyAction.Unsubscribe:
        // Handle unsubscribe
        if (selectedThread && unsubscribeLink && preferences) {
          this.handleUnsubscribe();
          handled = true;
        }
        break;
      case HotkeyAction.CopySchedulingUrl:
        this.props.dispatchAction(commandControlShow(CommandControlType.SchedulingPages));
        handled = true;
        break;

      // send immediately with double cmd/ctrl+enter
      case HotkeyAction.Send:
      case HotkeyAction.SendImmediately:
        if (this.props.sendingDraft) {
          this.props.dispatchAction(draftSendNow(this.props.sendingDraft));
        }
        break;

      default:
        break;
    }

    if (handled) {
      e.preventDefault();
      e.stopPropagation();
    }
  }

  handleUndo = () => {
    const { selectedMailbox } = this.props;
    if (selectedMailbox) {
      this.props.dispatchAction(threadMoveUndo(selectedMailbox));
    }
  }

  handleUnsubscribe = () => {
    const { selectedThread, unsubscribeLink, preferences, submitCommitMeta } = this.props;
    if (selectedThread && unsubscribeLink && preferences) {
      window.open(unsubscribeLink, '_blank');
      recordEvent(UserEvents.Unsubscribed);
      switch (preferences.unsubscribeBehavior) {
        case UnsubscribeBehavior.Archive:
          submitCommitMeta({
            singleResource: selectedThread.id,
            box: ThreadBox.Archive,
          });
          break;
        case UnsubscribeBehavior.Trash:
          submitCommitMeta({
            singleResource: selectedThread.id,
            box: ThreadBox.Trash,
          });
          break;
        default:
          break;
      }
    }
  }

  handleStartOnboarding = () => {
    const { dispatchAction, accounts, preferences, features } = this.props;
    dispatchAction(onboardingPatch({ skipped: false, type: { exploreModal: true } }));
    if (!preferences) {
      return;
    }

    const mailToString = 'Poly Bot <onboarding@polymail.io>?subject= '
    + 'Send your first message with tracking!&body=Hello! \n\n'
    + 'Polymail automatically gives you insights on the emails you send!'
    + '\n\n'
    + 'Try clicking send to see how it works!';
    browserHistory.push('/mail');
    const draft = createMailToDraft(accounts[0], preferences, features, mailToString);
    dispatchAction(draftCreate(draft));
  }

  handleSelectedDraftsDelete = (ids: string[]) => {
    const draftIds = ids;
    const title = draftIds.length > 1
      ? `Delete ${draftIds.length} drafts?`
      : 'Delete this draft?';
    const message = draftIds.length > 1
      ? 'These drafts will be permanently deleted.'
      : 'This draft will be permanently deleted.';
    const discardProps: ConfirmProps = {
      title,
      message,
      onConfirm: () => {
        draftIds.forEach((draftId) => {
          this.props.dispatchAction(draftDelete(draftId));
        });
        this.setState({ confirmProps: undefined });
      },
      confirmCopy: 'OK',
      onCancel: () => {
        this.setState({ confirmProps: undefined });
      },
    };

    this.setState({ confirmProps: discardProps });
  }

  renderOnboardingFooter = () => {
    const {
      dispatchAction,
      accounts,
      preferences,
      showCompleteOnboarding,
      showCreateDraftOnboarding,
      showCommandCenterOnboarding,
      showMentionsTabOnboarding,
      showInviteTeamOnboarding,
      features,
    } = this.props;

    const onboardingHandlers = [
      {
        id: 'createDraft',
        title: 'Real-time insights on every message',
        message: 'Track when people open, click' +
         ' and engage with your emails by sending them with Polymail',
        buttonCopy: 'Try it',
        onClick: () => {
          if (preferences) {
            const mailToString = 'Poly Bot <onboarding@polymail.io>?subject= '
            +  'Send your first message with tracking!&body=Hello! \n\n'
            + 'Polymail automatically tracks your messages to notify you when theyve been read.'
            + '\n\n'
            + 'Try clicking send to see how it works!';
            const draft = createMailToDraft(accounts[0], preferences, features, mailToString);
            dispatchAction(draftCreate(draft));
          }
          dispatchAction(onboardingPatch({ skipped: false, type: { createDraft: true } }));
        },
        onSkip: () => {
          dispatchAction(onboardingPatch({
            skipped: true,
            type: {
              createDraft: true,
              sendMessage: true,
              threadDetailTracking: true,
            },
          }));
        },
      },
      {
        id: 'sequences',
        title: 'Automate outreach with Sequences',
        message: 'Reaching out to potential customers or clients?' +
          ' Automate your email outreach and follow-up with Sequences.',
        buttonCopy: 'Try Sequences',
        onClick: () => {
          dispatchAction(onboardingPatch({ skipped: false, type: { sequences: true } }));
          browserHistory.push('/sequences');
        },
        onSkip: () => {
          dispatchAction(onboardingPatch({ skipped: true, type: { sequences: true } }));
        },
      },
      {
        id: 'scheduling',
        title: 'Schedule meetings instantly',
        message: 'Create your personal Polymail calendar page ' +
          ' to share available meeting times and let people book meetings with you instantly.',
        buttonCopy: 'Set Up Calendar',
        onClick: () => {
          browserHistory.push('/settings/calendar');
          dispatchAction(onboardingPatch({ skipped: false, type: { scheduling: true } }));
        },
        onSkip: () => {
          dispatchAction(onboardingPatch({ skipped: true, type: { scheduling: true } }));
        },
      },
      {
        id: 'downloadNativeApps',
        title: 'You\'re all set!',
        message: 'Download the Polymail apps for Mac and iOS'
          + ' for full access to messages and notifications on the go.',
        buttonCopy: 'Download',
        onClick: () => {
          dispatchAction(onboardingPatch({ skipped: false, type: { downloadNativeApps: true } }));
          logAmplitudeEvent(AmplitudeEvents.TourStepThirteen, { skipped: false });
        },
        onSkip: () => {
          dispatchAction(onboardingPatch({ skipped: false, type: { downloadNativeApps: true } }));
          logAmplitudeEvent(AmplitudeEvents.TourStepThirteen, { skipped: true });
        },
      },
      {
        id: 'commandCenter',
        title: 'Get around Polymail faster ',
        messageHtml: (
          <div>
            {'Use '}
              <KeyboardSymbol character={'Cmd'} />
              <KeyboardSymbol character={KeyCode.J} />
            {' to easily navigate and perform actions in Polymail!'}
          </div>
        ),
        buttonCopy: 'Try it',
        onClick: () => {
          dispatchAction(commandControlShow(CommandControlType.Thread));
          dispatchAction(onboardingPatch({ skipped: false, type: { commandCenter: true } }));
        },
        onSkip: () => {
          dispatchAction(onboardingPatch({ skipped: true, type: { commandCenter: true } }));
        },
      },
      {
        id: 'mentions',
        title: 'You\'ve been mentioned!',
        message: 'Someone @mentioned you in a comment! Click the Mentions tab'
        + ' to view conversations you\'ve been @mentioned in',
        buttonCopy: 'See Mentions',
        onClick: () => {
          browserHistory.push('/mentions');
          dispatchAction(onboardingPatch({ skipped: false, type: { mentionsTab: true } }));
        },
        onSkip: () => {
          dispatchAction(onboardingPatch({ skipped: false, type: { mentionsTab: true } }));
        },
      },
      {
        id: 'teamInvite',
        title: 'Just Add Teammates',
        message: 'To get the most out of Polymail, invite some coworkers to start collaborating',
        buttonCopy: 'Invite Teammates',
        onClick: () => {
          dispatchAction(hotkeyUpdate(HotkeyAction.InviteTeamMembers));
        },
        onSkip: () => {
          dispatchAction(onboardingPatch({ skipped: true, type: { inviteTeamMembers: true } }));
        },
      },
    ];
    let props: any|undefined;
    if (showCommandCenterOnboarding) {
      props = onboardingHandlers[4];
    }

    if (showCompleteOnboarding) {
      props = onboardingHandlers[3];
      const downloadIOSClick = () => {
        window.open('https://itunes.apple.com/us/app/polymail/id1082058386?mt=8', '_blank');
      };

      const downloadElectronClick = () => {
        window.open(
          'https://polymail.io/download?download_mac=1&utm_source=webapp',
          '_blank',
        );
      };
      props.buttonHtml = (
        <div className="download-buttons">
          <div className="info-section">
            Download Polymail for:
            {!isElectronMac &&
              <button
                className="download-button"
                onClick={downloadElectronClick}
              >
                <AppleIconWhite height="15px" width="15px" className="icon" />
                Mac
              </button>
            }
            <button
              onClick={downloadIOSClick}
              className="download-button"
            >
              <AppleIconWhite height="15px" width="15px" className="icon" />
              iOS
            </button>
          </div>
        </div>
      );
      props.skipCopy = 'Done';
    }

    if (showMentionsTabOnboarding) {
      props = onboardingHandlers[5];
    }

    if (showInviteTeamOnboarding) {
      props = onboardingHandlers[6];
    }

    if (showCreateDraftOnboarding) {
      props = onboardingHandlers[0];
    }

    if (!props) {
      return null;
    }

    return (
      <OnboardingFooter
        {...props}
      />
    );
  }

  render() {
    const {
      selectedMailbox,
      sessionId,
      mailCount,
      selectedThread,
      draft,
      initialSyncing,
      showExploreModal,
      showTeamInviteJoinedPrompt,
    } = this.props;
    const { confirmProps, blockConfirmProps } = this.state;

    const isInboxZero = mailCount && mailCount.inboxRead + mailCount.inboxUnread === 0
      && !draft
      && !selectedThread
      && !initialSyncing
      && selectedMailbox && !selectedMailbox.query
      && selectedMailbox.type === MailboxType.Inbox;
    // TODO (Cindy & Shin): Figure out better way of connecting container
    const ThreadListConnect = ThreadList as any;
    const LeftSidebarConnect = LeftSidebar as any;

    return (
      <div
        className="threads"
        onKeyDown={this.handleKeyDown}
      >
        <ContactSupport />
        {blockConfirmProps &&
          <BlockConfirm
            {...blockConfirmProps}
          />
        }
        {confirmProps &&
          <Confirm
            {...confirmProps}
          />
        }
        <div className="main-app">
          <LeftSidebarConnect accounts={[]} />
          <ThreadListConnect
            onUndo={this.handleUndo}
          />
          {!isInboxZero &&
            <ThreadDetail
              onThreadDelete={this.handleThreadDelete}
            />
          }
          {isInboxZero && <InboxZero id={sessionId} />}
          <RightSidebar page={Page.Threads} />
          <CommandControl default={CommandControlType.Thread}/>
        </div>
        <Toast />
        {this.renderOnboardingFooter()}
        {showTeamInviteJoinedPrompt && (
          <TeamJoined />
        )}
        {showExploreModal && !showTeamInviteJoinedPrompt &&
          <GettingStarted
            onNext={this.handleStartOnboarding}
          />
        }
      </div>
    );
  }
}

export default Threads;
