import { Message, TeamMember, Team, Address, Attachment, Draft } from '@src/types';
import { AsanaTask } from '@src/types/asana';

export const enum SharedThreadPermissionScope {
  Public = 'public',
  Team = 'team',
  Private = 'private',
}

export interface SharedThreadPermission {
  scope: SharedThreadPermissionScope;
  users?: string[];
  drafts?: boolean;
}

export const enum SharedThreadsMode {
  User = 'user',
  Mentions = 'mentions',
}

export const enum SharedThreadFilterMode {
  Unread = 'unread',
  Archive = 'archive',
  Comments = 'comments',
  New = 'new',
  All = 'all',
}

export interface SharedThreadFilter {
  mode: SharedThreadFilterMode;
  query?: string;
}

export interface SharedThread {
  team: string;
  id: string;
  sort: Date;
  sharedThread?: string;
  user: string;
  account: string;
  thread: string;
  subject: string;
  snippet: string;
  participants: string[];
  participantAddresses: Address[];
  read: boolean;
  lastEntry: string;
  lastAuthor?: string;
  mentioned: string[];
  permissions?: SharedThreadPermission;
  publicUrl: string;
  messageCount: number;
  hasAttachment: boolean;
  archived: boolean;
  commentCount: number;
  ownerCommentCount: number;
  drafts?: string[];
}

export interface SharedTeamInfo {
  members: TeamMember[];
  team: Team;
}

export const enum SharedThreadActivityType {
  View = 'view',
  Comment = 'comment',
  Reply = 'reply',
}

export interface SharedThreadActivityUser {
  firstName: string;
  lastName: string;
  photoUrl: string;
}

export interface SharedThreadActivity {
  type: SharedThreadActivityType;
  ttl?: number;
  sharedThread: string;
  user?: SharedThreadActivityUser;
  draft?: string;
  userId: string;
  timestamp: Date;
}

export interface Comment {
  team: string;
  sharedThread: string;
  id: string;
  created: Date;
  updated: Date;
  user: string;
  lastRfcId: string;
  draft?: string;
  body: string;
  read: boolean;
  attachments: Attachment[];
}

export const enum CellType {
  Comment = 'comment',
  Draft = 'draft',
  Message = 'message',
  Task = 'task',
  Typing = 'typing',
}

export interface Cell {
  type: CellType;
  date: Date;
  data: Comment|Message|AsanaTask|SharedThreadActivity|Draft;
}
