import React from 'react';
import { withRouter } from 'react-router-dom';
import classnames from 'classnames';
import OverviewCell, { OverviewCellType } from './components/OverviewCell';
import PolymailLogo from '@src/routes/Login/Icons/PolymailLogoDarkMode@3x.png';

import './YearInReview.scss';
import { Spring, animated, config } from 'react-spring';
import ReactVisibilitySensor from 'react-visibility-sensor';
import WavesSeparator from './components/WavesSeparator/WavesSeparator';
import { StatYearlySummary } from '@src/api/analytics';
import { Me, Social, SocialNetwork } from '@src/types';
import moment from 'moment';
import SquigglyArrow from './components/Icons/SquigglyArrow.svg';
import CalendarIcon from '@src/routes/Calendar/components/Icons/SchedulingOnboardingIcon.svg';
import ContactCardCell from './components/ContactCardCell/ContactCardCell';
import { findIndex } from 'lodash';
import AwardCell, { AwardCellType } from './components/AwardCell/AwardCell';
import EmptyStatCell from './components/EmptyStatCell/EmptyStatCell';
import { isElectron } from '@src/lib/electron';
import SocialIcon, { IconType } from '@src/components/RightSidebar/components/ContactInfo/components/SocialIcon';

export interface StateProps {
  stats?: StatYearlySummary;
  me?: Me;
  error?: boolean;
}

export interface DispatchProps {
  fetchYearlySummary: () => void;
  readYearlySummary: () => void;
}

interface State {
  backgroundColor: string;
  showInfoSection?: boolean;
  showOverviewSection?: boolean;
  showFavoriteContact?: boolean;
  showSendingStatsSection?: boolean;
  selectedContact?: string;
  showReturnButton?: boolean;
}

class YearInReview extends React.PureComponent<StateProps & DispatchProps, State> {
  constructor() {
    super();
    this.state = { backgroundColor: 'azure' };
  }

  componentDidMount() {
    const { stats, fetchYearlySummary, readYearlySummary } = this.props;
    if (!stats) {
      fetchYearlySummary();
    }

    if (stats && !stats.seen) {
      readYearlySummary();
    }
  }

  componentDidUpdate(oldProps: StateProps) {
    const { stats, readYearlySummary } = this.props;

    if (stats && !stats.seen && stats.seen !== oldProps.stats?.seen) {
      readYearlySummary();
    }
  }

  handleContactHover = (contact: string) => {
    this.setState({ selectedContact: contact });
  }

  handleInfoVisible = (visible: boolean) => {
    if (visible) {
      this.setState({ showInfoSection: true });
    }
  }

  handleOverviewVisible = (visible: boolean) => {
    if (visible) {
      this.setState({ showOverviewSection: true });
    }
  }

  handleFavoriteContact = (visible: boolean) => {
    if (visible) {
      this.setState({ showFavoriteContact: true });
    }
  }

  handleSendingVisible = (visible: boolean) => {
    if (visible) {
      this.setState({ showSendingStatsSection: true });
    }
  }

  handleReturnToApp = (e: React.MouseEvent<HTMLButtonElement>) => {
    e.stopPropagation();
    e.preventDefault();

    window.location.href = window.location.origin;
  }

  renderFavoriteSender = () => {
    const { showFavoriteContact, selectedContact } = this.state;
    const { stats } = this.props;

    if (!stats || !stats.topExchanges) {
      return null;
    }

    const { topExchanges } = stats;

    // Compute Descriptor
    const contactIndex = selectedContact &&
      findIndex(topExchanges, { email: selectedContact }) || 0;
    const fullContact = topExchanges[contactIndex];

    // Position descriptor
    let positionDescriptor = 'the most';

    if (contactIndex === 1) {
      positionDescriptor = 'the 2nd most';
    }

    if (contactIndex === 2) {
      positionDescriptor = 'the 3rd most';
    }

    const description = (
      <div
        className="description"
        key={'descriptor' + fullContact.email}
      >
        {/* tslint:disable-next-line:max-line-length */}
        You exchanged {positionDescriptor} emails with <b>{fullContact.givenName} {fullContact.familyName}</b> this year!
        <br />
        <br />
        {/* tslint:disable-next-line:max-line-length */}
        You sent <b>{fullContact.sent}</b> messages and received <b>{fullContact.received}</b> emails back. What a lucky person! 
      </div>
    );

    // Dont return section if failed
    if (!fullContact) {
      return null;
    }

    // Social copy
    const socialCopy = `My favorite person to email in 2020 was ${fullContact.givenName} ${fullContact.familyName}! We exchanged ${fullContact.total} messages over email this year!`;

    return (
      <ReactVisibilitySensor
            onChange={this.handleFavoriteContact}
          >
          <div className="favorite-sender-section">
            <div className="section-container">
              {this.renderSocialIntent(socialCopy)}
              <div className="copy-column">
                <div className="title">Who did you contact most in 2020?</div>
                  {description}
                </div>
                <div className="senders-column">
                {stats.topExchanges.slice(0, 3).map((c, i) => (
                  <Spring
                    key={c.email}
                    config={config.slow}
                    from={{ opacity: 0, t: 0 }}
                    to={{
                      opacity: showFavoriteContact ? 1 : 0,
                      t: showFavoriteContact ? 1 : 0,
                    }}
                    delay={(i + 1) * 400}
                  >
                    {styles => (
                      <ContactCardCell
                        key={c.email + '-card'}
                        styles={styles}
                        contact={c}
                        onHover={this.handleContactHover}
                        isSelected={selectedContact === c.email}
                      />
                    )}
                  </Spring>
                ))}
                </div>
              </div>
            </div>
          </ReactVisibilitySensor>
    );
  }

  renderOverview = () => {
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    // Calculate time estimate
    const timeEstimate = (stats.messageSent * 11 / 3600).toFixed(1);
    const timeEstimateSpan = (
      <span>
        {/* tslint:disable-next-line:max-line-length */}
        It would have taken the average person <b>{timeEstimate}</b> hours to read through all the emails you received this year!
      </span>
    );

    const socialCopy = `I read ${stats.threadRead} emails and sent ${stats.messageSent} emails in 2020. How many did you send? @Polymail #YearInReview`;

    return (
      <ReactVisibilitySensor
        onChange={this.handleOverviewVisible}
      >
        <div className="overview-section">
          <div className="section-container">
            {this.renderSocialIntent(socialCopy)}
            <div className="row-container">
              <div className="copy-column">
                <div className="title">
                  You've gone through quite a few emails this year!
                </div>
                <div className="description">
                  Great job staying on top of all your important communications!
                  <br />
                  <br />
                  {timeEstimateSpan}
                </div>
              </div>
              <div className="cells">
                <OverviewCell
                  title="Emails Received"
                  description="Number of emails you've read."
                  value={stats.threadRead}
                  type={OverviewCellType.Card}
                />
                <OverviewCell
                  title="Emails Sent"
                  description="Number of emails you've sent."
                  value={stats.messageSent}
                  type={OverviewCellType.Card}
                />
              </div>
            </div>
          </div>
        </div>
      </ReactVisibilitySensor>
    );
  }

  renderUnsubscriberSection = () => {
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    // Do not render collaboration section if no results
    const noResults = stats.filterApplied + stats.filterCreated  === 0;
    if (noResults) {
      return (
        <div className="unsubscriber-section">
        <div className="section-container">
          <img
            src="https://mailer-assets.polymail.io/2019/12-16-end-of-year-report-v2/broom.png"
            className="section-icon"
          />
          <div className="title">
            Blocked Senders
          </div>
          <div className="cells">
            <EmptyStatCell
              awardType={AwardCellType.Blocked}
              isCard
            />
          </div>
        </div>
      </div>
      );
    }

    // Calculate time estimate
    const timeEstimate = (stats.filterApplied * 11 / 3600).toFixed(1);
    const timeEstimateSpan = (
      <span>
        {/* tslint:disable-next-line:max-line-length */}
        You saved yourself <b>{timeEstimate}</b> hours from reading unwated emails through this year!
      </span>
    );

    const socialCopy = `I blocked ${stats.filterCreated} senders and blocked ${stats.filterApplied} emails, saving me ${timeEstimate} hours in 2020! @Polymail #YearInReview`;

    return (
      <div className="unsubscriber-section">
        <div className="section-container">
          {this.renderSocialIntent(socialCopy)}
          <img
            src="https://mailer-assets.polymail.io/2019/12-16-end-of-year-report-v2/broom.png"
            className="section-icon"
          />
          <div className="title">
            Good job being selective with your inbox!
          </div>
          <div className="description">
            {/* tslint:disable-next-line:max-line-length */}
            You saved some time this year by taking the initiative to remove senders you didn't want emails from anymore.
            <br />
            <br />
            {((stats.filterApplied * 11 / 3600) > 0.3) && (
              timeEstimateSpan
            )}
          </div>
            <div className="cells">
              <OverviewCell
                title="Senders Blocked"
                description="Number of senders you've decided to say goodbye to."
                value={stats.filterCreated}
                type={OverviewCellType.Card}
              />
              <OverviewCell
                title="Emails Blocked"
                description="The number of unwanted emails that we prevented from entering your inbox"
                value={stats.filterApplied}
                type={OverviewCellType.Card}
              />
          </div>
        </div>
      </div>
    );
  }

  renderSending = () => {
    const { showSendingStatsSection } = this.state;
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    return (
      <ReactVisibilitySensor
        onChange={this.handleSendingVisible}
      >
        <div className="sending-section">
          <FillerDiv />
          <div className="section-container">
            <div className="title">
              Let's break down the messages you've sent!
            </div>
            <div className="description white">
              {/* tslint:disable-next-line:max-line-length */}
              Great job making sure you sent quality messages this year!
            </div>
            <div className="cells">
                <Spring
                  from={{ opacity: 0 }}
                  to={{ opacity: showSendingStatsSection ? 1 : 0 }}
                >
                  {styles => (
                    <OverviewCell
                      title="Undo Sends"
                      description="Number of times you've saved yourself from a mistake!"
                      value={stats.undoSend}
                      type={OverviewCellType.Card}
                      styles={styles}
                      hasBackground
                    />
                  )}
                </Spring>
                <Spring
                  from={{ opacity: 0 }}
                  to={{ opacity: showSendingStatsSection ? 1 : 0 }}
                  delay={1000}
                >
                  {styles => (
                    <OverviewCell
                      title="Scheduled Emails"
                      description="Great job being considerate of your recipient's time!"
                      value={stats.sentLater}
                      type={OverviewCellType.Card}
                      styles={styles}
                      hasBackground
                    />
                  )}
                </Spring>
                <Spring
                  from={{ opacity: 0 }}
                  to={{ opacity: showSendingStatsSection ? 1 : 0 }}
                  delay={2000}
                >
                  {styles => (
                    <OverviewCell
                      title="Message Templates used"
                      description="How many times you saved time composing an email."
                      value={stats.sentTemplate}
                      type={OverviewCellType.Card}
                      styles={styles}
                      hasBackground
                    />
                  )}
                </Spring>
                <Spring
                  from={{ opacity: 0 }}
                  to={{ opacity: showSendingStatsSection ? 1 : 0 }}
                  delay={3000}
                >
                  {styles => (
                    <OverviewCell
                      title="Follow-ups used"
                      description="Good job making sure you stay on top of your emails!"
                      value={stats.sentFollowUp}
                      type={OverviewCellType.Card}
                      styles={styles}
                      hasBackground
                    />
                  )}
                </Spring>
            </div>
          </div>
          <FillerDiv />
        </div>
      </ReactVisibilitySensor>
    );
  }

  renderSocialIntent = (copy: string) => {
    const urlEncodedText = encodeURIComponent(copy);
    const socials: Social[] = [
      {
        username: '@polymail',
        url: `https://twitter.com/intent/tweet?text=${urlEncodedText}`,
        network: SocialNetwork.Twitter,
      },
    ];

    return (
      <div className="dist-section">
        {socials.map(s => (
          <SocialIcon
            social={s}
            type={IconType.Icon}
            key={`dist-s-${s.network}`}
          />
        ))}
      </div>
    );
  }

  renderOrganizationSection = () => {
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    const organizationString = `How you organized your messages`;

    return (
    <div className="organization-section">
      <div className="section-container">
        <img
          src="https://mailer-assets.polymail.io/2019/12-16-end-of-year-report-v2/folder.png"
          className="section-icon"
        />
        <div className="title">
          This is how you chose to tidy your inbox this year.
        </div>
        <div className="description">
          {organizationString}
        </div>
        <div className="cells">
          <OverviewCell
            title="Archive"
            description="How many emails you sent to the archive."
            value={stats.archive}
            type={OverviewCellType.TextBlock}
          />
          <OverviewCell
            title="Trash"
            description="Emails that you disposed of to never see again"
            value={stats.trash}
            type={OverviewCellType.TextBlock}
          />
          <OverviewCell
            title="Read Later"
            description="Number of emails you've snoozed to read later."
            value={stats.later}
            type={OverviewCellType.TextBlock}
          />
          <OverviewCell
            title="Labeled"
            description="Number of emails you've organized with a label."
            value={stats.list}
            type={OverviewCellType.TextBlock}
          />
        </div>
      </div>
    </div>
    );
  }

  renderCollaborationSection = () => {
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    // Do not render collaboration section if no results
    const noResults = stats.threadShared + stats.commentPosted  === 0;
    if (noResults) {
      return (
        <div className="collaboration-section">
          <div className="section-container">
            <div className="title white">
              Comments & Mentions
            </div>
            <div className="cells">
              <EmptyStatCell
                awardType={AwardCellType.SharedConversations}
              />
            </div>
          </div>
          <div className="skewed-background inbox" />
        </div>
      );
    }

    return (
      <div className="collaboration-section">
        <div className="section-container">
          <div className="title white">
            You were quite collaborative this year!
          </div>
          <div className="description white">
            {/* tslint:disable-next-line:max-line-length */}
            Great job making sure that the proper context was always provided on your important conversations!
          </div>
          <div className="cells">
            <OverviewCell
              title="Conversations Shared"
              description="You made it easy to contribute to your important conversations."
              value={stats.threadShared}
              type={OverviewCellType.Card}
              valueDescriptor="conversations"
              hasBackground
            />
            <OverviewCell
              title="Comments Posted"
              description="Times you didn't have to find context around a conversation"
              value={stats.commentPosted}
              type={OverviewCellType.Card}
              valueDescriptor="comments"
              hasBackground
            />
          </div>
        </div>
        <div className="skewed-background inbox" />
      </div>
    );
  }

  renderSchedulingSection = () => {
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    // Do not render collaboration section if no results
    const noResults = stats.schedulingMeetingCreated === 0;
    if (noResults) {
      return (
        <div className="scheduling-section">
          <div className="section-container">
          <img
            src="https://mailer-assets.polymail.io/2019/12-16-end-of-year-report-v2/calendar.png"
            className="section-icon"
          />
            <div className="title">
              Polymail Meeting Times
            </div>
            <div className="cells">
              <EmptyStatCell
                awardType={AwardCellType.Scheduling}
                isCard
              />
            </div>
          </div>
        </div>
      );
    }

    const socialCopy = `I scheduled ${stats.schedulingMeetingCreated} meetings with @Polymail in 2020! #YearInReview`;

    return (
      <div className="scheduling-section">
        <div className="section-container">
          {this.renderSocialIntent(socialCopy)}
        <img
          src="https://mailer-assets.polymail.io/2019/12-16-end-of-year-report-v2/calendar.png"
          className="section-icon"
        />
          <div className="title">
            Meetings Booked with Your Scheduling Page!
          </div>
          <div className="description">
            {/* tslint:disable-next-line:max-line-length */}
            No issue coordinating times to meet this year thanks to your Polymail Scheduling Page!
          </div>
          <div className="cells">
            <OverviewCell
              title="Meetings Scheduled"
              valueDescriptor="meetings"
              description="Good job getting meetings booked on your calendar!"
              value={stats.schedulingMeetingCreated}
              type={OverviewCellType.Card}
            />
          </div>
        </div>
      </div>
    );
  }

  renderAwardsSection = () => {
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    const allStats = [
      {
        type: AwardCellType.MessageSent,
        value: stats.messageSentPercentile,
      },
      {
        type: AwardCellType.LaterSent,
        value: stats.sendLaterCountPercentile,
      },
      {
        type: AwardCellType.FollowUp,
        value: stats.sendFollowUpCountPercentile,
      },
      {
        type: AwardCellType.Scheduling,
        value: stats.schedulingCountPercentile,
      },
      {
        type: AwardCellType.MessageTemplates,
        value: stats.sendTemplateCountPercentile,
      },
      {
        type: AwardCellType.Blocked,
        value: stats.blockAppliedCountPercentile,
      },
      {
        type: AwardCellType.ThreadRead,
        value: stats.threadReadCountPercentile,
      },
      {
        type: AwardCellType.ReadLater,
        value: stats.readLaterCountPercentile,
      },
      {
        type: AwardCellType.SharedConversations,
        value: stats.threadSharedCountPercentile,
      },
    ];

    const filteredAwards = allStats.filter(s => s.value > 0.8);
    const socialCopy = `I made the power users list in the @Polymail Year In Review for 2020`;

    return (
      <div className="awards-section">
        <div className="section-container">
          {this.renderSocialIntent(socialCopy)}
          <img
            src="https://mailer-assets.polymail.io/2019/12-16-end-of-year-report-v2/trophy.png"
            alt=""
            className="awards-icon"
          />
          <div className="title">
            Accolades you deserve to put up on the fridge!
          </div>
          <div className="description">
            {/* tslint:disable-next-line:max-line-length */}
            Great job on your email this year! You posted some achievements this year!
          </div>
          <div className="cells">
            {filteredAwards.map(a => (
              <AwardCell
                key={`award-type-${a.type}`}
                awardType={a.type}
                awardValue={a.value}
              />
            ))}
          </div>
        </div>
      </div>
    );
  }

  renderEndSection = () => {
    return (
      <div className="end-section">
        <div className="section-container">
          <div className="title">
            Closing 2020
          </div>
          <div className="description">
            {/* tslint:disable-next-line:max-line-length */}
            Congratulations on a productive 2020! We're truly appreciative of all the support you've given us and others this year. We hope 2021 is even more productive and fulfilling for you. If you enjoyed this Year in Review, it'd mean a ton for us if you'd share with your friends or let us know at @Polymail!
            <br />
            <br />
            <div className="sign-off">
              &nbsp;Polymail Team
            </div>
            <FillerDiv />
            <div className="return-section">
              <button
                onClick={this.handleReturnToApp}
                className="return-button"
              >
                Return to Polymail
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  renderBusiestSection = () => {
    const { stats } = this.props;
    if (!stats) {
      return null;
    }

    const socialCopy = `I sent the most messages in ${moment.months()[stats.maxMonth]} (${stats.maxMonthValue} emails) and ${moment.months()[stats.maxDow]} (${stats.maxDowValue} emails) in 2020. @Polymail #YearInReview`;

    return (
      <div className="busiest-section">
        <div className="section-container">
          {this.renderSocialIntent(socialCopy)}
          <CalendarIcon className="calendar-icon" />
          <div className="title white">
            When were you busiest this year?
          </div>
          <div className="description white">
            {/* tslint:disable-next-line:max-line-length */}
            Let's take a look at what your busiest day and months were by how many emails you sent out!
          </div>
          <div className="cells">
            <OverviewCell
              title={moment.months()[stats.maxMonth]}
              description="Congratulations on all the work you got done on this month!"
              value={stats.maxMonthValue}
              type={OverviewCellType.Card}
              valueDescriptor="messages sent"
              hasBackground
            />
            <OverviewCell
              title={moment.weekdays()[stats.maxDow]}
              description="In case someone asks you the best day to be reached"
              value={stats.maxDowValue}
              type={OverviewCellType.Card}
              valueDescriptor="messages sent"
              hasBackground
            />
          </div>
        </div>
        <div className="skewed-background archive" />
      </div>
    );
  }

  renderError = () => {
    return (
      <div
        className={classnames('year-in-review-app')}
      >
        <div
          className="main"
        >
        <div className="error-section">
          <div className="section-container">
            <div className="title">
              We were unable to generate your 2020 Year in Review
            </div>
            <div className="description">
              {/* tslint:disable-next-line:max-line-length */}
              Click the button below to return back to Polymail.
            </div>
            <FillerDiv />
            <div className="return-section">
              <button
                onClick={this.handleReturnToApp}
                className="return-button"
              >
                Return to Polymail
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
    );
  }

  handleScroll = (e: React.UIEvent<HTMLElement>) => {
    const { showReturnButton } = this.state;
    if (e.currentTarget.scrollTop > 100) {
      if (!showReturnButton) {
        this.setState({ showReturnButton: true });
      }
    } else {
      this.setState({ showReturnButton: false });
    }
  }

  render() {
    const { error } = this.props;
    const {
      backgroundColor,
      showReturnButton,
     } = this.state;

    const infoDescription = `With all the crazy things going on in the world, we wanted to take a pause to celebrate all the work you put in to connect and communicate with others. Let's take a look at all that you've gotten done in Polymail this year!`;

    // Handle error case for Year in Review to allow user to return back to the app
    if (!!error) {
      return this.renderError();
    }

    return (
      <div
        className={classnames('year-in-review-app', { [backgroundColor]: true })}
        onScroll={this.handleScroll}
      >
        <button
          className={classnames('top-return-button', {
            show: showReturnButton,
            electron: !!isElectron,
          })}
          onClick={this.handleReturnToApp}
        >
          Return to Polymail
        </button>
        <div
          className="main"
        >
          <div className="hero-section">
            <div className="logo">
              <img src={PolymailLogo} alt="" className=""/>
            </div>
            <Spring
              from={{ opacity: 0 }}
              to={{ opacity: 1 }}
              delay={500}
            >
              {styles => (
                <animated.div
                  className="title"
                  style={styles}
                >
                  2020
                </animated.div>
              )}
            </Spring>
            <Spring
              from={{ opacity: 0 }}
              to={{ opacity: 1 }}
              delay={1500}
            >
            {styles => (
              <animated.div
                className="description"
                style={styles}
              >
                Your Year in Review
              </animated.div>
              )}
            </Spring>
            <WavesSeparator />
          </div>
          <ReactVisibilitySensor
              onChange={this.handleInfoVisible}
          >
          <div className="intro-section">
            <div
              className="title"
            >
              Celebrating Your Work in 2020
            </div>
            <div
              className="description"
            >
              {infoDescription}
            </div>
            <SquigglyArrow className="down-arrow" />
          </div>
          </ReactVisibilitySensor>

          {/* Overview Section */}
          {this.renderOverview()}

          <FillerDiv />
          <FillerDiv />

          {/* Sending Section */}
          {this.renderSending()}

          <FillerDiv />

          {/* Sender Section */}
          {this.renderFavoriteSender()}
          <FillerDiv />

          {/* DOW/MOY Section */}
          {this.renderBusiestSection()}
          <FillerDiv />

          {/* Organization Section */}
          {this.renderOrganizationSection()}
          <FillerDiv />
          <FillerDiv />

          {/* Blocked/Unsubscriber Section */}
          {this.renderUnsubscriberSection()}
          <FillerDiv />

          {/* Scheduling Section */}
          {this.renderSchedulingSection()}
          <FillerDiv />

          {/* Collaboration Section */}
          {this.renderCollaborationSection()}

          {/* Awards Section */}
          {this.renderAwardsSection()}

          <FillerDiv />

          <img
            src="https://mailer-assets.polymail.io/2020/12-29-end-of-year-report/wave.png"
            alt=""
            className="wave-static flipped"
          />

          <FillerDiv />
          {/* Invite to Polymail Section */}
          {this.renderEndSection()}
          <FillerDiv />
        </div>
      </div>
    );
  }
}

